INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(1, 'الا یا ایها الساقی ادر کاسا و ناولها\r\nکه عشق آسان نمود اول ولی افتاد مشکل ها\r\nبه بوی نافه ای کاخر صبا زان طره بگشاید\r\nز تاب جعد مشکینش چه خون افتاد در دل ها\r\nمرا در منزل جانان چه امن عیش چون هر دم\r\nجرس فریاد می دارد که بربندید محمل ها\r\nبه می سجاده رنگین کن گرت پیر مغان گوید\r\nکه سالک بی خبر نبود ز راه و رسم منزل ها\r\nشب تاریک و بیم موج و گردابی چنین هایل\r\nکجا دانند حال ما سبکباران ساحل ها\r\nهمه کارم ز خود کامی به بدنامی کشید آخر\r\nنهان کی ماند آن رازی کز او سازند محفل ها\r\nحضوری گر همی خواهی از او غایب مشو حافظ\r\nمتی ما تلق من تهوی دع الدنیا و اهملها', 'مشکلات تان بزودی حل خواهد شد و شما به نیت خودتان خواهید رسید. بعد از تاریکی و غم، روشنایی در انتظار شماست. خودتان را برای کاری که می خواهید انجام دهید آماده سازید. با توکل به حق و راه و رسم دینداری به کام دلتان می رسید. راز خودتان را به کسی نگویید. '),
(2, 'صلاح کار کجا و من خراب کجا\r\nببین تفاوت ره کز کجاست تا به کجا\r\nدلم ز صومعه بگرفت و خرقه سالوس\r\nکجاست دیر مغان و شراب ناب کجا\r\nچه نسبت است به رندی صلاح و تقوا را\r\nسماع وعظ کجا نغمه رباب کجا\r\nز روی دوست دل دشمنان چه دریابد\r\nچراغ مرده کجا شمع آفتاب کجا\r\nچو کحل بینش ما خاک آستان شماست\r\nکجا رویم بفرما از این جناب کجا\r\nمبین به سیب زنخدان که چاه در راه است\r\nکجا همی روی ای دل بدین شتاب کجا\r\nبشد که یاد خوشش باد روزگار وصال\r\nخود آن کرشمه کجا رفت و آن عتاب کجا\r\nقرار و خواب ز حافظ طمع مدار ای دوست\r\nقرار چیست صبوری کدام و خواب کجا', 'رشته ی کار از دستتان رها شده، برای رسیدن به هدف خواب و رویا را کنار بگذار و تقوی پیشه کن. عجله و شتاب نداشته باش، صبور باش، روزگار وصل نزدیک است. دوست را از دشمن تشخیص بده، گذشته ها را فراموش کن و به روشنایی ها بیندیش. '),
(3, 'اگر آن ترک شیرازی به دست آرد دل ما را\r\nبه خال هندویش بخشم سمرقند و بخارا را\r\nبده ساقی می باقی که در جنت نخواهی یافت\r\nکنار آب رکن آباد و گلگشت مصلا را\r\nفغان کاین لولیان شوخ شیرین کار شهرآشوب\r\nچنان بردند صبر از دل که ترکان خوان یغما را\r\nز عشق ناتمام ما جمال یار مستغنی است\r\nبه آب و رنگ و خال و خط چه حاجت روی زیبا را\r\nمن از آن حسن روزافزون که یوسف داشت دانستم\r\nکه عشق از پرده عصمت برون آرد زلیخا را\r\nاگر دشنام فرمایی و گر نفرین دعا گویم\r\nجواب تلخ می زیبد لب لعل شکرخا را\r\nنصیحت گوش کن جانا که از جان دوست تر دارند\r\nجوانان سعادتمند پند پیر دانا را\r\nحدیث از مطرب و می گو و راز دهر کمتر جو\r\nکه کس نگشود و نگشاید به حکمت این معما را\r\nغزل گفتی و در سفتی بیا و خوش بخوان حافظ\r\nکه بر نظم تو افشاند فلک عقد ثریا را\r\n', 'به قولی که داده ای عمل کن. کار ناتمامت را تمام کن ولی پایت را از گلیمت درازتر نکن چون باعث رسوایی ات می شود. در حق کسی بدی نکن چون نتیجه اش دامنت را می گیرد. نصیحت این پیر دانا را گوش کن تا سعادتمند شوی و گره ی کار تو باز شود. اقبال و ستاره ی تو چون خوشه ی پروین است. '),
(4, 'صبا به لطف بگو آن غزال رعنا را\r\nکه سر به کوه و بیابان تو داده ای ما را\r\nشکرفروش که عمرش دراز باد چرا\r\nتفقدی نکند طوطی شکرخا را\r\nغرور حسنت اجازت مگر نداد ای گل\r\nکه پرسشی نکنی عندلیب شیدا را\r\nبه خلق و لطف توان کرد صید اهل نظر\r\nبه بند و دام نگیرند مرغ دانا را\r\nندانم از چه سبب رنگ آشنایی نیست\r\nسهی قدان سیه چشم ماه سیما را\r\nچو با حبیب نشینی و باده پیمایی\r\nبه یاد دار محبان بادپیما را\r\nجز این قدر نتوان گفت در جمال تو عیب\r\nکه وضع مهر و وفا نیست روی زیبا را\r\nدر آسمان نه عجب گر به گفته حافظ\r\nسرود زهره به رقص آورد مسیحا را\r\n', 'به لطف و عنایت خداوند و با همت بلند به آرزوی دیرین خودتان خواهید رسید. غرور را کنار بگذارید و پیشنهاد خودتان را عنوان کنید. در کارتان مشورت نمائید و امیدتان را از دست ندهید. تکیه بر ظواهر ننمائید. دست به دعا بردارید. با توکل به حق هر کار غیرممکن ممکن می شود. '),
(5, 'دل می رود ز دستم صاحب دلان خدا را\r\nدردا که راز پنهان خواهد شد آشکارا\r\nکشتی شکستگانیم ای باد شرطه برخیز\r\nباشد که بازبینیم دیدار آشنا را\r\nده روزه مهر گردون افسانه است و افسون\r\nنیکی به جای یاران فرصت شمار یارا\r\nدر حلقه گل و مل خوش خواند دوش بلبل\r\nهات الصبوح هبوا یا ایها السکارا\r\nای صاحب کرامت شکرانه سلامت\r\nروزی تفقدی کن درویش بی نوا را\r\nآسایش دو گیتی تفسیر این دو حرف است\r\nبا دوستان مروت با دشمنان مدارا\r\nدر کوی نیک نامی ما را گذر ندادند\r\nگر تو نمی پسندی تغییر کن قضا را\r\nآن تلخ وش که صوفی ام الخبائثش خواند\r\nاشهی لنا و احلی من قبله العذارا\r\nهنگام تنگدستی در عیش کوش و مستی\r\nکاین کیمیای هستی قارون کند گدا را\r\nسرکش مشو که چون شمع از غیرتت بسوزد\r\nدلبر که در کف او موم است سنگ خارا\r\nآیینه سکندر جام می است بنگر\r\nتا بر تو عرضه دارد احوال ملک دارا\r\nخوبان پارسی گو بخشندگان عمرند\r\nساقی بده بشارت رندان پارسا را\r\nحافظ به خود نپوشید این خرقه می آلود\r\nای شیخ پاکدامن معذور دار ما را\r\n', 'کاری را که می خواهید انجام بدهید پنهان کنید. کار را هر چند کوچک است بزرگ بدانید، خیال پردازی را کنار بگذارید و وقت را غنیمت شمارید تا صاحب مقام و منزلت شوید. روزگار تنگدستی به پایان رسیده و زمان آسایش است. عمرتان طولانی است وضعیت فعلی تان تغییر می کند. '),
(6, 'به ملازمان سلطان که رساند این دعا را\r\nکه به شکر پادشاهی ز نظر مران گدا را\r\nز رقیب دیوسیرت به خدای خود پناهم\r\nمگر آن شهاب ثاقب مددی دهد خدا را\r\nمژه سیاهت ار کرد به خون ما اشارت\r\nز فریب او بیندیش و غلط مکن نگارا\r\nدل عالمی بسوزی چو عذار برفروزی\r\nتو از این چه سود داری که نمی کنی مدارا\r\nهمه شب در این امیدم که نسیم صبحگاهی\r\nبه پیام آشنایان بنوازد آشنا را\r\nچه قیامت است جانا که به عاشقان نمودی\r\nدل و جان فدای رویت بنما عذار ما را\r\nبه خدا که جرعه ای ده تو به حافظ سحرخیز\r\nکه دعای صبحگاهی اثری کند شما را\r\n', 'در همه حال شکر خدا را به جا آورید. در این کار رقیبی دارید که باید او را از میدان به در کنید تا به دلربای خودتان برسید. نماز صبح را بجا آورید و دست به دعا بردارید. فکرهای شیطانی را برای رسیدن به مقصود فراموش کنید. '),
(7, 'صوفی بیا که آینه صافیست جام را\r\nتا بنگری صفای می لعل فام را\r\nراز درون پرده ز رندان مست پرس\r\nکاین حال نیست زاهد عالی مقام را\r\nعنقا شکار کس نشود دام بازچین\r\nکان جا همیشه باد به دست است دام را\r\nدر بزم دور یک دو قدح درکش و برو\r\nیعنی طمع مدار وصال دوام را\r\nای دل شباب رفت و نچیدی گلی ز عیش\r\nپیرانه سر مکن هنری ننگ و نام را\r\nدر عیش نقد کوش که چون آبخور نماند\r\nآدم بهشت روضه دارالسلام را\r\nما را بر آستان تو بس حق خدمت است\r\nای خواجه بازبین به ترحم غلام را\r\nحافظ مرید جام می است ای صبا برو\r\nوز بنده بندگی برسان شیخ جام را\r\n', 'دلت را مثل آیینه صاف کن. پرده های تاریک را از رویت کنار بزن تا حقیقت را ببینی آن موقع است که به مقام عالی می رسی. طمع نداشته باش تا عمرت طولانی باشد. بهشت تو آنجاست که حق مردم را به جا آوری. این همان راهیست که تو را به هدف می رساند. '),
(8, 'ساقیا برخیز و درده جام را\r\nخاک بر سر کن غم ایام را\r\nساغر می بر کفم نه تا ز بر\r\nبرکشم این دلق ازرق فام را\r\nگر چه بدنامیست نزد عاقلان\r\nما نمی خواهیم ننگ و نام را\r\nباده درده چند از این باد غرور\r\nخاک بر سر نفس نافرجام را\r\nدود آه سینه نالان من\r\nسوخت این افسردگان خام را\r\nمحرم راز دل شیدای خود\r\nکس نمی بینم ز خاص و عام را\r\nبا دلارامی مرا خاطر خوش است\r\nکز دلم یک باره برد آرام را\r\nننگرد دیگر به سرو اندر چمن\r\nهر که دید آن سرو سیم اندام را\r\nصبر کن حافظ به سختی روز و شب\r\nعاقبت روزی بیابی کام را\r\n', 'یا علی بگو و از جا بلند شو، تلاش کن. تنبلی را کنار بگذار و غم ایام را مخور. مشکلات مادی تو حل می شود. بدنامی دامنت را نمی گیرد. نفس اماره ی خود را از بین ببر و راز دل خود را به کسی نگو. تو به دلارام خودت می رسی. صبر داشته باش و مقاومت کن تا روزی به کام دلت برسی. '),
(9, 'رونق عهد شباب است دگر بستان را\r\nمی رسد مژده گل بلبل خوش الحان را\r\nای صبا گر به جوانان چمن بازرسی\r\nخدمت ما برسان سرو و گل و ریحان را\r\nگر چنین جلوه کند مغبچه باده فروش\r\nخاکروب در میخانه کنم مژگان را\r\nای که بر مه کشی از عنبر سارا چوگان\r\nمضطرب حال مگردان من سرگردان را\r\nترسم این قوم که بر دردکشان می خندند\r\nدر سر کار خرابات کنند ایمان را\r\nیار مردان خدا باش که در کشتی نوح\r\nهست خاکی که به آبی نخرد طوفان را\r\nبرو از خانه گردون به در و نان مطلب\r\nکان سیه کاسه در آخر بکشد مهمان را\r\nهر که را خوابگه آخر مشتی خاک است\r\nگو چه حاجت که به افلاک کشی ایوان را\r\nماه کنعانی من مسند مصر آن تو شد\r\nوقت آن است که بدرود کنی زندان را\r\nحافظا می خور و رندی کن و خوش باش ولی\r\nدام تزویر مکن چون دگران قرآن را\r\n', 'قدر جوانی را بدانید. منتظر خبری خوش باشید که برای آن دست به دعا برداشته اید. از چیزی نترسید، ایمانتان شما را از هر طوفانی نجات می بخشد. به بازوی خودتان متکی باشید. خودتان را از بند مادی رها کنید و به قران توسل جوئید. '),
(10, 'دوش از مسجد سوی میخانه آمد پیر ما\r\nچیست یاران طریقت بعد از این تدبیر ما\r\nما مریدان روی سوی قبله چون آریم چون\r\nروی سوی خانه خمار دارد پیر ما\r\nدر خرابات طریقت ما به هم منزل شویم\r\nکاین چنین رفته ست در عهد ازل تقدیر ما\r\nعقل اگر داند که دل دربند زلفش چون خوش است\r\nعاقلان دیوانه گردند از پی زنجیر ما\r\nروی خوبت آیتی از لطف بر ما کشف کرد\r\nزان زمان جز لطف و خوبی نیست در تفسیر ما\r\nبا دل سنگینت آیا هیچ درگیرد شبی\r\nآه آتشناک و سوز سینه شبگیر ما\r\nتیر آه ما ز گردون بگذرد حافظ خموش\r\nرحم کن بر جان خود پرهیز کن از تیر ما\r\n', 'راهی را که می روید درست است از تدبیر و اندیشه ی خودتان استفاده کنید. به زیارت کعبه مشرف می شوید. به زودی در جمعی حاضر می شوید که تقدیر و آینده ی شما در آنجا رقم زده می شود و به نشانه هایی دست پیدا می کنید که بسیار خوب است. پرهیزگار باشید تا از تیر بلا در امان بمانید. '),
(11, 'ساقی به نور باده برافروز جام ما\r\nمطرب بگو که کار جهان شد به کام ما\r\nما در پیاله عکس رخ یار دیده ایم\r\nای بی خبر ز لذت شرب مدام ما\r\nهرگز نمیرد آن که دلش زنده شد به عشق\r\nثبت است بر جریده عالم دوام ما\r\nچندان بود کرشمه و ناز سهی قدان\r\nکاید به جلوه سرو صنوبرخرام ما\r\nای باد اگر به گلشن احباب بگذری\r\nزنهار عرضه ده بر جانان پیام ما\r\nگو نام ما ز یاد به عمدا چه می بری\r\nخود آید آن که یاد نیاری ز نام ما\r\nمستی به چشم شاهد دلبند ما خوش است\r\nزان رو سپرده اند به مستی زمام ما\r\nترسم که صرفه ای نبرد روز بازخواست\r\nنان حلال شیخ ز آب حرام ما\r\nحافظ ز دیده دانه اشکی همی فشان\r\nباشد که مرغ وصل کند قصد دام ما\r\nدریای اخضر فلک و کشتی هلال\r\nهستند غرق نعمت حاجی قوام ما\r\n', 'دل خویش را به سراب و چیزهای زودگذر خوش نکن. عشق تو را جوان نگه می دارد از آینده نترس چون مثل آب روشن و پاک است. غصه نخور به وصال یار خواهی رسید. به زیارت می روید و به زودی غرق در نعمت می شوید. '),
(12, 'ای فروغ ماه حسن از روی رخشان شما\r\nآب روی خوبی از چاه زنخدان شما\r\nعزم دیدار تو دارد جان بر لب آمده\r\nبازگردد یا برآید چیست فرمان شما\r\nکس به دور نرگست طرفی نبست از عافیت\r\nبه که نفروشند مستوری به مستان شما\r\nبخت خواب آلود ما بیدار خواهد شد مگر\r\nزان که زد بر دیده آبی روی رخشان شما\r\nبا صبا همراه بفرست از رخت گلدسته ای\r\nبو که بویی بشنویم از خاک بستان شما\r\nعمرتان باد و مراد ای ساقیان بزم جم\r\nگر چه جام ما نشد پرمی به دوران شما\r\nدل خرابی می کند دلدار را آگه کنید\r\nزینهار ای دوستان جان من و جان شما\r\nکی دهد دست این غرض یا رب که همدستان شوند\r\nخاطر مجموع ما زلف پریشان شما\r\nدور دار از خاک و خون دامن چو بر ما بگذری\r\nکاندر این ره کشته بسیارند قربان شما\r\nای صبا با ساکنان شهر یزد از ما بگو\r\nکای سر حق ناشناسان گوی چوگان شما\r\nگر چه دوریم از بساط قرب همت دور نیست\r\nبنده شاه شماییم و ثناخوان شما\r\nای شهنشاه بلنداختر خدا را همتی\r\nتا ببوسم همچو اختر خاک ایوان شما\r\nمی کند حافظ دعایی بشنو آمینی بگو\r\nروزی ما باد لعل شکرافشان شما\r\n', 'بخت و اقبال بیدار شده است. خشونت و غضب را از خودتان دور کنید. عده ای به شما کمک می کنند دست آنها را پس نزنید. عاقبت خودتان را به زر نفروشید. ناامیدی باعث پریشانی تان شده است. این گوی و این میدان همت طلب کنید تا خداوند نیز شما را یاری فرماید. '),
(13, 'می دمد صبح و کله بست سحاب\r\nالصبوح الصبوح یا اصحاب\r\nمی چکد ژاله بر رخ لاله\r\nالمدام المدام یا احباب\r\nمی وزد از چمن نسیم بهشت\r\nهان بنوشید دم به دم می ناب\r\nتخت زمرد زده است گل به چمن\r\nراح چون لعل آتشین دریاب\r\nدر میخانه بسته اند دگر\r\nافتتح یا مفتح الابواب\r\nلب و دندانت را حقوق نمک\r\nهست بر جان و سینه های کباب\r\nاین چنین موسمی عجب باشد\r\nکه ببندند میکده به شتاب\r\nبر رخ ساقی پری پیکر\r\nهمچو حافظ بنوش باده ناب\r\n', 'در کارت عجله نداشته باش و اصرار نکن. اگر می خواهی به مقصود برسی باید مشکلات زیادی را پشت سر بگذاری. این در بسته وقتی باز می شود که حقوق دیگران را رعایت کنی. غم خوردن سودی ندارد. با گذشت زمان به مراد خود خواهی رسید. '),
(14, 'گفتم ای سلطان خوبان رحم کن بر این غریب\r\nگفت در دنبال دل ره گم کند مسکین غریب\r\nگفتمش مگذر زمانی گفت معذورم بدار\r\nخانه پروردی چه تاب آرد غم چندین غریب\r\nخفته بر سنجاب شاهی نازنینی را چه غم\r\nگر ز خار و خاره سازد بستر و بالین غریب\r\nای که در زنجیر زلفت جای چندین آشناست\r\nخوش فتاد آن خال مشکین بر رخ رنگین غریب\r\nمی نماید عکس می در رنگ روی مه وشت\r\nهمچو برگ ارغوان بر صفحه نسرین غریب\r\nبس غریب افتاده است آن مور خط گرد رخت\r\nگر چه نبود در نگارستان خط مشکین غریب\r\nگفتم ای شام غریبان طره شبرنگ تو\r\nدر سحرگاهان حذر کن چون بنالد این غریب\r\nگفت حافظ آشنایان در مقام حیرتند\r\nدور نبود گر نشیند خسته و مسکین غریب\r\n', 'ناشکری نکن فقط به فکر خودت نباش به دیگران هم نگاه کن. از بلاتکلیفی نجات خواهی یافت. دوستان و آشنایان کمکت می کنند. کمی طاقت بیاور. فکر نکن که تنهایی، خدا با توست. تو به مقامی می رسی که همگان حیرت می کنند و این زمان دور نیست. '),
(15, 'ای شاهد قدسی که کشد بند نقابت\r\nو ای مرغ بهشتی که دهد دانه و آبت\r\nخوابم بشد از دیده در این فکر جگرسوز\r\nکاغوش که شد منزل آسایش و خوابت\r\nدرویش نمی پرسی و ترسم که نباشد\r\nاندیشه آمرزش و پروای ثوابت\r\nراه دل عشاق زد آن چشم خماری\r\nپیداست از این شیوه که مست است شرابت\r\nتیری که زدی بر دلم از غمزه خطا رفت\r\nتا باز چه اندیشه کند رای صوابت\r\nهر ناله و فریاد که کردم نشنیدی\r\nپیداست نگارا که بلند است جنابت\r\nدور است سر آب از این بادیه هش دار\r\nتا غول بیابان نفریبد به سرابت\r\nتا در ره پیری به چه آیین روی ای دل\r\nباری به غلط صرف شد ایام شبابت\r\nای قصر دل افروز که منزلگه انسی\r\nیا رب مکناد آفت ایام خرابت\r\nحافظ نه غلامیست که از خواجه گریزد\r\nصلحی کن و بازآ که خرابم ز عتابت\r\n', 'از در آشتی درآی، وگرنه ایام جوانیت خراب می شود. کاری نکن که در زمان پیری افسوس جوانی را بخوری. در همه حال به خدا پناه ببر. مواظب باش در راهی که قدم گذاشته ای به خطا نروی. به هشدار پیران گوش کن. فریب ظواهر را نخور و فقط بنده ی خداوند باش. '),
(16, 'خمی که ابروی شوخ تو در کمان انداخت\r\nبه قصد جان من زار ناتوان انداخت\r\nنبود نقش دو عالم که رنگ الفت بود\r\nزمانه طرح محبت نه این زمان انداخت\r\nبه یک کرشمه که نرگس به خودفروشی کرد\r\nفریب چشم تو صد فتنه در جهان انداخت\r\nشراب خورده و خوی کرده می روی به چمن\r\nکه آب روی تو آتش در ارغوان انداخت\r\nبه بزمگاه چمن دوش مست بگذشتم\r\nچو از دهان توام غنچه در گمان انداخت\r\nبنفشه طره مفتول خود گره می زد\r\nصبا حکایت زلف تو در میان انداخت\r\nز شرم آن که به روی تو نسبتش کردم\r\nسمن به دست صبا خاک در دهان انداخت\r\nمن از ورع می و مطرب ندیدمی زین پیش\r\nهوای مغبچگانم در این و آن انداخت\r\nکنون به آب می لعل خرقه می شویم\r\nنصیبه ازل از خود نمی توان انداخت\r\nمگر گشایش حافظ در این خرابی بود\r\nکه بخشش ازلش در می مغان انداخت\r\nجهان به کام من اکنون شود که دور زمان\r\nمرا به بندگی خواجه جهان انداخت\r\n', 'هوشیار باش رقیبان می خواهند تو را خوار کنند و از در الفت وارد شده اند و طرح محبت با تو خواهند ریخت. فریب آنها را نخور. خودت گره ها را باز کن. از چیزی خجالت نکش به سفری کوتاه می روی و در این سفر گشایشی در کارت می شود و تو به کام دل خود می رسی و در آن زمان بندگی خداوند را فراموش نکن. '),
(17, 'سینه از آتش دل در غم جانانه بسوخت\r\nآتشی بود در این خانه که کاشانه بسوخت\r\nتنم از واسطه دوری دلبر بگداخت\r\nجانم از آتش مهر رخ جانانه بسوخت\r\nسوز دل بین که ز بس آتش اشکم دل شمع\r\nدوش بر من ز سر مهر چو پروانه بسوخت\r\nآشنایی نه غریب است که دلسوز من است\r\nچون من از خویش برفتم دل بیگانه بسوخت\r\nخرقه زهد مرا آب خرابات ببرد\r\nخانه عقل مرا آتش میخانه بسوخت\r\nچون پیاله دلم از توبه که کردم بشکست\r\nهمچو لاله جگرم بی می و خمخانه بسوخت\r\nماجرا کم کن و بازآ که مرا مردم چشم\r\nخرقه از سر به درآورد و به شکرانه بسوخت\r\nترک افسانه بگو حافظ و می نوش دمی\r\nکه نخفتیم شب و شمع به افسانه بسوخت\r\n', 'به هر کس وفا کردی جفا دیده ای. سینه ای مالامال از درد داری. تا کی می خواهی پریشان حال باشی. دست از غم بردار. توبه کن تا دلت صاف شود. انتظارات بیجا را کنار بگذار. رویا و خیال را فراموش کن و به واقعیات بپرداز و از آنچه تا کنون نصیبت شده لذت ببر و عقل را به پای دل هدر نده.  '),
(18, 'ساقیا آمدن عید مبارک بادت\r\nوان مواعید که کردی مرواد از یادت\r\nدر شگفتم که در این مدت ایام فراق\r\nبرگرفتی ز حریفان دل و دل می دادت\r\nبرسان بندگی دختر رز گو به درآی\r\nکه دم و همت ما کرد ز بند آزادت\r\nشادی مجلسیان در قدم و مقدم توست\r\nجای غم باد مر آن دل که نخواهد شادت\r\nشکر ایزد که ز تاراج خزان رخنه نیافت\r\nبوستان سمن و سرو و گل و شمشادت\r\nچشم بد دور کز آن تفرقه ات بازآورد\r\nطالع نامور و دولت مادرزادت\r\nحافظ از دست مده دولت این کشتی نوح\r\nور نه طوفان حوادث ببرد بنیادت\r\n', 'به عهدی که بسته ای وفا کن تا روزگارت مثل عید همیشه خوش و بهاری باشد. غم از دلت بیرون می رود و جای آن را شادی می گیرد. همت داشته باش. طالع تو بسیار روشن است. شکر خدا را بجا بیاور زیرا خزان به زندگی تو راه ندارد. و از این فرصتی که به دست آورده ای نهایت استفاده را ببر. '),
(19, 'ای نسیم سحر آرامگه یار کجاست\r\nمنزل آن مه عاشق کش عیار کجاست\r\nشب تار است و ره وادی ایمن در پیش\r\nآتش طور کجا موعد دیدار کجاست\r\nهر که آمد به جهان نقش خرابی دارد\r\nدر خرابات بگویید که هشیار کجاست\r\nآن کس است اهل بشارت که اشارت داند\r\nنکته ها هست بسی محرم اسرار کجاست\r\nهر سر موی مرا با تو هزاران کار است\r\nما کجاییم و ملامت گر بی کار کجاست\r\nبازپرسید ز گیسوی شکن در شکنش\r\nکاین دل غمزده سرگشته گرفتار کجاست\r\nعقل دیوانه شد آن سلسله مشکین کو\r\nدل ز ما گوشه گرفت ابروی دلدار کجاست\r\nساقی و مطرب و می جمله مهیاست ولی\r\nعیش بی یار مهیا نشود یار کجاست\r\nحافظ از باد خزان در چمن دهر مرنج\r\nفکر معقول بفرما گل بی خار کجاست\r\n', 'گم کرده ی خود را پیدا می کنی. وعده ی دیدار نزدیک است. هوشیار باش. خبر خوبی به تو خواهند داد. با مشکلات بجنگ و از بخت خود گله نداشته باش بعد از هر تاریکی روشنایی هست . سعی کن عقلت بر قلبت غلبه داشته باشد. با این حال فکر نکن همه ی خوبی ها در تو جمع شده و عاری از هرگونه بدی هستی.  '),
(20, 'روزه یک سو شد و عید آمد و دل ها برخاست\r\nمی ز خمخانه به جوش آمد و می باید خواست\r\nنوبه زهدفروشان گران جان بگذشت\r\nوقت رندی و طرب کردن رندان پیداست\r\nچه ملامت بود آن را که چنین باده خورد\r\nاین چه عیب است بدین بی خردی وین چه خطاست\r\nباده نوشی که در او روی و ریایی نبود\r\nبهتر از زهدفروشی که در او روی و ریاست\r\nما نه رندان ریاییم و حریفان نفاق\r\nآن که او عالم سر است بدین حال گواست\r\nفرض ایزد بگذاریم و به کس بد نکنیم\r\nوان چه گویند روا نیست نگوییم رواست\r\nچه شود گر من و تو چند قدح باده خوریم\r\nباده از خون رزان است نه از خون شماست\r\nاین چه عیب است کز آن عیب خلل خواهد بود\r\nور بود نیز چه شد مردم بی عیب کجاست\r\n', 'خودت را به دست خداوند بسپار. اینقدر از روزگار شکایت نکن و در مقابل خواسته ی خدا ایستادگی نکن وگرنه سرگردان می شوی. خدا خود بر احوال دل همه آگاه است پس گله نداشته باش. خدا را از یاد نبر چون در همه حال اوست که راهنمای توست. '),
(21, 'دل و دینم شد و دلبر به ملامت برخاست\r\nگفت با ما منشین کز تو سلامت برخاست\r\nکه شنیدی که در این بزم دمی خوش بنشست\r\nکه نه در آخر صحبت به ندامت برخاست\r\nشمع اگر زان لب خندان به زبان لافی زد\r\nپیش عشاق تو شب ها به غرامت برخاست\r\nدر چمن باد بهاری ز کنار گل و سرو\r\nبه هواداری آن عارض و قامت برخاست\r\nمست بگذشتی و از خلوتیان ملکوت\r\nبه تماشای تو آشوب قیامت برخاست\r\nپیش رفتار تو پا برنگرفت از خجلت\r\nسرو سرکش که به ناز از قد و قامت برخاست\r\nحافظ این خرقه بینداز مگر جان ببری\r\nکاتش از خرقه سالوس و کرامت برخاست\r\n', 'ایمانت را قوی تر کن تا از رنج ملامت نجات یابی. گرفتاریت برطرف می شود. اگر در عین غمگینی خندیدی شاهکار کرده ای. کسانی که طرفداری تو را می کنند و از آشوب و غوغا نجاتت می دهند. تغییری در خود و افکارت بده. بدی ها را آتش بزن و بخشندگی را در خود پرورش بده. '),
(22, 'چو بشنوی سخن اهل دل مگو که خطاست\r\nسخن شناس نه ای جان من خطا این جاست\r\nسرم به دنیی و عقبی فرو نمی آید\r\nتبارک الله از این فتنه ها که در سر ماست\r\nدر اندرون من خسته دل ندانم کیست\r\nکه من خموشم و او در فغان و در غوغاست\r\nدلم ز پرده برون شد کجایی ای مطرب\r\nبنال هان که از این پرده کار ما به نواست\r\nمرا به کار جهان هرگز التفات نبود\r\nرخ تو در نظر من چنین خوشش آراست\r\nنخفته ام ز خیالی که می پزد دل من\r\nخمار صدشبه دارم شرابخانه کجاست\r\nچنین که صومعه آلوده شد ز خون دلم\r\nگرم به باده بشویید حق به دست شماست\r\nاز آن به دیر مغانم عزیز می دارند\r\nکه آتشی که نمیرد همیشه در دل ماست\r\nچه ساز بود که در پرده می زد آن مطرب\r\nکه رفت عمر و هنوزم دماغ پر ز هواست\r\nندای عشق تو دیشب در اندرون دادند\r\nفضای سینه حافظ هنوز پر ز صداست\r\n', 'غرور نداشته باش با اهل فن و نظر مشورت کن تا از خطاها به دور باشی. احساس خستگی می کنی و درونت غوغاست به ائمه متوسل شو تا به حقیقت برسی. تو را با دنیای فانی کاری نیست پس به باقیات برس و سینه ات را با یاد خدا پر کن تا به وصال یار برسی. '),
(23, 'خیال روی تو در هر طریق همره ماست\r\nنسیم موی تو پیوند جان آگه ماست\r\nبه رغم مدعیانی که منع عشق کنند\r\nجمال چهره تو حجت موجه ماست\r\nببین که سیب زنخدان تو چه می گوید\r\nهزار یوسف مصری فتاده در چه ماست\r\nاگر به زلف دراز تو دست ما نرسد\r\nگناه بخت پریشان و دست کوته ماست\r\nبه حاجب در خلوت سرای خاص بگو\r\nفلان ز گوشه نشینان خاک درگه ماست\r\nبه صورت از نظر ما اگر چه محجوب است\r\nهمیشه در نظر خاطر مرفه ماست\r\nاگر به سالی حافظ دری زند بگشای\r\nکه سال هاست که مشتاق روی چون مه ماست\r\n', 'با خیال او زندگی می کنی. مدعی هستی که عاشق شده ای اما می دانی که برای رسیدن به وصال یار باید صبر داشته باشی. با گوشه نشینی کار به جایی نمی بری. تلاش کن، هر چند که سال ها طول بکشد. ولی عاقبت به مراد خودت می رسی. '),
(24, 'مطلب طاعت و پیمان و صلاح از من مست\r\nکه به پیمانه کشی شهره شدم روز الست\r\nمن همان دم که وضو ساختم از چشمه عشق\r\nچارتکبیر زدم یک سره بر هر چه که هست\r\nمی بده تا دهمت آگهی از سر قضا\r\nکه به روی که شدم عاشق و از بوی که مست\r\nکمر کوه کم است از کمر مور این جا\r\nناامید از در رحمت مشو ای باده پرست\r\nبجز آن نرگس مستانه که چشمش مرساد\r\nزیر این طارم فیروزه کسی خوش ننشست\r\nجان فدای دهنش باد که در باغ نظر\r\nچمن آرای جهان خوشتر از این غنچه نبست\r\nحافظ از دولت عشق تو سلیمانی شد\r\nیعنی از وصل تواش نیست بجز باد به دست\r\n', 'صلاح در این است که فعلا به عهدی که بسته ای عمل کنی. درست است که پشیمان شده ای اما خود را به دست سرنوشت سپرده ای. رحمت خداوند شامل حالت می شود و اقبال بلند و ثروت زیادی به تو می بخشد. اما مواظب باش با وزیدن باد غفلت زود همه چیز را از دست ندهی. '),
(25, 'شکفته شد گل حمرا و گشت بلبل مست\r\nصلای سرخوشی ای صوفیان باده پرست\r\nاساس توبه که در محکمی چو سنگ نمود\r\nببین که جام زجاجی چه طرفه اش بشکست\r\nبیار باده که در بارگاه استغنا\r\nچه پاسبان و چه سلطان چه هوشیار و چه مست\r\nاز این رباط دودر چون ضرورت است رحیل\r\nرواق و طاق معیشت چه سربلند و چه پست\r\nمقام عیش میسر نمی شود بی رنج\r\nبلی به حکم بلا بسته اند عهد الست\r\nبه هست و نیست مرنجان ضمیر و خوش می باش\r\nکه نیستیست سرانجام هر کمال که هست\r\nشکوه آصفی و اسب باد و منطق طیر\r\nبه باد رفت و از او خواجه هیچ طرف نبست\r\nبه بال و پر مرو از ره که تیر پرتابی\r\nهوا گرفت زمانی ولی به خاک نشست\r\nزبان کلک تو حافظ چه شکر آن گوید\r\nکه گفته سخنت می برند دست به دست\r\n', 'به سهولت و راحتی به مقام و مادیات نخواهی رسید. همه چیزت باید در حد کمال باشد. باطن خوب، کار حلال، هوشیاری و تقوی، در این صورت است که به هدف خواهی رسید و به هر مقام و منزلتی که رسیدی سر تعظیم در برابر خداوند فرود آر وگرنه باز همه چیز را از دست خواهی داد. '),
(26, 'زلف آشفته و خوی کرده و خندان لب و مست\r\nپیرهن چاک و غزل خوان و صراحی در دست\r\nنرگسش عربده جوی و لبش افسوس کنان\r\nنیم شب دوش به بالین من آمد بنشست\r\nسر فرا گوش من آورد به آواز حزین\r\nگفت ای عاشق دیرینه من خوابت هست\r\nعاشقی را که چنین باده شبگیر دهند\r\nکافر عشق بود گر نشود باده پرست\r\nبرو ای زاهد و بر دردکشان خرده مگیر\r\nکه ندادند جز این تحفه به ما روز الست\r\nآن چه او ریخت به پیمانه ما نوشیدیم\r\nاگر از خمر بهشت است وگر باده مست\r\nخنده جام می و زلف گره گیر نگار\r\nای بسا توبه که چون توبه حافظ بشکست\r\n', 'چرا کاری را که به نفع تو نیست انجام می دهی و چیزی را که ارزشی ندارد به بهای گزاف می خواهی به دست آوری. گره کارت به دست خودت باز می شود. پس منتظر غیب منشین. متکی به بازوی خودت باش تا به مراد خود برسی. '),
(27, 'در دیر مغان آمد یارم قدحی در دست\r\nمست از می و میخواران از نرگس مستش مست\r\nدر نعل سمند او شکل مه نو پیدا\r\nوز قد بلند او بالای صنوبر پست\r\nآخر به چه گویم هست از خود خبرم چون نیست\r\nوز بهر چه گویم نیست با وی نظرم چون هست\r\nشمع دل دمسازم بنشست چو او برخاست\r\nو افغان ز نظربازان برخاست چو او بنشست\r\nگر غالیه خوش بو شد در گیسوی او پیچید\r\nور وسمه کمانکش گشت در ابروی او پیوست\r\nبازآی که بازآید عمر شده حافظ\r\nهر چند که ناید باز تیری که بشد از شست\r\n', 'از خواب غفلت بیدار شو. دوباره شروع کن و ناامید نباش. دلت را با یاد خدا جلا بده تا شیطان از تو دور شود. کاری را می خواهی انجام بدهی که نتیجه ی بسیار خوبی دارد. لطف دیگران شامل حالت می شود و ابرهای تیره را از آسمان دلت کنار می زند. '),
(28, 'به جان خواجه و حق قدیم و عهد درست\r\nکه مونس دم صبحم دعای دولت توست\r\nسرشک من که ز طوفان نوح دست برد\r\nز لوح سینه نیارست نقش مهر تو شست\r\nبکن معامله ای وین دل شکسته بخر\r\nکه با شکستگی ارزد به صد هزار درست\r\nزبان مور به آصف دراز گشت و رواست\r\nکه خواجه خاتم جم یاوه کرد و بازنجست\r\nدلا طمع مبر از لطف بی نهایت دوست\r\nچو لاف عشق زدی سر بباز چابک و چست\r\nبه صدق کوش که خورشید زاید از نفست\r\nکه از دروغ سیه روی گشت صبح نخست\r\nشدم ز دست تو شیدای کوه و دشت و هنوز\r\nنمی کنی به ترحم نطاق سلسله سست\r\nمرنج حافظ و از دلبران حفاظ مجوی\r\nگناه باغ چه باشد چو این گیاه نرست\r\n', 'افسوس ایام گذشته سودی ندارد. به آینده فکر کن. معامله ای می کنی که بسیار سودمند است. ولی طمع نکن. صداقت داشته باش و حقیقت را بگو. کار امروز را به فردا نینداز. از یار خود رنجیده خاطر نباش. دروغ و بدی ها را فراموش کن تا خورشید عشق به رویت لبخند بزند. '),
(29, 'ما را ز خیال تو چه پروای شراب است\r\nخم گو سر خود گیر که خمخانه خراب است\r\nگر خمر بهشت است بریزید که بی دوست\r\nهر شربت عذبم که دهی عین عذاب است\r\nافسوس که شد دلبر و در دیده گریان\r\nتحریر خیال خط او نقش بر آب است\r\nبیدار شو ای دیده که ایمن نتوان بود\r\nزین سیل دمادم که در این منزل خواب است\r\nمعشوق عیان می گذرد بر تو ولیکن\r\nاغیار همی بیند از آن بسته نقاب است\r\nگل بر رخ رنگین تو تا لطف عرق دید\r\nدر آتش شوق از غم دل غرق گلاب است\r\nسبز است در و دشت بیا تا نگذاریم\r\nدست از سر آبی که جهان جمله سراب است\r\nدر کنج دماغم مطلب جای نصیحت\r\nکاین گوشه پر از زمزمه چنگ و رباب است\r\nحافظ چه شد ار عاشق و رند است و نظرباز\r\nبس طور عجب لازم ایام شباب است\r\n', 'حوادث زیادی در آینده برایت اتفاق می افتد پس بیدار و هوشیار باش. برای رسیدن به خوشی آینده، روزگار امروزت را خراب نکن. از اینکه بعضی ها همه چیز دارند و تو نداری ناراحت نباش زیرا تو گوهری داری که بقیه از داشتن آن محرومند. دنیا سرابی بیش نیست حتی سبزه و گل و دشت چند صباحی می مانند. تکیه بر این عالم فانی نکن. '),
(30, 'زلفت هزار دل به یکی تار مو ببست\r\nراه هزار چاره گر از چار سو ببست\r\nتا عاشقان به بوی نسیمش دهند جان\r\nبگشود نافه ای و در آرزو ببست\r\nشیدا از آن شدم که نگارم چو ماه نو\r\nابرو نمود و جلوه گری کرد و رو ببست\r\nساقی به چند رنگ می اندر پیاله ریخت\r\nاین نقش ها نگر که چه خوش در کدو ببست\r\nیا رب چه غمزه کرد صراحی که خون خم\r\nبا نعره های قلقلش اندر گلو ببست\r\nمطرب چه پرده ساخت که در پرده سماع\r\nبر اهل وجد و حال در های و هو ببست\r\nحافظ هر آن که عشق نورزید و وصل خواست\r\nاحرام طوف کعبه دل بی وضو ببست\r\n', 'چرا ناامید شده ای هزاران راه چاره وجود دارد. برای رسیدن به آرزویت تلاش کن. مبنای عشق را هوس نگذار چه بسا که شیطان راهبر تو باشد. اگر برای رسیدن به معشوق عاشق نباشی و با جمال ظاهر وصال طلبی جز دردسر عایدت نخواهد شد. '),
(31, 'آن شب قدری که گویند اهل خلوت امشب است\r\nیا رب این تاثیر دولت در کدامین کوکب است\r\nتا به گیسوی تو دست ناسزایان کم رسد\r\nهر دلی از حلقه ای در ذکر یارب یارب است\r\nکشته چاه زنخدان توام کز هر طرف\r\nصد هزارش گردن جان زیر طوق غبغب است\r\nشهسوار من که مه آیینه دار روی اوست\r\nتاج خورشید بلندش خاک نعل مرکب است\r\nعکس خوی بر عارضش بین کآفتاب گرم رو\r\nدر هوای آن عرق تا هست هر روزش تب است\r\nمن نخواهم کرد ترک لعل یار و جام می\r\nزاهدان معذور داریدم که اینم مذهب است\r\nاندر آن ساعت که بر پشت صبا بندند زین\r\nبا سلیمان چون برانم من که مورم مرکب است\r\nآن که ناوک بر دل من زیر چشمی می زند\r\nقوت جان حافظش در خنده زیر لب است\r\nآب حیوانش ز منقار بلاغت می چکد\r\nزاغ کلک من به نام ایزد چه عالی مشرب است\r\n', 'ستاره ی اقبال تو می درخشد. در راهی که قدم گذاشته ای پیروز می شوی و به مقام عالی می رسی در این زمان از یاد خدا غافل مشو. هر چند به پای قارون نمی رسی. همین بس که در عشق ورزیدن و رسیدن به کمال سعی و اهتمام می کنی. '),
(32, 'خدا چو صورت ابروی دلگشای تو بست\r\nگشاد کار من اندر کرشمه های تو بست\r\nمرا و سرو چمن را به خاک راه نشاند\r\nزمانه تا قصب نرگس قبای تو بست\r\nز کار ما و دل غنچه صد گره بگشود\r\nنسیم گل چو دل اندر پی هوای تو بست\r\nمرا به بند تو دوران چرخ راضی کرد\r\nولی چه سود که سررشته در رضای تو بست\r\nچو نافه بر دل مسکین من گره مفکن\r\nکه عهد با سر زلف گره گشای تو بست\r\nتو خود وصال دگر بودی ای نسیم وصال\r\nخطا نگر که دل امید در وفای تو بست\r\nز دست جور تو گفتم ز شهر خواهم رفت\r\nبه خنده گفت که حافظ برو که پای تو بست\r\n', 'به مراد دل خود می رسی و این از برای دعاهای سحریت می باشد. فکر نکن همه کارها را تنها انجام داده ای بلکه بقیه نیز سبب ساز بوده اند تا بتوانی به مقصود برسی. بر عهد خود وفادار بمان. روزگار بر وفق مراد توست پس رضای خداوند را هم در نظر بگیر و تو هم برای دیگران سبب ساز باش. '),
(33, 'خلوت گزیده را به تماشا چه حاجت است\r\nچون کوی دوست هست به صحرا چه حاجت است\r\nجانا به حاجتی که تو را هست با خدا\r\nکآخر دمی بپرس که ما را چه حاجت است\r\nای پادشاه حسن خدا را بسوختیم\r\nآخر سوال کن که گدا را چه حاجت است\r\nارباب حاجتیم و زبان سوال نیست\r\nدر حضرت کریم تمنا چه حاجت است\r\nمحتاج قصه نیست گرت قصد خون ماست\r\nچون رخت از آن توست به یغما چه حاجت است\r\nجام جهان نماست ضمیر منیر دوست\r\nاظهار احتیاج خود آن جا چه حاجت است\r\nآن شد که بار منت ملاح بردمی\r\nگوهر چو دست داد به دریا چه حاجت است\r\nای مدعی برو که مرا با تو کار نیست\r\nاحباب حاضرند به اعدا چه حاجت است\r\nای عاشق گدا چو لب روح بخش یار\r\nمی داندت وظیفه تقاضا چه حاجت است\r\nحافظ تو ختم کن که هنر خود عیان شود\r\nبا مدعی نزاع و محاکا چه حاجت است\r\n', 'چرا فکر می کنی کسی اندوه تو را نمی داند. خیال نکن که کسی ناله ها و آه های تو را درک نمی کند. از یاد خدا غافل نشو که تمام احوالات تو واقف است. گوهری را که تو در پی اش هستی حتی در عمق ترین دریاها که باشد به تو می دهد. از تو حرکت از خدا برکت. '),
(34, 'رواق منظر چشم من آشیانه توست\r\nکرم نما و فرود آ که خانه خانه توست\r\nبه لطف خال و خط از عارفان ربودی دل\r\nلطیفه های عجب زیر دام و دانه توست\r\nدلت به وصل گل ای بلبل صبا خوش باد\r\nکه در چمن همه گلبانگ عاشقانه توست\r\nعلاج ضعف دل ما به لب حوالت کن\r\nکه این مفرح یاقوت در خزانه توست\r\nبه تن مقصرم از دولت ملازمتت\r\nولی خلاصه جان خاک آستانه توست\r\nمن آن نیم که دهم نقد دل به هر شوخی\r\nدر خزانه به مهر تو و نشانه توست\r\nتو خود چه لعبتی ای شهسوار شیرین کار\r\nکه توسنی چو فلک رام تازیانه توست\r\nچه جای من که بلغزد سپهر شعبده باز\r\nاز این حیل که در انبانه بهانه توست\r\nسرود مجلست اکنون فلک به رقص آرد\r\nکه شعر حافظ شیرین سخن ترانه توست\r\n', 'تو مقبول درگاه خداوندی و رحمت او شامل حالت می باشد. دولت و عزت نصیب تو شده و به لطف خداوند است که به ثروت می رسی. کاری را که می خواهی انجام بدهی بسیار سودمند است اما مواظب باش، خدا را فراموش نکنی که هر چه داری از اوست. همه در مقابلت هیچ اند پس از قدرت خود بصورت تازیانه استفاده نکن. '),
(35, 'برو به کار خود ای واعظ این چه فریادست\r\nمرا فتاد دل از ره تو را چه افتادست\r\nمیان او که خدا آفریده است از هیچ\r\nدقیقه ایست که هیچ آفریده نگشادست\r\nبه کام تا نرساند مرا لبش چون نای\r\nنصیحت همه عالم به گوش من بادست\r\nگدای کوی تو از هشت خلد مستغنیست\r\nاسیر عشق تو از هر دو عالم آزادست\r\nاگر چه مستی عشقم خراب کرد ولی\r\nاساس هستی من زان خراب آبادست\r\nدلا منال ز بیداد و جور یار که یار\r\nتو را نصیب همین کرد و این از آن دادست\r\nبرو فسانه مخوان و فسون مدم حافظ\r\nکز این فسانه و افسون مرا بسی یادست\r\n', 'دست به هر چه بزنی طلا می شود. کسب و کارت رونق می گیرد. عجله کن وقت دارد می گذرد، دقیقه ها و ثانیه ها هم باید برایت مهم باشند. از دو دلی خلاصی خواهی یافت و راه درست برایت روشن می شود. از جور زمانه ناله نکن که از این به بعد همه چیز نصیبت می شود. '),
(36, 'تا سر زلف تو در دست نسیم افتادست\r\nدل سودازده از غصه دو نیم افتادست\r\nچشم جادوی تو خود عین سواد سحر است\r\nلیکن این هست که این نسخه سقیم افتادست\r\nدر خم زلف تو آن خال سیه دانی چیست\r\nنقطه دوده که در حلقه جیم افتادست\r\nزلف مشکین تو در گلشن فردوس عذار\r\nچیست طاووس که در باغ نعیم افتادست\r\nدل من در هوس روی تو ای مونس جان\r\nخاک راهیست که در دست نسیم افتادست\r\nهمچو گرد این تن خاکی نتواند برخاست\r\nاز سر کوی تو زان رو که عظیم افتادست\r\nسایه قد تو بر قالبم ای عیسی دم\r\nعکس روحیست که بر عظم رمیم افتادست\r\nآن که جز کعبه مقامش نبد از یاد لبت\r\nبر در میکده دیدم که مقیم افتادست\r\nحافظ گمشده را با غمت ای یار عزیز\r\nاتحادیست که در عهد قدیم افتادست\r\n', 'اجر ثوابی را که کرده اید زود خواهید دید. نشانه هایی از یار هویدا شده برای رسیدن به او جور خواهید کشید. بیماریتان خیلی زود درمان می شود نگران نباشید. به سفر زیارتی می روید و در آنجا گمشده ی دل خودتان را می یابید و با خدای خودتان پیمان می بندید. '),
(37, 'بیا که قصر امل سخت سست بنیادست\r\nبیار باده که بنیاد عمر بر بادست\r\nغلام همت آنم که زیر چرخ کبود\r\nز هر چه رنگ تعلق پذیرد آزادست\r\nچه گویمت که به میخانه دوش مست و خراب\r\nسروش عالم غیبم چه مژده ها دادست\r\nکه ای بلندنظر شاهباز سدره نشین\r\nنشیمن تو نه این کنج محنت آبادست\r\nتو را ز کنگره عرش می زنند صفیر\r\nندانمت که در این دامگه چه افتادست\r\nنصیحتی کنمت یاد گیر و در عمل آر\r\nکه این حدیث ز پیر طریقتم یادست\r\nغم جهان مخور و پند من مبر از یاد\r\nکه این لطیفه عشقم ز ره روی یادست\r\nرضا به داده بده وز جبین گره بگشای\r\nکه بر من و تو در اختیار نگشادست\r\nمجو درستی عهد از جهان سست نهاد\r\nکه این عجوز عروس هزاردامادست\r\nنشان عهد و وفا نیست در تبسم گل\r\nبنال بلبل بی دل که جای فریادست\r\nحسد چه می بری ای سست نظم بر حافظ\r\nقبول خاطر و لطف سخن خدادادست\r\n', 'به آرزوهای دست یافتنی فکر کن. فکر آینده باش. همت کن بنیاد زندگیت را بر مبنای رنگ و ریا مگذار که همه چیز از بین رفتنی است. صید خوبی نصیبت می شود که تو را از رنج نجات می دهد. مراقب دام هایی که برایت پهن کرده اند باش. '),
(38, 'بی مهر رخت روز مرا نور نماندست\r\nوز عمر مرا جز شب دیجور نماندست\r\nهنگام وداع تو ز بس گریه که کردم\r\nدور از رخ تو چشم مرا نور نماندست\r\nمی رفت خیال تو ز چشم من و می گفت\r\nهیهات از این گوشه که معمور نماندست\r\nوصل تو اجل را ز سرم دور همی داشت\r\nاز دولت هجر تو کنون دور نماندست\r\nنزدیک شد آن دم که رقیب تو بگوید\r\nدور از رخت این خسته رنجور نماندست\r\nصبر است مرا چاره هجران تو لیکن\r\nچون صبر توان کرد که مقدور نماندست\r\nدر هجر تو گر چشم مرا آب روان است\r\nگو خون جگر ریز که معذور نماندست\r\nحافظ ز غم از گریه نپرداخت به خنده\r\nماتم زده را داعیه سور نماندست\r\n', 'زندگی بدون او برایت میسر نیست. غم دوری و هجران را نمی توان تحمل کنی ولی فعلا چاره ای جز این نداری و با گریه و زاری کردن کاری درست نمی شود. شاید در آینده وصال یار میسر شود. با امید رسیدن به مقصود با مرگ دست و پنجه نرم کن. '),
(39, 'باغ مرا چه حاجت سرو و صنوبر است\r\nشمشاد خانه پرور ما از که کمتر است\r\nای نازنین پسر تو چه مذهب گرفته ای\r\nکت خون ما حلالتر از شیر مادر است\r\nچون نقش غم ز دور ببینی شراب خواه\r\nتشخیص کرده ایم و مداوا مقرر است\r\nاز آستان پیر مغان سر چرا کشیم\r\nدولت در آن سرا و گشایش در آن در است\r\nیک قصه بیش نیست غم عشق وین عجب\r\nکز هر زبان که می شنوم نامکرر است\r\nدی وعده داد وصلم و در سر شراب داشت\r\nامروز تا چه گوید و بازش چه در سر است\r\nشیراز و آب رکنی و این باد خوش نسیم\r\nعیبش مکن که خال رخ هفت کشور است\r\nفرق است از آب خضر که ظلمات جای او است\r\nتا آب ما که منبعش الله اکبر است\r\nما آبروی فقر و قناعت نمی بریم\r\nبا پادشه بگوی که روزی مقدر است\r\nحافظ چه طرفه شاخ نباتیست کلک تو\r\nکش میوه دلپذیرتر از شهد و شکر است\r\n', 'کسی می خواهد با وعده و وعید شما را گول بزند، بسیار مراقب باشید. درآمد حلال و کم را به درآمد حرام و زیاد ترجیح دهید، در کلامتان ذکر خداوند را فراموش نکنید تا شما را از فقر به پادشاهی برساند. برای رسیدن به مراد باید هفت خوان رستم را طی کنید. قناعت کنید تا زندگی برایتان از شکر هم شیرین تر شود. '),
(40, 'المنه لله که در میکده باز است\r\nزان رو که مرا بر در او روی نیاز است\r\nخم ها همه در جوش و خروشند ز مستی\r\nوان می که در آن جاست حقیقت نه مجاز است\r\nاز وی همه مستی و غرور است و تکبر\r\nوز ما همه بیچارگی و عجز و نیاز است\r\nرازی که بر غیر نگفتیم و نگوییم\r\nبا دوست بگوییم که او محرم راز است\r\nشرح شکن زلف خم اندر خم جانان\r\nکوته نتوان کرد که این قصه دراز است\r\nبار دل مجنون و خم طره لیلی\r\nرخساره محمود و کف پای ایاز است\r\nبردوخته ام دیده چو باز از همه عالم\r\nتا دیده من بر رخ زیبای تو باز است\r\nدر کعبه کوی تو هر آن کس که بیاید\r\nاز قبله ابروی تو در عین نماز است\r\nای مجلسیان سوز دل حافظ مسکین\r\nاز شمع بپرسید که در سوز و گداز است\r\n', 'خداوند نعمت و رحمت زیادی به تو ارزانی داشته و آنچه را که احتیاج داری فراهم نموده است، پس مغرور نباش. او از حال تو خئب آگاه است. روزی تو قطع نمی شود. در زندگی به پستی و بلندی اعتقاد داشته باش قبله ی معبود تو فقط و فقط خداوند باشد. '),
(41, 'اگر چه باده فرح بخش و باد گل بیز است\r\nبه بانگ چنگ مخور می که محتسب تیز است\r\nصراحی ای و حریفی گرت به چنگ افتد\r\nبه عقل نوش که ایام فتنه انگیز است\r\nدر آستین مرقع پیاله پنهان کن\r\nکه همچو چشم صراحی زمانه خون ریز است\r\nبه آب دیده بشوییم خرقه ها از می\r\nکه موسم ورع و روزگار پرهیز است\r\nمجوی عیش خوش از دور باژگون سپهر\r\nکه صاف این سر خم جمله دردی آمیز است\r\nسپهر برشده پرویزنیست خون افشان\r\nکه ریزه اش سر کسری و تاج پرویز است\r\nعراق و فارس گرفتی به شعر خوش حافظ\r\nبیا که نوبت بغداد و وقت تبریز است\r\n', 'زمانه روی بد خود را به تو نشان می دهد. همه چیز را از تو پنهان کرده به فتنه ها توجه نداشته باش فعلا سیر نزولی را طی می کنی و این برایت دردآور است. برای رهایی از این ورطه، دست به دعا بردار. نمازت را سر وقت بخوان. متوسل به ائمه شو تا نجات پیدا کنی. '),
(42, 'حال دل با تو گفتنم هوس است\r\nخبر دل شنفتنم هوس است\r\nطمع خام بین که قصه فاش\r\nاز رقیبان نهفتنم هوس است\r\nشب قدری چنین عزیز و شریف\r\nبا تو تا روز خفتنم هوس است\r\nوه که دردانه ای چنین نازک\r\nدر شب تار سفتنم هوس است\r\nای صبا امشبم مدد فرمای\r\nکه سحرگه شکفتنم هوس است\r\nاز برای شرف به نوک مژه\r\nخاک راه تو رفتنم هوس است\r\nهمچو حافظ به رغم مدعیان\r\nشعر رندانه گفتنم هوس است\r\n', 'بیش از این طمع نکن چون حریفان از تو پیشی می گیرند. بر مبنای هوی و هوس کاری را انجام نده چون عاقبت خوشی در پی ندارد. آن مرادی که مدت هاست دنبالش می گردی به زودی خواهی یافت. انسانیت و شرف خود را در گرو این کار نگذار. '),
(43, 'صحن بستان ذوق بخش و صحبت یاران خوش است\r\nوقت گل خوش باد کز وی وقت میخواران خوش است\r\nاز صبا هر دم مشام جان ما خوش می شود\r\nآری آری طیب انفاس هواداران خوش است\r\nناگشوده گل نقاب آهنگ رحلت ساز کرد\r\nناله کن بلبل که گلبانگ دل افکاران خوش است\r\nمرغ خوشخوان را بشارت باد کاندر راه عشق\r\nدوست را با ناله شب های بیداران خوش است\r\nنیست در بازار عالم خوشدلی ور زان که هست\r\nشیوه رندی و خوش باشی عیاران خوش است\r\nاز زبان سوسن آزاده ام آمد به گوش\r\nکاندر این دیر کهن کار سبکباران خوش است\r\nحافظا ترک جهان گفتن طریق خوشدلیست\r\nتا نپنداری که احوال جهان داران خوش است\r\n', 'خبر خوشی را می شنوی که به خاطرش انتظار کشیده ای و روز به روز کارت بهتر می شود. اما تعلقات تو نیز بیشتر می شود. مواظب صفای باطن خویش باش تا جدا شدن از دنیا برایت راحت باشد. خوشی خود را با دیگران تقسیم کن تا همیشه سبک بال و آسوده خاطر باشی. '),
(44, 'کنون که بر کف گل جام باده صاف است\r\nبه صد هزار زبان بلبلش در اوصاف است\r\nبخواه دفتر اشعار و راه صحرا گیر\r\nچه وقت مدرسه و بحث کشف کشاف است\r\nفقیه مدرسه دی مست بود و فتوی داد\r\nکه می حرام ولی به ز مال اوقاف است\r\nبه درد و صاف تو را حکم نیست خوش درکش\r\nکه هر چه ساقی ما کرد عین الطاف است\r\nببر ز خلق و چو عنقا قیاس کار بگیر\r\nکه صیت گوشه نشینان ز قاف تا قاف است\r\nحدیث مدعیان و خیال همکاران\r\nهمان حکایت زردوز و بوریاباف است\r\nخموش حافظ و این نکته های چون زر سرخ\r\nنگاه دار که قلاب شهر صراف است\r\n', 'آنقدر پیشرفت می کنی که نامت بر سر زبان ها می افتد. در علم و تحصیل بکوش که در این راه با تو قیاس نیست. اما هوشیار باش حسد ورزان زیر پایت را خالی نکنند. با خدا باش، نمازت را به موقع بخوان تا او هم کمکت کند. '),
(45, 'در این زمانه رفیقی که خالی از خلل است\r\nصراحی می ناب و سفینه غزل است\r\nجریده رو که گذرگاه عافیت تنگ است\r\nپیاله گیر که عمر عزیز بی بدل است\r\nنه من ز بی عملی در جهان ملولم و بس\r\nملالت علما هم ز علم بی عمل است\r\nبه چشم عقل در این رهگذار پرآشوب\r\nجهان و کار جهان بی ثبات و بی محل است\r\nبگیر طره مه چهره ای و قصه مخوان\r\nکه سعد و نحس ز تاثیر زهره و زحل است\r\nدلم امید فراوان به وصل روی تو داشت\r\nولی اجل به ره عمر رهزن امل است\r\nبه هیچ دور نخواهند یافت هشیارش\r\nچنین که حافظ ما مست باده ازل است\r\n', 'قدر جوانی خویش را بدانید. فرصتهای طلایی را یکی یکی دارید از دست می دهید. به یار خود اعتماد کنید. با چشم عق به دور دست نگاه کنید. با یک اتفاق کوچک میدان را ترک نکنید. مقاوم باشید اگر هنری دارید آن را نشان بدهید که به نفع شماست و هوشیاری بهترین روش برای پیروزیتان می باشد. '),
(46, 'گل در بر و می در کف و معشوق به کام است\r\nسلطان جهانم به چنین روز غلام است\r\nگو شمع میارید در این جمع که امشب\r\nدر مجلس ما ماه رخ دوست تمام است\r\nدر مذهب ما باده حلال است ولیکن\r\nبی روی تو ای سرو گل اندام حرام است\r\nگوشم همه بر قول نی و نغمه چنگ است\r\nچشمم همه بر لعل لب و گردش جام است\r\nدر مجلس ما عطر میامیز که ما را\r\nهر لحظه ز گیسوی تو خوش بوی مشام است\r\nاز چاشنی قند مگو هیچ و ز شکر\r\nزان رو که مرا از لب شیرین تو کام است\r\nتا گنج غمت در دل ویرانه مقیم است\r\nهمواره مرا کوی خرابات مقام است\r\nاز ننگ چه گویی که مرا نام ز ننگ است\r\nوز نام چه پرسی که مرا ننگ ز نام است\r\nمیخواره و سرگشته و رندیم و نظرباز\r\nوان کس که چو ما نیست در این شهر کدام است\r\nبا محتسبم عیب مگویید که او نیز\r\nپیوسته چو ما در طلب عیش مدام است\r\nحافظ منشین بی می و معشوق زمانی\r\nکایام گل و یاسمن و عید صیام است\r\n', 'بخت بسیار خوبی نصیبتان شده که از خوبی زبانزد خاص و عام است. او در همه حال با شماست و لحظه ای از شما جدا نمی شود. دل ویرانه تان را سامان می بخشد. محبت خودتان را از او دریغ نکنید. در ایام بهار بشارتی به شما می دهند که لیاقت این بشارت را دارید چون مقامتان نزد خداوند بسیار بالاست. '),
(47, 'به کوی میکده هر سالکی که ره دانست\r\nدری دگر زدن اندیشه تبه دانست\r\nزمانه افسر رندی نداد جز به کسی\r\nکه سرفرازی عالم در این کله دانست\r\nبر آستانه میخانه هر که یافت رهی\r\nز فیض جام می اسرار خانقه دانست\r\nهر آن که راز دو عالم ز خط ساغر خواند\r\nرموز جام جم از نقش خاک ره دانست\r\nورای طاعت دیوانگان ز ما مطلب\r\nکه شیخ مذهب ما عاقلی گنه دانست\r\nدلم ز نرگس ساقی امان نخواست به جان\r\nچرا که شیوه آن ترک دل سیه دانست\r\nز جور کوکب طالع سحرگهان چشمم\r\nچنان گریست که ناهید دید و مه دانست\r\nحدیث حافظ و ساغر که می زند پنهان\r\nچه جای محتسب و شحنه پادشه دانست\r\nبلندمرتبه شاهی که نه رواق سپهر\r\nنمونه ای ز خم طاق بارگه دانست\r\n', 'پل های پشت سر خود را خراب نکن. سرت را بالا بگیر. هنوز رموز زندگی را نمی دانی. ستاره ی دولت و اقبال تو چون ناهید درخشان است. پیشنهاد خود را مطرح کن و دل به دریا بزن. زمانی پیروز می شوی که خودت مشکلاتت را حل کنی و به امید دیگران ننشینی. '),
(48, 'صوفی از پرتو می راز نهانی دانست\r\nگوهر هر کس از این لعل توانی دانست\r\nقدر مجموعه گل مرغ سحر داند و بس\r\nکه نه هر کو ورقی خواند معانی دانست\r\nعرضه کردم دو جهان بر دل کارافتاده\r\nبجز از عشق تو باقی همه فانی دانست\r\nآن شد اکنون که ز ابنای عوام اندیشم\r\nمحتسب نیز در این عیش نهانی دانست\r\nدلبر آسایش ما مصلحت وقت ندید\r\nور نه از جانب ما دل نگرانی دانست\r\nسنگ و گل را کند از یمن نظر لعل و عقیق\r\nهر که قدر نفس باد یمانی دانست\r\nای که از دفتر عقل آیت عشق آموزی\r\nترسم این نکته به تحقیق ندانی دانست\r\nمی بیاور که ننازد به گل باغ جهان\r\nهر که غارتگری باد خزانی دانست\r\nحافظ این گوهر منظوم که از طبع انگیخت\r\nز اثر تربیت آصف ثانی دانست\r\n', 'هنوز خیلی خامی چیزهایی را که به دست آورده ای از دست خواهی داد. استراحت و آسایش دیگر کافی است به عقل خودت رجوع کن. نفس اماره را در خود از بین ببر که این نفس بنیادت را از بین می برد چون باد پائیزی بهار دل تو را پیر می کند و این را هم بدان که همگان از کارهای پنهانی تو باخبرند. '),
(49, 'روضه خلد برین خلوت درویشان است\r\nمایه محتشمی خدمت درویشان است\r\nگنج عزلت که طلسمات عجایب دارد\r\nفتح آن در نظر رحمت درویشان است\r\nقصر فردوس که رضوانش به دربانی رفت\r\nمنظری از چمن نزهت درویشان است\r\nآن چه زر می شود از پرتو آن قلب سیاه\r\nکیمیاییست که در صحبت درویشان است\r\nآن که پیشش بنهد تاج تکبر خورشید\r\nکبریاییست که در حشمت درویشان است\r\nدولتی را که نباشد غم از آسیب زوال\r\nبی تکلف بشنو دولت درویشان است\r\nخسروان قبله حاجات جهانند ولی\r\nسببش بندگی حضرت درویشان است\r\nروی مقصود که شاهان به دعا می طلبند\r\nمظهرش آینه طلعت درویشان است\r\nاز کران تا به کران لشکر ظلم است ولی\r\nاز ازل تا به ابد فرصت درویشان است\r\nای توانگر مفروش این همه نخوت که تو را\r\nسر و زر در کنف همت درویشان است\r\nگنج قارون که فرو می شود از قهر هنوز\r\nخوانده باشی که هم از غیرت درویشان است\r\nمن غلام نظر آصف عهدم کو را\r\nصورت خواجگی و سیرت درویشان است\r\nحافظ ار آب حیات ازلی می خواهی\r\nمنبعش خاک در خلوت درویشان است\r\n', 'فریب مال و منال دنیا را نخور و در پی آرزوهای دور و دراز نباش. با قناعت و سادگی زندگی کردن بهتر از زندگی ای است که هر روزش پر از سختی و غم است. تکبر و غرور را رها کن و با ایمان باش. '),
(50, 'به دام زلف تو دل مبتلای خویشتن است\r\nبکش به غمزه که اینش سزای خویشتن است\r\nگرت ز دست برآید مراد خاطر ما\r\nبه دست باش که خیری به جای خویشتن است\r\nبه جانت ای بت شیرین دهن که همچون شمع\r\nشبان تیره مرادم فنای خویشتن است\r\nچو رای عشق زدی با تو گفتم ای بلبل\r\nمکن که آن گل خندان برای خویشتن است\r\nبه مشک چین و چگل نیست بوی گل محتاج\r\nکه نافه هاش ز بند قبای خویشتن است\r\nمرو به خانه ارباب بی مروت دهر\r\nکه گنج عافیتت در سرای خویشتن است\r\nبسوخت حافظ و در شرط عشقبازی او\r\nهنوز بر سر عهد و وفای خویشتن است\r\n', 'با هر کس طرح دوستی نریز که هر چه صدمه دیده ای از دوست بد بوده است. در عشق شکست خورده ای. اما همه چیز دوباره رو به راه می شود. عشق پاک و بی آلایشت را نصیب هر کسی نکن زیرا آنان ظاهر خوب و باطنی تیره دارند. در زمان احتیاج هم در هر خانه ای را نزن که حاجت تو در زور بازویت می باشد. '),
(51, 'لعل سیراب به خون تشنه لب یار من است\r\nوز پی دیدن او دادن جان کار من است\r\nشرم از آن چشم سیه بادش و مژگان دراز\r\nهر که دل بردن او دید و در انکار من است\r\nساروان رخت به دروازه مبر کان سر کو\r\nشاهراهیست که منزلگه دلدار من است\r\nبنده طالع خویشم که در این قحط وفا\r\nعشق آن لولی سرمست خریدار من است\r\nطبله عطر گل و زلف عبیرافشانش\r\nفیض یک شمه ز بوی خوش عطار من است\r\nباغبان همچو نسیمم ز در خویش مران\r\nکآب گلزار تو از اشک چو گلنار من است\r\nشربت قند و گلاب از لب یارم فرمود\r\nنرگس او که طبیب دل بیمار من است\r\nآن که در طرز غزل نکته به حافظ آموخت\r\nیار شیرین سخن نادره گفتار من است\r\n', 'به درگاه خداوند دست نیاز بلند کرده ای و عاجزانه طلب مراد می کنی. دنبال طبیبی برای درمان دل بیمار خود هستی. حاضری برای رسیدن به مقصود جان خود را هم فدا کنی. بالاخره فیض رحمت خداوندی شامل حالت می شود. روزگار تلخ برایت شیرین شده، نسیم بهاری هم دل سرد و بی روح تو را به گلزاری مصفا مبدل خواهد ساخت. '),
(52, 'روزگاریست که سودای بتان دین من است\r\nغم این کار نشاط دل غمگین من است\r\nدیدن روی تو را دیده جان بین باید\r\nوین کجا مرتبه چشم جهان بین من است\r\nیار من باش که زیب فلک و زینت دهر\r\nاز مه روی تو و اشک چو پروین من است\r\nتا مرا عشق تو تعلیم سخن گفتن کرد\r\nخلق را ورد زبان مدحت و تحسین من است\r\nدولت فقر خدایا به من ارزانی دار\r\nکاین کرامت سبب حشمت و تمکین من است\r\nواعظ شحنه شناس این عظمت گو مفروش\r\nزان که منزلگه سلطان دل مسکین من است\r\nیا رب این کعبه مقصود تماشاگه کیست\r\nکه مغیلان طریقش گل و نسرین من است\r\nحافظ از حشمت پرویز دگر قصه مخوان\r\nکه لبش جرعه کش خسرو شیرین من است\r\n', 'دیگر خسته شده ای. صبر و قرار نداری از سرگردانی خسته ای و این باعث دل شکستگی تو شده است. فقر و بی پولی عذابت می دهد. مقصود و مراد تو همان ثروت است که برای رسیدن به آن خود را به هر آب و آتشی می زنی. نترس روزی خواهد رسید که همه ی مردم تو را تحسین کنند و از تو کمک می خواهند. '),
(53, 'منم که گوشه میخانه خانقاه من است\r\nدعای پیر مغان ورد صبحگاه من است\r\nگرم ترانه چنگ صبوح نیست چه باک\r\nنوای من به سحر آه عذرخواه من است\r\nز پادشاه و گدا فارغم بحمدالله\r\nگدای خاک در دوست پادشاه من است\r\nغرض ز مسجد و میخانه ام وصال شماست\r\nجز این خیال ندارم خدا گواه من است\r\nمگر به تیغ اجل خیمه برکنم ور نی\r\nرمیدن از در دولت نه رسم و راه من است\r\nاز آن زمان که بر این آستان نهادم روی\r\nفراز مسند خورشید تکیه گاه من است\r\nگناه اگر چه نبود اختیار ما حافظ\r\nتو در طریق ادب باش و گو گناه من است\r\n', 'با یک عذرخواهی همه چیز درست می شود و انشاالله از بند غم رهایی خواهی یافت. خداوند از تو حرکت می خواهد و خود برکت عطا می فرماید. روی به آستان حق بیاور تا به مقام عالی برسی. نذری کرده ای که اگر از این ورطه ی غم نجات پیدا کنی تا دم مرگ از راه راست برنمی گردی. '),
(54, 'ز گریه مردم چشمم نشسته در خون است\r\nببین که در طلبت حال مردمان چون است\r\nبه یاد لعل تو و چشم مست میگونت\r\nز جام غم می لعلی که می خورم خون است\r\nز مشرق سر کو آفتاب طلعت تو\r\nاگر طلوع کند طالعم همایون است\r\nحکایت لب شیرین کلام فرهاد است\r\nشکنج طره لیلی مقام مجنون است\r\nدلم بجو که قدت همچو سرو دلجوی است\r\nسخن بگو که کلامت لطیف و موزون است\r\nز دور باده به جان راحتی رسان ساقی\r\nکه رنج خاطرم از جور دور گردون است\r\nاز آن دمی که ز چشمم برفت رود عزیز\r\nکنار دامن من همچو رود جیحون است\r\nچگونه شاد شود اندرون غمگینم\r\nبه اختیار که از اختیار بیرون است\r\nز بیخودی طلب یار می کند حافظ\r\nچو مفلسی که طلبکار گنج قارون است\r\n', 'روشنایی و آفتاب خوبی ها در حال طلوع است و این طلعت بسیار مبارک است و سعادت به همراه دارد. اتفاقات شیرینی رخ خواهد داد . شما از در آشتی در می آیید. لحن کلامت را زیباتر کن. خاطرات دردآور را فراموش کن. کاری را که می خواهی انجام بده چون سود سرشاری برایت دارد. '),
(55, 'خم زلف تو دام کفر و دین است\r\nز کارستان او یک شمه این است\r\nجمالت معجز حسن است لیکن\r\nحدیث غمزه ات سحر مبین است\r\nز چشم شوخ تو جان کی توان برد\r\nکه دایم با کمان اندر کمین است\r\nبر آن چشم سیه صد آفرین باد\r\nکه در عاشق کشی سحرآفرین است\r\nعجب علمیست علم هیات عشق\r\nکه چرخ هشتمش هفتم زمین است\r\nتو پنداری که بدگو رفت و جان برد\r\nحسابش با کرام الکاتبین است\r\nمشو حافظ ز کید زلفش ایمن\r\nکه دل برد و کنون دربند دین است\r\n', 'گوشه ای از کارت را به نمایش بگذار حتما مورد قبول واقع می شود و همگان تو را تحسین می کنند. حادثه عجیبی رخ می دهد که کمتر از معجزه نیست و این مسئله باعث می شود کمالات تو بیشتر شود. همیشه از بلایا ایمن هستی اما خود را از ایمان و دین جدا نکن. '),
(56, 'دل سراپرده محبت اوست\r\nدیده آیینه دار طلعت اوست\r\nمن که سر درنیاورم به دو کون\r\nگردنم زیر بار منت اوست\r\nتو و طوبی و ما و قامت یار\r\nفکر هر کس به قدر همت اوست\r\nگر من آلوده دامنم چه عجب\r\nهمه عالم گواه عصمت اوست\r\nمن که باشم در آن حرم که صبا\r\nپرده دار حریم حرمت اوست\r\nبی خیالش مباد منظر چشم\r\nزان که این گوشه جای خلوت اوست\r\nهر گل نو که شد چمن آرای\r\nز اثر رنگ و بوی صحبت اوست\r\nدور مجنون گذشت و نوبت ماست\r\nهر کسی پنج روز نوبت اوست\r\nملکت عاشقی و گنج طرب\r\nهر چه دارم ز یمن همت اوست\r\nمن و دل گر فدا شدیم چه باک\r\nغرض اندر میان سلامت اوست\r\nفقر ظاهر مبین که حافظ را\r\nسینه گنجینه محبت اوست\r\n', 'هر چقدر کوشش کنی همان قدر نتیجه می گیری. به امی این نباش که از غیب برایت کاری انجام شود. همه چیز برای موفقیت مهیاست کمی همت لازم است. کوچکترین خلافی باعث می شود هر چه را به دست آورده ای از دست بدهی. پایت را از گلیم خویش درازتر نکن. خداوند گواه بر اعمال توست. تلاشت را بیشتر کن، عمر زودگذر است. شما ذکرت سلامتی برای دیگران و خود باشد. '),
(57, 'آن سیه چرده که شیرینی عالم با اوست\r\nچشم میگون لب خندان دل خرم با اوست\r\nگر چه شیرین دهنان پادشهانند ولی\r\nاو سلیمان زمان است که خاتم با اوست\r\nروی خوب است و کمال هنر و دامن پاک\r\nلاجرم همت پاکان دو عالم با اوست\r\nخال مشکین که بدان عارض گندمگون است\r\nسر آن دانه که شد رهزن آدم با اوست\r\nدلبرم عزم سفر کرد خدا را یاران\r\nچه کنم با دل مجروح که مرهم با اوست\r\nبا که این نکته توان گفت که آن سنگین دل\r\nکشت ما را و دم عیسی مریم با اوست\r\nحافظ از معتقدان است گرامی دارش\r\nزان که بخشایش بس روح مکرم با اوست\r\n', 'آن کسی را که بسیار دوست می داری چند صباحی از او جدا می شوی و کسی را برای درد دل نمی یابی اما ایمان به خداوند و صبر باعث می شود این جدایی را تحمل کنی. فکر نکن که دیگر آخر زمان شده باز هم نکته ها و اتفاقات بسیار خوبی پیش خواهد آمد که دل مرده ی تو را زنده می کند. به امید بخشش خداوند باش. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(58, 'سر ارادت ما و آستان حضرت دوست\r\nکه هر چه بر سر ما می رود ارادت اوست\r\nنظیر دوست ندیدم اگر چه از مه و مهر\r\nنهادم آینه ها در مقابل رخ دوست\r\nصبا ز حال دل تنگ ما چه شرح دهد\r\nکه چون شکنج ورق های غنچه تو بر توست\r\nنه من سبوکش این دیر رندسوزم و بس\r\nبسا سرا که در این کارخانه سنگ و سبوست\r\nمگر تو شانه زدی زلف عنبرافشان را\r\nکه باد غالیه سا گشت و خاک عنبربوست\r\nنثار روی تو هر برگ گل که در چمن است\r\nفدای قد تو هر سروبن که بر لب جوست\r\nزبان ناطقه در وصف شوق نالان است\r\nچه جای کلک بریده زبان بیهده گوست\r\nرخ تو در دلم آمد مراد خواهم یافت\r\nچرا که حال نکو در قفای فال نکوست\r\nنه این زمان دل حافظ در آتش هوس است\r\nکه داغدار ازل همچو لاله خودروست\r\n', 'درد دل کردن با ائمه اطهار برای تو از یک دوست عادی بهتر است چون هر منفعتی می بینی از هدایت ایشان است. وجود خود را به آنها نزدیک کن تا بلکه دلت آرامش بیشتری پیدا کند. در این حالت است که به مراد می رسی و فالت نیکو می شود و غم دوری عزیزان را نمی کشی و از آتش نفس اماره در امان می مانی. '),
(59, 'دارم امید عاطفتی از جانب دوست\r\nکردم جنایتی و امیدم به عفو اوست\r\nدانم که بگذرد ز سر جرم من که او\r\nگر چه پریوش است ولیکن فرشته خوست\r\nچندان گریستم که هر کس که برگذشت\r\nدر اشک ما چو دید روان گفت کاین چه جوست\r\nهیچ است آن دهان و نبینم از او نشان\r\nموی است آن میان و ندانم که آن چه موست\r\nدارم عجب ز نقش خیالش که چون نرفت\r\nاز دیده ام که دم به دمش کار شست و شوست\r\nبی گفت و گوی زلف تو دل را همی کشد\r\nبا زلف دلکش تو که را روی گفت و گوست\r\nعمریست تا ز زلف تو بویی شنیده ام\r\nزان بوی در مشام دل من هنوز بوست\r\nحافظ بد است حال پریشان تو ولی\r\nبر بوی زلف یار پریشانیت نکوست\r\n', 'دوستی به تو کمک می کند، دست او را پس نزن. به خاطر کاری که کرده ای تو را می بخشد. خود را پنهان نکن. سری در این کار است که با گفتگو و مشورت حل می شود. مطمئن باش پریشانی ات برطرف شده و حالت نیکو می گردد. '),
(60, 'آن پیک نامور که رسید از دیار دوست\r\nآورد حرز جان ز خط مشکبار دوست\r\nخوش می دهد نشان جلال و جمال یار\r\nخوش می کند حکایت عز و وقار دوست\r\nدل دادمش به مژده و خجلت همی برم\r\nزین نقد قلب خویش که کردم نثار دوست\r\nشکر خدا که از مدد بخت کارساز\r\nبر حسب آرزوست همه کار و بار دوست\r\nسیر سپهر و دور قمر را چه اختیار\r\nدر گردشند بر حسب اختیار دوست\r\nگر باد فتنه هر دو جهان را به هم زند\r\nما و چراغ چشم و ره انتظار دوست\r\nکحل الجواهری به من آر ای نسیم صبح\r\nزان خاک نیکبخت که شد رهگذار دوست\r\nماییم و آستانه عشق و سر نیاز\r\nتا خواب خوش که را برد اندر کنار دوست\r\nدشمن به قصد حافظ اگر دم زند چه باک\r\nمنت خدای را که نیم شرمسار دوست\r\n', 'رسیدن خبر خوش نزدیک است که نوید فتح و خوشبختی می دهد. از حریفان و رقیبان نترس. خداوند فتنه ی آنها را خنثی می کند. بخت با توست، اطرافیانت تکیه گاه محکمی برای تو خواهند بد. به جای غرور وقار داشته باش. '),
(61, 'صبا اگر گذری افتدت به کشور دوست\r\nبیار نفحه ای از گیسوی معنبر دوست\r\nبه جان او که به شکرانه جان برافشانم\r\nاگر به سوی من آری پیامی از بر دوست\r\nو گر چنان که در آن حضرتت نباشد بار\r\nبرای دیده بیاور غباری از در دوست\r\nمن گدا و تمنای وصل او هیهات\r\nمگر به خواب ببینم خیال منظر دوست\r\nدل صنوبریم همچو بید لرزان است\r\nز حسرت قد و بالای چون صنوبر دوست\r\nاگر چه دوست به چیزی نمی خرد ما را\r\nبه عالمی نفروشیم مویی از سر دوست\r\nچه باشد ار شود از بند غم دلش آزاد\r\nچو هست حافظ مسکین غلام و چاکر دوست\r\n', 'منتظر خبری هستید که باید برای رسیدن این خبر انتظار زیاد بکشید و دست به دعا بردارید. ناامید نشوید به چیزی یا کسی شک نکنید. حسرت رسیدن این خبر نباید شما را از پای در بیاورد بندهای غم عاقبت از دلتان باز می شود اگر با خدا باشید. '),
(62, 'مرحبا ای پیک مشتاقان بده پیغام دوست\r\nتا کنم جان از سر رغبت فدای نام دوست\r\nواله و شیداست دایم همچو بلبل در قفس\r\nطوطی طبعم ز عشق شکر و بادام دوست\r\nزلف او دام است و خالش دانه آن دام و من\r\nبر امید دانه ای افتاده ام در دام دوست\r\nسر ز مستی برنگیرد تا به صبح روز حشر\r\nهر که چون من در ازل یک جرعه خورد از جام دوست\r\nبس نگویم شمه ای از شرح شوق خود از آنک\r\nدردسر باشد نمودن بیش از این ابرام دوست\r\nگر دهد دستم کشم در دیده همچون توتیا\r\nخاک راهی کان مشرف گردد از اقدام دوست\r\nمیل من سوی وصال و قصد او سوی فراق\r\nترک کام خود گرفتم تا برآید کام دوست\r\nحافظ اندر درد او می سوز و بی درمان بساز\r\nزان که درمانی ندارد درد بی آرام دوست\r\n', 'در کمک و خدمت به دیگران از هیچ چیزی مضایقه نمی کنید. برای دوست خوب جان خود را هم فدا می کنید اما انتظار نداشته باشید دوستان تان هم مثل شما رفتار کنند، زیرا این خصلت شماست. کمی در خدمت دل خودتان باشید و فکری برای درد خودتان بکنید. '),
(63, 'روی تو کس ندید و هزارت رقیب هست\r\nدر غنچه ای هنوز و صدت عندلیب هست\r\nگر آمدم به کوی تو چندان غریب نیست\r\nچون من در آن دیار هزاران غریب هست\r\nدر عشق خانقاه و خرابات فرق نیست\r\nهر جا که هست پرتو روی حبیب هست\r\nآن جا که کار صومعه را جلوه می دهند\r\nناقوس دیر راهب و نام صلیب هست\r\nعاشق که شد که یار به حالش نظر نکرد\r\nای خواجه درد نیست وگرنه طبیب هست\r\nفریاد حافظ این همه آخر به هرزه نیست\r\nهم قصه ای غریب و حدیثی عجیب هست\r\n', 'در راهی که قدم نهاده ای هزاران رقیب داری که باید از میدان به در کنی. احساس غریبی می کنی اما به همین زودی چشمت به جمال یار روشن می شود و همگان برای این وصل جشن خواهند گرفت. هیچ دردی نیست که درمان نداشته باشد. طبیب درد تو هم رسیدن به یار است. '),
(64, 'اگر چه عرض هنر پیش یار بی ادبیست\r\nزبان خموش ولیکن دهان پر از عربیست\r\nپری نهفته رخ و دیو در کرشمه حسن\r\nبسوخت دیده ز حیرت که این چه بوالعجبیست\r\nدر این چمن گل بی خار کس نچید آری\r\nچراغ مصطفوی با شرار بولهبیست\r\nسبب مپرس که چرخ از چه سفله پرور شد\r\nکه کام بخشی او را بهانه بی سببیست\r\nبه نیم جو نخرم طاق خانقاه و رباط\r\nمرا که مصطبه ایوان و پای خم طنبیست\r\nجمال دختر رز نور چشم ماست مگر\r\nکه در نقاب زجاجی و پرده عنبیست\r\nهزار عقل و ادب داشتم من ای خواجه\r\nکنون که مست خرابم صلاح بی ادبیست\r\nبیار می که چو حافظ هزارم استظهار\r\nبه گریه سحری و نیاز نیم شبیست\r\n', 'در کاری به رقابت پرداخته ای ولی پشت پرده کار انجام می دهی. نکند که در این راه خوی دیو پیدا کنی و فرشته بودن را فراموش کنی. هیچ گلی بی خار نیست، تو هم مستثنی نیستی. کار دیگران را بی ارزش نکن تا کار خودت پر بهاء شود که این دور از ادب است. '),
(65, 'خوشتر ز عیش و صحبت و باغ و بهار چیست\r\nساقی کجاست گو سبب انتظار چیست\r\nهر وقت خوش که دست دهد مغتنم شمار\r\nکس را وقوف نیست که انجام کار چیست\r\nپیوند عمر بسته به موییست هوش دار\r\nغمخوار خویش باش غم روزگار چیست\r\nمعنی آب زندگی و روضه ارم\r\nجز طرف جویبار و می خوشگوار چیست\r\nمستور و مست هر دو چو از یک قبیله اند\r\nما دل به عشوه که دهیم اختیار چیست\r\nراز درون پرده چه داند فلک خموش\r\nای مدعی نزاع تو با پرده دار چیست\r\nسهو و خطای بنده گرش اعتبار نیست\r\nمعنی عفو و رحمت آمرزگار چیست\r\nزاهد شراب کوثر و حافظ پیاله خواست\r\nتا در میانه خواسته کردگار چیست\r\n', 'از روزگار انتظار بی اندازه داری و مرتب گله می کنی. زیاد شکایت نکن. حوادثی برایت اتفاق می افتد تا امتحان شوی. غم دنیا را نخور اینها همه خواست خداوند است پس از روزهای خوش زندگیت نهایت استفاده را بکن و از آن لذت ببر و قدر نعمت های خدا را بدان. '),
(66, 'بنال بلبل اگر با منت سر یاریست\r\nکه ما دو عاشق زاریم و کار ما زاریست\r\nدر آن زمین که نسیمی وزد ز طره دوست\r\nچه جای دم زدن نافه های تاتاریست\r\nبیار باده که رنگین کنیم جامه زرق\r\nکه مست جام غروریم و نام هشیاریست\r\nخیال زلف تو پختن نه کار هر خامیست\r\nکه زیر سلسله رفتن طریق عیاریست\r\nلطیفه ایست نهانی که عشق از او خیزد\r\nکه نام آن نه لب لعل و خط زنگاریست\r\nجمال شخص نه چشم است و زلف و عارض و خال\r\nهزار نکته در این کار و بار دلداریست\r\nقلندران حقیقت به نیم جو نخرند\r\nقبای اطلس آن کس که از هنر عاریست\r\nبر آستان تو مشکل توان رسید آری\r\nعروج بر فلک سروری به دشواریست\r\nسحر کرشمه چشمت به خواب می دیدم\r\nزهی مراتب خوابی که به ز بیداریست\r\nدلش به ناله میازار و ختم کن حافظ\r\nکه رستگاری جاوید در کم آزاریست\r\n', 'خود را با زرق و برق دنیوی مشغول کرده ای. اما بدان که همه ی اینها تمام شدنی است و در اشتباه هستی. اینقدر دم از پول نزن زیرا در چشم اهل ادب و معرفت بی ارزش می شوی، از این رو در راه کسب هنر و ادب هم به اندازه ی پول تلاش کن تا پیشرفتت بیشتر شده و در بین مردم محبوب تر شوی. '),
(67, 'یا رب این شمع دل افروز ز کاشانه کیست\r\nجان ما سوخت بپرسید که جانانه کیست\r\nحالیا خانه برانداز دل و دین من است\r\nتا در آغوش که می خسبد و همخانه کیست\r\nباده لعل لبش کز لب من دور مباد\r\nراح روح که و پیمان ده پیمانه کیست\r\nدولت صحبت آن شمع سعادت پرتو\r\nبازپرسید خدا را که به پروانه کیست\r\nمی دهد هر کسش افسونی و معلوم نشد\r\nکه دل نازک او مایل افسانه کیست\r\nیا رب آن شاهوش ماه رخ زهره جبین\r\nدر یکتای که و گوهر یک دانه کیست\r\nگفتم آه از دل دیوانه حافظ بی تو\r\nزیر لب خنده زنان گفت که دیوانه کیست\r\n', 'اگر در عشق شکست خورده ای تقصیر کسی نیست و افسوس خوردن بی فایده است و از دست کسی کاری بر نمی آید. همت کن، از جای برخیز و کارهای گذشته را جبران کن انشاالله که موفق می شوی. دنیا به آخر نرسیده است باز هم وقت برای پیمان بستن و دولت زیاد داری. '),
(68, 'ماهم این هفته برون رفت و به چشمم سالیست\r\nحال هجران تو چه دانی که چه مشکل حالیست\r\nمردم دیده ز لطف رخ او در رخ او\r\nعکس خود دید گمان برد که مشکین خالیست\r\nمی چکد شیر هنوز از لب همچون شکرش\r\nگر چه در شیوه گری هر مژه اش قتالیست\r\nای که انگشت نمایی به کرم در همه شهر\r\nوه که در کار غریبان عجبت اهمالیست\r\nبعد از اینم نبود شائبه در جوهر فرد\r\nکه دهان تو در این نکته خوش استدلالیست\r\nمژده دادند که بر ما گذری خواهی کرد\r\nنیت خیر مگردان که مبارک فالیست\r\nکوه اندوه فراقت به چه حالت بکشد\r\nحافظ خسته که از ناله تنش چون نالیست\r\n', 'زیاد عجله می کنی، هر چند که همه می دانند چه روزهای سختی را پشت سر می گذاری. همگان ظاهر را می بینند پس برای حفظ آبرو ظواهر را حفظ ک. بسیار بی تجربه ای اما ادعای با تجربه بودن می کنی. کاری نکن که انگشت نما شوی، تو جوهر خوبی برای کار داری همین مسئله باعث می شود تو به مراد برسی و تن خسته ات التیام پیدا کند. '),
(69, 'کس نیست که افتاده آن زلف دوتا نیست\r\nدر رهگذر کیست که دامی ز بلا نیست\r\nچون چشم تو دل می برد از گوشه نشینان\r\nهمراه تو بودن گنه از جانب ما نیست\r\nروی تو مگر آینه لطف الهیست\r\nحقا که چنین است و در این روی و ریا نیست\r\nنرگس طلبد شیوه چشم تو زهی چشم\r\nمسکین خبرش از سر و در دیده حیا نیست\r\nاز بهر خدا زلف مپیرای که ما را\r\nشب نیست که صد عربده با باد صبا نیست\r\nبازآی که بی روی تو ای شمع دل افروز\r\nدر بزم حریفان اثر نور و صفا نیست\r\nتیمار غریبان اثر ذکر جمیل است\r\nجانا مگر این قاعده در شهر شما نیست\r\nدی می شد و گفتم صنما عهد به جای آر\r\nگفتا غلطی خواجه در این عهد وفا نیست\r\nگر پیر مغان مرشد من شد چه تفاوت\r\nدر هیچ سری نیست که سری ز خدا نیست\r\nعاشق چه کند گر نکشد بار ملامت\r\nبا هیچ دلاور سپر تیر قضا نیست\r\nدر صومعه زاهد و در خلوت صوفی\r\nجز گوشه ابروی تو محراب دعا نیست\r\nای چنگ فروبرده به خون دل حافظ\r\nفکرت مگر از غیرت قرآن و خدا نیست\r\n', 'دارید خودتان را خسته می کنید و کاری از پیش نمی برید. هر چه شما در فکر وصل یار هستید او در حال فرار است پس خودتان را آزار ندهید و پافشاری تان بی مورد است.قسمت اینطور بوده.فقط دست به دعا بردارید و متوسل به قرآن شوید و مرادتان را از حق تعالی بخواهید. انشاالله که خیر است.  '),
(70, 'مردم دیده ما جز به رخت ناظر نیست\r\nدل سرگشته ما غیر تو را ذاکر نیست\r\nاشکم احرام طواف حرمت می بندد\r\nگر چه از خون دل ریش دمی طاهر نیست\r\nبسته دام و قفس باد چو مرغ وحشی\r\nطایر سدره اگر در طلبت طایر نیست\r\nعاشق مفلس اگر قلب دلش کرد نثار\r\nمکنش عیب که بر نقد روان قادر نیست\r\nعاقبت دست بدان سرو بلندش برسد\r\nهر که را در طلبت همت او قاصر نیست\r\nاز روان بخشی عیسی نزنم دم هرگز\r\nزان که در روح فزایی چو لبت ماهر نیست\r\nمن که در آتش سودای تو آهی نزنم\r\nکی توان گفت که بر داغ دلم صابر نیست\r\nروز اول که سر زلف تو دیدم گفتم\r\nکه پریشانی این سلسله را آخر نیست\r\nسر پیوند تو تنها نه دل حافظ راست\r\nکیست آن کش سر پیوند تو در خاطر نیست\r\n', 'مطمئن باش به مقصود خواهی رسید.شک نداشته باش خودت می دانی که هدفت را شناخته ای و در چه راهی قدم می گذاری و به خود اعتماد داری و همین خیلی کمکت می کند. دام ها را گشوده و از قفس چون باد فرار کرده ای تا برای مرادت تلاش کن. مردم تو را تحسین می کنند و همگان دوستت دارند. '),
(71, 'زاهد ظاهرپرست از حال ما آگاه نیست\r\nدر حق ما هر چه گوید جای هیچ اکراه نیست\r\nدر طریقت هر چه پیش سالک آید خیر اوست\r\nدر صراط مستقیم ای دل کسی گمراه نیست\r\nتا چه بازی رخ نماید بیدقی خواهیم راند\r\nعرصه شطرنج رندان را مجال شاه نیست\r\nچیست این سقف بلند ساده بسیارنقش\r\nزین معما هیچ دانا در جهان آگاه نیست\r\nاین چه استغناست یا رب وین چه قادر حکمت است\r\nکاین همه زخم نهان هست و مجال آه نیست\r\nصاحب دیوان ما گویی نمی داند حساب\r\nکاندر این طغرا نشان حسبه لله نیست\r\nهر که خواهد گو بیا و هر چه خواهد گو بگو\r\nکبر و ناز و حاجب و دربان بدین درگاه نیست\r\nبر در میخانه رفتن کار یک رنگان بود\r\nخودفروشان را به کوی می فروشان راه نیست\r\nهر چه هست از قامت ناساز بی اندام ماست\r\nور نه تشریف تو بر بالای کس کوتاه نیست\r\nبنده پیر خراباتم که لطفش دایم است\r\nور نه لطف شیخ و زاهد گاه هست و گاه نیست\r\nحافظ ار بر صدر ننشیند ز عالی مشربیست\r\nعاشق دردی کش اندربند مال و جاه نیست\r\n', 'از سخن یاوه گویان ناراحت نشو تو همان راه راست را برو چون به نتیجه ی کارت اعتقاد داری. از استتهزاء حریفان و بی خردان ناراحت نشو چون یکرنگ هستی و کبر و غرور هم نداری. در بارگاه حق تعالی مورد عنایت قرار می گیری.روی حمایت و دلسوزی بستگان هم حساب نکن. '),
(72, 'راهیست راه عشق که هیچش کناره نیست\r\nآن جا جز آن که جان بسپارند چاره نیست\r\nهر گه که دل به عشق دهی خوش دمی بود\r\nدر کار خیر حاجت هیچ استخاره نیست\r\nما را ز منع عقل مترسان و می بیار\r\nکان شحنه در ولایت ما هیچ کاره نیست\r\nاز چشم خود بپرس که ما را که می کشد\r\nجانا گناه طالع و جرم ستاره نیست\r\nاو را به چشم پاک توان دید چون هلال\r\nهر دیده جای جلوه آن ماه پاره نیست\r\nفرصت شمر طریقه رندی که این نشان\r\nچون راه گنج بر همه کس آشکاره نیست\r\nنگرفت در تو گریه حافظ به هیچ رو\r\nحیران آن دلم که کم از سنگ خاره نیست\r\n', 'در انجام کاری که در پیش داری درنگ و شتاب نکن. عشق و محبت و ایمان به هدف تو را بهتر و زودتر به هدف می رسانند. در امر ازدواج موفق خواهی شد.  '),
(73, 'روشن از پرتو رویت نظری نیست که نیست\r\nمنت خاک درت بر بصری نیست که نیست\r\nناظر روی تو صاحب نظرانند آری\r\nسر گیسوی تو در هیچ سری نیست که نیست\r\nاشک غماز من ار سرخ برآمد چه عجب\r\nخجل از کرده خود پرده دری نیست که نیست\r\nتا به دامن ننشیند ز نسیمش گردی\r\nسیل خیز از نظرم رهگذری نیست که نیست\r\nتا دم از شام سر زلف تو هر جا نزنند\r\nبا صبا گفت و شنیدم سحری نیست که نیست\r\nمن از این طالع شوریده برنجم ور نی\r\nبهره مند از سر کویت دگری نیست که نیست\r\nاز حیای لب شیرین تو ای چشمه نوش\r\nغرق آب و عرق اکنون شکری نیست که نیست\r\nمصلحت نیست که از پرده برون افتد راز\r\nور نه در مجلس رندان خبری نیست که نیست\r\nشیر در بادیه عشق تو روباه شود\r\nآه از این راه که در وی خطری نیست که نیست\r\nآب چشمم که بر او منت خاک در توست\r\nزیر صد منت او خاک دری نیست که نیست\r\nاز وجودم قدری نام و نشان هست که هست\r\nور نه از ضعف در آن جا اثری نیست که نیست\r\nغیر از این نکته که حافظ ز تو ناخشنود است\r\nدر سراپای وجودت هنری نیست که نیست\r\n', 'سر تو بر هیچ کس پوشیده نیست. موانع زاد و سختی پیش پایت هست که برای رسیدن به معشوق به راحتی از تمام آنها می گذری. از خستگی شکایت نکن. مرد باش. اگر ضعیف باشی در اول راه جا مانده و اثری از عشق و معشوق نمی یابی. '),
(74, 'حاصل کارگه کون و مکان این همه نیست\r\nباده پیش آر که اسباب جهان این همه نیست\r\nاز دل و جان شرف صحبت جانان غرض است\r\nغرض این است وگرنه دل و جان این همه نیست\r\nمنت سدره و طوبی ز پی سایه مکش\r\nکه چو خوش بنگری ای سرو روان این همه نیست\r\nدولت آن است که بی خون دل آید به کنار\r\nور نه با سعی و عمل باغ جنان این همه نیست\r\nپنج روزی که در این مرحله مهلت داری\r\nخوش بیاسای زمانی که زمان این همه نیست\r\nبر لب بحر فنا منتظریم ای ساقی\r\nفرصتی دان که ز لب تا به دهان این همه نیست\r\nزاهد ایمن مشو از بازی غیرت زنهار\r\nکه ره از صومعه تا دیر مغان این همه نیست\r\nدردمندی من سوخته زار و نزار\r\nظاهرا حاجت تقریر و بیان این همه نیست\r\nنام حافظ رقم نیک پذیرفت ولی\r\nپیش رندان رقم سود و زیان این همه نیست\r\n', 'مسئله به این کوچکی را بزرگ نکن. چند صباحی را که زنده ای خوش باش و به خاطر مسائل کوچک خود را آزار نده. می خواهی همه چیز را بدون هیچ سعی و تلاشی به راحتی به دست بیاوری. در راه خدا قدم نمی گذاری ولی انتظار بهشت داری. نامت نیکوست به عاقبت کاری که می کنی فکر کن. '),
(75, 'خواب آن نرگس فتان تو بی چیزی نیست\r\nتاب آن زلف پریشان تو بی چیزی نیست\r\nاز لبت شیر روان بود که من می گفتم\r\nاین شکر گرد نمکدان تو بی چیزی نیست\r\nجان درازی تو بادا که یقین می دانم\r\nدر کمان ناوک مژگان تو بی چیزی نیست\r\nمبتلایی به غم محنت و اندوه فراق\r\nای دل این ناله و افغان تو بی چیزی نیست\r\nدوش باد از سر کویش به گلستان بگذشت\r\nای گل این چاک گریبان تو بی چیزی نیست\r\nدرد عشق ار چه دل از خلق نهان می دارد\r\nحافظ این دیده گریان تو بی چیزی نیست\r\n', 'شکست تو در این راه پله ی صعودی است در راهی و هرگونه نقصان حکمتی دارد. چه بسا سلامتی تو در فقر باشد. امیدت را از دست نده. فعلا صلاح در این است که روزگار را اینطور طی کنی و یقین بدان راز بودن تو در این است که با روزگار صلح کنی. '),
(76, 'جز آستان توام در جهان پناهی نیست\r\nسر مرا بجز این در حواله گاهی نیست\r\nعدو چو تیغ کشد من سپر بیندازم\r\nکه تیغ ما بجز از ناله ای و آهی نیست\r\nچرا ز کوی خرابات روی برتابم\r\nکز این به هم به جهان هیچ رسم و راهی نیست\r\nزمانه گر بزند آتشم به خرمن عمر\r\nبگو بسوز که بر من به برگ کاهی نیست\r\nغلام نرگس جماش آن سهی سروم\r\nکه از شراب غرورش به کس نگاهی نیست\r\nمباش در پی آزار و هر چه خواهی کن\r\nکه در شریعت ما غیر از این گناهی نیست\r\nعنان کشیده رو ای پادشاه کشور حسن\r\nکه نیست بر سر راهی که دادخواهی نیست\r\nچنین که از همه سو دام راه می بینم\r\nبه از حمایت زلفش مرا پناهی نیست\r\nخزینه دل حافظ به زلف و خال مده\r\nکه کارهای چنین حد هر سیاهی نیست\r\n', 'از شر شیطان به خدا پناه ببر، جز خدا کسی با تو نیست. فعلا جور و ستم بر روی سرت سایه انداخته و با گوشه نشینی و آه کشیدن کار درست نمی شود. در راهی که قدم می گذاری کسی حمایتت نمی کند. با آرزوی مرگ کردن هم کاری از پیش نمی بری. نه خود را آزار بده و نه دیگران را. پایان شب سیه سپید است. انشاالله. '),
(77, 'بلبلی برگ گلی خوش رنگ در منقار داشت\r\nو اندر آن برگ و نوا خوش ناله های زار داشت\r\nگفتمش در عین وصل این ناله و فریاد چیست\r\nگفت ما را جلوه معشوق در این کار داشت\r\nیار اگر ننشست با ما نیست جای اعتراض\r\nپادشاهی کامران بود از گدایی عار داشت\r\nدر نمی گیرد نیاز و ناز ما با حسن دوست\r\nخرم آن کز نازنینان بخت برخوردار داشت\r\nخیز تا بر کلک آن نقاش جان افشان کنیم\r\nکاین همه نقش عجب در گردش پرگار داشت\r\nگر مرید راه عشقی فکر بدنامی مکن\r\nشیخ صنعان خرقه رهن خانه خمار داشت\r\nوقت آن شیرین قلندر خوش که در اطوار سیر\r\nذکر تسبیح ملک در حلقه زنار داشت\r\nچشم حافظ زیر بام قصر آن حوری سرشت\r\nشیوه جنات تجری تحتها الانهار داشت\r\n', 'از اینکه اگر پیروز نشوی می ترسی و مدام در حال رنجیدن هستی تا رنج نبینی به مقصود نمی رسی. اگر قرار باشد با نبودن درد و مشقت وصل حاصل شود، نتیجه ی کار برایت لذت بخش نخواهد بود و در راه رسیدن به مقصود امکان از دست دادن مال و آبرو نیز می باشد. '),
(78, 'دیدی که یار جز سر جور و ستم نداشت\r\nبشکست عهد وز غم ما هیچ غم نداشت\r\nیا رب مگیرش ار چه دل چون کبوترم\r\nافکند و کشت و عزت صید حرم نداشت\r\nبر من جفا ز بخت من آمد وگرنه یار\r\nحاشا که رسم لطف و طریق کرم نداشت\r\nبا این همه هر آن که نه خواری کشید از او\r\nهر جا که رفت هیچ کسش محترم نداشت\r\nساقی بیار باده و با محتسب بگو\r\nانکار ما مکن که چنین جام جم نداشت\r\nهر راهرو که ره به حریم درش نبرد\r\nمسکین برید وادی و ره در حرم نداشت\r\nحافظ ببر تو گوی فصاحت که مدعی\r\nهیچش هنر نبود و خبر نیز هم نداشت\r\n', 'چرا عاقل کند کاری که باز آرد پشیمانی. از کاری که کرده ای بسیار نادم و پشیمانی. با شکست مواجه شده ای و فکر می کنی دیگر کسی تو را دوست ندارد و مورد احترام نیستی. اما مرور زمان همه چیز را دست می کند. مطمئن باش این شکست مقدمه ای است برای پیروزی بزرگ انشاالله. '),
(79, 'کنون که می دمد از بوستان نسیم بهشت\r\nمن و شراب فرح بخش و یار حورسرشت\r\nگدا چرا نزند لاف سلطنت امروز\r\nکه خیمه سایه ابر است و بزمگه لب کشت\r\nچمن حکایت اردیبهشت می گوید\r\nنه عاقل است که نسیه خرید و نقد بهشت\r\nبه می عمارت دل کن که این جهان خراب\r\nبر آن سر است که از خاک ما بسازد خشت\r\nوفا مجوی ز دشمن که پرتوی ندهد\r\nچو شمع صومعه افروزی از چراغ کنشت\r\nمکن به نامه سیاهی ملامت من مست\r\nکه آگه است که تقدیر بر سرش چه نوشت\r\nقدم دریغ مدار از جنازه حافظ\r\nکه گر چه غرق گناه است می رود به بهشت\r\n', 'حالا که همه چیز دارد درست می شود پشت پا به آنه نزن و کارها را خراب نکن زیرا به زودی پشیمان شده و بعد می فهمی که چه گوهری را از کف داده ای. از دشمن دیرین خود کمک نخواه چون تیشه به ریشه ات می زند. هر چند که همه چیز بهم ریخته است ولی سر و سامان گرفته و مشکلاتت حل می شود. '),
(80, 'عیب رندان مکن ای زاهد پاکیزه سرشت\r\nکه گناه دگران بر تو نخواهند نوشت\r\nمن اگر نیکم و گر بد تو برو خود را باش\r\nهر کسی آن درود عاقبت کار که کشت\r\nهمه کس طالب یارند چه هشیار و چه مست\r\nهمه جا خانه عشق است چه مسجد چه کنشت\r\nسر تسلیم من و خشت در میکده ها\r\nمدعی گر نکند فهم سخن گو سر و خشت\r\nناامیدم مکن از سابقه لطف ازل\r\nتو پس پرده چه دانی که که خوب است و که زشت\r\nنه من از پرده تقوا به درافتادم و بس\r\nپدرم نیز بهشت ابد از دست بهشت\r\nحافظا روز اجل گر به کف آری جامی\r\nیک سر از کوی خرابات برندت به بهشت\r\n', 'عیب جویی دیگران نکن. اول خودت را برطرف کن. تو چه خوب باشی و چه بد پای خودت نوشته می شود. این حق توست که هر طور بخواهی زندگی کنی و هدفی داشته باشی. حاجات تو یکی پس از دیگری برآورده شده و باز هم با نیاز به درگاه خدا می روی. تقوی را پیشه کن تا از پیشگاه حق رانده نشوی. '),
(81, 'صبحدم مرغ چمن با گل نوخاسته گفت\r\nناز کم کن که در این باغ بسی چون تو شکفت\r\nگل بخندید که از راست نرنجیم ولی\r\nهیچ عاشق سخن سخت به معشوق نگفت\r\nگر طمع داری از آن جام مرصع می لعل\r\nای بسا در که به نوک مژه ات باید سفت\r\nتا ابد بوی محبت به مشامش نرسد\r\nهر که خاک در میخانه به رخساره نرفت\r\nدر گلستان ارم دوش چو از لطف هوا\r\nزلف سنبل به نسیم سحری می آشفت\r\nگفتم ای مسند جم جام جهان بینت کو\r\nگفت افسوس که آن دولت بیدار بخفت\r\nسخن عشق نه آن است که آید به زبان\r\nساقیا می ده و کوتاه کن این گفت و شنفت\r\nاشک حافظ خرد و صبر به دریا انداخت\r\nچه کند سوز غم عشق نیارست نهفت\r\n', 'از حرف سنجیده و درست ناراحت نشو. بدان که مصلحت تو را می خواهند هر چند که فکر کنی کسی درکت نمی کند. کاری را که اصرار به انجام آن داری عاقبت خوشی ندارد. در افکارت تجدید نظر کن. مشورت با اهل فن به تو کمک می کند، خر در کنار صبر نیازهای تو را نیز برطرف می سازد. '),
(82, 'آن ترک پری چهره که دوش از بر ما رفت\r\nآیا چه خطا دید که از راه خطا رفت\r\nتا رفت مرا از نظر آن چشم جهان بین\r\nکس واقف ما نیست که از دیده چه ها رفت\r\nبر شمع نرفت از گذر آتش دل دوش\r\nآن دود که از سوز جگر بر سر ما رفت\r\nدور از رخ تو دم به دم از گوشه چشمم\r\nسیلاب سرشک آمد و طوفان بلا رفت\r\nاز پای فتادیم چو آمد غم هجران\r\nدر درد بمردیم چو از دست دوا رفت\r\nدل گفت وصالش به دعا باز توان یافت\r\nعمریست که عمرم همه در کار دعا رفت\r\nاحرام چه بندیم چو آن قبله نه این جاست\r\nدر سعی چه کوشیم چو از مروه صفا رفت\r\nدی گفت طبیب از سر حسرت چو مرا دید\r\nهیهات که رنج تو ز قانون شفا رفت\r\nای دوست به پرسیدن حافظ قدمی نه\r\nزان پیش که گویند که از دار فنا رفت\r\n', 'کارهای مهم را بازیچه نگیر چون خطا می کی. حرفی را زده ای که عدهای از تو رنجیده اند، دل آنها را بدست بیاور، چون در زمان پیری و تنهایی کسی نیست که دیگر با او همدلی کنی و هم سخن شوی. ماهی را هر وقت از آب بگیری تازه است. الان هم دیر نیست. طبیب تو خداوند اس روبه سوی او آور تا تو را شفا دهد و از مشقات رهایت سازد. '),
(83, 'گر ز دست زلف مشکینت خطایی رفت رفت\r\nور ز هندوی شما بر ما جفایی رفت رفت\r\nبرق عشق ار خرمن پشمینه پوشی سوخت سوخت\r\nجور شاه کامران گر بر گدایی رفت رفت\r\nدر طریقت رنجش خاطر نباشد می بیار\r\nهر کدورت را که بینی چون صفایی رفت رفت\r\nعشقبازی را تحمل باید ای دل پای دار\r\nگر ملالی بود بود و گر خطایی رفت رفت\r\nگر دلی از غمزه دلدار باری برد برد\r\nور میان جان و جانان ماجرایی رفت رفت\r\nاز سخن چینان ملالت ها پدید آمد ولی\r\nگر میان همنشینان ناسزایی رفت رفت\r\nعیب حافظ گو مکن واعظ که رفت از خانقاه\r\nپای آزادی چه بندی گر به جایی رفت رفت\r\n', 'آبی که ریخته شد دیگر جمع نمی شود. کاریست که انجام شده در صدد باش تا جبرانش کنی. هیچ سر بی گناهی بالای دار نمی رود. شما از این ورطه نجات پیدا می کنی. کدورت را با صفای دل به خوبی جواب بده. بندها باز می شوند و تو به آزادی خواهی رسید. '),
(84, 'ساقی بیار باده که ماه صیام رفت\r\nدرده قدح که موسم ناموس و نام رفت\r\nوقت عزیز رفت بیا تا قضا کنیم\r\nعمری که بی حضور صراحی و جام رفت\r\nمستم کن آن چنان که ندانم ز بیخودی\r\nدر عرصه خیال که آمد کدام رفت\r\nبر بوی آن که جرعه جامت به ما رسد\r\nدر مصطبه دعای تو هر صبح و شام رفت\r\nدل را که مرده بود حیاتی به جان رسید\r\nتا بویی از نسیم می اش در مشام رفت\r\nزاهد غرور داشت سلامت نبرد راه\r\nرند از ره نیاز به دارالسلام رفت\r\nنقد دلی که بود مرا صرف باده شد\r\nقلب سیاه بود از آن در حرام رفت\r\nدر تاب توبه چند توان سوخت همچو عود\r\nمی ده که عمر در سر سودای خام رفت\r\nدیگر مکن نصیحت حافظ که ره نیافت\r\nگمگشته ای که باده نابش به کام رفت\r\n', 'برای آینده ی خود چه کاری انجام داده ای. وقت گذشته، عمرت هم تلف شده بدون اینکه برای فردایت فکری کرده باشی. این حاصل غرور خودت می باشد. غرورت را کنار بگذار تا دوباره متولد شده و بتوانی آینده را بسازی. توبه کن تا عمر از دست رفته دوباره بازگردد و قلب تاریکت روشن گردد. '),
(85, 'شربتی از لب لعلش نچشیدیم و برفت\r\nروی مه پیکر او سیر ندیدیم و برفت\r\nگویی از صحبت ما نیک به تنگ آمده بود\r\nبار بربست و به گردش نرسیدیم و برفت\r\nبس که ما فاتحه و حرز یمانی خواندیم\r\nوز پی اش سوره اخلاص دمیدیم و برفت\r\nعشوه دادند که بر ما گذری خواهی کرد\r\nدیدی آخر که چنین عشوه خریدیم و برفت\r\nشد چمان در چمن حسن و لطافت لیکن\r\nدر گلستان وصالش نچمیدیم و برفت\r\nهمچو حافظ همه شب ناله و زاری کردیم\r\nکای دریغا به وداعش نرسیدیم و برفت\r\n', 'قدر چیزی را که داشته ای ندانستی. حالا که از دست داده ای افسوس می خوری چرا خوب رفتار نکردی و او رنجیده خاطر از پیش تو رفت. غرورت را کنار بگذر باز هم پیش قدم شو تا به وصالش برسی. لطف خداوند باز هم شامل حالت می شود. '),
(86, 'ساقی بیا که یار ز رخ پرده برگرفت\r\nکار چراغ خلوتیان باز درگرفت\r\nآن شمع سرگرفته دگر چهره برفروخت\r\nوین پیر سالخورده جوانی ز سر گرفت\r\nآن عشوه داد عشق که مفتی ز ره برفت\r\nوان لطف کرد دوست که دشمن حذر گرفت\r\nزنهار از آن عبارت شیرین دلفریب\r\nگویی که پسته تو سخن در شکر گرفت\r\nبار غمی که خاطر ما خسته کرده بود\r\nعیسی دمی خدا بفرستاد و برگرفت\r\nهر سروقد که بر مه و خور حسن می فروخت\r\nچون تو درآمدی پی کاری دگر گرفت\r\nزین قصه هفت گنبد افلاک پرصداست\r\nکوته نظر ببین که سخن مختصر گرفت\r\nحافظ تو این سخن ز که آموختی که بخت\r\nتعویذ کرد شعر تو را و به زر گرفت\r\n', 'سخن کوتاه می کنیم و می گوییم که مرادت خیلی زود برآورده می شود. دلت دوباره جوان می شود. دشمنان شکست خوردند و این از لطف خدا بود. خستگی از تنت بیرون می رود و در کاری که مراد توست جز پیروزی و ثروت چیزی دیگری نیست و این همان اجابت دعاهایت می باشد. '),
(87, 'حسنت به اتفاق ملاحت جهان گرفت\r\nآری به اتفاق جهان می توان گرفت\r\nافشای راز خلوتیان خواست کرد شمع\r\nشکر خدا که سر دلش در زبان گرفت\r\nزین آتش نهفته که در سینه من است\r\nخورشید شعله ایست که در آسمان گرفت\r\nمی خواست گل که دم زند از رنگ و بوی دوست\r\nاز غیرت صبا نفسش در دهان گرفت\r\nآسوده بر کنار چو پرگار می شدم\r\nدوران چو نقطه عاقبتم در میان گرفت\r\nآن روز شوق ساغر می خرمنم بسوخت\r\nکآتش ز عکس عارض ساقی در آن گرفت\r\nخواهم شدن به کوی مغان آستین فشان\r\nزین فتنه ها که دامن آخرزمان گرفت\r\nمی خور که هر که آخر کار جهان بدید\r\nاز غم سبک برآمد و رطل گران گرفت\r\nبر برگ گل به خون شقایق نوشته اند\r\nکان کس که پخته شد می چون ارغوان گرفت\r\nحافظ چو آب لطف ز نظم تو می چکد\r\nحاسد چگونه نکته تواند بر آن گرفت\r\n', 'تنهایی کاری از پیش نمی بری، احتیاج به یک همکار و همدل داری که در ضمن راز تو را فاش نکند. گرمای عشق تو بدون کمک دوست سرد می شود و به یار نمی رسد. آسوده خاطر باش که او تکی گاه خوبی برای رسیدن تو به حاجاتت می باشد و راه را برایت میسر می سازد. '),
(88, 'شنیده ام سخنی خوش که پیر کنعان گفت\r\nفراق یار نه آن می کند که بتوان گفت\r\nحدیث هول قیامت که گفت واعظ شهر\r\nکنایتیست که از روزگار هجران گفت\r\nنشان یار سفرکرده از که پرسم باز\r\nکه هر چه گفت برید صبا پریشان گفت\r\nفغان که آن مه نامهربان مهرگسل\r\nبه ترک صحبت یاران خود چه آسان گفت\r\nمن و مقام رضا بعد از این و شکر رقیب\r\nکه دل به درد تو خو کرد و ترک درمان گفت\r\nغم کهن به می سالخورده دفع کنید\r\nکه تخم خوشدلی این است پیر دهقان گفت\r\nگره به باد مزن گر چه بر مراد رود\r\nکه این سخن به مثل باد با سلیمان گفت\r\nبه مهلتی که سپهرت دهد ز راه مرو\r\nتو را که گفت که این زال ترک دستان گفت\r\nمزن ز چون و چرا دم که بنده مقبل\r\nقبول کرد به جان هر سخن که جانان گفت\r\nکه گفت حافظ از اندیشه تو آمد باز\r\nمن این نگفته ام آن کس که گفت بهتان گفت\r\n', 'تهمتی به شما زده اند که از طرف دشمن است.درد هجر بزرگی را تحمل می کنید. اما تن به رضای خداوند دهید. مطمئن باشید جواب خوبی ها و ثوابی را که کرده اید در جایی دیگر خواهید گرفت. حرف های خود را بر اساس فرض و شک نزنید که بر علیه خودتان استفاده می کنند. بذر خوبی بکارید تا شاهد رویش خوبترین ها باشید. '),
(89, 'یا رب سببی ساز که یارم به سلامت\r\nبازآید و برهاندم از بند ملامت\r\nخاک ره آن یار سفرکرده بیارید\r\nتا چشم جهان بین کنمش جای اقامت\r\nفریاد که از شش جهتم راه ببستند\r\nآن خال و خط و زلف و رخ و عارض و قامت\r\nامروز که در دست توام مرحمتی کن\r\nفردا که شوم خاک چه سود اشک ندامت\r\nای آن که به تقریر و بیان دم زنی از عشق\r\nما با تو نداریم سخن خیر و سلامت\r\nدرویش مکن ناله ز شمشیر احبا\r\nکاین طایفه از کشته ستانند غرامت\r\nدر خرقه زن آتش که خم ابروی ساقی\r\nبر می شکند گوشه محراب امامت\r\nحاشا که من از جور و جفای تو بنالم\r\nبیداد لطیفان همه لطف است و کرامت\r\nکوته نکند بحث سر زلف تو حافظ\r\nپیوسته شد این سلسله تا روز قیامت\r\n', 'راه به جایی نداری. همه درها به رویت بسته شده و فکر می کنی دنیا نیز از هر جهت روی خشم به تو نشان می دهد. دیگران هم به جای کمک مسخره ات می کنند و مدام ایراد می گیرند. پس تو هم رازهای درونت را فاش نکن. امیدوار باش. تو تا روز قیامت با جور زمانه در جنگ و ستیز هستی. '),
(90, 'ای هدهد صبا به سبا می فرستمت\r\nبنگر که از کجا به کجا می فرستمت\r\nحیف است طایری چو تو در خاکدان غم\r\nزین جا به آشیان وفا می فرستمت\r\nدر راه عشق مرحله قرب و بعد نیست\r\nمی بینمت عیان و دعا می فرستمت\r\nهر صبح و شام قافله ای از دعای خیر\r\nدر صحبت شمال و صبا می فرستمت\r\nتا لشکر غمت نکند ملک دل خراب\r\nجان عزیز خود به نوا می فرستمت\r\nای غایب از نظر که شدی همنشین دل\r\nمی گویمت دعا و ثنا می فرستمت\r\nدر روی خود تفرج صنع خدای کن\r\nکآیینه خدای نما می فرستمت\r\nتا مطربان ز شوق منت آگهی دهند\r\nقول و غزل به ساز و نوا می فرستمت\r\nساقی بیا که هاتف غیبم به مژده گفت\r\nبا درد صبر کن که دوا می فرستمت\r\nحافظ سرود مجلس ما ذکر خیر توست\r\nبشتاب هان که اسب و قبا می فرستمت\r\n', 'در انجام کار تاخیر جایز نیست. لطف همگان شامل حال توست. به تعهداتی که داده ای حتما عمل کن که این خود فرجی برای رسیدن به حاجاتت می باشد. رنج هایی که متحمل می شوی بی جواب نمی ماند. در وفای به عهد نیز سرآمد خاص و عام هستی. حیف نیست که به خاطر مسائل کوچک غم به دلت راه دهی. '),
(91, 'ای غایب از نظر به خدا می سپارمت\r\nجانم بسوختی و به دل دوست دارمت\r\nتا دامن کفن نکشم زیر پای خاک\r\nباور مکن که دست ز دامن بدارمت\r\nمحراب ابرویت بنما تا سحرگهی\r\nدست دعا برآرم و در گردن آرمت\r\nگر بایدم شدن سوی هاروت بابلی\r\nصد گونه جادویی بکنم تا بیارمت\r\nخواهم که پیش میرمت ای بی وفا طبیب\r\nبیمار بازپرس که در انتظارمت\r\nصد جوی آب بسته ام از دیده بر کنار\r\nبر بوی تخم مهر که در دل بکارمت\r\nخونم بریخت و از غم عشقم خلاص داد\r\nمنت پذیر غمزه خنجر گذارمت\r\nمی گریم و مرادم از این سیل اشکبار\r\nتخم محبت است که در دل بکارمت\r\nبارم ده از کرم سوی خود تا به سوز دل\r\nدر پای دم به دم گهر از دیده بارمت\r\nحافظ شراب و شاهد و رندی نه وضع توست\r\nفی الجمله می کنی و فرو می گذارمت\r\n', 'او تو را خیلی دوست دارد و تا زمان مرگ فراموشت نخواهد کرد ولی خود را در مقابلش کوچک نکن. تو هم به او فکر می کنی. مصلحت در این است که یار عشق خود را بروز ندهد و همه چیز را با شور دل تحمل کند تا تو هم در مراحل عشق به پای او برسی. '),
(92, 'میر من خوش می روی کاندر سر و پا میرمت\r\nخوش خرامان شو که پیش قد رعنا میرمت\r\nگفته بودی کی بمیری پیش من تعجیل چیست\r\nخوش تقاضا می کنی پیش تقاضا میرمت\r\nعاشق و مخمور و مهجورم بت ساقی کجاست\r\nگو که بخرامد که پیش سروبالا میرمت\r\nآن که عمری شد که تا بیمارم از سودای او\r\nگو نگاهی کن که پیش چشم شهلا میرمت\r\nگفته لعل لبم هم درد بخشد هم دوا\r\nگاه پیش درد و گه پیش مداوا میرمت\r\nخوش خرامان می روی چشم بد از روی تو دور\r\nدارم اندر سر خیال آن که در پا میرمت\r\nگر چه جای حافظ اندر خلوت وصل تو نیست\r\nای همه جای تو خوش پیش همه جا میرمت\r\n', 'تحول عظیمی در خود بوجود می آوری که این تحول هم مادی است و هم معنوی. در این کار تعجیل جایز است. شما از جایی به جای دیگر نقل مکان می کنید. سختی می کشید ولی راحتی در پی آن می باشد. خیالاتی را که در سر داری عملی کن که تو از این به بعد خوش خواهی بود. '),
(93, 'چه لطف بود که ناگاه رشحه قلمت\r\nحقوق خدمت ما عرضه کرد بر کرمت\r\nبه نوک خامه رقم کرده ای سلام مرا\r\nکه کارخانه دوران مباد بی رقمت\r\nنگویم از من بی دل به سهو کردی یاد\r\nکه در حساب خرد نیست سهو بر قلمت\r\nمرا ذلیل مگردان به شکر این نعمت\r\nکه داشت دولت سرمد عزیز و محترمت\r\nبیا که با سر زلفت قرار خواهم کرد\r\nکه گر سرم برود برندارم از قدمت\r\nز حال ما دلت آگه شود مگر وقتی\r\nکه لاله بردمد از خاک کشتگان غمت\r\nروان تشنه ما را به جرعه ای دریاب\r\nچو می دهند زلال خضر ز جام جمت\r\nهمیشه وقت تو ای عیسی صبا خوش باد\r\nکه جان حافظ دلخسته زنده شد به دمت\r\n', 'با یک سند پول زیادی نصیبتان می شود. سر نماز همیشه دعا می کنید که خوار و ذلیل نشوید. در هر حال شما همیشه محترم و عزیز خواهید بود. در سرنوشت تو اینطور رقم زده شده که تا غم نخوری به مراد نمی رسی. پس امیدت را از دست نده چون دل خسته ی تو دوباره شاد می شود. '),
(94, 'زان یار دلنوازم شکریست با شکایت\r\nگر نکته دان عشقی بشنو تو این حکایت\r\nبی مزد بود و منت هر خدمتی که کردم\r\nیا رب مباد کس را مخدوم بی عنایت\r\nرندان تشنه لب را آبی نمی دهد کس\r\nگویی ولی شناسان رفتند از این ولایت\r\nدر زلف چون کمندش ای دل مپیچ کان جا\r\nسرها بریده بینی بی جرم و بی جنایت\r\nچشمت به غمزه ما را خون خورد و می پسندی\r\nجانا روا نباشد خون ریز را حمایت\r\nدر این شب سیاهم گم گشت راه مقصود\r\nاز گوشه ای برون آی ای کوکب هدایت\r\nاز هر طرف که رفتم جز وحشتم نیفزود\r\nزنهار از این بیابان وین راه بی نهایت\r\nای آفتاب خوبان می جوشد اندرونم\r\nیک ساعتم بگنجان در سایه عنایت\r\nاین راه را نهایت صورت کجا توان بست\r\nکش صد هزار منزل بیش است در بدایت\r\nهر چند بردی آبم روی از درت نتابم\r\nجور از حبیب خوشتر کز مدعی رعایت\r\nعشقت رسد به فریاد ار خود به سان حافظ\r\nقرآن ز بر بخوانی در چارده روایت\r\n', 'اگر کار را برای رضای خداوند انجام داده ای پس چرا دیگر شکایت می کنی. خداوند مزد تو را می دهد. پیر دانایی راه تاریک تو را با علم خودش روشن می کند ولی راه بسیار طولانی و مقصد دور است. تو از عاقبت گناه با خبری پس چرا گناه می کنی. با توسل به قرآن آبروی ریخته ات بر می گردد. '),
(95, 'مدامم مست می دارد نسیم جعد گیسویت\r\nخرابم می کند هر دم فریب چشم جادویت\r\nپس از چندین شکیبایی شبی یا رب توان دیدن\r\nکه شمع دیده افروزیم در محراب ابرویت\r\nسواد لوح بینش را عزیز از بهر آن دارم\r\nکه جان را نسخه ای باشد ز لوح خال هندویت\r\nتو گر خواهی که جاویدان جهان یک سر بیارایی\r\nصبا را گو که بردارد زمانی برقع از رویت\r\nو گر رسم فنا خواهی که از عالم براندازی\r\nبرافشان تا فروریزد هزاران جان ز هر مویت\r\nمن و باد صبا مسکین دو سرگردان بی حاصل\r\nمن از افسون چشمت مست و او از بوی گیسویت\r\nزهی همت که حافظ راست از دنیی و از عقبی\r\nنیاید هیچ در چشمش بجز خاک سر کویت\r\n', 'اراده ات بسیار قوی است و با آن می توانی دنیا را زیر و رو کنی. همه چیز را زیبا می بینی و از این دید خود راضی هم هستی. زیبایی ها آنقدر تو را مست کرده که به خزان فکر نمی کنی. گاهی خود را مثل باد سبک می دانی و دلت می خواهد همراه آن سرگردان باشی ولی این خیالات برایت ثمری ندارد پس اراده ی قوی خویش را به کار برده، دنیا و آخرت خود را بساز. '),
(96, 'درد ما را نیست درمان الغیاث\r\nهجر ما را نیست پایان الغیاث\r\nدین و دل بردند و قصد جان کنند\r\nالغیاث از جور خوبان الغیاث\r\nدر بهای بوسه ای جانی طلب\r\nمی کنند این دلستانان الغیاث\r\nخون ما خوردند این کافردلان\r\nای مسلمانان چه درمان الغیاث\r\nهمچو حافظ روز و شب بی خویشتن\r\nگشته ام سوزان و گریان الغیاث\r\n', 'درد دل تو و فریادها و آه های جگر سوزت درمان ندارد. هر چند که تقوی پیشه کرده ای و دل و دین را یکجا پای وصل گذاشته ای. هر چه می کشی از آشنایان است حاضری حتی برای رسیدن به وصال یار جانت را هم فدا کنی. با گریه و زاری نیز مشکلت حل نمی شود. '),
(97, 'تویی که بر سر خوبان کشوری چون تاج\r\nسزد اگر همه دلبران دهندت باج\r\nدو چشم شوخ تو برهم زده خطا و حبش\r\nبه چین زلف تو ماچین و هند داده خراج\r\nبیاض روی تو روشن چو عارض رخ روز\r\nسواد زلف سیاه تو هست ظلمت داج\r\nدهان شهد تو داده رواج آب خضر\r\nلب چو قند تو برد از نبات مصر رواج\r\nاز این مرض به حقیقت شفا نخواهم یافت\r\nکه از تو درد دل ای جان نمی رسد به علاج\r\nچرا همی شکنی جان من ز سنگ دلی\r\nدل ضعیف که باشد به نازکی چو زجاج\r\nلب تو خضر و دهان تو آب حیوان است\r\nقد تو سرو و میان موی و بر به هیات عاج\r\nفتاد در دل حافظ هوای چون تو شهی\r\nکمینه ذره خاک در تو بودی کاج\r\n', 'اینقدر ظاهربین نباش. ظواهر تو را فریب داده اند و بابت این مسئله از تو سوء استفاده می کنند. فکر می کنی اگر جواب رد بشنوی میمیری و شفای دردت را فقط رسیدن به یار میدانی. هر چند که او کوچکترین توجهی به تو ندارد. خودت را خسته نکن. '),
(98, 'اگر به مذهب تو خون عاشق است مباح\r\nصلاح ما همه آن است کان تو راست صلاح\r\nسواد زلف سیاه تو جاعل الظلمات\r\nبیاض روی چو ماه تو فالق الاصباح\r\nز چین زلف کمندت کسی نیافت خلاص\r\nاز آن کمانچه ابرو و تیر چشم نجاح\r\nز دیده ام شده یک چشمه در کنار روان\r\nکه آشنا نکند در میان آن ملاح\r\nلب چو آب حیات تو هست قوت جان\r\nوجود خاکی ما را از اوست ذکر رواح\r\nبداد لعل لبت بوسه ای به صد زاری\r\nگرفت کام دلم ز او به صد هزار الحاح\r\nدعای جان تو ورد زبان مشتاقان\r\nهمیشه تا که بود متصل مسا و صباح\r\nصلاح و توبه و تقوی ز ما مجو حافظ\r\nز رند و عاشق و مجنون کسی نیافت صلاح\r\n', 'هر چیزی را که برای خود نمی پسندی برای دیگران هم مپسند. خود را گرفتار مسئله ای کرده ای که نجات از آن بسیار سخت است. آنقدر خود را غرق در این کار کرده ای که اطرافیان را از یاد برده ای. مقاومت کن حتی اگر مجبور شوی التماس کنی. خود را رهایی ده. '),
(99, 'دل من در هوای روی فرخ\r\nبود آشفته همچون موی فرخ\r\nبجز هندوی زلفش هیچ کس نیست\r\nکه برخوردار شد از روی فرخ\r\nسیاهی نیکبخت است آن که دایم\r\nبود همراز و هم زانوی فرخ\r\nشود چون بید لرزان سرو آزاد\r\nاگر بیند قد دلجوی فرخ\r\nبده ساقی شراب ارغوانی\r\nبه یاد نرگس جادوی فرخ\r\nدوتا شد قامتم همچون کمانی\r\nز غم پیوسته چون ابروی فرخ\r\nنسیم مشک تاتاری خجل کرد\r\nشمیم زلف عنبربوی فرخ\r\nاگر میل دل هر کس به جایست\r\nبود میل دل من سوی فرخ\r\nغلام همت آنم که باشد\r\nچو حافظ بنده و هندوی فرخ\r\n', 'آنقدر در حال و هوای یار به وصال فکر کرده ای که زندگی را فراموش کرده و تمام سختی ها را به جان خریده ای. دل . دیده ات را با گریه به تاراج داده دیگر اشکی باقی نمانده است. هر چه مقدر و قسمت باشد همان می شود. امیدت را هیچ وقت از دست نده چون خداوند ارحم و الراحمین است. '),
(100, 'دی پیر می فروش که ذکرش به خیر باد\r\nگفتا شراب نوش و غم دل ببر ز یاد\r\nگفتم به باد می دهدم باده نام و ننگ\r\nگفتا قبول کن سخن و هر چه باد باد\r\nسود و زیان و مایه چو خواهد شدن ز دست\r\nاز بهر این معامله غمگین مباش و شاد\r\nبادت به دست باشد اگر دل نهی به هیچ\r\nدر معرضی که تخت سلیمان رود به باد\r\nحافظ گرت ز پند حکیمان ملالت است\r\nکوته کنیم قصه که عمرت دراز باد\r\n', 'توکل به خدا کن. در کاری که انجام می دهی بدی وجود ندارد و عاقبت بسیار خوبی دارد. قبول کن چیزی را از دست نمی دهی. غصه نخور. شاد باش که به مقام و پول یکجا دست پیدا می کنی. از پند دیگران ناراحت نشو. عمرت هم طولانی است. '),
(101, 'شراب و عیش نهان چیست کار بی بنیاد\r\nزدیم بر صف رندان و هر چه بادا باد\r\nگره ز دل بگشا و از سپهر یاد مکن\r\nکه فکر هیچ مهندس چنین گره نگشاد\r\nز انقلاب زمانه عجب مدار که چرخ\r\nاز این فسانه هزاران هزار دارد یاد\r\nقدح به شرط ادب گیر زان که ترکیبش\r\nز کاسه سر جمشید و بهمن است و قباد\r\nکه آگه است که کاووس و کی کجا رفتند\r\nکه واقف است که چون رفت تخت جم بر باد\r\nز حسرت لب شیرین هنوز می بینم\r\nکه لاله می دمد از خون دیده فرهاد\r\nمگر که لاله بدانست بی وفایی دهر\r\nکه تا بزاد و بشد جام می ز کف ننهاد\r\nبیا بیا که زمانی ز می خراب شویم\r\nمگر رسیم به گنجی در این خراب آباد\r\nنمی دهند اجازت مرا به سیر و سفر\r\nنسیم باد مصلا و آب رکن آباد\r\nقدح مگیر چو حافظ مگر به ناله چنگ\r\nکه بسته اند بر ابریشم طرب دل شاد\r\n', 'در رویا و خیال پردازی ها غرق شده ای. آرزوهایت تمامی ندارد و دور و دراز است. وقت گران مایه ی خود را تلف می کنی. هیچکس آنقدر عمر نمی کند که به تمام آرزوهایش برسد. پس همین چند صباحی را که زنده هستی از زندگیت لذت ببر. آنچه از زندگی که باید قسمتت باشد حتما به تو خواهد رسید. '),
(102, 'دوش آگهی ز یار سفرکرده داد باد\r\nمن نیز دل به باد دهم هر چه باد باد\r\nکارم بدان رسید که همراز خود کنم\r\nهر شام برق لامع و هر بامداد باد\r\nدر چین طره تو دل بی حفاظ من\r\nهرگز نگفت مسکن مالوف یاد باد\r\nامروز قدر پند عزیزان شناختم\r\nیا رب روان ناصح ما از تو شاد باد\r\nخون شد دلم به یاد تو هر گه که در چمن\r\nبند قبای غنچه گل می گشاد باد\r\nاز دست رفته بود وجود ضعیف من\r\nصبحم به بوی وصل تو جان بازداد باد\r\nحافظ نهاد نیک تو کامت برآورد\r\nجان ها فدای مردم نیکونهاد باد\r\n', 'یاد ایام قدیم دلت را شاد می کند. به یاد روزهایی می افتی که صبح و شام آزاد بودی. درد دل زیادی داری و به دنبال یک هم راز و هم سخن می گردی. خود را ضعیف می پنداری اما دلت قوی است. حالا می فهمی که نصیحت بزرگان چقدر باعث موفقیتت شده. تمام خاطرات با قید تاریخ روی قلبت حک شده است و تو تا آخر عمرت به نیک نامی و کامروایی زندگی می کنی. '),
(103, 'روز وصل دوستداران یاد باد\r\nیاد باد آن روزگاران یاد باد\r\nکامم از تلخی غم چون زهر گشت\r\nبانگ نوش شادخواران یاد باد\r\nگر چه یاران فارغند از یاد من\r\nاز من ایشان را هزاران یاد باد\r\nمبتلا گشتم در این بند و بلا\r\nکوشش آن حق گزاران یاد باد\r\nگر چه صد رود است در چشمم مدام\r\nزنده رود باغ کاران یاد باد\r\nراز حافظ بعد از این ناگفته ماند\r\nای دریغا رازداران یاد باد\r\n', 'خیلی غمگین هستی. فکر می کنی همه تو را فراموش کرده اند و دیگر وفا وجود ندارد. تدبیری برای غم خود کن. رازت را به کسی نگو. فعلا در دام بلا هستی که با سعی خودت نجات پیدا می کنی. به عنایت خداوند امیدوار باش. '),
(104, 'جمالت آفتاب هر نظر باد\r\nز خوبی روی خوبت خوبتر باد\r\nهمای زلف شاهین شهپرت را\r\nدل شاهان عالم زیر پر باد\r\nکسی کو بسته زلفت نباشد\r\nچو زلفت درهم و زیر و زبر باد\r\nدلی کو عاشق رویت نباشد\r\nهمیشه غرقه در خون جگر باد\r\nبتا چون غمزه ات ناوک فشاند\r\nدل مجروح من پیشش سپر باد\r\nچو لعل شکرینت بوسه بخشد\r\nمذاق جان من ز او پرشکر باد\r\nمرا از توست هر دم تازه عشقی\r\nتو را هر ساعتی حسنی دگر باد\r\nبه جان مشتاق روی توست حافظ\r\nتو را در حال مشتاقان نظر باد\r\n', 'روز به روز کارت رونق بیشتر می گیرد. همای سعادت بالای سرت سایه انداخته است. در جستجوی چیزی هستی که نزدیک توست. بیشتر دقت کن، پیدایش می کنی. هر کس خواست به تو بلایی برساند خود دچار بلا شد. هر لحظه مقامت و زندگیت بهتر از لحظه ی پیش می شود. در این حال از یاد خدا غافل نشو. '),
(105, 'صوفی ار باده به اندازه خورد نوشش باد\r\nور نه اندیشه این کار فراموشش باد\r\nآن که یک جرعه می از دست تواند دادن\r\nدست با شاهد مقصود در آغوشش باد\r\nپیر ما گفت خطا بر قلم صنع نرفت\r\nآفرین بر نظر پاک خطاپوشش باد\r\nشاه ترکان سخن مدعیان می شنود\r\nشرمی از مظلمه خون سیاووشش باد\r\nگر چه از کبر سخن با من درویش نگفت\r\nجان فدای شکرین پسته خاموشش باد\r\nچشمم از آینه داران خط و خالش گشت\r\nلبم از بوسه ربایان بر و دوشش باد\r\nنرگس مست نوازش کن مردم دارش\r\nخون عاشق به قدح گر بخورد نوشش باد\r\nبه غلامی تو مشهور جهان شد حافظ\r\nحلقه بندگی زلف تو در گوشش باد\r\n', 'در جمع آوری پول و ثروت خیلی زیاده روی می کنی. بیش از اندازه برایت دردسر ساز می شود. قسمتی از مالت را انفاق کن آن وقت است که به مقاصد دیگر خود هم می رسی. مدعی هستی که مظلوم واقع شده ای اما اینطور نیست بلکه یک عده هستند که زیردست تو مظلوم واقع می شوند و تو به آنها فکر نمی کنی. مردم دار باش. کرم داشته باش. تو هم بنده ی خدا باش تا همیشه از خوبی زبانزد باشی.  '),
(106, 'تنت به ناز طبیبان نیازمند مباد\r\nوجود نازکت آزرده گزند مباد\r\nسلامت همه آفاق در سلامت توست\r\nبه هیچ عارضه شخص تو دردمند مباد\r\nجمال صورت و معنی ز امن صحت توست\r\nکه ظاهرت دژم و باطنت نژند مباد\r\nدر این چمن چو درآید خزان به یغمایی\r\nرهش به سرو سهی قامت بلند مباد\r\nدر آن بساط که حسن تو جلوه آغازد\r\nمجال طعنه بدبین و بدپسند مباد\r\nهر آن که روی چو ماهت به چشم بد بیند\r\nبر آتش تو بجز جان او سپند مباد\r\nشفا ز گفته شکرفشان حافظ جوی\r\nکه حاجتت به علاج گلاب و قند مباد\r\n', 'از اینکه همیشه سلامت و تندرست هستی خدا را شکر نمی کنی. با اینکه ظاهرا خشن هستی اما باطنی بس لطیف داری. از چشم زخم ایمن هستی. رقیب نداری از طعنه ی اطرافیان نرنج. کار خودت را انجام بده. حاجت تو برآورده می شود. '),
(107, 'حسن تو همیشه در فزون باد\r\nرویت همه ساله لاله گون باد\r\nاندر سر ما خیال عشقت\r\nهر روز که باد در فزون باد\r\nهر سرو که در چمن درآید\r\nدر خدمت قامتت نگون باد\r\nچشمی که نه فتنه تو باشد\r\nچون گوهر اشک غرق خون باد\r\nچشم تو ز بهر دلربایی\r\nدر کردن سحر ذوفنون باد\r\nهر جا که دلیست در غم تو\r\nبی صبر و قرار و بی سکون باد\r\nقد همه دلبران عالم\r\nپیش الف قدت چو نون باد\r\nهر دل که ز عشق توست خالی\r\nاز حلقه وصل تو برون باد\r\nلعل تو که هست جان حافظ\r\nدور از لب مردمان دون باد\r\n', 'خوبی های و محسنات تان همه را مجذوب خود ساخته و این یکی ار دلایل خوبی باطن شماست. دوستان و آشنایان جملگی شما را دوست دارند و هواخواهتان هستند. همه در مقابل تان خودشان را کوچک می دانند. فتنه را خاموش و آشتی ها را برپا می سازید. کسی از شما ناراحتی ندارد. همه در حق شما دعا و ثنا می گویند. '),
(108, 'خسروا گوی فلک در خم چوگان تو باد\r\nساحت کون و مکان عرصه میدان تو باد\r\nزلف خاتون ظفر شیفته پرچم توست\r\nدیده فتح ابد عاشق جولان تو باد\r\nای که انشا عطارد صفت شوکت توست\r\nعقل کل چاکر طغراکش دیوان تو باد\r\nطیره جلوه طوبی قد چون سرو تو شد\r\nغیرت خلد برین ساحت بستان تو باد\r\nنه به تنها حیوانات و نباتات و جماد\r\nهر چه در عالم امر است به فرمان تو باد\r\n', 'شما پیشرفت زیادی می کنید. با حسن خلقی که دارید همه به شما علاقمند هستند. از خوبی و مهربانی دست بر ندار که راز موفقیت شما در همین خصلت است. '),
(109, 'دیر است که دلدار پیامی نفرستاد\r\nننوشت سلامی و کلامی نفرستاد\r\nصد نامه فرستادم و آن شاه سواران\r\nپیکی ندوانید و سلامی نفرستاد\r\nسوی من وحشی صفت عقل رمیده\r\nآهوروشی کبک خرامی نفرستاد\r\nدانست که خواهد شدنم مرغ دل از دست\r\nو از آن خط چون سلسله دامی نفرستاد\r\nفریاد که آن ساقی شکرلب سرمست\r\nدانست که مخمورم و جامی نفرستاد\r\nچندان که زدم لاف کرامات و مقامات\r\nهیچم خبر از هیچ مقامی نفرستاد\r\nحافظ به ادب باش که واخواست نباشد\r\nگر شاه پیامی به غلامی نفرستاد\r\n', 'از انتظار خسته شده اید. منتظر جواب ثانیه شماری می کنید. باز هم شانس خود را محک بزنید. برای رسیدن به جواب به کسی دروغ نگویید، چون مشت شما باز می شود. متوسل به حیله نشوید. صبر داشته باشید. انشاالله جواب خواهید گرفت. '),
(110, 'پیرانه سرم عشق جوانی به سر افتاد\r\nوان راز که در دل بنهفتم به درافتاد\r\nاز راه نظر مرغ دلم گشت هواگیر\r\nای دیده نگه کن که به دام که درافتاد\r\nدردا که از آن آهوی مشکین سیه چشم\r\nچون نافه بسی خون دلم در جگر افتاد\r\nاز رهگذر خاک سر کوی شما بود\r\nهر نافه که در دست نسیم سحر افتاد\r\nمژگان تو تا تیغ جهان گیر برآورد\r\nبس کشته دل زنده که بر یک دگر افتاد\r\nبس تجربه کردیم در این دیر مکافات\r\nبا دردکشان هر که درافتاد برافتاد\r\nگر جان بدهد سنگ سیه لعل نگردد\r\nبا طینت اصلی چه کند بدگهر افتاد\r\nحافظ که سر زلف بتان دست کشش بود\r\nبس طرفه حریفیست کش اکنون به سر افتاد\r\n', 'اتفاقی افتاده که باعث شده به خود بیایی و از خواب غفلت بیدار شوی. کارهای گذشته را جبران کن. در گذشته تجربه های زیادی به دست آورده ای که در آینده همگی به دردت خواهند خورد. کدورت های قدیمی را فراموش کن. با باطنی خوب و خوش زندگی کن و این اتفاق را به فال نیک بگیر. '),
(111, 'عکس روی تو چو در آینه جام افتاد\r\nعارف از خنده می در طمع خام افتاد\r\nحسن روی تو به یک جلوه که در آینه کرد\r\nاین همه نقش در آیینه اوهام افتاد\r\nاین همه عکس می و نقش نگارین که نمود\r\nیک فروغ رخ ساقیست که در جام افتاد\r\nغیرت عشق زبان همه خاصان ببرید\r\nکز کجا سر غمش در دهن عام افتاد\r\nمن ز مسجد به خرابات نه خود افتادم\r\nاینم از عهد ازل حاصل فرجام افتاد\r\nچه کند کز پی دوران نرود چون پرگار\r\nهر که در دایره گردش ایام افتاد\r\nدر خم زلف تو آویخت دل از چاه زنخ\r\nآه کز چاه برون آمد و در دام افتاد\r\nآن شد ای خواجه که در صومعه بازم بینی\r\nکار ما با رخ ساقی و لب جام افتاد\r\nزیر شمشیر غمش رقص کنان باید رفت\r\nکان که شد کشته او نیک سرانجام افتاد\r\nهر دمش با من دلسوخته لطفی دگر است\r\nاین گدا بین که چه شایسته انعام افتاد\r\nصوفیان جمله حریفند و نظرباز ولی\r\nزین میان حافظ دلسوخته بدنام افتاد\r\n', 'هر کسی که شما را برای اولین بار می بیند پی خوش قلبی و ساده دلی شما می برد. چون باطنی خوب دارید فکر می کنید همه مثل شما می باشند. پس مواظب باشید از شما سوء استفاده نکنند، کمی زرنگ تر باشید. خوددار باشید. برای هر کسی دلسوزی نکنید که هر کسی شایسته ی دلسوزی نیست. '),
(112, 'آن که رخسار تو را رنگ گل و نسرین داد\r\nصبر و آرام تواند به من مسکین داد\r\nوان که گیسوی تو را رسم تطاول آموخت\r\nهم تواند کرمش داد من غمگین داد\r\nمن همان روز ز فرهاد طمع ببریدم\r\nکه عنان دل شیدا به لب شیرین داد\r\nگنج زر گر نبود کنج قناعت باقیست\r\nآن که آن داد به شاهان به گدایان این داد\r\nخوش عروسیست جهان از ره صورت لیکن\r\nهر که پیوست بدو عمر خودش کاوین داد\r\nبعد از این دست من و دامن سرو و لب جوی\r\nخاصه اکنون که صبا مژده فروردین داد\r\nدر کف غصه دوران دل حافظ خون شد\r\nاز فراق رخت ای خواجه قوام الدین داد\r\n', 'زمانی به گنج و ثروت می رسی که قناعت داشته باشی. در آن صورت است که خداوند هم کرمش را بیشتر خواهد کرد. حاجتی داری که خیلی زود به آن می رسی به شرط اینکه حق دیگران را ضایع نکنی و بدان جوانی و زیبایی همیشه ماندنی نیست پس جمالت مغرور نباش. '),
(113, 'بنفشه دوش به گل گفت و خوش نشانی داد\r\nکه تاب من به جهان طره فلانی داد\r\nدلم خزانه اسرار بود و دست قضا\r\nدرش ببست و کلیدش به دلستانی داد\r\nشکسته وار به درگاهت آمدم که طبیب\r\nبه مومیایی لطف توام نشانی داد\r\nتنش درست و دلش شاد باد و خاطر خوش\r\nکه دست دادش و یاری ناتوانی داد\r\nبرو معالجه خود کن ای نصیحتگو\r\nشراب و شاهد شیرین که را زیانی داد\r\nگذشت بر من مسکین و با رقیبان گفت\r\nدریغ حافظ مسکین من چه جانی داد\r\n', 'مقدمات رسیدن به مرادت مهیا شده. دست قضا همه چیز را روبراه کرده و درهای بسته به رویت باز می شوند و دوباره جوان می شوی و شادابی خود را به دست می آوری. فقط مواظب حریفان خود باش که اینبار هم سرمایه ی تو را از بین نبرند. اسرارت را هم برای خودت حفظ کن. '),
(114, 'همای اوج سعادت به دام ما افتد\r\nاگر تو را گذری بر مقام ما افتد\r\nحباب وار براندازم از نشاط کلاه\r\nاگر ز روی تو عکسی به جام ما افتد\r\nشبی که ماه مراد از افق شود طالع\r\nبود که پرتو نوری به بام ما افتد\r\nبه بارگاه تو چون باد را نباشد بار\r\nکی اتفاق مجال سلام ما افتد\r\nچو جان فدای لبش شد خیال می بستم\r\nکه قطره ای ز زلالش به کام ما افتد\r\nخیال زلف تو گفتا که جان وسیله مساز\r\nکز این شکار فراوان به دام ما افتد\r\nبه ناامیدی از این در مرو بزن فالی\r\nبود که قرعه دولت به نام ما افتد\r\nز خاک کوی تو هر گه که دم زند حافظ\r\nنسیم گلشن جان در مشام ما افتد\r\n', 'تقاضای خویش را بلند بگو تا شاید کسی حرف هایت را بشنود و یا اینکه شکار به دام تو بیافتد یعنی ایکه به مقصد برسی. این فال را هم به این امید باز کرده ای که دولت و شام نصیبت شود. پس آن روز بسیار نزدیک است و تو به بلندترین جای خوشبختی و مقام دست پیدا می کنی. '),
(115, 'درخت دوستی بنشان که کام دل به بار آرد\r\nنهال دشمنی برکن که رنج بی شمار آرد\r\nچو مهمان خراباتی به عزت باش با رندان\r\nکه درد سر کشی جانا گرت مستی خمار آرد\r\nشب صحبت غنیمت دان که بعد از روزگار ما\r\nبسی گردش کند گردون بسی لیل و نهار آرد\r\nعماری دار لیلی را که مهد ماه در حکم است\r\nخدا را در دل اندازش که بر مجنون گذار آرد\r\nبهار عمر خواه ای دل وگرنه این چمن هر سال\r\nچو نسرین صد گل آرد بار و چون بلبل هزار آرد\r\nخدا را چون دل ریشم قراری بست با زلفت\r\nبفرما لعل نوشین را که زودش باقرار آرد\r\nدر این باغ از خدا خواهد دگر پیرانه سر حافظ\r\nنشیند بر لب جویی و سروی در کنار آرد\r\n', 'دنیا پستی و بلندی های زیادی دارد. گاهی خوب و گاهی بد، گاهی خزان و گاهی بهار در هر صورت تو فقط به دوستی ها بیندیش تا التیام دل بی قرار دیگران باشی. بدون دوست خود را تهی می دانی از خدا می خواهی همین چند صباحی را که زنده هستی در کنار دوست باشی و با دوست بمیری. '),
(116, 'کسی که حسن و خط دوست در نظر دارد\r\nمحقق است که او حاصل بصر دارد\r\nچو خامه در ره فرمان او سر طاعت\r\nنهاده ایم مگر او به تیغ بردارد\r\nکسی به وصل تو چون شمع یافت پروانه\r\nکه زیر تیغ تو هر دم سری دگر دارد\r\nبه پای بوس تو دست کسی رسید که او\r\nچو آستانه بدین در همیشه سر دارد\r\nز زهد خشک ملولم کجاست باده ناب\r\nکه بوی باده مدامم دماغ تر دارد\r\nز باده هیچت اگر نیست این نه بس که تو را\r\nدمی ز وسوسه عقل بی خبر دارد\r\nکسی که از ره تقوا قدم برون ننهاد\r\nبه عزم میکده اکنون ره سفر دارد\r\nدل شکسته حافظ به خاک خواهد برد\r\nچو لاله داغ هوایی که بر جگر دارد\r\n', 'به هر کسی که ظاهر عالی و خوب داشت اعتماد نکنید. حرف های زیبا و با ادب او شما را تحت تاثیر قرار ندهد. در اصل طبل توخالی است. به حرف و نصیحت کسی گوش کنید که واقعاً برایتان دل می سوزاند و با تجربه تر از شماست. دلتان می خواهد به یک سفر بروید برایتان خوب است. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(117, 'دل ما به دور رویت ز چمن فراغ دارد\r\nکه چو سرو پایبند است و چو لاله داغ دارد\r\nسر ما فرونیاید به کمان ابروی کس\r\nکه درون گوشه گیران ز جهان فراغ دارد\r\nز بنفشه تاب دارم که ز زلف او زند دم\r\nتو سیاه کم بها بین که چه در دماغ دارد\r\nبه چمن خرام و بنگر بر تخت گل که لاله\r\nبه ندیم شاه ماند که به کف ایاغ دارد\r\nشب ظلمت و بیابان به کجا توان رسیدن\r\nمگر آن که شمع رویت به رهم چراغ دارد\r\nمن و شمع صبحگاهی سزد ار به هم بگرییم\r\nکه بسوختیم و از ما بت ما فراغ دارد\r\nسزدم چو ابر بهمن که بر این چمن بگریم\r\nطرب آشیان بلبل بنگر که زاغ دارد\r\nسر درس عشق دارد دل دردمند حافظ\r\nکه نه خاطر تماشا نه هوای باغ دارد\r\n', 'چاره ای غیر از سکوت ندارید. راهتان را گم کرده اید. بسیار بی تاب هستید. همه تو را دست کم می گیرند نسبت به این موضوع بی تفاوت باشید. کسانی با تو هم صحبت هستند که در ظاهر نصیحت ولی در باطن و پشت سر با تو دشمنی می کنند. '),
(118, 'آن کس که به دست جام دارد\r\nسلطانی جم مدام دارد\r\nآبی که خضر حیات از او یافت\r\nدر میکده جو که جام دارد\r\nسررشته جان به جام بگذار\r\nکاین رشته از او نظام دارد\r\nما و می و زاهدان و تقوا\r\nتا یار سر کدام دارد\r\nبیرون ز لب تو ساقیا نیست\r\nدر دور کسی که کام دارد\r\nنرگس همه شیوه های مستی\r\nاز چشم خوشت به وام دارد\r\nذکر رخ و زلف تو دلم را\r\nوردیست که صبح و شام دارد\r\nبر سینه ریش دردمندان\r\nلعلت نمکی تمام دارد\r\nدر چاه ذقن چو حافظ ای جان\r\nحسن تو دو صد غلام دارد\r\n', 'هر کس که در خانه ی او را زد ناامید بر نمی گردد. پس برای بازگشت سلامتی، دست به سوی او دراز کنید. از او بخواهید در ازای سلامتی، روح و جسم خویش را در راهش صرف کنید. خداوند هم به این حسن نیت شما از سر گناهتان خواهد گذشت. انشاالله. '),
(119, 'دلی که غیب نمای است و جام جم دارد\r\nز خاتمی که دمی گم شود چه غم دارد\r\nبه خط و خال گدایان مده خزینه دل\r\nبه دست شاهوشی ده که محترم دارد\r\nنه هر درخت تحمل کند جفای خزان\r\nغلام همت سروم که این قدم دارد\r\nرسید موسم آن کز طرب چو نرگس مست\r\nنهد به پای قدح هر که شش درم دارد\r\nزر از بهای می اکنون چو گل دریغ مدار\r\nکه عقل کل به صدت عیب متهم دارد\r\nز سر غیب کس آگاه نیست قصه مخوان\r\nکدام محرم دل ره در این حرم دارد\r\nدلم که لاف تجرد زدی کنون صد شغل\r\nبه بوی زلف تو با باد صبحدم دارد\r\nمراد دل ز که پرسم که نیست دلداری\r\nکه جلوه نظر و شیوه کرم دارد\r\nز جیب خرقه حافظ چه طرف بتوان بست\r\nکه ما صمد طلبیدیم و او صنم دارد\r\n', 'تو که خودت به همه چیز واقف هستی پس چرا غصه می خوری. کاری نکن که احترام خودت را از دست بدهی. سرمایه ی اندک تو تبدیل به بزرگترین سرمایه ها خواهد شد. و این بخاطر تدبیر و عقل خودت می باشد. تو از خدا طلب کن او هم به تو کرم می کند.  '),
(120, 'بتی دارم که گرد گل ز سنبل سایه بان دارد\r\nبهار عارضش خطی به خون ارغوان دارد\r\nغبار خط بپوشانید خورشید رخش یا رب\r\nبقای جاودانش ده که حسن جاودان دارد\r\nچو عاشق می شدم گفتم که بردم گوهر مقصود\r\nندانستم که این دریا چه موج خون فشان دارد\r\nز چشمت جان نشاید برد کز هر سو که می بینم\r\nکمین از گوشه ای کرده ست و تیر اندر کمان دارد\r\nچو دام طره افشاند ز گرد خاطر عشاق\r\nبه غماز صبا گوید که راز ما نهان دارد\r\nبیفشان جرعه ای بر خاک و حال اهل دل بشنو\r\nکه از جمشید و کیخسرو فراوان داستان دارد\r\nچو در رویت بخندد گل مشو در دامش ای بلبل\r\nکه بر گل اعتمادی نیست گر حسن جهان دارد\r\nخدا را داد من بستان از او ای شحنه مجلس\r\nکه می با دیگری خورده ست و با من سر گران دارد\r\nبه فتراک ار همی بندی خدا را زود صیدم کن\r\nکه آفت هاست در تاخیر و طالب را زیان دارد\r\nز سروقد دلجویت مکن محروم چشمم را\r\nبدین سرچشمه اش بنشان که خوش آبی روان دارد\r\nز خوف هجرم ایمن کن اگر امید آن داری\r\nکه از چشم بداندیشان خدایت در امان دارد\r\nچه عذر بخت خود گویم که آن عیار شهرآشوب\r\nبه تلخی کشت حافظ را و شکر در دهان دارد\r\n', 'شما مثل کسی هستید که بدون تامل کاری را انجام می دهد. ظواهر شما را فریفته است. تازه مشکلات شما بعد از این شروع می شود. بهتر است در مورد کاری که می خواهید انجام دهید مشورت کنید وگرنه به شما آفت می رسد. تاخیر جایز نیست از تجربه ی دیگران استفاده کنید. '),
(121, 'هر آن کو خاطر مجموع و یار نازنین دارد\r\nسعادت همدم او گشت و دولت همنشین دارد\r\nحریم عشق را درگه بسی بالاتر از عقل است\r\nکسی آن آستان بوسد که جان در آستین دارد\r\nدهان تنگ شیرینش مگر ملک سلیمان است\r\nکه نقش خاتم لعلش جهان زیر نگین دارد\r\nلب لعل و خط مشکین چو آنش هست و اینش هست\r\nبنازم دلبر خود را که حسنش آن و این دارد\r\nبه خواری منگر ای منعم ضعیفان و نحیفان را\r\nکه صدر مجلس عشرت گدای رهنشین دارد\r\nچو بر روی زمین باشی توانایی غنیمت دان\r\nکه دوران ناتوانی ها بسی زیر زمین دارد\r\nبلاگردان جان و تن دعای مستمندان است\r\nکه بیند خیر از آن خرمن که ننگ از خوشه چین دارد\r\nصبا از عشق من رمزی بگو با آن شه خوبان\r\nکه صد جمشید و کیخسرو غلام کمترین دارد\r\nو گر گوید نمی خواهم چو حافظ عاشق مفلس\r\nبگوییدش که سلطانی گدایی همنشین دارد\r\n', 'آفرین بر شما که تصمیم عاقلانه ای گرفته اید. سعادت و دولت یکجا نصیبتان می شود. در کاری که انجام می دهید ذلت و خواری وجود ندارد. به وصال یاری می رسید که از هر سو حسن دارد. توانایی های خودتان را نشان بدهید و مثل همیشه به مستمندان کمک کنید. '),
(122, 'هر آن که جانب اهل خدا نگه دارد\r\nخداش در همه حال از بلا نگه دارد\r\nحدیث دوست نگویم مگر به حضرت دوست\r\nکه آشنا سخن آشنا نگه دارد\r\nدلا معاش چنان کن که گر بلغزد پای\r\nفرشته ات به دو دست دعا نگه دارد\r\nگرت هواست که معشوق نگسلد پیمان\r\nنگاه دار سر رشته تا نگه دارد\r\nصبا بر آن سر زلف ار دل مرا بینی\r\nز روی لطف بگویش که جا نگه دارد\r\nچو گفتمش که دلم را نگاه دار چه گفت\r\nز دست بنده چه خیزد خدا نگه دارد\r\nسر و زر و دل و جانم فدای آن یاری\r\nکه حق صحبت مهر و وفا نگه دارد\r\nغبار راه راهگذارت کجاست تا حافظ\r\nبه یادگار نسیم صبا نگه دارد\r\n', 'در همه حال تعادل را حفظ کنید. زیاده روی در هر کاری به شما بلا می رساند. اگر می خواهید عهد و پیمان بین شما از بین نرود حد خود را حفظ کنید. در هر حال از خداوند کمک بطلبید. از دست بنده ی خدا کاری ساخته نیست. '),
(123, 'مطرب عشق عجب ساز و نوایی دارد\r\nنقش هر نغمه که زد راه به جایی دارد\r\nعالم از ناله عشاق مبادا خالی\r\nکه خوش آهنگ و فرح بخش هوایی دارد\r\nپیر دردی کش ما گر چه ندارد زر و زور\r\nخوش عطابخش و خطاپوش خدایی دارد\r\nمحترم دار دلم کاین مگس قندپرست\r\nتا هواخواه تو شد فر همایی دارد\r\nاز عدالت نبود دور گرش پرسد حال\r\nپادشاهی که به همسایه گدایی دارد\r\nاشک خونین بنمودم به طبیبان گفتند\r\nدرد عشق است و جگرسوز دوایی دارد\r\nستم از غمزه میاموز که در مذهب عشق\r\nهر عمل اجری و هر کرده جزایی دارد\r\nنغز گفت آن بت ترسابچه باده پرست\r\nشادی روی کسی خور که صفایی دارد\r\nخسروا حافظ درگاه نشین فاتحه خواند\r\nو از زبان تو تمنای دعایی دارد\r\n', 'در زندگی، عشق و محبت را فراموش نکن مخصوصاً در زندگی خصوصی خود. ازدواج موفقیت آمیزی در پیش داری. با کسی که دوست داری زندگی سرشار از عشق و محبت خواهی داشت.  '),
(124, 'آن که از سنبل او غالیه تابی دارد\r\nباز با دلشدگان ناز و عتابی دارد\r\nاز سر کشته خود می گذری همچون باد\r\nچه توان کرد که عمر است و شتابی دارد\r\nماه خورشید نمایش ز پس پرده زلف\r\nآفتابیست که در پیش سحابی دارد\r\nچشم من کرد به هر گوشه روان سیل سرشک\r\nتا سهی سرو تو را تازه تر آبی دارد\r\nغمزه شوخ تو خونم به خطا می ریزد\r\nفرصتش باد که خوش فکر صوابی دارد\r\nآب حیوان اگر این است که دارد لب دوست\r\nروشن است این که خضر بهره سرابی دارد\r\nچشم مخمور تو دارد ز دلم قصد جگر\r\nترک مست است مگر میل کبابی دارد\r\nجان بیمار مرا نیست ز تو روی سوال\r\nای خوش آن خسته که از دوست جوابی دارد\r\nکی کند سوی دل خسته حافظ نظری\r\nچشم مستش که به هر گوشه خرابی دارد\r\n', 'گذشت داشته باشید. عفو با بزرگان است. لذتی که در عفو هست در انتقام نیست. عمر مثل باد می گذرد. فرصت را از دستندهید. دل خسته ای را به دست آورید که ثوابی بالاتر از این وجود ندارد. '),
(125, 'شاهد آن نیست که مویی و میانی دارد\r\nبنده طلعت آن باش که آنی دارد\r\nشیوه حور و پری گر چه لطیف است ولی\r\nخوبی آن است و لطافت که فلانی دارد\r\nچشمه چشم مرا ای گل خندان دریاب\r\nکه به امید تو خوش آب روانی دارد\r\nگوی خوبی که برد از تو که خورشید آن جا\r\nنه سواریست که در دست عنانی دارد\r\nدل نشان شد سخنم تا تو قبولش کردی\r\nآری آری سخن عشق نشانی دارد\r\nخم ابروی تو در صنعت تیراندازی\r\nبرده از دست هر آن کس که کمانی دارد\r\nدر ره عشق نشد کس به یقین محرم راز\r\nهر کسی بر حسب فکر گمانی دارد\r\nبا خرابات نشینان ز کرامات ملاف\r\nهر سخن وقتی و هر نکته مکانی دارد\r\nمرغ زیرک نزند در چمنش پرده سرای\r\nهر بهاری که به دنباله خزانی دارد\r\nمدعی گو لغز و نکته به حافظ مفروش\r\nکلک ما نیز زبانی و بیانی دارد\r\n', 'هر حرفی را در هر جا نزنید که به ضرر خودتان تمام می شود. خیلی زود گول ظاهر را می خورید. صورت زیبا نشان از سیرت زیبا نیست. مهم اخلاق و قلب پاک افراد است. '),
(126, 'جان بی جمال جانان میل جهان ندارد\r\nهر کس که این ندارد حقا که آن ندارد\r\nبا هیچ کس نشانی زان دلستان ندیدم\r\nیا من خبر ندارم یا او نشان ندارد\r\nهر شبنمی در این ره صد بحر آتشین است\r\nدردا که این معما شرح و بیان ندارد\r\nسرمنزل فراغت نتوان ز دست دادن\r\nای ساروان فروکش کاین ره کران ندارد\r\nچنگ خمیده قامت می خواندت به عشرت\r\nبشنو که پند پیران هیچت زیان ندارد\r\nای دل طریق رندی از محتسب بیاموز\r\nمست است و در حق او کس این گمان ندارد\r\nاحوال گنج قارون کایام داد بر باد\r\nدر گوش دل فروخوان تا زر نهان ندارد\r\nگر خود رقیب شمع است اسرار از او بپوشان\r\nکان شوخ سربریده بند زبان ندارد\r\nکس در جهان ندارد یک بنده همچو حافظ\r\nزیرا که چون تو شاهی کس در جهان ندارد\r\n', 'نامید نشوید. بیشتر جستجو کنید. هر چند که هر چه در پی اش می گردید کمتر نشانی از او پیدا می کنید. از راهنما کمک بگیرید وگرنه هیچ وقت به مقصود نمی رسید. مالی را از دست داده اید که بدون تدبیر و تفکر به دست نخواهید آورد. '),
(127, 'روشنی طلعت تو ماه ندارد\r\nپیش تو گل رونق گیاه ندارد\r\nگوشه ابروی توست منزل جانم\r\nخوشتر از این گوشه پادشاه ندارد\r\nتا چه کند با رخ تو دود دل من\r\nآینه دانی که تاب آه ندارد\r\nشوخی نرگس نگر که پیش تو بشکفت\r\nچشم دریده ادب نگاه ندارد\r\nدیدم و آن چشم دل سیه که تو داری\r\nجانب هیچ آشنا نگاه ندارد\r\nرطل گرانم ده ای مرید خرابات\r\nشادی شیخی که خانقاه ندارد\r\nخون خور و خامش نشین که آن دل نازک\r\nطاقت فریاد دادخواه ندارد\r\nگو برو و آستین به خون جگر شوی\r\nهر که در این آستانه راه ندارد\r\nنی من تنها کشم تطاول زلفت\r\nکیست که او داغ آن سیاه ندارد\r\nحافظ اگر سجده تو کرد مکن عیب\r\nکافر عشق ای صنم گناه ندارد\r\n', 'بسیار انسان خوش شانسی هستید. هر کجا می روید بقیه کنار می کشند چون موفقیت تان در آنجا حتمی است. کاری را که انجام می دهید از دست هیچکس بر نمی آید و این به خاطر ذوق و سلیقه ی خودتان می باشد. مواظب باشید راه خطا نروید چون دشمنان می خواهند شما را خراب کنند. '),
(128, 'نیست در شهر نگاری که دل ما ببرد\r\nبختم ار یار شود رختم از این جا ببرد\r\nکو حریفی کش سرمست که پیش کرمش\r\nعاشق سوخته دل نام تمنا ببرد\r\nباغبانا ز خزان بی خبرت می بینم\r\nآه از آن روز که بادت گل رعنا ببرد\r\nرهزن دهر نخفته ست مشو ایمن از او\r\nاگر امروز نبرده ست که فردا ببرد\r\nدر خیال این همه لعبت به هوس می بازم\r\nبو که صاحب نظری نام تماشا ببرد\r\nعلم و فضلی که به چل سال دلم جمع آورد\r\nترسم آن نرگس مستانه به یغما ببرد\r\nبانگ گاوی چه صدا بازدهد عشوه مخر\r\nسامری کیست که دست از ید بیضا ببرد\r\nجام مینایی می سد ره تنگ دلیست\r\nمنه از دست که سیل غمت از جا ببرد\r\nراه عشق ار چه کمینگاه کمانداران است\r\nهر که دانسته رود صرفه ز اعدا ببرد\r\nحافظ ار جان طلبد غمزه مستانه یار\r\nخانه از غیر بپرداز و بهل تا ببرد\r\n', 'سر در گم شده اید، طلب کمک می کنید. انتظار کرم و بخشش دارید در حالی که کسی به شما کمک نمی کند. خودتان همت داشته و از علم و دانایی خودتان استفاده کنید. در این راه عنایت خداوند شامل حال شماست و دلتنگی تان برطرف می شود. '),
(129, 'اگر نه باده غم دل ز یاد ما ببرد\r\nنهیب حادثه بنیاد ما ز جا ببرد\r\nاگر نه عقل به مستی فروکشد لنگر\r\nچگونه کشتی از این ورطه بلا ببرد\r\nفغان که با همه کس غایبانه باخت فلک\r\nکه کس نبود که دستی از این دغا ببرد\r\nگذار بر ظلمات است خضر راهی کو\r\nمباد کآتش محرومی آب ما ببرد\r\nدل ضعیفم از آن می کشد به طرف چمن\r\nکه جان ز مرگ به بیماری صبا ببرد\r\nطبیب عشق منم باده ده که این معجون\r\nفراغت آرد و اندیشه خطا ببرد\r\nبسوخت حافظ و کس حال او به یار نگفت\r\nمگر نسیم پیامی خدای را ببرد\r\n', 'حادثه ای برایت اتفاق افتاده که حتی عقل هم عاجز از حل آنمی باشد. در تاریکی هستی که روزنه ای برای فرار پیدا نمی کنی. دلت را قوی کن. با فکر کردن به مرگ چیزی حل نمی شود. امیدوار باش، نسیم الهی در حال وزیدن است و مشکلت مثل معجزه ای برطرف می شود. '),
(130, 'سحر بلبل حکایت با صبا کرد\r\nکه عشق روی گل با ما چه ها کرد\r\nاز آن رنگ رخم خون در دل افتاد\r\nو از آن گلشن به خارم مبتلا کرد\r\nغلام همت آن نازنینم\r\nکه کار خیر بی روی و ریا کرد\r\nمن از بیگانگان دیگر ننالم\r\nکه با من هر چه کرد آن آشنا کرد\r\nگر از سلطان طمع کردم خطا بود\r\nور از دلبر وفا جستم جفا کرد\r\nخوشش باد آن نسیم صبحگاهی\r\nکه درد شب نشینان را دوا کرد\r\nنقاب گل کشید و زلف سنبل\r\nگره بند قبای غنچه وا کرد\r\nبه هر سو بلبل عاشق در افغان\r\nتنعم از میان باد صبا کرد\r\nبشارت بر به کوی می فروشان\r\nکه حافظ توبه از زهد ریا کرد\r\nوفا از خواجگان شهر با من\r\nکمال دولت و دین بوالوفا کرد\r\n', 'خود کرده را تدبیر نیست. همت کن از دوست کمک بخواه. ریا و حیله را کنار بگذار و راه راست را پیش بگیر آن وقت می بینی که چطور خداوند به راحتی بلایا را برطرف می کند. تو هم به خودت بیا با خدا عهد و پیمان ببند تا به فر دولت برسی. '),
(131, 'بیا که ترک فلک خوان روزه غارت کرد\r\nهلال عید به دور قدح اشارت کرد\r\nثواب روزه و حج قبول آن کس برد\r\nکه خاک میکده عشق را زیارت کرد\r\nمقام اصلی ما گوشه خرابات است\r\nخداش خیر دهاد آن که این عمارت کرد\r\nبهای باده چون لعل چیست جوهر عقل\r\nبیا که سود کسی برد کاین تجارت کرد\r\nنماز در خم آن ابروان محرابی\r\nکسی کند که به خون جگر طهارت کرد\r\nفغان که نرگس جماش شیخ شهر امروز\r\nنظر به دردکشان از سر حقارت کرد\r\nبه روی یار نظر کن ز دیده منت دار\r\nکه کار دیده نظر از سر بصارت کرد\r\nحدیث عشق ز حافظ شنو نه از واعظ\r\nاگر چه صنعت بسیار در عبارت کرد\r\n', 'کسب و کارتان پررونق می شود و به سود کلانی دست پیدا می کنید. در حال معامله زهد و تقوی را کنار نگذارید چون برکت مال تان از بین می رود. به زیارت مکه ی مکرمه می روید. به کسی به چشم حقارت نگاه نکنید تا همیشه مقام و منزلت داشته باشید. '),
(132, 'به آب روشن می عارفی طهارت کرد\r\nعلی الصباح که میخانه را زیارت کرد\r\nهمین که ساغر زرین خور نهان گردید\r\nهلال عید به دور قدح اشارت کرد\r\nخوشا نماز و نیاز کسی که از سر درد\r\nبه آب دیده و خون جگر طهارت کرد\r\nامام خواجه که بودش سر نماز دراز\r\nبه خون دختر رز خرقه را قصارت کرد\r\nدلم ز حلقه زلفش به جان خرید آشوب\r\nچه سود دید ندانم که این تجارت کرد\r\nاگر امام جماعت طلب کند امروز\r\nخبر دهید که حافظ به می طهارت کرد\r\n', 'رو به سوی خدا آورید آن وقت دلتان آرام می گیرد و طهارت روح و روان پیدا می کنید. نماز را سر وقت بخوانید تا در بهشت قصر و حور و پری نصیبتان شود. این اضطراب زمانی از بین می رود و به مقصود تان می رسید که عاری از گناه باشید. '),
(133, 'صوفی نهاد دام و سر حقه باز کرد\r\nبنیاد مکر با فلک حقه باز کرد\r\nبازی چرخ بشکندش بیضه در کلاه\r\nزیرا که عرض شعبده با اهل راز کرد\r\nساقی بیا که شاهد رعنای صوفیان\r\nدیگر به جلوه آمد و آغاز ناز کرد\r\nاین مطرب از کجاست که ساز عراق ساخت\r\nو آهنگ بازگشت به راه حجاز کرد\r\nای دل بیا که ما به پناه خدا رویم\r\nزان چه آستین کوته و دست دراز کرد\r\nصنعت مکن که هر که محبت نه راست باخت\r\nعشقش به روی دل در معنی فراز کرد\r\nفردا که پیشگاه حقیقت شود پدید\r\nشرمنده ره روی که عمل بر مجاز کرد\r\nای کبک خوش خرام کجا می روی بایست\r\nغره مشو که گربه زاهد نماز کرد\r\nحافظ مکن ملامت رندان که در ازل\r\nما را خدا ز زهد ریا بی نیاز کرد\r\n', 'کسی با ریا و حیله باب دوستی با شما باز کرده است. تازه اول کار است. از همین جا راهت را عوض کن و به خدا پناه ببر. دست دشمن زود رو می شود. اگر این کار را نکنی در آینده ای نزدیک از کرده ی خود پشیمان می شوی. '),
(134, 'بلبلی خون دلی خورد و گلی حاصل کرد\r\nباد غیرت به صدش خار پریشان دل کرد\r\nطوطی ای را به خیال شکری دل خوش بود\r\nناگهش سیل فنا نقش امل باطل کرد\r\nقره العین من آن میوه دل یادش باد\r\nکه چه آسان بشد و کار مرا مشکل کرد\r\nساروان بار من افتاد خدا را مددی\r\nکه امید کرمم همره این محمل کرد\r\nروی خاکی و نم چشم مرا خوار مدار\r\nچرخ فیروزه طربخانه از این کهگل کرد\r\nآه و فریاد که از چشم حسود مه چرخ\r\nدر لحد ماه کمان ابروی من منزل کرد\r\nنزدی شاه رخ و فوت شد امکان حافظ\r\nچه کنم بازی ایام مرا غافل کرد\r\n', 'شما تلاش خودتان را کرده اید، ناراحت نباشید. وجدان تان را ناراحت نکنید تا حدودی هم موفق شده اید ولی حسود زیاد دارید که آنها باعث اخلال در کارتان شده اند. به ائمه توسل جویید از بازی ایام غافل نشوید. فرصت باز هم پیش می آید. '),
(135, 'چو باد عزم سر کوی یار خواهم کرد\r\nنفس به بوی خوشش مشکبار خواهم کرد\r\nبه هرزه بی می و معشوق عمر می گذرد\r\nبطالتم بس از امروز کار خواهم کرد\r\nهر آبروی که اندوختم ز دانش و دین\r\nنثار خاک ره آن نگار خواهم کرد\r\nچو شمع صبحدمم شد ز مهر او روشن\r\nکه عمر در سر این کار و بار خواهم کرد\r\nبه یاد چشم تو خود را خراب خواهم ساخت\r\nبنای عهد قدیم استوار خواهم کرد\r\nصبا کجاست که این جان خون گرفته چو گل\r\nفدای نکهت گیسوی یار خواهم کرد\r\nنفاق و زرق نبخشد صفای دل حافظ\r\nطریق رندی و عشق اختیار خواهم کرد\r\n', 'عجله کنید روزهایتان را به بطالت می گذرانید این باعث می شود که علم و دین را فراموش کنید. به یاد عهدی بیافتید که در زمان قدیم با خود و خدا بسته اید. صفای دل را در راه عشق پیدا کنید که بدون عشق باز هم عمرت به هدر می رود. '),
(136, 'دست در حلقه آن زلف دوتا نتوان کرد\r\nتکیه بر عهد تو و باد صبا نتوان کرد\r\nآن چه سعی است من اندر طلبت بنمایم\r\nاین قدر هست که تغییر قضا نتوان کرد\r\nدامن دوست به صد خون دل افتاد به دست\r\nبه فسوسی که کند خصم رها نتوان کرد\r\nعارضش را به مثل ماه فلک نتوان گفت\r\nنسبت دوست به هر بی سر و پا نتوان کرد\r\nسروبالای من آن گه که درآید به سماع\r\nچه محل جامه جان را که قبا نتوان کرد\r\nنظر پاک تواند رخ جانان دیدن\r\nکه در آیینه نظر جز به صفا نتوان کرد\r\nمشکل عشق نه در حوصله دانش ماست\r\nحل این نکته بدین فکر خطا نتوان کرد\r\nغیرتم کشت که محبوب جهانی لیکن\r\nروز و شب عربده با خلق خدا نتوان کرد\r\nمن چه گویم که تو را نازکی طبع لطیف\r\nتا به حدیست که آهسته دعا نتوان کرد\r\nبجز ابروی تو محراب دل حافظ نیست\r\nطاعت غیر تو در مذهب ما نتوان کرد\r\n', 'تقدیر را نمی توان عوض کرد. با چه می جنگی نمی توانی سرنوشت خود را تغییر دهی. البته تو نظر بدی نداری. با مردم هم سر جنگ نداشته باش. از خداوند طلب یاری کن و نیز هر کسی ار راه رسید نمی تواند رفیق شفیقی برایت باشد. '),
(137, 'دل از من برد و روی از من نهان کرد\r\nخدا را با که این بازی توان کرد\r\nشب تنهاییم در قصد جان بود\r\nخیالش لطف های بی کران کرد\r\nچرا چون لاله خونین دل نباشم\r\nکه با ما نرگس او سرگران کرد\r\nکه را گویم که با این درد جان سوز\r\nطبیبم قصد جان ناتوان کرد\r\nبدان سان سوخت چون شمعم که بر من\r\nصراحی گریه و بربط فغان کرد\r\nصبا گر چاره داری وقت وقت است\r\nکه درد اشتیاقم قصد جان کرد\r\nمیان مهربانان کی توان گفت\r\nکه یار ما چنین گفت و چنان کرد\r\nعدو با جان حافظ آن نکردی\r\nکه تیر چشم آن ابروکمان کرد\r\n', 'بسیار ناراحت هستی، پلی شده ای برای موفقیت دیگران. جلوی ضرر را از هر جا بگیری به نفع توست. حاجتی داری که برای رسیدن به آن طاقت را از کف داده ای زود به نیت خودت می رسی به اندازه ی یک چشم برهم زدن. '),
(138, 'یاد باد آن که ز ما وقت سفر یاد نکرد\r\nبه وداعی دل غمدیده ما شاد نکرد\r\nآن جوان بخت که می زد رقم خیر و قبول\r\nبنده پیر ندانم ز چه آزاد نکرد\r\nکاغذین جامه به خوناب بشویم که فلک\r\nرهنمونیم به پای علم داد نکرد\r\nدل به امید صدایی که مگر در تو رسد\r\nناله ها کرد در این کوه که فرهاد نکرد\r\nسایه تا بازگرفتی ز چمن مرغ سحر\r\nآشیان در شکن طره شمشاد نکرد\r\nشاید ار پیک صبا از تو بیاموزد کار\r\nزان که چالاکتر از این حرکت باد نکرد\r\nکلک مشاطه صنعش نکشد نقش مراد\r\nهر که اقرار بدین حسن خداداد نکرد\r\nمطربا پرده بگردان و بزن راه عراق\r\nکه بدین راه بشد یار و ز ما یاد نکرد\r\nغزلیات عراقیست سرود حافظ\r\nکه شنید این ره دلسوز که فریاد نکرد\r\n', 'گذشته ها گذشته. کینه ها را فراموش کنید و از خودتان مروت و گذشت نشان بدهید. منتظر جواب نامه ای هستید. امیدوار باشید. جوابش خواهد رسید. شما سرمشق دیگران هستید. کری کنید که دیگران از شما عبرت بگیرند. '),
(139, 'رو بر رهش نهادم و بر من گذر نکرد\r\nصد لطف چشم داشتم و یک نظر نکرد\r\nسیل سرشک ما ز دلش کین به درنبرد\r\nدر سنگ خاره قطره باران اثر نکرد\r\nیا رب تو آن جوان دلاور نگاه دار\r\nکز تیر آه گوشه نشینان حذر نکرد\r\nماهی و مرغ دوش ز افغان من نخفت\r\nوان شوخ دیده بین که سر از خواب برنکرد\r\nمی خواستم که میرمش اندر قدم چو شمع\r\nاو خود گذر به ما چو نسیم سحر نکرد\r\nجانا کدام سنگ دل بی کفایتیست\r\nکو پیش زخم تیغ تو جان را سپر نکرد\r\nکلک زبان بریده حافظ در انجمن\r\nبا کس نگفت راز تو تا ترک سر نکرد\r\n', 'امیدوار باش. ناامیدی علامت نرسیدن است. بالاخره یکی از نیات تو مورد قبول خداوند واقع می شود. با این غصه ای که می خوری خود را بیمار کرده ای اطرافیان تو هم از غصه ی تو غصه می خورند خوددار باش و امیدوار. '),
(140, 'دلبر برفت و دلشدگان را خبر نکرد\r\nیاد حریف شهر و رفیق سفر نکرد\r\nیا بخت من طریق مروت فروگذاشت\r\nیا او به شاهراه طریقت گذر نکرد\r\nگفتم مگر به گریه دلش مهربان کنم\r\nچون سخت بود در دل سنگش اثر نکرد\r\nشوخی مکن که مرغ دل بی قرار من\r\nسودای دام عاشقی از سر به درنکرد\r\nهر کس که دید روی تو بوسید چشم من\r\nکاری که کرد دیده من بی نظر نکرد\r\nمن ایستاده تا کنمش جان فدا چو شمع\r\nاو خود گذر به ما چو نسیم سحر نکرد\r\n', 'به جمالت مغرور شده ای. محبت و دوست داشتن را فراموش کرده و به دنبال مال دنیا هستی. این را بدان که ظاهر زیبا و مال دنیا ماندنی نیست. این محبت و عشق است که هیچ وقت پایانی ندارد. به یار خود نظر کن و از راهی که رفته ای برگرد. '),
(141, 'دیدی ای دل که غم عشق دگربار چه کرد\r\nچون بشد دلبر و با یار وفادار چه کرد\r\nآه از آن نرگس جادو که چه بازی انگیخت\r\nآه از آن مست که با مردم هشیار چه کرد\r\nاشک من رنگ شفق یافت ز بی مهری یار\r\nطالع بی شفقت بین که در این کار چه کرد\r\nبرقی از منزل لیلی بدرخشید سحر\r\nوه که با خرمن مجنون دل افگار چه کرد\r\nساقیا جام می ام ده که نگارنده غیب\r\nنیست معلوم که در پرده اسرار چه کرد\r\nآن که پرنقش زد این دایره مینایی\r\nکس ندانست که در گردش پرگار چه کرد\r\nفکر عشق آتش غم در دل حافظ زد و سوخت\r\nیار دیرینه ببینید که با یار چه کرد\r\n', 'با کوچکترین اتفاقی فکر می کنید دنیا به آخر رسیده و بسیار غصه می خورید. خداوند هر چه که برای شما تقدیر کرده همان رقم خواهد خورد. خودتان به فکر چاره باشید و از یار قدیم خودتان کمک بخواهید در نهایت این خواست خدا بوده است. '),
(142, 'دوستان دختر رز توبه ز مستوری کرد\r\nشد سوی محتسب و کار به دستوری کرد\r\nآمد از پرده به مجلس عرقش پاک کنید\r\nتا نگویند حریفان که چرا دوری کرد\r\nمژدگانی بده ای دل که دگر مطرب عشق\r\nراه مستانه زد و چاره مخموری کرد\r\nنه به هفت آب که رنگش به صد آتش نرود\r\nآن چه با خرقه زاهد می انگوری کرد\r\nغنچه گلبن وصلم ز نسیمش بشکفت\r\nمرغ خوشخوان طرب از برگ گل سوری کرد\r\nحافظ افتادگی از دست مده زان که حسود\r\nعرض و مال و دل و دین در سر مغروری کرد\r\n', 'از اینکه مژده ی رسیدن به مقصود را به تو داده اند مغرور نشو و ایمانت را از دست نده. مالت را بیخود به کسی نبخش. کمی خوددار باش به دست آوردن هر چند آسان است وای نگه داشتن آن سخت می باشد. یک حاجت دیگر هم داری که به آن مراد هم میرسی. '),
(143, 'سال ها دل طلب جام جم از ما می کرد\r\nوان چه خود داشت ز بیگانه تمنا می کرد\r\nگوهری کز صدف کون و مکان بیرون است\r\nطلب از گمشدگان لب دریا می کرد\r\nمشکل خویش بر پیر مغان بردم دوش\r\nکو به تایید نظر حل معما می کرد\r\nدیدمش خرم و خندان قدح باده به دست\r\nو اندر آن آینه صد گونه تماشا می کرد\r\nگفتم این جام جهان بین به تو کی داد حکیم\r\nگفت آن روز که این گنبد مینا می کرد\r\nبی دلی در همه احوال خدا با او بود\r\nاو نمی دیدش و از دور خدا را می کرد\r\nاین همه شعبده خویش که می کرد این جا\r\nسامری پیش عصا و ید بیضا می کرد\r\nگفت آن یار کز او گشت سر دار بلند\r\nجرمش این بود که اسرار هویدا می کرد\r\nفیض روح القدس ار باز مدد فرماید\r\nدیگران هم بکنند آن چه مسیحا می کرد\r\nگفتمش سلسله زلف بتان از پی چیست\r\nگفت حافظ گله ای از دل شیدا می کرد\r\n', 'دست نیاز کسی را رد نکنید. اگر مشکلی دارید فقط رو به سوی خدا کنید که او حلال مشکلات است. در همه حال با خدا باشید کسانی هستند که شما را بازیچه قرار می دهند، به آنان اطمینان نکنید زیرا اسرار شما را فاش می کنند و باعث ناراحتی تان می شوند. '),
(144, 'به سر جام جم آن گه نظر توانی کرد\r\nکه خاک میکده کحل بصر توانی کرد\r\nمباش بی می و مطرب که زیر طاق سپهر\r\nبدین ترانه غم از دل به در توانی کرد\r\nگل مراد تو آن گه نقاب بگشاید\r\nکه خدمتش چو نسیم سحر توانی کرد\r\nگدایی در میخانه طرفه اکسیریست\r\nگر این عمل بکنی خاک زر توانی کرد\r\nبه عزم مرحله عشق پیش نه قدمی\r\nکه سودها کنی ار این سفر توانی کرد\r\nتو کز سرای طبیعت نمی روی بیرون\r\nکجا به کوی طریقت گذر توانی کرد\r\nجمال یار ندارد نقاب و پرده ولی\r\nغبار ره بنشان تا نظر توانی کرد\r\nبیا که چاره ذوق حضور و نظم امور\r\nبه فیض بخشی اهل نظر توانی کرد\r\nولی تو تا لب معشوق و جام می خواهی\r\nطمع مدار که کار دگر توانی کرد\r\nدلا ز نور هدایت گر آگهی یابی\r\nچو شمع خنده زنان ترک سر توانی کرد\r\nگر این نصیحت شاهانه بشنوی حافظ\r\nبه شاهراه حقیقت گذر توانی کرد\r\n', 'جوانیت را به نحو احسن بگذران خوش باش نه آن که در پی اش غم باشد. رسیدن به مراد بسیار سهل است به شرط اینکه غلام خدا باشی و گدایی در خانه ی او را بکنی. به سفری می روی که بسیار سودمند است. در کارت طمع نباشد وگرنه کارها را خراب می کنی بسیار خوش شانسی و دست به هر چه بزنی طلا می شود. '),
(145, 'چه مستیست ندانم که رو به ما آورد\r\nکه بود ساقی و این باده از کجا آورد\r\nتو نیز باده به چنگ آر و راه صحرا گیر\r\nکه مرغ نغمه سرا ساز خوش نوا آورد\r\nدلا چو غنچه شکایت ز کار بسته مکن\r\nکه باد صبح نسیم گره گشا آورد\r\nرسیدن گل و نسرین به خیر و خوبی باد\r\nبنفشه شاد و کش آمد سمن صفا آورد\r\nصبا به خوش خبری هدهد سلیمان است\r\nکه مژده طرب از گلشن سبا آورد\r\nعلاج ضعف دل ما کرشمه ساقیست\r\nبرآر سر که طبیب آمد و دوا آورد\r\nمرید پیر مغانم ز من مرنج ای شیخ\r\nچرا که وعده تو کردی و او به جا آورد\r\nبه تنگ چشمی آن ترک لشکری نازم\r\nکه حمله بر من درویش یک قبا آورد\r\nفلک غلامی حافظ کنون به طوع کند\r\nکه التجا به در دولت شما آورد\r\n', 'همه چیز به خیر و خوبی تمام می شود. عجله نکن گره از کار بسته ی تو باز می شود. یک خبر خوش در راه است. منتظر باش. با شنیدن این خبر بیماریت نیز شفا پیدا می کند ولی یادت باشد نذری که کرده ای حتما ادا کنی. وعده ای هم که داده ای بجا آور دولت دو در خانه ی شماست. '),
(146, 'صبا وقت سحر بویی ز زلف یار می آورد\r\nدل شوریده ما را به بو در کار می آورد\r\nمن آن شکل صنوبر را ز باغ دیده برکندم\r\nکه هر گل کز غمش بشکفت محنت بار می آورد\r\nفروغ ماه می دیدم ز بام قصر او روشن\r\nکه رو از شرم آن خورشید در دیوار می آورد\r\nز بیم غارت عشقش دل پرخون رها کردم\r\nولی می ریخت خون و ره بدان هنجار می آورد\r\nبه قول مطرب و ساقی برون رفتم گه و بی گه\r\nکز آن راه گران قاصد خبر دشوار می آورد\r\nسراسر بخشش جانان طریق لطف و احسان بود\r\nاگر تسبیح می فرمود اگر زنار می آورد\r\nعفاالله چین ابرویش اگر چه ناتوانم کرد\r\nبه عشوه هم پیامی بر سر بیمار می آورد\r\nعجب می داشتم دیشب ز حافظ جام و پیمانه\r\nولی منعش نمی کردم که صوفی وار می آورد\r\n', 'به قولی که به خودت داده ای عمل کن وگرنه غم به دنبال می آوری. اگر این کار را کنی خانه ی دلت روشن می شود. دیگر ترسی از مشکلات نداری، قاصدی خبری برایت می آورد که در آن خبر از لطف و بخشش است. تعجب نکن در اراده ی خداوند هر چیزی امکان پذیر است. '),
(147, 'نسیم باد صبا دوشم آگهی آورد\r\nکه روز محنت و غم رو به کوتهی آورد\r\nبه مطربان صبوحی دهیم جامه چاک\r\nبدین نوید که باد سحرگهی آورد\r\nبیا بیا که تو حور بهشت را رضوان\r\nدر این جهان ز برای دل رهی آورد\r\nهمی رویم به شیراز با عنایت بخت\r\nزهی رفیق که بختم به همرهی آورد\r\nبه جبر خاطر ما کوش کاین کلاه نمد\r\nبسا شکست که با افسر شهی آورد\r\nچه ناله ها که رسید از دلم به خرمن ماه\r\nچو یاد عارض آن ماه خرگهی آورد\r\nرساند رایت منصور بر فلک حافظ\r\nکه التجا به جناب شهنشهی آورد\r\n', 'نوید فتح و پیروزی خیلی زود به شما داده می شود و آنقدر خوشحال می شوید مثل اینکه بهشت و یک حوری به شما بخشیده اند. بخت با شماست و این بخاطر سجده در مقابل معبود الهی می باشد همیشه از خدا اطاعت کنید تا به پادشاهی برسید. '),
(148, 'یارم چو قدح به دست گیرد\r\nبازار بتان شکست گیرد\r\nهر کس که بدید چشم او گفت\r\nکو محتسبی که مست گیرد\r\nدر بحر فتاده ام چو ماهی\r\nتا یار مرا به شست گیرد\r\nدر پاش فتاده ام به زاری\r\nآیا بود آن که دست گیرد\r\nخرم دل آن که همچو حافظ\r\nجامی ز می الست گیرد\r\n', 'شما از کلیه ی رقیبان خود سبقت می گیرید و همه را از میدان به در می کنید. شانس پیروزی در دست خودتان هست. آن را گم نکنید یا نگذارید رقیبان آن را از دستتان بربایند. '),
(149, 'دلم جز مهر مه رویان طریقی بر نمی گیرد\r\nز هر در می دهم پندش ولیکن در نمی گیرد\r\nخدا را ای نصیحتگو حدیث ساغر و می گو\r\nکه نقشی در خیال ما از این خوشتر نمی گیرد\r\nبیا ای ساقی گلرخ بیاور باده رنگین\r\nکه فکری در درون ما از این بهتر نمی گیرد\r\nصراحی می کشم پنهان و مردم دفتر انگارند\r\nعجب گر آتش این زرق در دفتر نمی گیرد\r\nمن این دلق مرقع را بخواهم سوختن روزی\r\nکه پیر می فروشانش به جامی بر نمی گیرد\r\nاز آن رو هست یاران را صفاها با می لعلش\r\nکه غیر از راستی نقشی در آن جوهر نمی گیرد\r\nسر و چشمی چنین دلکش تو گویی چشم از او بردوز\r\nبرو کاین وعظ بی معنی مرا در سر نمی گیرد\r\nنصیحتگوی رندان را که با حکم قضا جنگ است\r\nدلش بس تنگ می بینم مگر ساغر نمی گیرد\r\nمیان گریه می خندم که چون شمع اندر این مجلس\r\nزبان آتشینم هست لیکن در نمی گیرد\r\nچه خوش صید دلم کردی بنازم چشم مستت را\r\nکه کس مرغان وحشی را از این خوشتر نمی گیرد\r\nسخن در احتیاج ما و استغنای معشوق است\r\nچه سود افسونگری ای دل که در دلبر نمی گیرد\r\nمن آن آیینه را روزی به دست آرم سکندروار\r\nاگر می گیرد این آتش زمانی ور نمی گیرد\r\nخدا را رحمی ای منعم که درویش سر کویت\r\nدری دیگر نمی داند رهی دیگر نمی گیرد\r\nبدین شعر تر شیرین ز شاهنشه عجب دارم\r\nکه سر تا پای حافظ را چرا در زر نمی گیرد\r\n', 'چرا کاری را که فقط خودت دوست داری انجام می دهی و به نظر دیگران احترام نمی گذاری. با این کار عاقبت خوشی نخواهی داشت. راه راست را پیش بگیر. ریا و تزویر نکن. با حکم خداوند نجنگ تا راه های موفقیت در مقابل دیدگانت روشن شود. '),
(150, 'ساقی ار باده از این دست به جام اندازد\r\nعارفان را همه در شرب مدام اندازد\r\nور چنین زیر خم زلف نهد دانه خال\r\nای بسا مرغ خرد را که به دام اندازد\r\nای خوشا دولت آن مست که در پای حریف\r\nسر و دستار نداند که کدام اندازد\r\nزاهد خام که انکار می و جام کند\r\nپخته گردد چو نظر بر می خام اندازد\r\nروز در کسب هنر کوش که می خوردن روز\r\nدل چون آینه در زنگ ظلام اندازد\r\nآن زمان وقت می صبح فروغ است که شب\r\nگرد خرگاه افق پرده شام اندازد\r\nباده با محتسب شهر ننوشی زنهار\r\nبخورد باده ات و سنگ به جام اندازد\r\nحافظا سر ز کله گوشه خورشید برآر\r\nبختت ار قرعه بدان ماه تمام اندازد\r\n', 'نوبتی هم باشد نوبت توست که موفقیت شامل حالت شود و از این پیروزی اطرافیانت نیز بی بهره نمانند. مواظب دور و بر خودت باش عده ای می خواهند که چوب لای چرخ تو بگذارند. تو کوشش خودت را بکن، خداوند حیله ی آنها را خنثی می کند. '),
(151, 'دمی با غم به سر بردن جهان یک سر نمی ارزد\r\nبه می بفروش دلق ما کز این بهتر نمی ارزد\r\nبه کوی می فروشانش به جامی بر نمی گیرند\r\nزهی سجاده تقوا که یک ساغر نمی ارزد\r\nرقیبم سرزنش ها کرد کز این به آب رخ برتاب\r\nچه افتاد این سر ما را که خاک در نمی ارزد\r\nشکوه تاج سلطانی که بیم جان در او درج است\r\nکلاهی دلکش است اما به ترک سر نمی ارزد\r\nچه آسان می نمود اول غم دریا به بوی سود\r\nغلط کردم که این طوفان به صد گوهر نمی ارزد\r\nتو را آن به که روی خود ز مشتاقان بپوشانی\r\nکه شادی جهان گیری غم لشکر نمی ارزد\r\nچو حافظ در قناعت کوش و از دنیی دون بگذر\r\nکه یک جو منت دونان دو صد من زر نمی ارزد\r\n', 'دنیا و تمام آنچه که در آن است نمی ارزد که بخواهی به خاطرش اشک بریزی و غصه بخوری. گول بعضی از افراد را نخور که می خواهند تو را از ایمانت جدا کنند و به راه کج بکشانند. سجاده ی تقوی خویش را به خواندن نماز رنگین کن. اگر به حرف دوستانت گوش کنی پشیمان می شوی. قناعت پیشه کن تا اینکه تاج سلطنت روی سرت بگذارند. '),
(152, 'در ازل پرتو حسنت ز تجلی دم زد\r\nعشق پیدا شد و آتش به همه عالم زد\r\nجلوه ای کرد رخت دید ملک عشق نداشت\r\nعین آتش شد از این غیرت و بر آدم زد\r\nعقل می خواست کز آن شعله چراغ افروزد\r\nبرق غیرت بدرخشید و جهان برهم زد\r\nمدعی خواست که آید به تماشاگه راز\r\nدست غیب آمد و بر سینه نامحرم زد\r\nدیگران قرعه قسمت همه بر عیش زدند\r\nدل غمدیده ما بود که هم بر غم زد\r\nجان علوی هوس چاه زنخدان تو داشت\r\nدست در حلقه آن زلف خم اندر خم زد\r\nحافظ آن روز طربنامه عشق تو نوشت\r\nکه قلم بر سر اسباب دل خرم زد\r\n', 'تو بسیار یکرنگ و صادق هستی. عشقی واقعی داری اما دیگران به تو دروغ گفته اند و خود را با فریب دیگران سرگرم می کنند وای تو صبور و عاشق بودی و به حاجات قلبی خودت می رسی و خیلی زود غصه را فراموش می کنی و روی خوشی و خوشبختی را می بینی. '),
(153, 'سحر چون خسرو خاور علم بر کوهساران زد\r\nبه دست مرحمت یارم در امیدواران زد\r\nچو پیش صبح روشن شد که حال مهر گردون چیست\r\nبرآمد خنده خوش بر غرور کامگاران زد\r\nنگارم دوش در مجلس به عزم رقص چون برخاست\r\nگره بگشود از ابرو و بر دل های یاران زد\r\nمن از رنگ صلاح آن دم به خون دل بشستم دست\r\nکه چشم باده پیمایش صلا بر هوشیاران زد\r\nکدام آهن دلش آموخت این آیین عیاری\r\nکز اول چون برون آمد ره شب زنده داران زد\r\nخیال شهسواری پخت و شد ناگه دل مسکین\r\nخداوندا نگه دارش که بر قلب سواران زد\r\nدر آب و رنگ رخسارش چه جان دادیم و خون خوردیم\r\nچو نقشش دست داد اول رقم بر جان سپاران زد\r\nمنش با خرقه پشمین کجا اندر کمند آرم\r\nزره مویی که مژگانش ره خنجرگزاران زد\r\nشهنشاه مظفر فر شجاع ملک و دین منصور\r\nکه جود بی دریغش خنده بر ابر بهاران زد\r\nاز آن ساعت که جام می به دست او مشرف شد\r\nزمانه ساغر شادی به یاد میگساران زد\r\nز شمشیر سرافشانش ظفر آن روز بدرخشید\r\nکه چون خورشید انجم سوز تنها بر هزاران زد\r\nدوام عمر و ملک او بخواه از لطف حق ای دل\r\nکه چرخ این سکه دولت به دور روزگاران زد\r\nنظر بر قرعه توفیق و یمن دولت شاه است\r\nبده کام دل حافظ که فال بختیاران زد\r\n', 'مثل کوه استوار باش. صبح صادق در حال دمیدن است و تو در همین صبح به کام دل می رسی. این فال برای تو دولت و سعادت و مقام را یکجا بشارت می دهد. نکند زمانی که به تمام حاجاتت رسیدی خدا را فراموش کنی و منش و اخلاقت تغییر کند که در این زمان خداوند همه چیز را از تو خواهد گرفت. '),
(154, 'راهی بزن که آهی بر ساز آن توان زد\r\nشعری بخوان که با او رطل گران توان زد\r\nبر آستان جانان گر سر توان نهادن\r\nگلبانگ سربلندی بر آسمان توان زد\r\nقد خمیده ما سهلت نماید اما\r\nبر چشم دشمنان تیر از این کمان توان زد\r\nدر خانقه نگنجد اسرار عشقبازی\r\nجام می مغانه هم با مغان توان زد\r\nدرویش را نباشد برگ سرای سلطان\r\nماییم و کهنه دلقی کآتش در آن توان زد\r\nاهل نظر دو عالم در یک نظر ببازند\r\nعشق است و داو اول بر نقد جان توان زد\r\nگر دولت وصالت خواهد دری گشودن\r\nسرها بدین تخیل بر آستان توان زد\r\nعشق و شباب و رندی مجموعه مراد است\r\nچون جمع شد معانی گوی بیان توان زد\r\nشد رهزن سلامت زلف تو وین عجب نیست\r\nگر راه زن تو باشی صد کاروان توان زد\r\nحافظ به حق قرآن کز شید و زرق بازآی\r\nباشد که گوی عیشی در این جهان توان زد\r\n', 'تصمیم شما باعث سربلندی تان و نیز مایه ی افتخار برای همگان خواهد شد. دنیای مادی و معنوی تان تضمین می شود. درهای بسته به رویتان باز می شود و شما خیلی زود به مراد دلتان می رسی اما مواظب سلامتی خودتان باشید که هر چه دارید از دولت قرآن است. '),
(155, 'اگر روم ز پی اش فتنه ها برانگیزد\r\nور از طلب بنشینم به کینه برخیزد\r\nو گر به رهگذری یک دم از وفاداری\r\nچو گرد در پی اش افتم چو باد بگریزد\r\nو گر کنم طلب نیم بوسه صد افسوس\r\nز حقه دهنش چون شکر فروریزد\r\nمن آن فریب که در نرگس تو می بینم\r\nبس آب روی که با خاک ره برآمیزد\r\nفراز و شیب بیابان عشق دام بلاست\r\nکجاست شیردلی کز بلا نپرهیزد\r\nتو عمر خواه و صبوری که چرخ شعبده باز\r\nهزار بازی از این طرفه تر برانگیزد\r\nبر آستانه تسلیم سر بنه حافظ\r\nکه گر ستیزه کنی روزگار بستیزد\r\n', 'همیشه همه ی کارها بر وفق مراد نیست. خیلی هم تلاش نکن و آنچه برایت مقدر شده به تو می رسد بی خود، خود را به زمین و زمان نزن. کمی تامل کن. صبر داشته باش. دلت را قوی کن تا بتوانی فراز و نشیب زندگی را طی کنی. هر چند که زندگی تو را به بازی گرفته است. تسلیم خداوند باش نه دل خود. '),
(156, 'به حسن و خلق و وفا کس به یار ما نرسد\r\nتو را در این سخن انکار کار ما نرسد\r\nاگر چه حسن فروشان به جلوه آمده اند\r\nکسی به حسن و ملاحت به یار ما نرسد\r\nبه حق صحبت دیرین که هیچ محرم راز\r\nبه یار یک جهت حق گزار ما نرسد\r\nهزار نقش برآید ز کلک صنع و یکی\r\nبه دلپذیری نقش نگار ما نرسد\r\nهزار نقد به بازار کائنات آرند\r\nیکی به سکه صاحب عیار ما نرسد\r\nدریغ قافله عمر کان چنان رفتند\r\nکه گردشان به هوای دیار ما نرسد\r\nدلا ز رنج حسودان مرنج و واثق باش\r\nکه بد به خاطر امیدوار ما نرسد\r\nچنان بزی که اگر خاک ره شوی کس را\r\nغبار خاطری از ره گذار ما نرسد\r\nبسوخت حافظ و ترسم که شرح قصه او\r\nبه سمع پادشه کامگار ما نرسد\r\n', 'در انتخابی که کرده اید شک نکنید . او از هر جهت لایق است. رازدار و با ایمان و با اصل و نسب است. نگذارید وقت بگذرد ممکن است حسودان کارها را خراب کنند و شما در رسیدن به مقصود عمری را در پی او بگردید. '),
(157, 'هر که را با خط سبزت سر سودا باشد\r\nپای از این دایره بیرون ننهد تا باشد\r\nمن چو از خاک لحد لاله صفت برخیزم\r\nداغ سودای توام سر سویدا باشد\r\nتو خود ای گوهر یک دانه کجایی آخر\r\nکز غمت دیده مردم همه دریا باشد\r\nاز بن هر مژه ام آب روان است بیا\r\nاگرت میل لب جوی و تماشا باشد\r\nچون گل و می دمی از پرده برون آی و درآ\r\nکه دگرباره ملاقات نه پیدا باشد\r\nظل ممدود خم زلف توام بر سر باد\r\nکاندر این سایه قرار دل شیدا باشد\r\nچشمت از ناز به حافظ نکند میل آری\r\nسرگرانی صفت نرگس رعنا باشد\r\n', 'هر کس با خدا بود و از راه او بیرون نرفت، غمی به دل او راه پیدا نمی کند و هر لحظه ی عمر خود را وقف او می نماید. فرصت را غنیمت بدان راه برگشت نداری. آفرین بر تو که عقلت به تو دستور می دهد نه دلت. خواندن نماز و داشتن ایمان راه را برایت هموار می سازد. '),
(158, 'من و انکار شراب این چه حکایت باشد\r\nغالبا این قدرم عقل و کفایت باشد\r\nتا به غایت ره میخانه نمی دانستم\r\nور نه مستوری ما تا به چه غایت باشد\r\nزاهد و عجب و نماز و من و مستی و نیاز\r\nتا تو را خود ز میان با که عنایت باشد\r\nزاهد ار راه به رندی نبرد معذور است\r\nعشق کاریست که موقوف هدایت باشد\r\nمن که شب ها ره تقوا زده ام با دف و چنگ\r\nاین زمان سر به ره آرم چه حکایت باشد\r\nبنده پیر مغانم که ز جهلم برهاند\r\nپیر ما هر چه کند عین عنایت باشد\r\nدوش از این غصه نخفتم که رفیقی می گفت\r\nحافظ ار مست بود جای شکایت باشد\r\n', 'به عقل خودتان اعتماد ندارید و درایت تان را منکر می شوید. کارهای خودتان را که بسیار خوب هم هستند پنهان می کنید. لطف خدا همیشه با شماست. در هر کاری از هدایت بزرگان استفاده می کنید تا از نادانی رهایی یابید. غصه هایتان برطرف می شود به شرط آنکه دست از شکایت بردارید. '),
(159, 'نقد صوفی نه همه صافی بی غش باشد\r\nای بسا خرقه که مستوجب آتش باشد\r\nصوفی ما که ز ورد سحری مست شدی\r\nشامگاهش نگران باش که سرخوش باشد\r\nخوش بود گر محک تجربه آید به میان\r\nتا سیه روی شود هر که در او غش باشد\r\nخط ساقی گر از این گونه زند نقش بر آب\r\nای بسا رخ که به خونابه منقش باشد\r\nناز پرورد تنعم نبرد راه به دوست\r\nعاشقی شیوه رندان بلاکش باشد\r\nغم دنیی دنی چند خوری باده بخور\r\nحیف باشد دل دانا که مشوش باشد\r\nدلق و سجاده حافظ ببرد باده فروش\r\nگر شرابش ز کف ساقی مه وش باشد\r\n', 'تو مورد اعتماد دیگرانی. اگر در کار خود غل و غش داشته باشی هم خودت را از بین می بری هم دیگران را پس از تجربه ی خودت استفاده کن و دست دیگران را هم بگیر. حیف از توست که با حیله و ریا بخواهی زندگی کنی. ذات تو مثل یک سجاده پاک و بی ریاست. مراقب شیطان باش. '),
(160, 'خوش است خلوت اگر یار یار من باشد\r\nنه من بسوزم و او شمع انجمن باشد\r\nمن آن نگین سلیمان به هیچ نستانم\r\nکه گاه گاه بر او دست اهرمن باشد\r\nروا مدار خدایا که در حریم وصال\r\nرقیب محرم و حرمان نصیب من باشد\r\nهمای گو مفکن سایه شرف هرگز\r\nدر آن دیار که طوطی کم از زغن باشد\r\nبیان شوق چه حاجت که سوز آتش دل\r\nتوان شناخت ز سوزی که در سخن باشد\r\nهوای کوی تو از سر نمی رود آری\r\nغریب را دل سرگشته با وطن باشد\r\nبه سان سوسن اگر ده زبان شود حافظ\r\nچو غنچه پیش تواش مهر بر دهن باشد\r\n', 'دعاهایتان برآورده خواهد شد و رقیبانتان موفق نمی شوند. فکر خودتان را به چیزهای دیگری هم معطوف کنید. روال عادی زندگی از دستتان خارج شده است. چیز با ارزشی در دست دارید که برایتان بسیار عزیز هم هست. نگذارید به دست نااهلان بیفتد. رازدار باشید. '),
(161, 'کی شعر تر انگیزد خاطر که حزین باشد\r\nیک نکته از این معنی گفتیم و همین باشد\r\nاز لعل تو گر یابم انگشتری زنهار\r\nصد ملک سلیمانم در زیر نگین باشد\r\nغمناک نباید بود از طعن حسود ای دل\r\nشاید که چو وابینی خیر تو در این باشد\r\nهر کو نکند فهمی زین کلک خیال انگیز\r\nنقشش به حرام ار خود صورتگر چین باشد\r\nجام می و خون دل هر یک به کسی دادند\r\nدر دایره قسمت اوضاع چنین باشد\r\nدر کار گلاب و گل حکم ازلی این بود\r\nکاین شاهد بازاری وان پرده نشین باشد\r\nآن نیست که حافظ را رندی بشد از خاطر\r\nکاین سابقه پیشین تا روز پسین باشد\r\n', 'تا زمانی که غمگین و ناراحت باشی، نی توانی تصمیم درست بگیری. با طعنه ی حسودان میدان را خالی نکن به روی خودت نیاور. از خداوند کمک بخواه. فعلا اوضاع همین است باید بسازی ولی دست بدخواهان رو می شود و تو دوباره نامت بر سر زبان ها افتد و روزگارت از گذشته بهتر می شود. '),
(162, 'خوش آمد گل وز آن خوشتر نباشد\r\nکه در دستت بجز ساغر نباشد\r\nزمان خوشدلی دریاب و در یاب\r\nکه دایم در صدف گوهر نباشد\r\nغنیمت دان و می خور در گلستان\r\nکه گل تا هفته دیگر نباشد\r\nایا پرلعل کرده جام زرین\r\nببخشا بر کسی کش زر نباشد\r\nبیا ای شیخ و از خمخانه ما\r\nشرابی خور که در کوثر نباشد\r\nبشوی اوراق اگر همدرس مایی\r\nکه علم عشق در دفتر نباشد\r\nز من بنیوش و دل در شاهدی بند\r\nکه حسنش بسته زیور نباشد\r\nشرابی بی خمارم بخش یا رب\r\nکه با وی هیچ درد سر نباشد\r\nمن از جان بنده سلطان اویسم\r\nاگر چه یادش از چاکر نباشد\r\nبه تاج عالم آرایش که خورشید\r\nچنین زیبنده افسر نباشد\r\nکسی گیرد خطا بر نظم حافظ\r\nکه هیچش لطف در گوهر نباشد\r\n', 'قدر جوانیت را بدان و به خاطر مسائل کوچک غمگین نباش. وجودت مثل گلستانی است که چند روزی سبز و با طراوت است و بعد خزان زندگی آن را از بین می برد. اگر می خواهی عمری دراز داشته باشی به مستمندان کمک کن. ناجی تو کسی غیر از خدا نیست و اوست که زینت دهنده ی دلهاست. '),
(163, 'گل بی رخ یار خوش نباشد\r\nبی باده بهار خوش نباشد\r\nطرف چمن و طواف بستان\r\nبی لاله عذار خوش نباشد\r\nرقصیدن سرو و حالت گل\r\nبی صوت هزار خوش نباشد\r\nبا یار شکرلب گل اندام\r\nبی بوس و کنار خوش نباشد\r\nهر نقش که دست عقل بندد\r\nجز نقش نگار خوش نباشد\r\nجان نقد محقر است حافظ\r\nاز بهر نثار خوش نباشد\r\n', 'ظاهر زیبا و جمال بی عیب بدون سیرت پاک ارزشی ندارد پس فقط به ظواهر تکیه نکن و رفتار و ادب و اخلاق را نیز مد نظر داشته باش. هیچ گلی بی خار نیست. تو هم سعی کن ایرادهای خود را برطرف نمایی. در انتخاب نیز، تشخیص درست با توست. زیاد دقت کن. '),
(164, 'نفس باد صبا مشک فشان خواهد شد\r\nعالم پیر دگرباره جوان خواهد شد\r\nارغوان جام عقیقی به سمن خواهد داد\r\nچشم نرگس به شقایق نگران خواهد شد\r\nاین تطاول که کشید از غم هجران بلبل\r\nتا سراپرده گل نعره زنان خواهد شد\r\nگر ز مسجد به خرابات شدم خرده مگیر\r\nمجلس وعظ دراز است و زمان خواهد شد\r\nای دل ار عشرت امروز به فردا فکنی\r\nمایه نقد بقا را که ضمان خواهد شد\r\nماه شعبان منه از دست قدح کاین خورشید\r\nاز نظر تا شب عید رمضان خواهد شد\r\nگل عزیز است غنیمت شمریدش صحبت\r\nکه به باغ آمد از این راه و از آن خواهد شد\r\nمطربا مجلس انس است غزل خوان و سرود\r\nچند گویی که چنین رفت و چنان خواهد شد\r\nحافظ از بهر تو آمد سوی اقلیم وجود\r\nقدمی نه به وداعش که روان خواهد شد\r\n', 'تغییر و تحولات بسیار زیادی در زندگیت رخ می دهد. دوباره دلت جوان می شود. قدر سلامتی خود و اطرافیانت را بدان. ماه شعبان و رمضان فرصت بسیار خوبی است برای دعا و رسیدن به حاجات. بسیار خوش هستی چون همه چیز برایت آفریده شده است. '),
(165, 'مرا مهر سیه چشمان ز سر بیرون نخواهد شد\r\nقضای آسمان است این و دیگرگون نخواهد شد\r\nرقیب آزارها فرمود و جای آشتی نگذاشت\r\nمگر آه سحرخیزان سوی گردون نخواهد شد\r\nمرا روز ازل کاری بجز رندی نفرمودند\r\nهر آن قسمت که آن جا رفت از آن افزون نخواهد شد\r\nخدا را محتسب ما را به فریاد دف و نی بخش\r\nکه ساز شرع از این افسانه بی قانون نخواهد شد\r\nمجال من همین باشد که پنهان عشق او ورزم\r\nکنار و بوس و آغوشش چه گویم چون نخواهد شد\r\nشراب لعل و جای امن و یار مهربان ساقی\r\nدلا کی به شود کارت اگر اکنون نخواهد شد\r\nمشوی ای دیده نقش غم ز لوح سینه حافظ\r\nکه زخم تیغ دلدار است و رنگ خون نخواهد شد\r\n', 'به امید قسمت خود باش. از رقیب خود دوری کن کار را از روی عقل و منطق انجام بده. عجله نکن همه چیز به موقع درست خواهد شد. با گریه و زاری کردن چیزی درست نمی شود. دست به دعا بردار. نماز صبح را فراموش نکن که هر چه حاجت بگیری از نماز صبح است. '),
(166, 'روز هجران و شب فرقت یار آخر شد\r\nزدم این فال و گذشت اختر و کار آخر شد\r\nآن همه ناز و تنعم که خزان می فرمود\r\nعاقبت در قدم باد بهار آخر شد\r\nشکر ایزد که به اقبال کله گوشه گل\r\nنخوت باد دی و شوکت خار آخر شد\r\nصبح امید که بد معتکف پرده غیب\r\nگو برون آی که کار شب تار آخر شد\r\nآن پریشانی شب های دراز و غم دل\r\nهمه در سایه گیسوی نگار آخر شد\r\nباورم نیست ز بدعهدی ایام هنوز\r\nقصه غصه که در دولت یار آخر شد\r\nساقیا لطف نمودی قدحت پرمی باد\r\nکه به تدبیر تو تشویش خمار آخر شد\r\nدر شمار ار چه نیاورد کسی حافظ را\r\nشکر کان محنت بی حد و شمار آخر شد\r\n', 'ستاره ی بخت و اقبال شما می درخشد و فالتان بسیار فرخنده است. نعمت و مال به سویتان می آید. به ذکر و ثنای خداوند بپردازید. اتفاقات عجیبی رخ می دهند که همگی بسیار خوب هستند و دولت به همراه می آورند و این به خاطر تدبیر و عقل خودت می باشد. در همه حال با خدا باش.  '),
(167, 'ستاره ای بدرخشید و ماه مجلس شد\r\nدل رمیده ما را رفیق و مونس شد\r\nنگار من که به مکتب نرفت و خط ننوشت\r\nبه غمزه مساله آموز صد مدرس شد\r\nبه بوی او دل بیمار عاشقان چو صبا\r\nفدای عارض نسرین و چشم نرگس شد\r\nبه صدر مصطبه ام می نشاند اکنون دوست\r\nگدای شهر نگه کن که میر مجلس شد\r\nخیال آب خضر بست و جام اسکندر\r\nبه جرعه نوشی سلطان ابوالفوارس شد\r\nطربسرای محبت کنون شود معمور\r\nکه طاق ابروی یار منش مهندس شد\r\nلب از ترشح می پاک کن برای خدا\r\nکه خاطرم به هزاران گنه موسوس شد\r\nکرشمه تو شرابی به عاشقان پیمود\r\nکه علم بی خبر افتاد و عقل بی حس شد\r\nچو زر عزیز وجود است نظم من آری\r\nقبول دولتیان کیمیای این مس شد\r\nز راه میکده یاران عنان بگردانید\r\nچرا که حافظ از این راه رفت و مفلس شد\r\n', 'اگر تا به امروز دوست و همراهی نداشته ای به خاطر این است که خودت نمی خواستی تا امروز دنیا بر وفق مرادت نبوده وای این دوران رو به اتمام است و بخت و اقبال خوشی در راه است. منفی باف نباش، اختیارت را دست کسی نده. '),
(168, 'گداخت جان که شود کار دل تمام و نشد\r\nبسوختیم در این آرزوی خام و نشد\r\nبه لابه گفت شبی میر مجلس تو شوم\r\nشدم به رغبت خویشش کمین غلام و نشد\r\nپیام داد که خواهم نشست با رندان\r\nبشد به رندی و دردی کشیم نام و نشد\r\nرواست در بر اگر می طپد کبوتر دل\r\nکه دید در ره خود تاب و پیچ دام و نشد\r\nبدان هوس که به مستی ببوسم آن لب لعل\r\nچه خون که در دلم افتاد همچو جام و نشد\r\nبه کوی عشق منه بی دلیل راه قدم\r\nکه من به خویش نمودم صد اهتمام و نشد\r\nفغان که در طلب گنج نامه مقصود\r\nشدم خراب جهانی ز غم تمام و نشد\r\nدریغ و درد که در جست و جوی گنج حضور\r\nبسی شدم به گدایی بر کرام و نشد\r\nهزار حیله برانگیخت حافظ از سر فکر\r\nدر آن هوس که شود آن نگار رام و نشد\r\n', 'راهی که انتخاب نموده اید راه درستی نیست و بر مبنای هوی و هوس این راه را می روید برگردید چون غیر از ضرر چیز دیگری ندارد. سودی نمی کنید و کسی هم به شما کمک نمی کند. به پیغامی که برایتان می فرستند اهمیت ندهید چون می خواهند شما را به دام بیاندازند. '),
(169, 'یاری اندر کس نمی بینیم یاران را چه شد\r\nدوستی کی آخر آمد دوستداران را چه شد\r\nآب حیوان تیره گون شد خضر فرخ پی کجاست\r\nخون چکید از شاخ گل باد بهاران را چه شد\r\nکس نمی گوید که یاری داشت حق دوستی\r\nحق شناسان را چه حال افتاد یاران را چه شد\r\nلعلی از کان مروت برنیامد سال هاست\r\nتابش خورشید و سعی باد و باران را چه شد\r\nشهر یاران بود و خاک مهربانان این دیار\r\nمهربانی کی سر آمد شهریاران را چه شد\r\nگوی توفیق و کرامت در میان افکنده اند\r\nکس به میدان در نمی آید سواران را چه شد\r\nصد هزاران گل شکفت و بانگ مرغی برنخاست\r\nعندلیبان را چه پیش آمد هزاران را چه شد\r\nزهره سازی خوش نمی سازد مگر عودش بسوخت\r\nکس ندارد ذوق مستی میگساران را چه شد\r\nحافظ اسرار الهی کس نمی داند خموش\r\nاز که می پرسی که دور روزگاران را چه شد\r\n', 'از وضعی که دارید خسته شده اید و کسی یاری تان نمی کند. همگی شما را فراموش کرده اند به هر کسی که خوبی کردید بدی دیده اید. در انتظار رحمت الهی بی قراری می کنید. شانس به شما رو می کند ولی آن را پس می زنید. در هر حال هر چه تقدیر الهی باشد همان است. '),
(170, 'زاهد خلوت نشین دوش به میخانه شد\r\nاز سر پیمان برفت با سر پیمانه شد\r\nصوفی مجلس که دی جام و قدح می شکست\r\nباز به یک جرعه می عاقل و فرزانه شد\r\nشاهد عهد شباب آمده بودش به خواب\r\nباز به پیرانه سر عاشق و دیوانه شد\r\nمغبچه ای می گذشت راه زن دین و دل\r\nدر پی آن آشنا از همه بیگانه شد\r\nآتش رخسار گل خرمن بلبل بسوخت\r\nچهره خندان شمع آفت پروانه شد\r\nگریه شام و سحر شکر که ضایع نگشت\r\nقطره باران ما گوهر یک دانه شد\r\nنرگس ساقی بخواند آیت افسونگری\r\nحلقه اوراد ما مجلس افسانه شد\r\nمنزل حافظ کنون بارگه پادشاست\r\nدل بر دلدار رفت جان بر جانانه شد\r\n', 'دعاهای تو بر سر نماز صبح و شب بی ثمر نماند خداوند دری به رویت باز کرد و آن رسیدن به عقل و فرزانگی است. دوست را از دشمن به راحتی می توانید تشخیص دهید و می فهمید که از کجا به شما آفت می رسد از آنجا دوری می کنید و در نهایت تصمیم های عاقلانه می گیرید. '),
(171, 'دوش از جناب آصف پیک بشارت آمد\r\nکز حضرت سلیمان عشرت اشارت آمد\r\nخاک وجود ما را از آب دیده گل کن\r\nویرانسرای دل را گاه عمارت آمد\r\nاین شرح بی نهایت کز زلف یار گفتند\r\nحرفیست از هزاران کاندر عبارت آمد\r\nعیبم بپوش زنهار ای خرقه می آلود\r\nکان پاک پاکدامن بهر زیارت آمد\r\nامروز جای هر کس پیدا شود ز خوبان\r\nکان ماه مجلس افروز اندر صدارت آمد\r\nبر تخت جم که تاجش معراج آسمان است\r\nهمت نگر که موری با آن حقارت آمد\r\nاز چشم شوخش ای دل ایمان خود نگه دار\r\nکان جادوی کمانکش بر عزم غارت آمد\r\nآلوده ای تو حافظ فیضی ز شاه درخواه\r\nکان عنصر سماحت بهر طهارت آمد\r\nدریاست مجلس او دریاب وقت و در یاب\r\nهان ای زیان رسیده وقت تجارت آمد\r\n', 'خود را کوچک نشمار. تو فرد بسیار بزرگ و کاردانی در جامعه خواهی شد. در همه حال رو به سوی خدا بیاور که در عین ناامیدی فقط اوست که به تو امید می بخشد. بشارتی به تو می دهند که دل ویران تو را تبدیل به عمارت می کند وقت مناسبی پیش آمده تا از یک معامله سود کلانی ببری. '),
(172, 'عشق تو نهال حیرت آمد\r\nوصل تو کمال حیرت آمد\r\nبس غرقه حال وصل کآخر\r\nهم بر سر حال حیرت آمد\r\nیک دل بنما که در ره او\r\nبر چهره نه خال حیرت آمد\r\nنه وصل بماند و نه واصل\r\nآن جا که خیال حیرت آمد\r\nاز هر طرفی که گوش کردم\r\nآواز سوال حیرت آمد\r\nشد منهزم از کمال عزت\r\nآن را که جلال حیرت آمد\r\nسر تا قدم وجود حافظ\r\nدر عشق نهال حیرت آمد\r\n', 'با رسیدن به وصال یار کمالات تو بیشتر خواهد شد. از هر طرف درهای رحمت به سویت باز می شود. با کمک یار جهانی را زیر و رو می کنی که همگان در حیرت می مانند. توسل به خدا کن و همت داشته باش. '),
(173, 'در نمازم خم ابروی تو با یاد آمد\r\nحالتی رفت که محراب به فریاد آمد\r\nاز من اکنون طمع صبر و دل و هوش مدار\r\nکان تحمل که تو دیدی همه بر باد آمد\r\nباده صافی شد و مرغان چمن مست شدند\r\nموسم عاشقی و کار به بنیاد آمد\r\nبوی بهبود ز اوضاع جهان می شنوم\r\nشادی آورد گل و باد صبا شاد آمد\r\nای عروس هنر از بخت شکایت منما\r\nحجله حسن بیارای که داماد آمد\r\nدلفریبان نباتی همه زیور بستند\r\nدلبر ماست که با حسن خداداد آمد\r\nزیر بارند درختان که تعلق دارند\r\nای خوشا سرو که از بار غم آزاد آمد\r\nمطرب از گفته حافظ غزلی نغز بخوان\r\nتا بگویم که ز عهد طربم یاد آمد\r\n', 'هر چه دارید از ایمان و دل پاک شماست. نتیجه ی صبوری و درایت خودتان را به زودی خواهید دید. زمینه را برای رسیدن به مقصود خودتان فراهم آورید. وضعی پیش می آید که شما از نظر مادی و معنوی تغییر می کنید و بار معنویاتتان بیشتر می شود. '),
(174, 'مژده ای دل که دگر باد صبا بازآمد\r\nهدهد خوش خبر از طرف سبا بازآمد\r\nبرکش ای مرغ سحر نغمه داوودی باز\r\nکه سلیمان گل از باد هوا بازآمد\r\nعارفی کو که کند فهم زبان سوسن\r\nتا بپرسد که چرا رفت و چرا بازآمد\r\nمردمی کرد و کرم لطف خداداد به من\r\nکان بت ماه رخ از راه وفا بازآمد\r\nلاله بوی می نوشین بشنید از دم صبح\r\nداغ دل بود به امید دوا بازآمد\r\nچشم من در ره این قافله راه بماند\r\nتا به گوش دلم آواز درا بازآمد\r\nگر چه حافظ در رنجش زد و پیمان بشکست\r\nلطف او بین که به لطف از در ما بازآمد\r\n', 'خبر خوشی را به زودی به شما می دهند که علاوه بر شما دیگران نیز خوشحال می شوند. این خبر خوش معمایی است برای شما که چطور شد همه چیز بر وفق مراد پیش آمد. البته که لطف خداوند بوده است. '),
(175, 'صبا به تهنیت پیر می فروش آمد\r\nکه موسم طرب و عیش و ناز و نوش آمد\r\nهوا مسیح نفس گشت و باد نافه گشای\r\nدرخت سبز شد و مرغ در خروش آمد\r\nتنور لاله چنان برفروخت باد بهار\r\nکه غنچه غرق عرق گشت و گل به جوش آمد\r\nبه گوش هوش نیوش از من و به عشرت کوش\r\nکه این سخن سحر از هاتفم به گوش آمد\r\nز فکر تفرقه بازآی تا شوی مجموع\r\nبه حکم آن که چو شد اهرمن سروش آمد\r\nز مرغ صبح ندانم که سوسن آزاد\r\nچه گوش کرد که با ده زبان خموش آمد\r\nچه جای صحبت نامحرم است مجلس انس\r\nسر پیاله بپوشان که خرقه پوش آمد\r\nز خانقاه به میخانه می رود حافظ\r\nمگر ز مستی زهد ریا به هوش آمد\r\n', 'فردی به کمک تان می شتابد و شما را از ورطه ی درد و فلاکت نجات می بخشد. لباس عافیت بر تنتان می کند. کمی عصبانی هستید سعی کنید بر خودتان مسلط باشید. بین کسی جدایی نیاندازید تا بلایا از شما دور شود. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(176, 'سحرم دولت بیدار به بالین آمد\r\nگفت برخیز که آن خسرو شیرین آمد\r\nقدحی درکش و سرخوش به تماشا بخرام\r\nتا ببینی که نگارت به چه آیین آمد\r\nمژدگانی بده ای خلوتی نافه گشای\r\nکه ز صحرای ختن آهوی مشکین آمد\r\nگریه آبی به رخ سوختگان بازآورد\r\nناله فریادرس عاشق مسکین آمد\r\nمرغ دل باز هوادار کمان ابرویست\r\nای کبوتر نگران باش که شاهین آمد\r\nساقیا می بده و غم مخور از دشمن و دوست\r\nکه به کام دل ما آن بشد و این آمد\r\nرسم بدعهدی ایام چو دید ابر بهار\r\nگریه اش بر سمن و سنبل و نسرین آمد\r\nچون صبا گفته حافظ بشنید از بلبل\r\nعنبرافشان به تماشای ریاحین آمد\r\n', 'بلند شو ببین که چگونه دشمنانت شکست خورده اند و غم هایت به پایان رسیده. مالی به تو می رسد که باعث می شود مشکلاتت حل شود. گرمای خوشبختی را حس می کنی و ابرهای تیره از آسمان دلت کنار می روند و تو پیروز و موفق می شوی. '),
(177, 'نه هر که چهره برافروخت دلبری داند\r\nنه هر که آینه سازد سکندری داند\r\nنه هر که طرف کله کج نهاد و تند نشست\r\nکلاه داری و آیین سروری داند\r\nتو بندگی چو گدایان به شرط مزد مکن\r\nکه دوست خود روش بنده پروری داند\r\nغلام همت آن رند عافیت سوزم\r\nکه در گداصفتی کیمیاگری داند\r\nوفا و عهد نکو باشد ار بیاموزی\r\nوگرنه هر که تو بینی ستمگری داند\r\nبباختم دل دیوانه و ندانستم\r\nکه آدمی بچه ای شیوه پری داند\r\nهزار نکته باریکتر ز مو این جاست\r\nنه هر که سر بتراشد قلندری داند\r\nمدار نقطه بینش ز خال توست مرا\r\nکه قدر گوهر یک دانه جوهری داند\r\nبه قد و چهره هر آن کس که شاه خوبان شد\r\nجهان بگیرد اگر دادگستری داند\r\nز شعر دلکش حافظ کسی بود آگاه\r\nکه لطف طبع و سخن گفتن دری داند\r\n', 'ظاهربین نباش و تصمیم را بر مبنای ظواهر نگیر. حواست را جمع کن می خواهند تو را فریب بدهند. آنها از تو خیلی زرنگ تر هستند. کسی را در کنارت داری که راهنمایی ات می کند. قدرش را بدان. اگر به حرفش گوش دهی تمام جهان در دست توست. '),
(178, 'هر که شد محرم دل در حرم یار بماند\r\nوان که این کار ندانست در انکار بماند\r\nاگر از پرده برون شد دل من عیب مکن\r\nشکر ایزد که نه در پرده پندار بماند\r\nصوفیان واستدند از گرو می همه رخت\r\nدلق ما بود که در خانه خمار بماند\r\nمحتسب شیخ شد و فسق خود از یاد ببرد\r\nقصه ماست که در هر سر بازار بماند\r\nهر می لعل کز آن دست بلورین ستدیم\r\nآب حسرت شد و در چشم گهربار بماند\r\nجز دل من کز ازل تا به ابد عاشق رفت\r\nجاودان کس نشنیدیم که در کار بماند\r\nگشت بیمار که چون چشم تو گردد نرگس\r\nشیوه تو نشدش حاصل و بیمار بماند\r\nاز صدای سخن عشق ندیدم خوشتر\r\nیادگاری که در این گنبد دوار بماند\r\nداشتم دلقی و صد عیب مرا می پوشید\r\nخرقه رهن می و مطرب شد و زنار بماند\r\nبر جمال تو چنان صورت چین حیران شد\r\nکه حدیثش همه جا در در و دیوار بماند\r\nبه تماشاگه زلفش دل حافظ روزی\r\nشد که بازآید و جاوید گرفتار بماند\r\n', 'به عهد و پیمانی که بسته ای پایبند باش. حریم خود را حفظ کن وگرنه خیلی زود آبرویت می رود. عشق را در دل خود جاودانه کن تا هیچ وقت دلت تیره نگردد. محبت و عشق تو همه را حیران می کند و نامت را بر سر زبان ها می اندازد. '),
(179, 'رسید مژده که ایام غم نخواهد ماند\r\nچنان نماند چنین نیز هم نخواهد ماند\r\nمن ار چه در نظر یار خاکسار شدم\r\nرقیب نیز چنین محترم نخواهد ماند\r\nچو پرده دار به شمشیر می زند همه را\r\nکسی مقیم حریم حرم نخواهد ماند\r\nچه جای شکر و شکایت ز نقش نیک و بد است\r\nچو بر صحیفه هستی رقم نخواهد ماند\r\nسرود مجلس جمشید گفته اند این بود\r\nکه جام باده بیاور که جم نخواهد ماند\r\nغنیمتی شمر ای شمع وصل پروانه\r\nکه این معامله تا صبحدم نخواهد ماند\r\nتوانگرا دل درویش خود به دست آور\r\nکه مخزن زر و گنج درم نخواهد ماند\r\nبدین رواق زبرجد نوشته اند به زر\r\nکه جز نکویی اهل کرم نخواهد ماند\r\nز مهربانی جانان طمع مبر حافظ\r\nکه نقش جور و نشان ستم نخواهد ماند\r\n', 'بسیار ناامید هستی و فکر می کنی تمام غم های عالم در دل توست ولی امیدت را از دست نده زیرا سرانجام همه بدبختی ها تو تمام می شود. غم و ناراحتی جای خود را به شادی و آشتی می دهند. به مستمندان کمک کن تا باز هم در زمان گرفتاری خداوند نیز مشکل تو را حل کند. '),
(180, 'ای پسته تو خنده زده بر حدیث قند\r\nمشتاقم از برای خدا یک شکر بخند\r\nطوبی ز قامت تو نیارد که دم زند\r\nزین قصه بگذرم که سخن می شود بلند\r\nخواهی که برنخیزدت از دیده رود خون\r\nدل در وفای صحبت رود کسان مبند\r\nگر جلوه می نمایی و گر طعنه می زنی\r\nما نیستیم معتقد شیخ خودپسند\r\nز آشفتگی حال من آگاه کی شود\r\nآن را که دل نگشت گرفتار این کمند\r\nبازار شوق گرم شد آن سروقد کجاست\r\nتا جان خود بر آتش رویش کنم سپند\r\nجایی که یار ما به شکرخنده دم زند\r\nای پسته کیستی تو خدا را به خود مخند\r\nحافظ چو ترک غمزه ترکان نمی کنی\r\nدانی کجاست جای تو خوارزم یا خجند\r\n', 'همه ی کارها را به شوخی و خنده نگیر. مردم را مسخره نکن چون با این کار دل دیگران را می شکنی. با طعنه و نیش سخن نگو و به آشفته حالی و گرفتاری مردم خنده نکن. شاید وقتی تو هم گرفتار شوی. این کار را ترک کن تا همیشه مورد قبول مردم واقع شوی و راهت را بیابی. '),
(181, 'بعد از این دست من و دامن آن سرو بلند\r\nکه به بالای چمان از بن و بیخم برکند\r\nحاجت مطرب و می نیست تو برقع بگشا\r\nکه به رقص آوردم آتش رویت چو سپند\r\nهیچ رویی نشود آینه حجله بخت\r\nمگر آن روی که مالند در آن سم سمند\r\nگفتم اسرار غمت هر چه بود گو می باش\r\nصبر از این بیش ندارم چه کنم تا کی و چند\r\nمکش آن آهوی مشکین مرا ای صیاد\r\nشرم از آن چشم سیه دار و مبندش به کمند\r\nمن خاکی که از این در نتوانم برخاست\r\nاز کجا بوسه زنم بر لب آن قصر بلند\r\nباز مستان دل از آن گیسوی مشکین حافظ\r\nزان که دیوانه همان به که بود اندر بند\r\n', 'به قول هایی که داده ای عمل کن. به جای اینکه همه چیز را درست کنی همه را خراب تر کرده ای. دیگر روی برگشتن نداری. اسرارت فاش شده است. بی قراری، کار را از آنچه که هست بدتر نکن. رو به سوی خدا کن تا تو را از سرگردانی نجات دهد. '),
(182, 'حسب حالی ننوشتی و شد ایامی چند\r\nمحرمی کو که فرستم به تو پیغامی چند\r\nما بدان مقصد عالی نتوانیم رسید\r\nهم مگر پیش نهد لطف شما گامی چند\r\nچون می از خم به سبو رفت و گل افکند نقاب\r\nفرصت عیش نگه دار و بزن جامی چند\r\nقند آمیخته با گل نه علاج دل ماست\r\nبوسه ای چند برآمیز به دشنامی چند\r\nزاهد از کوچه رندان به سلامت بگذر\r\nتا خرابت نکند صحبت بدنامی چند\r\nعیب می جمله چو گفتی هنرش نیز بگو\r\nنفی حکمت مکن از بهر دل عامی چند\r\nای گدایان خرابات خدا یار شماست\r\nچشم انعام مدارید ز انعامی چند\r\nپیر میخانه چه خوش گفت به دردی کش خویش\r\nکه مگو حال دل سوخته با خامی چند\r\nحافظ از شوق رخ مهر فروغ تو بسوخت\r\nکامگارا نظری کن سوی ناکامی چند\r\n', 'به مقصود خودت نمی رسی.مگر با خدا باشی. حاجات تو زمانی برآورده می شوند که نماز حاجات بخوانی. تو باید خوبی خودت را به همگان ثابت کنی. حس خودت را نشان بده اگر در عین نداری به مستمندان کمک کنی مردی. '),
(183, 'دوش وقت سحر از غصه نجاتم دادند\r\nواندر آن ظلمت شب آب حیاتم دادند\r\nبیخود از شعشعه پرتو ذاتم کردند\r\nباده از جام تجلی صفاتم دادند\r\nچه مبارک سحری بود و چه فرخنده شبی\r\nآن شب قدر که این تازه براتم دادند\r\nبعد از این روی من و آینه وصف جمال\r\nکه در آن جا خبر از جلوه ذاتم دادند\r\nمن اگر کامروا گشتم و خوشدل چه عجب\r\nمستحق بودم و این ها به زکاتم دادند\r\nهاتف آن روز به من مژده این دولت داد\r\nکه بدان جور و جفا صبر و ثباتم دادند\r\nاین همه شهد و شکر کز سخنم می ریزد\r\nاجر صبریست کز آن شاخ نباتم دادند\r\nهمت حافظ و انفاس سحرخیزان بود\r\nکه ز بند غم ایام نجاتم دادند\r\n', 'اگر به تمام نیات خودتان می رسید منظور این است که لیاقت داشته و مورد لطف خداوند قرار گرفته اید زیرا با تمام وجود خدا را می پرستید و وجود خودتان را از خدا می دانید. صبرتان باعث شد مژدگانی دولت به شما بدهند و جور و جفا را از شما دور کنند. '),
(184, 'دوش دیدم که ملایک در میخانه زدند\r\nگل آدم بسرشتند و به پیمانه زدند\r\nساکنان حرم ستر و عفاف ملکوت\r\nبا من راه نشین باده مستانه زدند\r\nآسمان بار امانت نتوانست کشید\r\nقرعه کار به نام من دیوانه زدند\r\nجنگ هفتاد و دو ملت همه را عذر بنه\r\nچون ندیدند حقیقت ره افسانه زدند\r\nشکر ایزد که میان من و او صلح افتاد\r\nصوفیان رقص کنان ساغر شکرانه زدند\r\nآتش آن نیست که از شعله او خندد شمع\r\nآتش آن است که در خرمن پروانه زدند\r\nکس چو حافظ نگشاد از رخ اندیشه نقاب\r\nتا سر زلف سخن را به قلم شانه زدند\r\n', 'وظیفه ای به عهده تان گذاشته اند که فکر می کردید از پس آن برنمی آیید اما با ایمان راسخ و دوری از گناه به خوبی از انجام آن برآمدید. شکر خدا را به جا می آورید که از یک حادثه ی بد نجات پیدا کرده اید. بیشتر فکر کنید. '),
(185, 'نقدها را بود آیا که عیاری گیرند\r\nتا همه صومعه داران پی کاری گیرند\r\nمصلحت دید من آن است که یاران همه کار\r\nبگذارند و خم طره یاری گیرند\r\nخوش گرفتند حریفان سر زلف ساقی\r\nگر فلکشان بگذارد که قراری گیرند\r\nقوت بازوی پرهیز به خوبان مفروش\r\nکه در این خیل حصاری به سواری گیرند\r\nیا رب این بچه ترکان چه دلیرند به خون\r\nکه به تیر مژه هر لحظه شکاری گیرند\r\nرقص بر شعر تر و ناله نی خوش باشد\r\nخاصه رقصی که در آن دست نگاری گیرند\r\nحافظ ابنای زمان را غم مسکینان نیست\r\nزین میان گر بتوان به که کناری گیرند\r\n', 'مصلحت در این است که به کمک یار بشتابید. خودتان را کنترل کنید. فعلا باید از صحبت کردن پرهیز کنید تا بتوانید یار را از بند نجات دهید. عده ای می خواهند به شما آسیب برسانند از آنها دوری کنید. '),
(186, 'گر می فروش حاجت رندان روا کند\r\nایزد گنه ببخشد و دفع بلا کند\r\nساقی به جام عدل بده باده تا گدا\r\nغیرت نیاورد که جهان پربلا کند\r\nحقا کز این غمان برسد مژده امان\r\nگر سالکی به عهد امانت وفا کند\r\nگر رنج پیش آید و گر راحت ای حکیم\r\nنسبت مکن به غیر که این ها خدا کند\r\nدر کارخانه ای که ره عقل و فضل نیست\r\nفهم ضعیف رای فضولی چرا کند\r\nمطرب بساز پرده که کس بی اجل نمرد\r\nوان کو نه این ترانه سراید خطا کند\r\nما را که درد عشق و بلای خمار کشت\r\nیا وصل دوست یا می صافی دوا کند\r\nجان رفت در سر می و حافظ به عشق سوخت\r\nعیسی دمی کجاست که احیای ما کند\r\n', 'وجدانت ناراحت است. در پی این باش تا گناهت را جبران کنی خداوند نیز تو را می بخشد و نجاتت می دهد. خبر خوبی در راه است که فرصتی دوباره به تو داده می شود تا به عهد خود وفا کنی. عمرت به بطالت می گذرد. عقل و دانش خودت را اضافه کن تا این چند صباح را بتوانی با آرامش زندگی کنی. '),
(187, 'دلا بسوز که سوز تو کارها بکند\r\nنیاز نیم شبی دفع صد بلا بکند\r\nعتاب یار پری چهره عاشقانه بکش\r\nکه یک کرشمه تلافی صد جفا بکند\r\nز ملک تا ملکوتش حجاب بردارند\r\nهر آن که خدمت جام جهان نما بکند\r\nطبیب عشق مسیحادم است و مشفق لیک\r\nچو درد در تو نبیند که را دوا بکند\r\nتو با خدای خود انداز کار و دل خوش دار\r\nکه رحم اگر نکند مدعی خدا بکند\r\nز بخت خفته ملولم بود که بیداری\r\nبه وقت فاتحه صبح یک دعا بکند\r\nبسوخت حافظ و بویی به زلف یار نبرد\r\nمگر دلالت این دولتش صبا بکند\r\n', 'شما قدمی در راه خدا بردارید آن وقت می بینید که خداوند چطور تلافی عمل خوب شما را می دهد. اول اینکه بلا را از شم دفع کرده و راه را برایتان روشن می کند. بیماریتان را شفا می دهد. حاجتی دارید که با دعا به درگاه خدا برآورده می شود. انشالله. '),
(188, 'مرا به رندی و عشق آن فضول عیب کند\r\nکه اعتراض بر اسرار علم غیب کند\r\nکمال سر محبت ببین نه نقص گناه\r\nکه هر که بی هنر افتد نظر به عیب کند\r\nز عطر حور بهشت آن نفس برآید بوی\r\nکه خاک میکده ما عبیر جیب کند\r\nچنان زند ره اسلام غمزه ساقی\r\nکه اجتناب ز صهبا مگر صهیب کند\r\nکلید گنج سعادت قبول اهل دل است\r\nمباد آن که در این نکته شک و ریب کند\r\nشبان وادی ایمن گهی رسد به مراد\r\nکه چند سال به جان خدمت شعیب کند\r\nز دیده خون بچکاند فسانه حافظ\r\nچو یاد وقت زمان شباب و شیب کند\r\n', 'به زمین و زمان چنگ انداخته ای و به زور می خواهی همه چیز را به دست بیاوری. تا راه راست نروی و سر بر آستان خداوند فرود نیاوری به مرادت نمی رسی. کلید گنج خوشبختی تو قدم گذاشتن در راه راست می باشد. '),
(189, 'طایر دولت اگر باز گذاری بکند\r\nیار بازآید و با وصل قراری بکند\r\nدیده را دستگه در و گهر گر چه نماند\r\nبخورد خونی و تدبیر نثاری بکند\r\nدوش گفتم بکند لعل لبش چاره من\r\nهاتف غیب ندا داد که آری بکند\r\nکس نیارد بر او دم زند از قصه ما\r\nمگرش باد صبا گوش گذاری بکند\r\nداده ام باز نظر را به تذروی پرواز\r\nبازخواند مگرش نقش و شکاری بکند\r\nشهر خالیست ز عشاق بود کز طرفی\r\nمردی از خویش برون آید و کاری بکند\r\nکو کریمی که ز بزم طربش غمزده ای\r\nجرعه ای درکشد و دفع خماری بکند\r\nیا وفا یا خبر وصل تو یا مرگ رقیب\r\nبود آیا که فلک زین دو سه کاری بکند\r\nحافظا گر نروی از در او هم روزی\r\nگذری بر سرت از گوشه کناری بکند\r\n', 'حاضر برای رسیدن به مرادت هر چه داری بدهی ولی فکر می کنی کسی به حرفت گوش نمی دهد و همه از تو دوری می کنند. این طور نیست یارانت به کمکت آمده و دستت را می گیرند. در حق تو بخشش می کنند. خبر رسیدن به مقصود و ناکامی دشمن در راه است. '),
(190, 'کلک مشکین تو روزی که ز ما یاد کند\r\nببرد اجر دو صد بنده که آزاد کند\r\nقاصد منزل سلمی که سلامت بادش\r\nچه شود گر به سلامی دل ما شاد کند\r\nامتحان کن که بسی گنج مرادت بدهند\r\nگر خرابی چو مرا لطف تو آباد کند\r\nیا رب اندر دل آن خسرو شیرین انداز\r\nکه به رحمت گذری بر سر فرهاد کند\r\nشاه را به بود از طاعت صدساله و زهد\r\nقدر یک ساعته عمری که در او داد کند\r\nحالیا عشوه ناز تو ز بنیادم برد\r\nتا دگرباره حکیمانه چه بنیاد کند\r\nگوهر پاک تو از مدحت ما مستغنیست\r\nفکر مشاطه چه با حسن خداداد کند\r\nره نبردیم به مقصود خود اندر شیراز\r\nخرم آن روز که حافظ ره بغداد کند\r\n', 'قاصدی خوش خبر به سمت شما می آید که به همراه خود رسیدن به مراد و لطف یار را مژده می دهد. مدت درازی صبر کردید و از خدا طلب حاجت نموده اید. گوهری با ارزش به شما بخشیده اند سعی کنید آن را با کمال و جمال حفظ کنید. '),
(191, 'آن کیست کز روی کرم با ما وفاداری کند\r\nبر جای بدکاری چو من یک دم نکوکاری کند\r\nاول به بانگ نای و نی آرد به دل پیغام وی\r\nوان گه به یک پیمانه می با من وفاداری کند\r\nدلبر که جان فرسود از او کام دلم نگشود از او\r\nنومید نتوان بود از او باشد که دلداری کند\r\nگفتم گره نگشوده ام زان طره تا من بوده ام\r\nگفتا منش فرموده ام تا با تو طراری کند\r\nپشمینه پوش تندخو از عشق نشنیده است بو\r\nاز مستیش رمزی بگو تا ترک هشیاری کند\r\nچون من گدای بی نشان مشکل بود یاری چنان\r\nسلطان کجا عیش نهان با رند بازاری کند\r\nزان طره پرپیچ و خم سهل است اگر بینم ستم\r\nاز بند و زنجیرش چه غم هر کس که عیاری کند\r\nشد لشکر غم بی عدد از بخت می خواهم مدد\r\nتا فخر دین عبدالصمد باشد که غمخواری کند\r\nبا چشم پرنیرنگ او حافظ مکن آهنگ او\r\nکان طره شبرنگ او بسیار طراری کند\r\n', 'به هر کسی اعتماد نکن و راز دل با او نداشته باش چون تمام غم های دنیا به دلت روانه می شود. دشمن درحال نیرنگ زدن است، مراقب باش. به دنبال کسی بگرد که وفادار باشد و به تو امید ببخشد و گره ی کار تو را باز کند، نه اینکه در عین گرفتاری ترکت کند. '),
(192, 'سرو چمان من چرا میل چمن نمی کند\r\nهمدم گل نمی شود یاد سمن نمی کند\r\nدی گله ای ز طره اش کردم و از سر فسوس\r\nگفت که این سیاه کج گوش به من نمی کند\r\nتا دل هرزه گرد من رفت به چین زلف او\r\nزان سفر دراز خود عزم وطن نمی کند\r\nپیش کمان ابرویش لابه همی کنم ولی\r\nگوش کشیده است از آن گوش به من نمی کند\r\nبا همه عطف دامنت آیدم از صبا عجب\r\nکز گذر تو خاک را مشک ختن نمی کند\r\nچون ز نسیم می شود زلف بنفشه پرشکن\r\nوه که دلم چه یاد از آن عهدشکن نمی کند\r\nدل به امید روی او همدم جان نمی شود\r\nجان به هوای کوی او خدمت تن نمی کند\r\nساقی سیم ساق من گر همه درد می دهد\r\nکیست که تن چو جام می جمله دهن نمی کند\r\nدستخوش جفا مکن آب رخم که فیض ابر\r\nبی مدد سرشک من در عدن نمی کند\r\nکشته غمزه تو شد حافظ ناشنیده پند\r\nتیغ سزاست هر که را درد سخن نمی کند\r\n', 'دست روی دست گذاشته و منتظری بخت خودش به سراغت بیاید ولی این طور نیست خودت هم همت داشته باش وگرنه هر چه ساخته ای خراب می شود و به حاجت خود نخواهی رسید. امیدوار باش، فکر بی وفایی نکن ابرهای رحمت به سویت روانه اند. به نصیحت بزرگان گوش کن. '),
(193, 'در نظربازی ما بی خبران حیرانند\r\nمن چنینم که نمودم دگر ایشان دانند\r\nعاقلان نقطه پرگار وجودند ولی\r\nعشق داند که در این دایره سرگردانند\r\nجلوه گاه رخ او دیده من تنها نیست\r\nماه و خورشید همین آینه می گردانند\r\nعهد ما با لب شیرین دهنان بست خدا\r\nما همه بنده و این قوم خداوندانند\r\nمفلسانیم و هوای می و مطرب داریم\r\nآه اگر خرقه پشمین به گرو نستانند\r\nوصل خورشید به شبپره اعمی نرسد\r\nکه در آن آینه صاحب نظران حیرانند\r\nلاف عشق و گله از یار زهی لاف دروغ\r\nعشقبازان چنین مستحق هجرانند\r\nمگرم چشم سیاه تو بیاموزد کار\r\nور نه مستوری و مستی همه کس نتوانند\r\nگر به نزهتگه ارواح برد بوی تو باد\r\nعقل و جان گوهر هستی به نثار افشانند\r\nزاهد ار رندی حافظ نکند فهم چه شد\r\nدیو بگریزد از آن قوم که قرآن خوانند\r\nگر شوند آگه از اندیشه ما مغبچگان\r\nبعد از این خرقه صوفی به گرو نستانند\r\n', 'شما پیشنهاد خودتان را داده اید حالا منتظر جواب هستید از سرگردانی نجات پیدا می کنید. عهدی خواهید بست و امانتی خواهید گرفت که باید تا آخر از او مراقبت کنید. در کاری که انجام می دهید از دروغ و بزرگنمایی پرهیز کنید. اگر دروغ بگویید مستحق هجران و دوری هستید. قرآن زیاد بخوانید تا شیطان همیشه از شما دور باشد. '),
(194, 'سمن بویان غبار غم چو بنشینند بنشانند\r\nپری رویان قرار از دل چو بستیزند بستانند\r\nبه فتراک جفا دل ها چو بربندند بربندند\r\nز زلف عنبرین جان ها چو بگشایند بفشانند\r\nبه عمری یک نفس با ما چو بنشینند برخیزند\r\nنهال شوق در خاطر چو برخیزند بنشانند\r\nسرشک گوشه گیران را چو دریابند در یابند\r\nرخ مهر از سحرخیزان نگردانند اگر دانند\r\nز چشمم لعل رمانی چو می خندند می بارند\r\nز رویم راز پنهانی چو می بینند می خوانند\r\nدوای درد عاشق را کسی کو سهل پندارد\r\nز فکر آنان که در تدبیر درمانند در مانند\r\nچو منصور از مراد آنان که بردارند بر دارند\r\nبدین درگاه حافظ را چو می خوانند می رانند\r\nدر این حضرت چو مشتاقان نیاز آرند ناز آرند\r\nکه با این درد اگر دربند درمانند درمانند\r\n', 'با یک اتفاق کوچک خیلی زود از پا در می افتی و فکر می کنی دیگر کارت تمام است. همه ی کارهایت نیمه تمام باقی مانده است. خودت می دانی اگر اراده کنی همیشه بهترین ها مال توست ولی تنبلی کرده و تدبیر کارت را به دست دیگری می سپاری. خودت را کنار نکش و همیشه در میدان کار و کوشش باش. تو موفق می شوی. '),
(195, 'غلام نرگس مست تو تاجدارانند\r\nخراب باده لعل تو هوشیارانند\r\nتو را صبا و مرا آب دیده شد غماز\r\nو گر نه عاشق و معشوق رازدارانند\r\nز زیر زلف دوتا چون گذر کنی بنگر\r\nکه از یمین و یسارت چه سوگوارانند\r\nگذار کن چو صبا بر بنفشه زار و ببین\r\nکه از تطاول زلفت چه بی قرارانند\r\nنصیب ماست بهشت ای خداشناس برو\r\nکه مستحق کرامت گناهکارانند\r\nنه من بر آن گل عارض غزل سرایم و بس\r\nکه عندلیب تو از هر طرف هزارانند\r\nتو دستگیر شو ای خضر پی خجسته که من\r\nپیاده می روم و همرهان سوارانند\r\nبیا به میکده و چهره ارغوانی کن\r\nمرو به صومعه کان جا سیاه کارانند\r\nخلاص حافظ از آن زلف تابدار مباد\r\nکه بستگان کمند تو رستگارانند\r\n', 'خواهان بیشمار داری که از نظر مقام و منزلت حتی از تو بالاترند. گاهی در بعضی موارد مدعی هستی وای خودت می دانی که هنوز دست چپ و راستت را نمی شناسی. هنوز اول راهی، از فراز و نشیب نترس. آینده ی بسیار مبارک و خوبی داری هر چند که گاهی گناه می کنی. '),
(196, 'آنان که خاک را به نظر کیمیا کنند\r\nآیا بود که گوشه چشمی به ما کنند\r\nدردم نهفته به ز طبیبان مدعی\r\nباشد که از خزانه غیبم دوا کنند\r\nمعشوق چون نقاب ز رخ در نمی کشد\r\nهر کس حکایتی به تصور چرا کنند\r\nچون حسن عاقبت نه به رندی و زاهدیست\r\nآن به که کار خود به عنایت رها کنند\r\nبی معرفت مباش که در من یزید عشق\r\nاهل نظر معامله با آشنا کنند\r\nحالی درون پرده بسی فتنه می رود\r\nتا آن زمان که پرده برافتد چه ها کنند\r\nگر سنگ از این حدیث بنالد عجب مدار\r\nصاحب دلان حکایت دل خوش ادا کنند\r\nمی خور که صد گناه ز اغیار در حجاب\r\nبهتر ز طاعتی که به روی و ریا کنند\r\nپیراهنی که آید از او بوی یوسفم\r\nترسم برادران غیورش قبا کنند\r\nبگذر به کوی میکده تا زمره حضور\r\nاوقات خود ز بهر تو صرف دعا کنند\r\nپنهان ز حاسدان به خودم خوان که منعمان\r\nخیر نهان برای رضای خدا کنند\r\nحافظ دوام وصل میسر نمی شود\r\nشاهان کم التفات به حال گدا کنند\r\n', 'ناامید نباش. دست روی دست گذاشته ای در صورتی که باید خودت همت کنی و خداوند هم به تو کمک می کند. جفا پیشه نباش. کسی که فتنه ها را به پا کره به زودی رسوا می شود. حسودان نمی گذارند تو به مراد دلت برسی. گاهی هم وقت خود را صرف عبادت کن تا رضای خداوند شامل حالت شود. '),
(197, 'شاهدان گر دلبری زین سان کنند\r\nزاهدان را رخنه در ایمان کنند\r\nهر کجا آن شاخ نرگس بشکفد\r\nگلرخانش دیده نرگسدان کنند\r\nای جوان سروقد گویی ببر\r\nپیش از آن کز قامتت چوگان کنند\r\nعاشقان را بر سر خود حکم نیست\r\nهر چه فرمان تو باشد آن کنند\r\nپیش چشمم کمتر است از قطره ای\r\nاین حکایت ها که از طوفان کنند\r\nیار ما چون گیرد آغاز سماع\r\nقدسیان بر عرش دست افشان کنند\r\nمردم چشمم به خون آغشته شد\r\nدر کجا این ظلم بر انسان کنند\r\nخوش برآ با غصه ای دل کاهل راز\r\nعیش خوش در بوته هجران کنند\r\nسر مکش حافظ ز آه نیم شب\r\nتا چو صبحت آینه رخشان کنند\r\n', 'غافل از حال خویش شده ای. تو جوانی پرشور هستی که از تو برای رسیدن به موفقیت استفاده می کنند و خودت از قافله عقب مانده ای. آنها خیلی از تو پایین تر هستند. سعی کن تو هم عقب نمانی. ظلمی به تو شده که به خاطرش غصه می خوری، ولی خداوند جواب ظالمان را می دهد. '),
(198, 'گفتم کی ام دهان و لبت کامران کنند\r\nگفتا به چشم هر چه تو گویی چنان کنند\r\nگفتم خراج مصر طلب می کند لبت\r\nگفتا در این معامله کمتر زیان کنند\r\nگفتم به نقطه دهنت خود که برد راه\r\nگفت این حکایتیست که با نکته دان کنند\r\nگفتم صنم پرست مشو با صمد نشین\r\nگفتا به کوی عشق هم این و هم آن کنند\r\nگفتم هوای میکده غم می برد ز دل\r\nگفتا خوش آن کسان که دلی شادمان کنند\r\nگفتم شراب و خرقه نه آیین مذهب است\r\nگفت این عمل به مذهب پیر مغان کنند\r\nگفتم ز لعل نوش لبان پیر را چه سود\r\nگفتا به بوسه شکرینش جوان کنند\r\nگفتم که خواجه کی به سر حجله می رود\r\nگفت آن زمان که مشتری و مه قران کنند\r\nگفتم دعای دولت او ورد حافظ است\r\nگفت این دعا ملایک هفت آسمان کنند\r\n', 'اراده ای بسیار قوی و راسخ داری که هر چه بخواهی به دست می آوری. پس یا علی بگو تا دنیا را تسخیر کنی. دل ها را شاد کن. با خدا باش و قرآن بخوان تا غم ها از دلت پاک شود و راه دین را پیدا کنی تا با سرعت هر چه تمام تر به قله ی موفقیت صعود نمایی که در هفت آسمان همه تو را دعا کنند. '),
(199, 'واعظان کاین جلوه در محراب و منبر می کنند\r\nچون به خلوت می روند آن کار دیگر می کنند\r\nمشکلی دارم ز دانشمند مجلس بازپرس\r\nتوبه فرمایان چرا خود توبه کمتر می کنند\r\nگوییا باور نمی دارند روز داوری\r\nکاین همه قلب و دغل در کار داور می کنند\r\nیا رب این نودولتان را با خر خودشان نشان\r\nکاین همه ناز از غلام ترک و استر می کنند\r\nای گدای خانقه برجه که در دیر مغان\r\nمی دهند آبی که دل ها را توانگر می کنند\r\nحسن بی پایان او چندان که عاشق می کشد\r\nزمره دیگر به عشق از غیب سر بر می کنند\r\nبر در میخانه عشق ای ملک تسبیح گوی\r\nکاندر آن جا طینت آدم مخمر می کنند\r\nصبحدم از عرش می آمد خروشی عقل گفت\r\nقدسیان گویی که شعر حافظ از بر می کنند\r\n', 'مشکلی داری به جای اینکه آن را حل کنی کاری می کنی که اوضاع بدتر شود. با دغل بازی و دروغ کاری از پیش نمی بری. عقلت را به کار نمی اندازی. به عبادت بپرداز و کمی فکر کن تا دلت قوی شود. تو حسن های زیادی داری که پنهان مانده اند ذات تو خوب است ولی ظاهرا خود را بد نشان می دهی. '),
(200, 'دانی که چنگ و عود چه تقریر می کنند\r\nپنهان خورید باده که تعزیر می کنند\r\nناموس عشق و رونق عشاق می برند\r\nعیب جوان و سرزنش پیر می کنند\r\nجز قلب تیره هیچ نشد حاصل و هنوز\r\nباطل در این خیال که اکسیر می کنند\r\nگویند رمز عشق مگویید و مشنوید\r\nمشکل حکایتیست که تقریر می کنند\r\nما از برون در شده مغرور صد فریب\r\nتا خود درون پرده چه تدبیر می کنند\r\nتشویش وقت پیر مغان می دهند باز\r\nاین سالکان نگر که چه با پیر می کنند\r\nصد ملک دل به نیم نظر می توان خرید\r\nخوبان در این معامله تقصیر می کنند\r\nقومی به جد و جهد نهادند وصل دوست\r\nقومی دگر حواله به تقدیر می کنند\r\nفی الجمله اعتماد مکن بر ثبات دهر\r\nکاین کارخانه ایست که تغییر می کنند\r\nمی خور که شیخ و حافظ و مفتی و محتسب\r\nچون نیک بنگری همه تزویر می کنند\r\n', 'اینقدر به مال و ثروت خود مغرور نشو. به مال دنیا اعتمادی نیست هر آن می سوزد و از بین می رود. محبت و عشق را نمی توان با پول خرید. ثروت قلب را تیره می کند. برای عبادت نگو که معذورم تو هر چه داری از لطف خداست. همان هایی که در اطرافت هستند در زمان بدبختی تو را به نیم جو هم نمی خرند پس لااقل با خدا باش. '),
(201, 'شراب بی غش و ساقی خوش دو دام رهند\r\nکه زیرکان جهان از کمندشان نرهند\r\nمن ار چه عاشقم و رند و مست و نامه سیاه\r\nهزار شکر که یاران شهر بی گنهند\r\nجفا نه پیشه درویشیست و راهروی\r\nبیار باده که این سالکان نه مرد رهند\r\nمبین حقیر گدایان عشق را کاین قوم\r\nشهان بی کمر و خسروان بی کلهند\r\nبه هوش باش که هنگام باد استغنا\r\nهزار خرمن طاعت به نیم جو ننهند\r\nمکن که کوکبه دلبری شکسته شود\r\nچو بندگان بگریزند و چاکران بجهند\r\nغلام همت دردی کشان یک رنگم\r\nنه آن گروه که ازرق لباس و دل سیهند\r\nقدم منه به خرابات جز به شرط ادب\r\nکه سالکان درش محرمان پادشهند\r\nجناب عشق بلند است همتی حافظ\r\nکه عاشقان ره بی همتان به خود ندهند\r\n', 'یاران خوبی داری که همگی وفا می کنند و راه راست می روند هر چند که فقیرند ولی در عین تنگدستی کمکت می کنند. کاری نکن که این یاران ترکت کنند و تنها شوی آنها با تو یکرنگ هستند تو هم با آنها یکرنگ باش. همت کن تا پیروز شوی که یاران همراه تو هستند. '),
(202, 'بود آیا که در میکده ها بگشایند\r\nگره از کار فروبسته ما بگشایند\r\nاگر از بهر دل زاهد خودبین بستند\r\nدل قوی دار که از بهر خدا بگشایند\r\nبه صفای دل رندان صبوحی زدگان\r\nبس در بسته به مفتاح دعا بگشایند\r\nنامه تعزیت دختر رز بنویسید\r\nتا همه مغبچگان زلف دوتا بگشایند\r\nگیسوی چنگ ببرید به مرگ می ناب\r\nتا حریفان همه خون از مژه ها بگشایند\r\nدر میخانه ببستند خدایا مپسند\r\nکه در خانه تزویر و ریا بگشایند\r\nحافظ این خرقه که داری تو ببینی فردا\r\nکه چه زنار ز زیرش به دغا بگشایند\r\n', 'اگر می خواهی درهای حاجت به رویت باز شود از دروغ و ریا دوری کن. به صفای دلت و به خاطر دعاهایی که کرده ای درهای مراد به رویت گشوده شده و رقیبان و حسودان از ناراحتی خون گریه می کنند. نامه ای به دست تو خواهد رسید که عین مراد است. خداوند یار توست. '),
(203, 'سال ها دفتر ما در گرو صهبا بود\r\nرونق میکده از درس و دعای ما بود\r\nنیکی پیر مغان بین که چو ما بدمستان\r\nهر چه کردیم به چشم کرمش زیبا بود\r\nدفتر دانش ما جمله بشویید به می\r\nکه فلک دیدم و در قصد دل دانا بود\r\nاز بتان آن طلب ار حسن شناسی ای دل\r\nکاین کسی گفت که در علم نظر بینا بود\r\nدل چو پرگار به هر سو دورانی می کرد\r\nو اندر آن دایره سرگشته پابرجا بود\r\nمطرب از درد محبت عملی می پرداخت\r\nکه حکیمان جهان را مژه خون پالا بود\r\nمی شکفتم ز طرب زان که چو گل بر لب جوی\r\nبر سرم سایه آن سرو سهی بالا بود\r\nپیر گلرنگ من اندر حق ازرق پوشان\r\nرخصت خبث نداد ار نه حکایت ها بود\r\nقلب اندوده حافظ بر او خرج نشد\r\nکاین معامل به همه عیب نهان بینا بود\r\n', 'به خودت شک داری. آنچه را که در دست داری تو را ارضاء نمی کند. فکر می کنی همگان می خواهند به تو لطف کنند و از روی ترحم جواب سلامت را می دهند. از سرگردانی خسته شده ای. آرزوهای زیادی داری این قدر خود را کوچک نشمار. خدا از نیات قلبی تو آگاه است و باز هم یاریت می کند.  '),
(204, 'یاد باد آن که نهانت نظری با ما بود\r\nرقم مهر تو بر چهره ما پیدا بود\r\nیاد باد آن که چو چشمت به عتابم می کشت\r\nمعجز عیسویت در لب شکرخا بود\r\nیاد باد آن که صبوحی زده در مجلس انس\r\nجز من و یار نبودیم و خدا با ما بود\r\nیاد باد آن که رخت شمع طرب می افروخت\r\nوین دل سوخته پروانه ناپروا بود\r\nیاد باد آن که در آن بزمگه خلق و ادب\r\nآن که او خنده مستانه زدی صهبا بود\r\nیاد باد آن که چو یاقوت قدح خنده زدی\r\nدر میان من و لعل تو حکایت ها بود\r\nیاد باد آن که نگارم چو کمر بربستی\r\nدر رکابش مه نو پیک جهان پیما بود\r\nیاد باد آن که خرابات نشین بودم و مست\r\nوآنچه در مسجدم امروز کم است آن جا بود\r\nیاد باد آن که به اصلاح شما می شد راست\r\nنظم هر گوهر ناسفته که حافظ را بود\r\n', 'ایام جوانی و خوشگذرانی و راحتی زود می گذرد و از خود و اطرافت غافل نشو که ناگهان این روزهای خوش جای خود را به رنج و مشقت می دهند و پس در عین خوشی و راحتی فکر آینده و حوادث ناگوار هم باش و همیشه آماده ی جنگ با مشقات باش. '),
(205, 'تا ز میخانه و می نام و نشان خواهد بود\r\nسر ما خاک ره پیر مغان خواهد بود\r\nحلقه پیر مغان از ازلم در گوش است\r\nبر همانیم که بودیم و همان خواهد بود\r\nبر سر تربت ما چون گذری همت خواه\r\nکه زیارتگه رندان جهان خواهد بود\r\nبرو ای زاهد خودبین که ز چشم من و تو\r\nراز این پرده نهان است و نهان خواهد بود\r\nترک عاشق کش من مست برون رفت امروز\r\nتا دگر خون که از دیده روان خواهد بود\r\nچشمم آن دم که ز شوق تو نهد سر به لحد\r\nتا دم صبح قیامت نگران خواهد بود\r\nبخت حافظ گر از این گونه مدد خواهد کرد\r\nزلف معشوقه به دست دگران خواهد بود\r\n', 'فکر می کنید وضعیت شما تغییر ناپذیر است و این مسئله باعث دل سردی تان شده است. از یکنواختی خسته شده اید . می ترسید که آینده را نتوانید بسازید. ناامید نباشید این کار به دست خودتان حل می شود. همت کنید و زندگیتان را بسازید. '),
(206, 'پیش از اینت بیش از این اندیشه عشاق بود\r\nمهرورزی تو با ما شهره آفاق بود\r\nیاد باد آن صحبت شب ها که با نوشین لبان\r\nبحث سر عشق و ذکر حلقه عشاق بود\r\nپیش از این کاین سقف سبز و طاق مینا برکشند\r\nمنظر چشم مرا ابروی جانان طاق بود\r\nاز دم صبح ازل تا آخر شام ابد\r\nدوستی و مهر بر یک عهد و یک میثاق بود\r\nسایه معشوق اگر افتاد بر عاشق چه شد\r\nما به او محتاج بودیم او به ما مشتاق بود\r\nحسن مه رویان مجلس گر چه دل می برد و دین\r\nبحث ما در لطف طبع و خوبی اخلاق بود\r\nبر در شاهم گدایی نکته ای در کار کرد\r\nگفت بر هر خوان که بنشستم خدا رزاق بود\r\nرشته تسبیح اگر بگسست معذورم بدار\r\nدستم اندر دامن ساقی سیمین ساق بود\r\nدر شب قدر ار صبوحی کرده ام عیبم مکن\r\nسرخوش آمد یار و جامی بر کنار طاق بود\r\nشعر حافظ در زمان آدم اندر باغ خلد\r\nدفتر نسرین و گل را زینت اوراق بود\r\n', 'ناراحت و افسرده نباش و اینقدر فکر گذشته را نکن. فکر این باش به عهد و پیمانی که بسته ای عمل کنی. به دنبال کسب روزی باش که خداوند هر آنچه را که بخواهی از مال و دولت به تو می بخشد. فعلا نمی توانی به عهدی که بسته ای عمل کنی ولی سعی خودت را می کنی. این سعی و کوشش تو را به سر منزل مقصود می رساند. '),
(207, 'یاد باد آن که سر کوی توام منزل بود\r\nدیده را روشنی از خاک درت حاصل بود\r\nراست چون سوسن و گل از اثر صحبت پاک\r\nبر زبان بود مرا آن چه تو را در دل بود\r\nدل چو از پیر خرد نقل معانی می کرد\r\nعشق می گفت به شرح آن چه بر او مشکل بود\r\nآه از آن جور و تطاول که در این دامگه است\r\nآه از آن سوز و نیازی که در آن محفل بود\r\nدر دلم بود که بی دوست نباشم هرگز\r\nچه توان کرد که سعی من و دل باطل بود\r\nدوش بر یاد حریفان به خرابات شدم\r\nخم می دیدم خون در دل و پا در گل بود\r\nبس بگشتم که بپرسم سبب درد فراق\r\nمفتی عقل در این مساله لایعقل بود\r\nراستی خاتم فیروزه بواسحاقی\r\nخوش درخشید ولی دولت مستعجل بود\r\nدیدی آن قهقهه کبک خرامان حافظ\r\nکه ز سرپنجه شاهین قضا غافل بود\r\n', 'راهی برای تو گذاشته بودند که به سلامتی از آن گذر کردید. در ایام جوانی و خوشی غافل نباش که خوشی زودگذر است و بادهای نفاق به سویت می وزند، پس آماده باش تا در آن زمان راه و چاره را پیدا کنی. در ضمن به فکر باش تا یاری پیدا کنی که همیشه تکیه گاه تو باشد. '),
(208, 'خستگان را چو طلب باشد و قوت نبود\r\nگر تو بیداد کنی شرط مروت نبود\r\nما جفا از تو ندیدیم و تو خود نپسندی\r\nآن چه در مذهب ارباب طریقت نبود\r\nخیره آن دیده که آبش نبرد گریه عشق\r\nتیره آن دل که در او شمع محبت نبود\r\nدولت از مرغ همایون طلب و سایه او\r\nزان که با زاغ و زغن شهپر دولت نبود\r\nگر مدد خواستم از پیر مغان عیب مکن\r\nشیخ ما گفت که در صومعه همت نبود\r\nچون طهارت نبود کعبه و بتخانه یکیست\r\nنبود خیر در آن خانه که عصمت نبود\r\nحافظا علم و ادب ورز که در مجلس شاه\r\nهر که را نیست ادب لایق صحبت نبود\r\n', 'مردانگی و مروت نیست که بخواهی به ضعیفان زور بگویی. گاهی تحت تاثیر حرف های مردم آنچه را که برای خود نمی پسندی انجام می دهی. اگر دهن بین باشی شمع دلت خاموش شده و قلبت تیره می گردد. همیشه از خداوند کمک بخواه نه از بنده ی او. هر کاری را با وضو انجام بده تا لایق درگاه حق باشی. '),
(209, 'قتل این خسته به شمشیر تو تقدیر نبود\r\nور نه هیچ از دل بی رحم تو تقصیر نبود\r\nمن دیوانه چو زلف تو رها می کردم\r\nهیچ لایقترم از حلقه زنجیر نبود\r\nیا رب این آینه حسن چه جوهر دارد\r\nکه در او آه مرا قوت تاثیر نبود\r\nسر ز حسرت به در میکده ها برگردم\r\nچون شناسای تو در صومعه یک پیر نبود\r\nنازنینتر ز قدت در چمن ناز نرست\r\nخوشتر از نقش تو در عالم تصویر نبود\r\nتا مگر همچو صبا باز به کوی تو رسم\r\nحاصلم دوش بجز ناله شبگیر نبود\r\nآن کشیدم ز تو ای آتش هجران که چو شمع\r\nجز فنای خودم از دست تو تدبیر نبود\r\nآیتی بود عذاب انده حافظ بی تو\r\nکه بر هیچ کسش حاجت تفسیر نبود\r\n', 'تقصیر خود توست که همه چیز را از دست داده ای. تو لایق داشتن همه چیز بوده ای ولی الان حسرت همه ی آنها را می کشی. احساس پیری می کنی و فعلا کاری از دستت برنمی آید. عذاب و ناراحتی تو را همه می فهمند اما کسی کمکی به تو نمی کند. خودت باید دوباره همه چیز را بسازی. '),
(210, 'دوش در حلقه ما قصه گیسوی تو بود\r\nتا دل شب سخن از سلسله موی تو بود\r\nدل که از ناوک مژگان تو در خون می گشت\r\nباز مشتاق کمانخانه ابروی تو بود\r\nهم عفاالله صبا کز تو پیامی می داد\r\nور نه در کس نرسیدیم که از کوی تو بود\r\nعالم از شور و شر عشق خبر هیچ نداشت\r\nفتنه انگیز جهان غمزه جادوی تو بود\r\nمن سرگشته هم از اهل سلامت بودم\r\nدام راهم شکن طره هندوی تو بود\r\nبگشا بند قبا تا بگشاید دل من\r\nکه گشادی که مرا بود ز پهلوی تو بود\r\nبه وفای تو که بر تربت حافظ بگذر\r\nکز جهان می شد و در آرزوی روی تو بود\r\n', 'آفرین بر ایمان راسخ و اراده ی تو که باعث می شود جهانی را بهم بریزی و دنیایی را به خوبی بسازی، دام ها را بشکنی. خداوند به خاطر همین همت و ایمانت، دلت و راهت را همیشه روشن می کند و تو به تمام حاجات خود می رسی حتی اگر آرزویت عجیب باشد. '),
(211, 'دوش می آمد و رخساره برافروخته بود\r\nتا کجا باز دل غمزده ای سوخته بود\r\nرسم عاشق کشی و شیوه شهرآشوبی\r\nجامه ای بود که بر قامت او دوخته بود\r\nجان عشاق سپند رخ خود می دانست\r\nو آتش چهره بدین کار برافروخته بود\r\nگر چه می گفت که زارت بکشم می دیدم\r\nکه نهانش نظری با من دلسوخته بود\r\nکفر زلفش ره دین می زد و آن سنگین دل\r\nدر پی اش مشعلی از چهره برافروخته بود\r\nدل بسی خون به کف آورد ولی دیده بریخت\r\nالله الله که تلف کرد و که اندوخته بود\r\nیار مفروش به دنیا که بسی سود نکرد\r\nآن که یوسف به زر ناسره بفروخته بود\r\nگفت و خوش گفت برو خرقه بسوزان حافظ\r\nیا رب این قلب شناسی ز که آموخته بود\r\n', 'ذات و طینتی زیبا داری و همین برای معرفی تو کافی است. احتیاج به آراستن ظاهر نیست. اعتماد به نفس خود را تقویت کن. هر کس که تو را انتخاب کند عاشق طینتت می شود. تو هم ارزشمندتر از آن هستی که بخواهی خود را خیلی راحت به کسی معرفی کنی. '),
(212, 'یک دو جامم دی سحرگه اتفاق افتاده بود\r\nو از لب ساقی شرابم در مذاق افتاده بود\r\nاز سر مستی دگر با شاهد عهد شباب\r\nرجعتی می خواستم لیکن طلاق افتاده بود\r\nدر مقامات طریقت هر کجا کردیم سیر\r\nعافیت را با نظربازی فراق افتاده بود\r\nساقیا جام دمادم ده که در سیر طریق\r\nهر که عاشق وش نیامد در نفاق افتاده بود\r\nای معبر مژده ای فرما که دوشم آفتاب\r\nدر شکرخواب صبوحی هم وثاق افتاده بود\r\nنقش می بستم که گیرم گوشه ای زان چشم مست\r\nطاقت و صبر از خم ابروش طاق افتاده بود\r\nگر نکردی نصرت دین شاه یحیی از کرم\r\nکار ملک و دین ز نظم و اتساق افتاده بود\r\nحافظ آن ساعت که این نظم پریشان می نوشت\r\nطایر فکرش به دام اشتیاق افتاده بود\r\n', 'در صدد این هستی که گذشته را جبران کنی و به عافیت برسی. به خاطر این پشتکار مژده ای به تو می دهند که نشان دهنده ی به پایان رسیدن غم ها و تاریکی هاست. در راه خدا بخشش کن. پریشانی را از خود دور نما تا برای رسیدن به مقصود قویتر و استوارتر باشی.  '),
(213, 'گوهر مخزن اسرار همان است که بود\r\nحقه مهر بدان مهر و نشان است که بود\r\nعاشقان زمره ارباب امانت باشند\r\nلاجرم چشم گهربار همان است که بود\r\nاز صبا پرس که ما را همه شب تا دم صبح\r\nبوی زلف تو همان مونس جان است که بود\r\nطالب لعل و گهر نیست وگرنه خورشید\r\nهمچنان در عمل معدن و کان است که بود\r\nکشته غمزه خود را به زیارت دریاب\r\nزان که بیچاره همان دل نگران است که بود\r\nرنگ خون دل ما را که نهان می داری\r\nهمچنان در لب لعل تو عیان است که بود\r\nزلف هندوی تو گفتم که دگر ره نزند\r\nسال ها رفت و بدان سیرت و سان است که بود\r\nحافظا بازنما قصه خونابه چشم\r\nکه بر این چشمه همان آب روان است که بود\r\n', 'اسرار خویش را حفظ کن و هیچ نشانه ایی از این اسرار به کسی نگو. در جستجوی چیز با ارزشی هستی که هم ارزش معنوی و هم مادی دارد. گاهی آنقدر نگران می شوی که رشته ی کار از دستت خارج می شود. به قول حافظ نیت شما چون آب روانی در حال رسیدن است. '),
(214, 'دیدم به خواب خوش که به دستم پیاله بود\r\nتعبیر رفت و کار به دولت حواله بود\r\nچهل سال رنج و غصه کشیدیم و عاقبت\r\nتدبیر ما به دست شراب دوساله بود\r\nآن نافه مراد که می خواستم ز بخت\r\nدر چین زلف آن بت مشکین کلاله بود\r\nاز دست برده بود خمار غمم سحر\r\nدولت مساعد آمد و می در پیاله بود\r\nبر آستان میکده خون می خورم مدام\r\nروزی ما ز خوان قدر این نواله بود\r\nهر کو نکاشت مهر و ز خوبی گلی نچید\r\nدر رهگذار باد نگهبان لاله بود\r\nبر طرف گلشنم گذر افتاد وقت صبح\r\nآن دم که کار مرغ سحر آه و ناله بود\r\nدیدیم شعر دلکش حافظ به مدح شاه\r\nیک بیت از این قصیده به از صد رساله بود\r\nآن شاه تندحمله که خورشید شیرگیر\r\nپیشش به روز معرکه کمتر غزاله بود\r\n', 'نتیجه ی صبر خودت را می بینی. رنج هایی که کشیده ای به سر آمده و حالا نوبت بخت و مراد است. دولت با تو مساعدت کرده، پس قدر این همه عنایت الهی را بدان. در اصل آنچه را در گذشته کاشته ای امروز درو می کنی و به نیت خودت خواهی رسید. '),
(215, 'به کوی میکده یا رب سحر چه مشغله بود\r\nکه جوش شاهد و ساقی و شمع و مشعله بود\r\nحدیث عشق که از حرف و صوت مستغنیست\r\nبه ناله دف و نی در خروش و ولوله بود\r\nمباحثی که در آن مجلس جنون می رفت\r\nورای مدرسه و قال و قیل مساله بود\r\nدل از کرشمه ساقی به شکر بود ولی\r\nز نامساعدی بختش اندکی گله بود\r\nقیاس کردم و آن چشم جادوانه مست\r\nهزار ساحر چون سامریش در گله بود\r\nبگفتمش به لبم بوسه ای حوالت کن\r\nبه خنده گفت کی ات با من این معامله بود\r\nز اخترم نظری سعد در ره است که دوش\r\nمیان ماه و رخ یار من مقابله بود\r\nدهان یار که درمان درد حافظ داشت\r\nفغان که وقت مروت چه تنگ حوصله بود\r\n', 'خودت را با دیگری مقایسه نکن و گله از بخت خود نداشته باش. تمام مشغله و کارهای درهم تو سر و سامان پیدا می کند، مانعی پیش پای تو است که با فکر و تدبیر خودت برداشته می شود. درد تو درمان پذیر است، کمی حوصله داشته باش. '),
(216, 'آن یار کز او خانه ما جای پری بود\r\nسر تا قدمش چون پری از عیب بری بود\r\nدل گفت فروکش کنم این شهر به بویش\r\nبیچاره ندانست که یارش سفری بود\r\nتنها نه ز راز دل من پرده برافتاد\r\nتا بود فلک شیوه او پرده دری بود\r\nمنظور خردمند من آن ماه که او را\r\nبا حسن ادب شیوه صاحب نظری بود\r\nاز چنگ منش اختر بدمهر به دربرد\r\nآری چه کنم دولت دور قمری بود\r\nعذری بنه ای دل که تو درویشی و او را\r\nدر مملکت حسن سر تاجوری بود\r\nاوقات خوش آن بود که با دوست به سر رفت\r\nباقی همه بی حاصلی و بی خبری بود\r\nخوش بود لب آب و گل و سبزه و نسرین\r\nافسوس که آن گنج روان رهگذری بود\r\nخود را بکش ای بلبل از این رشک که گل را\r\nبا باد صبا وقت سحر جلوه گری بود\r\nهر گنج سعادت که خدا داد به حافظ\r\nاز یمن دعای شب و ورد سحری بود\r\n', 'در مقطع زمانی، کمی دچار مشکلات می شوی که بسیار زود حل می شود گاهی اوقات خود را به بطالت می گذرانی و بعد پشیمان می شوی. قدر مال خود را ندانسته و بی خود آن را خرج کرده ای که حالا افسوس می خوری. حالا هم دیر نشده از خدا بخواه تا دوباره تو را از مال بی نیاز کند. '),
(217, 'مسلمانان مرا وقتی دلی بود\r\nکه با وی گفتمی گر مشکلی بود\r\nبه گردابی چو می افتادم از غم\r\nبه تدبیرش امید ساحلی بود\r\nدلی همدرد و یاری مصلحت بین\r\nکه استظهار هر اهل دلی بود\r\nز من ضایع شد اندر کوی جانان\r\nچه دامنگیر یا رب منزلی بود\r\nهنر بی عیب حرمان نیست لیکن\r\nز من محرومتر کی سائلی بود\r\nبر این جان پریشان رحمت آرید\r\nکه وقتی کاردانی کاملی بود\r\nمرا تا عشق تعلیم سخن کرد\r\nحدیثم نکته هر محفلی بود\r\nمگو دیگر که حافظ نکته دان است\r\nکه ما دیدیم و محکم جاهلی بود\r\n', 'فکر می کنی کسی حرفت را نمی شنود و از مشکلت خبر ندارد. کسی تو را از گرداب غم رهایی نمی دهد. اما همه ی این ها مصلحت الهی است و تو مورد امتحان قرار گرفته ای باز هم رحمت خدا شامل حالت می شود و کاردانی تو را نجات می دهد بدان همه ی این مصائب به خاطر جهالت خودت بوده است. '),
(218, 'در ازل هر کو به فیض دولت ارزانی بود\r\nتا ابد جام مرادش همدم جانی بود\r\nمن همان ساعت که از می خواستم شد توبه کار\r\nگفتم این شاخ ار دهد باری پشیمانی بود\r\nخود گرفتم کافکنم سجاده چون سوسن به دوش\r\nهمچو گل بر خرقه رنگ می مسلمانی بود\r\nبی چراغ جام در خلوت نمی یارم نشست\r\nزان که کنج اهل دل باید که نورانی بود\r\nهمت عالی طلب جام مرصع گو مباش\r\nرند را آب عنب یاقوت رمانی بود\r\nگر چه بی سامان نماید کار ما سهلش مبین\r\nکاندر این کشور گدایی رشک سلطانی بود\r\nنیک نامی خواهی ای دل با بدان صحبت مدار\r\nخودپسندی جان من برهان نادانی بود\r\nمجلس انس و بهار و بحث شعر اندر میان\r\nنستدن جام می از جانان گران جانی بود\r\nدی عزیزی گفت حافظ می خورد پنهان شراب\r\nای عزیز من نه عیب آن به که پنهانی بود\r\n', 'اگر می خواهی شان و منزلت بالایی داشته باشی با بدان دوستی نکن و بی خود جان خود را فدای آنان نکن به جای این کار به آینده ات سرو سامانی ببخش تا به جای رسیدن به سلطانی به گدایی نیفتی. همت بالا داشته باش تا از آب یاقوت بگیری و همیشه دلت روشن باشد. '),
(219, 'کنون که در چمن آمد گل از عدم به وجود\r\nبنفشه در قدم او نهاد سر به سجود\r\nبنوش جام صبوحی به ناله دف و چنگ\r\nببوس غبغب ساقی به نغمه نی و عود\r\nبه دور گل منشین بی شراب و شاهد و چنگ\r\nکه همچو روز بقا هفته ای بود معدود\r\nشد از خروج ریاحین چو آسمان روشن\r\nزمین به اختر میمون و طالع مسعود\r\nز دست شاهد نازک عذار عیسی دم\r\nشراب نوش و رها کن حدیث عاد و ثمود\r\nجهان چو خلد برین شد به دور سوسن و گل\r\nولی چه سود که در وی نه ممکن است خلود\r\nچو گل سوار شود بر هوا سلیمان وار\r\nسحر که مرغ درآید به نغمه داوود\r\nبه باغ تازه کن آیین دین زردشتی\r\nکنون که لاله برافروخت آتش نمرود\r\nبخواه جام صبوحی به یاد آصف عهد\r\nوزیر ملک سلیمان عماد دین محمود\r\nبود که مجلس حافظ به یمن تربیتش\r\nهر آن چه می طلبد جمله باشدش موجود\r\n', 'حالا که فرصتی پیش آمده به نحو احسن از آن استفاده کن. این فرصت کوتاه است. در این مدت زمان کوتاه دولت و مال زیادی به دست خواهی آورد و این مال و مکنت نکند که تو را از دین جدا کند و خدا را فراموش کنی. همت و هر آنچه از خدا می خواهی بطلب. '),
(220, 'از دیده خون دل همه بر روی ما رود\r\nبر روی ما ز دیده چه گویم چه ها رود\r\nما در درون سینه هوایی نهفته ایم\r\nبر باد اگر رود دل ما زان هوا رود\r\nخورشید خاوری کند از رشک جامه چاک\r\nگر ماه مهرپرور من در قبا رود\r\nبر خاک راه یار نهادیم روی خویش\r\nبر روی ما رواست اگر آشنا رود\r\nسیل است آب دیده و هر کس که بگذرد\r\nگر خود دلش ز سنگ بود هم ز جا رود\r\nما را به آب دیده شب و روز ماجراست\r\nزان رهگذر که بر سر کویش چرا رود\r\nحافظ به کوی میکده دایم به صدق دل\r\nچون صوفیان صومعه دار از صفا رود\r\n', 'درد دل زیادی داری که نمی توانی به هر کسی بگویی. رو به سوی معشوق خود می کنی ولی او هم صدای دل شما را نمی شنود و رو بر می گرداند و از بی تفاوتی همه دل تنگ هستی. خودت فردی پاک دل هستی به خدایت توکل کن و بدان اگر خدا بخواهد مراد دلت را خواهد داد. '),
(221, 'چو دست بر سر زلفش زنم به تاب رود\r\nور آشتی طلبم با سر عتاب رود\r\nچو ماه نو ره بیچارگان نظاره\r\nزند به گوشه ابرو و در نقاب رود\r\nشب شراب خرابم کند به بیداری\r\nوگر به روز شکایت کنم به خواب رود\r\nطریق عشق پرآشوب و فتنه است ای دل\r\nبیفتد آن که در این راه با شتاب رود\r\nگدایی در جانان به سلطنت مفروش\r\nکسی ز سایه این در به آفتاب رود\r\nسواد نامه موی سیاه چون طی شد\r\nبیاض کم نشود گر صد انتخاب رود\r\nحباب را چو فتد باد نخوت اندر سر\r\nکلاه داریش اندر سر شراب رود\r\nحجاب راه تویی حافظ از میان برخیز\r\nخوشا کسی که در این راه بی حجاب رود\r\n', 'هر تیری که می اندازی به سنگ می خورد و نمی توانی به مراد دلت دست پیدا کنی. گاهی برای تفریح کارهایی می کنی که خیلی زود هم پشیمان می شوی. اصلا فکر نمی کردی که راه عشق اینقدر سخت باشد. از خدا بخواه تا راه راست را به تو نشان دهد تا بتوانی راحت تر به معشوق برسی. '),
(222, 'از سر کوی تو هر کو به ملالت برود\r\nنرود کارش و آخر به خجالت برود\r\nکاروانی که بود بدرقه اش حفظ خدا\r\nبه تجمل بنشیند به جلالت برود\r\nسالک از نور هدایت ببرد راه به دوست\r\nکه به جایی نرسد گر به ضلالت برود\r\nکام خود آخر عمر از می و معشوق بگیر\r\nحیف اوقات که یک سر به بطالت برود\r\nای دلیل دل گمگشته خدا را مددی\r\nکه غریب ار نبرد ره به دلالت ببرد\r\nحکم مستوری و مستی همه بر خاتم تست\r\nکس ندانست که آخر به چه حالت برود\r\nحافظ از چشمه حکمت به کف آور جامی\r\nبو که از لوح دلت نقش جهالت برود\r\n', 'از نادانی و جهالت خلاص می شوی. راهت را پیدا می کنی اما کمی سختی و ملامت می کشی. هر کسی تا سختی نکشد به راحتی نخواهند رسید. فردی کاردان کمکت می کند تا تو به کمال و جمال برسی. هیچ کس از آینده ی خود خبر ندارد ولی تو آینده ای روشن داری که به تمام نیات دل خود می رسی. '),
(223, 'هرگزم نقش تو از لوح دل و جان نرود\r\nهرگز از یاد من آن سرو خرامان نرود\r\nاز دماغ من سرگشته خیال دهنت\r\nبه جفای فلک و غصه دوران نرود\r\nدر ازل بست دلم با سر زلفت پیوند\r\nتا ابد سر نکشد و از سر پیمان نرود\r\nهر چه جز بار غمت بر دل مسکین من است\r\nبرود از دل من و از دل من آن نرود\r\nآن چنان مهر توام در دل و جان جای گرفت\r\nکه اگر سر برود از دل و از جان نرود\r\nگر رود از پی خوبان دل من معذور است\r\nدرد دارد چه کند کز پی درمان نرود\r\nهر که خواهد که چو حافظ نشود سرگردان\r\nدل به خوبان ندهد و از پی ایشان نرود\r\n', 'یاد یار لحظه ای از تو جدا نمی شود به خاطر جفایی که دیده ای مدام غصه می خوری پیمان را با تو شکسته اند و بار غم روی دلت سنگینی می کند. برای اینکه از این بار غم رهایی پیدا کنی و از سرگردانی نجات یابی فکر نکن. با خدایت به راز و نیاز بپرداز تا دلت دوباره شاد شود. '),
(224, 'خوشا دلی که مدام از پی نظر نرود\r\nبه هر درش که بخوانند بی خبر نرود\r\nطمع در آن لب شیرین نکردنم اولی\r\nولی چگونه مگس از پی شکر نرود\r\nسواد دیده غمدیده ام به اشک مشوی\r\nکه نقش خال توام هرگز از نظر نرود\r\nز من چو باد صبا بوی خود دریغ مدار\r\nچرا که بی سر زلف توام به سر نرود\r\nدلا مباش چنین هرزه گرد و هرجایی\r\nکه هیچ کار ز پیشت بدین هنر نرود\r\nمکن به چشم حقارت نگاه در من مست\r\nکه آبروی شریعت بدین قدر نرود\r\nمن گدا هوس سروقامتی دارم\r\nکه دست در کمرش جز به سیم و زر نرود\r\nتو کز مکارم اخلاق عالمی دگری\r\nوفای عهد من از خاطرت به درنرود\r\nسیاه نامه تر از خود کسی نمی بینم\r\nچگونه چون قلمم دود دل به سر نرود\r\nبه تاج هدهدم از ره مبر که باز سفید\r\nچو باشه در پی هر صید مختصر نرود\r\nبیار باده و اول به دست حافظ ده\r\nبه شرط آن که ز مجلس سخن به درنرود\r\n', 'همگان به تو غبطه می خورند و این به خاطر این است که در هیچ کاری طمع نمی کنی و برای مال دنیا حریص نیستی. دل بزرگی داری و برای همین خدا تو را از مال دنیا بی نیاز می سازد. عهدی را که بسته ای فراموش نکن. تاج دانایی بر سرت می نهند فقط مواظب شیطان باش تا صید او نشوی. '),
(225, 'ساقی حدیث سرو و گل و لاله می رود\r\nوین بحث با ثلاثه غساله می رود\r\nمی ده که نوعروس چمن حد حسن یافت\r\nکار این زمان ز صنعت دلاله می رود\r\nشکرشکن شوند همه طوطیان هند\r\nزین قند پارسی که به بنگاله می رود\r\nطی مکان ببین و زمان در سلوک شعر\r\nکاین طفل یک شبه ره یک ساله می رود\r\nآن چشم جادوانه عابدفریب بین\r\nکش کاروان سحر ز دنباله می رود\r\nاز ره مرو به عشوه دنیا که این عجوز\r\nمکاره می نشیند و محتاله می رود\r\nباد بهار می وزد از گلستان شاه\r\nو از ژاله باده در قدح لاله می رود\r\nحافظ ز شوق مجلس سلطان غیاث دین\r\nغافل مشو که کار تو از ناله می رود\r\n', 'خوشحال باش که به مراد دل می رسی و کار و صنعت تو بالا می گیرد و زبانزد همه ی مردم می شوی. تو ره صد ساله را می خواهی یک شبه طی بکنی. مواظب آدم های شیطان صفت باش که می خواهند خرابت کنند. نعمت بسیاری به سویت روانه است. عمری دراز می کنی که سرشار از شادی و خوشی است. '),
(226, 'ترسم که اشک در غم ما پرده در شود\r\nوین راز سر به مهر به عالم سمر شود\r\nگویند سنگ لعل شود در مقام صبر\r\nآری شود ولیک به خون جگر شود\r\nخواهم شدن به میکده گریان و دادخواه\r\nکز دست غم خلاص من آن جا مگر شود\r\nاز هر کرانه تیر دعا کرده ام روان\r\nباشد کز آن میانه یکی کارگر شود\r\nای جان حدیث ما بر دلدار بازگو\r\nلیکن چنان مگو که صبا را خبر شود\r\nاز کیمیای مهر تو زر گشت روی من\r\nآری به یمن لطف شما خاک زر شود\r\nدر تنگنای حیرتم از نخوت رقیب\r\nیا رب مباد آن که گدا معتبر شود\r\nبس نکته غیر حسن بباید که تا کسی\r\nمقبول طبع مردم صاحب نظر شود\r\nاین سرکشی که کنگره کاخ وصل راست\r\nسرها بر آستانه او خاک در شود\r\nحافظ چو نافه سر زلفش به دست توست\r\nدم درکش ار نه باد صبا را خبر شود\r\n', 'صبر داشته باش هر چند که دلت خون می شود راز خود را به کسی نگو خداوند داد تو را از ستمگران می گیرد. کاری از دستت برنمی آید، فقط دعا کن شاید بالاخره مورد قبول خداوند واقع شود. نکند اگر به حاجت رسیدی و معتبر شدی خدا را فراموش کنی بلکه باید بیشتر رو به خدا آوری. '),
(227, 'گر چه بر واعظ شهر این سخن آسان نشود\r\nتا ریا ورزد و سالوس مسلمان نشود\r\nرندی آموز و کرم کن که نه چندان هنر است\r\nحیوانی که ننوشد می و انسان نشود\r\nگوهر پاک بباید که شود قابل فیض\r\nور نه هر سنگ و گلی لولو و مرجان نشود\r\nاسم اعظم بکند کار خود ای دل خوش باش\r\nکه به تلبیس و حیل دیو مسلمان نشود\r\nعشق می ورزم و امید که این فن شریف\r\nچون هنرهای دگر موجب حرمان نشود\r\nدوش می گفت که فردا بدهم کام دلت\r\nسببی ساز خدایا که پشیمان نشود\r\nحسن خلقی ز خدا می طلبم خوی تو را\r\nتا دگر خاطر ما از تو پریشان نشود\r\nذره را تا نبود همت عالی حافظ\r\nطالب چشمه خورشید درخشان نشود\r\n', 'دورنگ نباش و با هر کشی طرح دوستی ریختی رنگ عوض نکن که اینکار هنر نیست. فکر نکن که خیلی زرنگ هستی. برای اینکه اسمت همه جا برده شود دست به هر کاری نزن. عشق ورزیدن و محبت کردن هنر است اگر به حیله های خود ادامه دهی پشیمان می شوی. همت کن و راهت را عوض کن. '),
(228, 'گر من از باغ تو یک میوه بچینم چه شود\r\nپیش پایی به چراغ تو ببینم چه شود\r\nیا رب اندر کنف سایه آن سرو بلند\r\nگر من سوخته یک دم بنشینم چه شود\r\nآخر ای خاتم جمشید همایون آثار\r\nگر فتد عکس تو بر نقش نگینم چه شود\r\nواعظ شهر چو مهر ملک و شحنه گزید\r\nمن اگر مهر نگاری بگزینم چه شود\r\nعقلم از خانه به دررفت و گر می این است\r\nدیدم از پیش که در خانه دینم چه شود\r\nصرف شد عمر گران مایه به معشوقه و می\r\nتا از آنم چه به پیش آید از اینم چه شود\r\nخواجه دانست که من عاشقم و هیچ نگفت\r\nحافظ ار نیز بداند که چنینم چه شود\r\n', 'با خیالبافی و حسرت کشیدن کاری از پیش نمی بری و به هیچ مقام و منزلتی هم نمی رسی. این طوری به جای عقل، جهالت به سراغت می آید و ایمانت هم کم می شود. عمر عزیز خود را به بطالت صرف کرده ای بدون اینکه به نتیجه ای برسی. به واقعیات بپرداز و رویاها را فراموش کن. '),
(229, 'بخت از دهان دوست نشانم نمی دهد\r\nدولت خبر ز راز نهانم نمی دهد\r\nاز بهر بوسه ای ز لبش جان همی دهم\r\nاینم همی ستاند و آنم نمی دهد\r\nمردم در این فراق و در آن پرده راه نیست\r\nیا هست و پرده دار نشانم نمی دهد\r\nزلفش کشید باد صبا چرخ سفله بین\r\nکان جا مجال بادوزانم نمی دهد\r\nچندان که بر کنار چو پرگار می شدم\r\nدوران چو نقطه ره به میانم نمی دهد\r\nشکر به صبر دست دهد عاقبت ولی\r\nبدعهدی زمانه زمانم نمی دهد\r\nگفتم روم به خواب و ببینم جمال دوست\r\nحافظ ز آه و ناله امانم نمی دهد\r\n', 'صبر کرده ای، تحمل کرده ای، عبادت کرده ای ولی نشانه ای از رسیدن به مراد نمی بینی. فکر می کنی دیگر برای جبران ضرر نداری و مدام دور خودت می چرخی بدون اینکه پیشرفتی داشته باشی، ولی عاقبت اجر صبر خود را می بینی و بد عهدی زمانه را فراموش می کنی. '),
(230, 'اگر به باده مشکین دلم کشد شاید\r\nکه بوی خیر ز زهد ریا نمی آید\r\nجهانیان همه گر منع من کنند از عشق\r\nمن آن کنم که خداوندگار فرماید\r\nطمع ز فیض کرامت مبر که خلق کریم\r\nگنه ببخشد و بر عاشقان ببخشاید\r\nمقیم حلقه ذکر است دل بدان امید\r\nکه حلقه ای ز سر زلف یار بگشاید\r\nتو را که حسن خداداده هست و حجله بخت\r\nچه حاجت است که مشاطه ات بیاراید\r\nچمن خوش است و هوا دلکش است و می بی غش\r\nکنون بجز دل خوش هیچ در نمی باید\r\nجمیله ایست عروس جهان ولی هش دار\r\nکه این مخدره در عقد کس نمی آید\r\nبه لابه گفتمش ای ماه رخ چه باشد اگر\r\nبه یک شکر ز تو دلخسته ای بیاساید\r\nبه خنده گفت که حافظ خدای را مپسند\r\nکه بوسه تو رخ ماه را بیالاید\r\n', 'اگر از دستور خدا عتاب کنی و به بندگان خدا ریا کنی همه کس و همه چیز از تو دور می شوند. طمع به مال مردم نکن که اگر مردم تو را ببخشند خدا تو را نمی بخشد پس با خدا باش. حلقه ی در خانه ی خدا را بگیر تا خدا تو را به حلقه ی یار برساند و از بلاها رهایی یابی و دل خسته ات آرام گیرد. '),
(231, 'گفتم غم تو دارم گفتا غمت سر آید\r\nگفتم که ماه من شو گفتا اگر برآید\r\nگفتم ز مهرورزان رسم وفا بیاموز\r\nگفتا ز خوبرویان این کار کمتر آید\r\nگفتم که بر خیالت راه نظر ببندم\r\nگفتا که شب رو است او از راه دیگر آید\r\nگفتم که بوی زلفت گمراه عالمم کرد\r\nگفتا اگر بدانی هم اوت رهبر آید\r\nگفتم خوشا هوایی کز باد صبح خیزد\r\nگفتا خنک نسیمی کز کوی دلبر آید\r\nگفتم که نوش لعلت ما را به آرزو کشت\r\nگفتا تو بندگی کن کو بنده پرور آید\r\nگفتم دل رحیمت کی عزم صلح دارد\r\nگفتا مگوی با کس تا وقت آن درآید\r\nگفتم زمان عشرت دیدی که چون سر آمد\r\nگفتا خموش حافظ کاین غصه هم سر آید\r\n', 'دوران غم دلت کوتاه است اما از همین غم تجربه ای به دست آورده ای که اساس خوشبختی ات می باشد. برای رهایی از غم بسیار بی تابی می کنی. تو بندگی خدا را بکن خداوند هم حاجت تو را می رساند و پروردگار بر همه چیز و همه ی دل ها آگاه و واقف است. '),
(232, 'بر سر آنم که گر ز دست برآید\r\nدست به کاری زنم که غصه سر آید\r\nخلوت دل نیست جای صحبت اضداد\r\nدیو چو بیرون رود فرشته درآید\r\nصحبت حکام ظلمت شب یلداست\r\nنور ز خورشید جوی بو که برآید\r\nبر در ارباب بی مروت دنیا\r\nچند نشینی که خواجه کی به درآید\r\nترک گدایی مکن که گنج بیابی\r\nاز نظر ره روی که در گذر آید\r\nصالح و طالح متاع خویش نمودند\r\nتا که قبول افتد و که در نظر آید\r\nبلبل عاشق تو عمر خواه که آخر\r\nباغ شود سبز و شاخ گل به بر آید\r\nغفلت حافظ در این سراچه عجب نیست\r\nهر که به میخانه رفت بی خبر آید\r\n', 'تصمیم خود را عملی کن و هر کاری از دستت بر می آید انجام بده. غصه های تو به پایان رسیده است. با تلاش و اراده ی خویش شیطان را در وجودت کشته ای، حال وقت برپا خاستن است. از خدا جدا نشو تا همیشه نتیجه ی کار بر وفق مرادت باشد. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(233, 'دست از طلب ندارم تا کام من برآید\r\nیا تن رسد به جانان یا جان ز تن برآید\r\nبگشای تربتم را بعد از وفات و بنگر\r\nکز آتش درونم دود از کفن برآید\r\nبنمای رخ که خلقی واله شوند و حیران\r\nبگشای لب که فریاد از مرد و زن برآید\r\nجان بر لب است و حسرت در دل که از لبانش\r\nنگرفته هیچ کامی جان از بدن برآید\r\nاز حسرت دهانش آمد به تنگ جانم\r\nخود کام تنگدستان کی زان دهن برآید\r\nگویند ذکر خیرش در خیل عشقبازان\r\nهر جا که نام حافظ در انجمن برآید\r\n', 'بسیار انسان سخت کوش و پشتکاری هستید و تا نتیجه ی کار حاصل نشود دست از عمل برنمی دارید. نتایج کارتان همیشه ماندنی است و بقیه نیز از آن استفاده می کنند. شما تا آخرین لحظات زندگی به تمام آرزوهایتان خواهید رسید. به فقرا کمک کنید. نام شما و ذکر خیرتان همه جا بر سر زبانهاست. '),
(234, 'چو آفتاب می از مشرق پیاله برآید\r\nز باغ عارض ساقی هزار لاله برآید\r\nنسیم در سر گل بشکند کلاله سنبل\r\nچو از میان چمن بوی آن کلاله برآید\r\nحکایت شب هجران نه آن حکایت حالیست\r\nکه شمه ای ز بیانش به صد رساله برآید\r\nز گرد خوان نگون فلک طمع نتوان داشت\r\nکه بی ملالت صد غصه یک نواله برآید\r\nبه سعی خود نتوان برد پی به گوهر مقصود\r\nخیال باشد کاین کار بی حواله برآید\r\nگرت چو نوح نبی صبر هست در غم طوفان\r\nبلا بگردد و کام هزارساله برآید\r\nنسیم زلف تو چون بگذرد به تربت حافظ\r\nز خاک کالبدش صد هزار لاله برآید\r\n', 'توقع خیلی زیادی از روزگار داری و دلت می خواهد بدون دردسر به همه چیز برسی. این خیالی بیش نیست بدون سعی و کوشش کار به جایی نخواهی برد. پس مثل حضرت نوح خودت آینده ات را بساز تا بلا و فقر رهایی پیدا کنی. '),
(235, 'زهی خجسته زمانی که یار بازآید\r\nبه کام غمزدگان غمگسار بازآید\r\nبه پیش خیل خیالش کشیدم ابلق چشم\r\nبدان امید که آن شهسوار بازآید\r\nاگر نه در خم چوگان او رود سر من\r\nز سر نگویم و سر خود چه کار بازآید\r\nمقیم بر سر راهش نشسته ام چون گرد\r\nبدان هوس که بدین رهگذار بازآید\r\nدلی که با سر زلفین او قراری داد\r\nگمان مبر که بدان دل قرار بازآید\r\nچه جورها که کشیدند بلبلان از دی\r\nبه بوی آن که دگر نوبهار بازآید\r\nز نقش بند قضا هست امید آن حافظ\r\nکه همچو سرو به دستم نگار بازآید\r\n', 'خوشحال باش. روزی فرخنده که منتظرش بودی رسیده است و این به خاطر صبر و امیدواری خود توست. دلت آرام می گیرد. ستم هایی که به تو کرده اند فراموش کن که تو به مرادت رسیده ای. این کار قضا و جزئی از سرنوشت تو بود. '),
(236, 'اگر آن طایر قدسی ز درم بازآید\r\nعمر بگذشته به پیرانه سرم بازآید\r\nدارم امید بر این اشک چو باران که دگر\r\nبرق دولت که برفت از نظرم بازآید\r\nآن که تاج سر من خاک کف پایش بود\r\nاز خدا می طلبم تا به سرم بازآید\r\nخواهم اندر عقبش رفت به یاران عزیز\r\nشخصم ار بازنیاید خبرم بازآید\r\nگر نثار قدم یار گرامی نکنم\r\nگوهر جان به چه کار دگرم بازآید\r\nکوس نودولتی از بام سعادت بزنم\r\nگر ببینم که مه نوسفرم بازآید\r\nمانعش غلغل چنگ است و شکرخواب صبوح\r\nور نه گر بشنود آه سحرم بازآید\r\nآرزومند رخ شاه چو ماهم حافظ\r\nهمتی تا به سلامت ز درم بازآید\r\n', 'فکر می کنی روزگار روی بد خود را به تو نشان می دهد و کسی دیگر به تو اعتماد ندارد و احترام نمی گذارد. می خواهی باز هم دولت از دست رفته را بازگردانی. حاضری برای رسیدن به دولت جان خود را هم فدا کنی. راه سخت است اما با همت تو همه ی کارها آسان می شود. '),
(237, 'نفس برآمد و کام از تو بر نمی آید\r\nفغان که بخت من از خواب در نمی آید\r\nصبا به چشم من انداخت خاکی از کویش\r\nکه آب زندگیم در نظر نمی آید\r\nقد بلند تو را تا به بر نمی گیرم\r\nدرخت کام و مرادم به بر نمی آید\r\nمگر به روی دلارای یار ما ور نی\r\nبه هیچ وجه دگر کار بر نمی آید\r\nمقیم زلف تو شد دل که خوش سوادی دید\r\nوز آن غریب بلاکش خبر نمی آید\r\nز شست صدق گشادم هزار تیر دعا\r\nولی چه سود یکی کارگر نمی آید\r\nبسم حکایت دل هست با نسیم سحر\r\nولی به بخت من امشب سحر نمی آید\r\nدر این خیال به سر شد زمان عمر و هنوز\r\nبلای زلف سیاهت به سر نمی آید\r\nز بس که شد دل حافظ رمیده از همه کس\r\nکنون ز حلقه زلفت به در نمی آید\r\n', 'بسیار بی قرار و بی تاب شده ای و همه چیز را از دست رفته می بینی . رسیدن به کام و مراد را محال می دانی. دعا هم می کنی ولی نتیجه نگرفته ای مطمئن باش آنقدر عمر می کنی که به تمام آرزوهایت برسی. دل از خدا جدا نکن و امیدوار باش که در آینده ای نزدیک به مراد خود خواهی رسید. '),
(238, 'جهان بر ابروی عید از هلال وسمه کشید\r\nهلال عید در ابروی یار باید دید\r\nشکسته گشت چو پشت هلال قامت من\r\nکمان ابروی یارم چو وسمه بازکشید\r\nمگر نسیم خطت صبح در چمن بگذشت\r\nکه گل به بوی تو بر تن چو صبح جامه درید\r\nنبود چنگ و رباب و نبید و عود که بود\r\nگل وجود من آغشته گلاب و نبید\r\nبیا که با تو بگویم غم ملالت دل\r\nچرا که بی تو ندارم مجال گفت و شنید\r\nبهای وصل تو گر جان بود خریدارم\r\nکه جنس خوب مبصر به هر چه دید خرید\r\nچو ماه روی تو در شام زلف می دیدم\r\nشبم به روی تو روشن چو روز می گردید\r\nبه لب رسید مرا جان و برنیامد کام\r\nبه سر رسید امید و طلب به سر نرسید\r\nز شوق روی تو حافظ نوشت حرفی چند\r\nبخوان ز نظمش و در گوش کن چو مروارید\r\n', 'یارتان حاضر به هر گونه فداکاری برای شما می باشد. هر ملامتی را به خاطر شما تحمل می کند و برای رسیدن به تو بهای سنگینی پرداخت کرده است. بدون شما زندگی برایش چون شب است. تو هم طلب او را برآورده ساز تا خوبی های او را جبران کرده باشی. '),
(239, 'رسید مژده که آمد بهار و سبزه دمید\r\nوظیفه گر برسد مصرفش گل است و نبید\r\nصفیر مرغ برآمد بط شراب کجاست\r\nفغان فتاد به بلبل نقاب گل که کشید\r\nز میوه های بهشتی چه ذوق دریابد\r\nهر آن که سیب زنخدان شاهدی نگزید\r\nمکن ز غصه شکایت که در طریق طلب\r\nبه راحتی نرسید آن که زحمتی نکشید\r\nز روی ساقی مه وش گلی بچین امروز\r\nکه گرد عارض بستان خط بنفشه دمید\r\nچنان کرشمه ساقی دلم ز دست ببرد\r\nکه با کسی دگرم نیست برگ گفت و شنید\r\nمن این مرقع رنگین چو گل بخواهم سوخت\r\nکه پیر باده فروشش به جرعه ای نخرید\r\nبهار می گذرد دادگسترا دریاب\r\nکه رفت موسم و حافظ هنوز می نچشید\r\n', 'شکایت و غصه را کنار بگذار. بدون زحمت و تلاش به جایی نمی رسی. به وظیفه ات عمل کن در این صورت است که مژده ی رسیدن به مراد را به تو می دهند. امروز سعی و تلاش کن فردا به بوستان آرزویت می رسی. پس وقت را تلف نکن که عمر مثل باد می گذرد. '),
(240, 'ابر آذاری برآمد باد نوروزی وزید\r\nوجه می می خواهم و مطرب که می گوید رسید\r\nشاهدان در جلوه و من شرمسار کیسه ام\r\nبار عشق و مفلسی صعب است می باید کشید\r\nقحط جود است آبروی خود نمی باید فروخت\r\nباده و گل از بهای خرقه می باید خرید\r\nگوییا خواهد گشود از دولتم کاری که دوش\r\nمن همی کردم دعا و صبح صادق می دمید\r\nبا لبی و صد هزاران خنده آمد گل به باغ\r\nاز کریمی گوییا در گوشه ای بویی شنید\r\nدامنی گر چاک شد در عالم رندی چه باک\r\nجامه ای در نیک نامی نیز می باید درید\r\nاین لطایف کز لب لعل تو من گفتم که گفت\r\nوین تطاول کز سر زلف تو من دیدم که دید\r\nعدل سلطان گر نپرسد حال مظلومان عشق\r\nگوشه گیران را ز آسایش طمع باید برید\r\nتیر عاشق کش ندانم بر دل حافظ که زد\r\nاین قدر دانم که از شعر ترش خون می چکید\r\n', 'از فقر و نداری خود شرمنده نباش، خوبی تو سرمایه ی دولت تو است. ابرهای نعمت و رحمت به سویت می آیند و نجات پیدا می کنی و کارت بالا می گیرد. از چیزی نترس زیرا کسی که راه درست می رود ترس به دل راه نمی دهد. خداوند عادل و روزی رسان است. تو را هم فراموش نکرده و به تو هم کرم می کند. '),
(241, 'معاشران ز حریف شبانه یاد آرید\r\nحقوق بندگی مخلصانه یاد آرید\r\nبه وقت سرخوشی از آه و ناله عشاق\r\nبه صوت و نغمه چنگ و چغانه یاد آرید\r\nچو لطف باده کند جلوه در رخ ساقی\r\nز عاشقان به سرود و ترانه یاد آرید\r\nچو در میان مراد آورید دست امید\r\nز عهد صحبت ما در میانه یاد آرید\r\nسمند دولت اگر چند سرکشیده رود\r\nز همرهان به سر تازیانه یاد آرید\r\nنمی خورید زمانی غم وفاداران\r\nز بی وفایی دور زمانه یاد آرید\r\nبه وجه مرحمت ای ساکنان صدر جلال\r\nز روی حافظ و این آستانه یاد آرید\r\n', 'شما فراموش نشده اید. باز هم کسانی هستند که قدرتان را بدانند و جواب زحمتتان را بدهند. شما کسی هستید که در عین خوشی و رسیدن به مراد دیگران را هم در شادی و خوشی خود سهیم کرده اید، اما دیگران در حق تان کم لطفی کرده اند ولی باز هم مرحمت خدا نصیب شما شده و خواهد سد. '),
(242, 'بیا که رایت منصور پادشاه رسید\r\nنوید فتح و بشارت به مهر و ماه رسید\r\nجمال بخت ز روی ظفر نقاب انداخت\r\nکمال عدل به فریاد دادخواه رسید\r\nسپهر دور خوش اکنون کند که ماه آمد\r\nجهان به کام دل اکنون رسد که شاه رسید\r\nز قاطعان طریق این زمان شوند ایمن\r\nقوافل دل و دانش که مرد راه رسید\r\nعزیز مصر به رغم برادران غیور\r\nز قعر چاه برآمد به اوج ماه رسید\r\nکجاست صوفی دجال فعل ملحدشکل\r\nبگو بسوز که مهدی دین پناه رسید\r\nصبا بگو که چه ها بر سرم در این غم عشق\r\nز آتش دل سوزان و دود آه رسید\r\nز شوق روی تو شاها بدین اسیر فراق\r\nهمان رسید کز آتش به برگ کاه رسید\r\nمرو به خواب که حافظ به بارگاه قبول\r\nز ورد نیم شب و درس صبحگاه رسید\r\n', 'شما پیروز می شوید و خبر بسیار خوبی نزدیک است که شما را به حد کمال می رساند و حق خودتان را می گیرید، به شرط اینکه در کارتان قاطع باشید و غفلت نکنید. به حضرت مهدی(ع) توکل کنید. مواظب اطرافیان باشید. خوابتان نبرد زیرا کسانی در کمینتان نشسته اند تا شما را به انحراف بکشانند. '),
(243, 'بوی خوش تو هر که ز باد صبا شنید\r\nاز یار آشنا سخن آشنا شنید\r\nای شاه حسن چشم به حال گدا فکن\r\nکاین گوش بس حکایت شاه و گدا شنید\r\nخوش می کنم به باده مشکین مشام جان\r\nکز دلق پوش صومعه بوی ریا شنید\r\nسر خدا که عارف سالک به کس نگفت\r\nدر حیرتم که باده فروش از کجا شنید\r\nیا رب کجاست محرم رازی که یک زمان\r\nدل شرح آن دهد که چه گفت و چه ها شنید\r\nاینش سزا نبود دل حق گزار من\r\nکز غمگسار خود سخن ناسزا شنید\r\nمحروم اگر شدم ز سر کوی او چه شد\r\nاز گلشن زمانه که بوی وفا شنید\r\nساقی بیا که عشق ندا می کند بلند\r\nکان کس که گفت قصه ما هم ز ما شنید\r\nما باده زیر خرقه نه امروز می خوریم\r\nصد بار پیر میکده این ماجرا شنید\r\nما می به بانگ چنگ نه امروز می کشیم\r\nبس دور شد که گنبد چرخ این صدا شنید\r\nپند حکیم محض صواب است و عین خیر\r\nفرخنده آن کسی که به سمع رضا شنید\r\nحافظ وظیفه تو دعا گفتن است و بس\r\nدربند آن مباش که نشنید یا شنید\r\n', 'تو که سرچشمه ی همه ی خوبی ها و حسن ها هستی نگاهی هم به زیردستان خود بکن. دنبال یک محرم راز بگرد تا بتوانی درد دل کنی. از رسیدن به کسی محروم شده ای اما مرور زمان و وفاداریت باعث رسیدن به مرادت می شود. پند پیران را گوش کن. کار ثواب انجام بده تا همیشه دعای خیر بدرقه ی راهت باشد. '),
(244, 'معاشران گره از زلف یار باز کنید\r\nشبی خوش است بدین قصه اش دراز کنید\r\nحضور خلوت انس است و دوستان جمعند\r\nو ان یکاد بخوانید و در فراز کنید\r\nرباب و چنگ به بانگ بلند می گویند\r\nکه گوش هوش به پیغام اهل راز کنید\r\nبه جان دوست که غم پرده بر شما ندرد\r\nگر اعتماد بر الطاف کارساز کنید\r\nمیان عاشق و معشوق فرق بسیار است\r\nچو یار ناز نماید شما نیاز کنید\r\nنخست موعظه پیر صحبت این حرف است\r\nکه از مصاحب ناجنس احتراز کنید\r\nهر آن کسی که در این حلقه نیست زنده به عشق\r\nبر او نمرده به فتوای من نماز کنید\r\nوگر طلب کند انعامی از شما حافظ\r\nحوالتش به لب یار دلنواز کنید\r\n', 'روزهای خوشی را سپری می کنید بابت این روزگار خوش بیشتر خدا را عبادت کنید. هر چه بیشتر با خدا باشید زودتر از غم رهایی پیدا می کنید. خداوند شما را بسیار دوست دارد و می خواهد که او را زیاد عبادت کنید. از صحبت با نامحرمان پرهیز کنید. امیدوار باشید که به حاجات خودتان می رسید. '),
(245, 'الا ای طوطی گویای اسرار\r\nمبادا خالیت شکر ز منقار\r\nسرت سبز و دلت خوش باد جاوید\r\nکه خوش نقشی نمودی از خط یار\r\nسخن سربسته گفتی با حریفان\r\nخدا را زین معما پرده بردار\r\nبه روی ما زن از ساغر گلابی\r\nکه خواب آلوده ایم ای بخت بیدار\r\nچه ره بود این که زد در پرده مطرب\r\nکه می رقصند با هم مست و هشیار\r\nاز آن افیون که ساقی در می افکند\r\nحریفان را نه سر ماند نه دستار\r\nسکندر را نمی بخشند آبی\r\nبه زور و زر میسر نیست این کار\r\nبیا و حال اهل درد بشنو\r\nبه لفظ اندک و معنی بسیار\r\nبت چینی عدوی دین و دل هاست\r\nخداوندا دل و دینم نگه دار\r\nبه مستوران مگو اسرار مستی\r\nحدیث جان مگو با نقش دیوار\r\nبه یمن دولت منصور شاهی\r\nعلم شد حافظ اندر نظم اشعار\r\nخداوندی به جای بندگان کرد\r\nخداوندا ز آفاتش نگه دار\r\n', 'شانس فقط یک بار در خانه ی شما را می زند پس چرا آن را پس می زنید و ندانسته به بازی می گیرید. فکر می کنید با زور می توانید همه چیز را به دست آورید ولی اینطور نیست. دل و دبینتان را به خدا بسپارید تا به یمن خوبی تان دولت و مال و سلامت به شما عطا فرماید. '),
(246, 'عید است و آخر گل و یاران در انتظار\r\nساقی به روی شاه ببین ماه و می بیار\r\nدل برگرفته بودم از ایام گل ولی\r\nکاری بکرد همت پاکان روزه دار\r\nدل در جهان مبند و به مستی سوال کن\r\nاز فیض جام و قصه جمشید کامگار\r\nجز نقد جان به دست ندارم شراب کو\r\nکان نیز بر کرشمه ساقی کنم نثار\r\nخوش دولتیست خرم و خوش خسروی کریم\r\nیا رب ز چشم زخم زمانش نگاه دار\r\nمی خور به شعر بنده که زیبی دگر دهد\r\nجام مرصع تو بدین در شاهوار\r\nگر فوت شد سحور چه نقصان صبوح هست\r\nاز می کنند روزه گشا طالبان یار\r\nزان جا که پرده پوشی عفو کریم توست\r\nبر قلب ما ببخش که نقدیست کم عیار\r\nترسم که روز حشر عنان بر عنان رود\r\nتسبیح شیخ و خرقه رند شرابخوار\r\nحافظ چو رفت روزه و گل نیز می رود\r\nناچار باده نوش که از دست رفت کار\r\n', 'از همه جا و همه کس ناامید شده ای، حتی همت بلندت نیز کارساز نشد. حسودان و رقیبان باعث عقب افتادن کارهای تو شده اند ولی خداوند حقشان را می پردازد و از گناهانت می گذرد و آنچه را که می خواهی به تو می بخشد. '),
(247, 'صبا ز منزل جانان گذر دریغ مدار\r\nوز او به عاشق بی دل خبر دریغ مدار\r\nبه شکر آن که شکفتی به کام بخت ای گل\r\nنسیم وصل ز مرغ سحر دریغ مدار\r\nحریف عشق تو بودم چو ماه نو بودی\r\nکنون که ماه تمامی نظر دریغ مدار\r\nجهان و هر چه در او هست سهل و مختصر است\r\nز اهل معرفت این مختصر دریغ مدار\r\nکنون که چشمه قند است لعل نوشینت\r\nسخن بگوی و ز طوطی شکر دریغ مدار\r\nمکارم تو به آفاق می برد شاعر\r\nاز او وظیفه و زاد سفر دریغ مدار\r\nچو ذکر خیر طلب می کنی سخن این است\r\nکه در بهای سخن سیم و زر دریغ مدار\r\nغبار غم برود حال خوش شود حافظ\r\nتو آب دیده از این رهگذر دریغ مدار\r\n', 'در انتظار خبری هستید که به زودی به شما خواهد رسید و به شکرانه ی این خبر عبادت خدا را به جای آورید. هر کار سختی در دنیا با اراده ی قوی تان بسیار آسان می گردد. ثروت زیادی به دست می آورید که در راه خدا انفاق هم می کنید و این باعث زایل شدن غم از دلتان می شود. '),
(248, 'ای صبا نکهتی از کوی فلانی به من آر\r\nزار و بیمار غمم راحت جانی به من آر\r\nقلب بی حاصل ما را بزن اکسیر مراد\r\nیعنی از خاک در دوست نشانی به من آر\r\nدر کمینگاه نظر با دل خویشم جنگ است\r\nز ابرو و غمزه او تیر و کمانی به من آر\r\nدر غریبی و فراق و غم دل پیر شدم\r\nساغر می ز کف تازه جوانی به من آر\r\nمنکران را هم از این می دو سه ساغر بچشان\r\nوگر ایشان نستانند روانی به من آر\r\nساقیا عشرت امروز به فردا مفکن\r\nیا ز دیوان قضا خط امانی به من آر\r\nدلم از دست بشد دوش چو حافظ می گفت\r\nکای صبا نکهتی از کوی فلانی به من آر\r\n', 'بر سر دو راهی قرار گرفته ای و نمی دانی کدام راه را باید انتخاب کنی. تنها هستی و دل گرفته ای داری. احساس پیری می کنی زیرا همه چیز را از خود قطع کرده ای حتی خوشی را ولی بدان آنچه سرنوشت تو باشد پیش خواهد آمد. امیدوار باش. غم را کنار بگذار تا به وصال یار برسی. '),
(249, 'ای صبا نکهتی از خاک ره یار بیار\r\nببر اندوه دل و مژده دلدار بیار\r\nنکته ای روح فزا از دهن دوست بگو\r\nنامه ای خوش خبر از عالم اسرار بیار\r\nتا معطر کنم از لطف نسیم تو مشام\r\nشمه ای از نفحات نفس یار بیار\r\nبه وفای تو که خاک ره آن یار عزیز\r\nبی غباری که پدید آید از اغیار بیار\r\nگردی از رهگذر دوست به کوری رقیب\r\nبهر آسایش این دیده خونبار بیار\r\nخامی و ساده دلی شیوه جانبازان نیست\r\nخبری از بر آن دلبر عیار بیار\r\nشکر آن را که تو در عشرتی ای مرغ چمن\r\nبه اسیران قفس مژده گلزار بیار\r\nکام جان تلخ شد از صبر که کردم بی دوست\r\nعشوه ای زان لب شیرین شکربار بیار\r\nروزگاریست که دل چهره مقصود ندید\r\nساقیا آن قدح آینه کردار بیار\r\nدلق حافظ به چه ارزد به می اش رنگین کن\r\nوان گهش مست و خراب از سر بازار بیار\r\n', 'اندوه دل و روح شما با خبر خوشی که در راه است برطرف می شود و همه چیز برایتان رنگ و بوی دیگری می گیرد. به کوری چشم حسودان شما به تمام آرزوهایتان خواهید رسید و از قفس غم رهایی پیدا می کنید، چشمتان به جمال یار روشن می شود و کامتان شیرین خواهند شد و این خبر به همگان خواهد رسید. '),
(250, 'روی بنمای و وجود خودم از یاد ببر\r\nخرمن سوختگان را همه گو باد ببر\r\nما چو دادیم دل و دیده به طوفان بلا\r\nگو بیا سیل غم و خانه ز بنیاد ببر\r\nزلف چون عنبر خامش که ببوید هیهات\r\nای دل خام طمع این سخن از یاد ببر\r\nسینه گو شعله آتشکده فارس بکش\r\nدیده گو آب رخ دجله بغداد ببر\r\nدولت پیر مغان باد که باقی سهل است\r\nدیگری گو برو و نام من از یاد ببر\r\nسعی نابرده در این راه به جایی نرسی\r\nمزد اگر می طلبی طاعت استاد ببر\r\nروز مرگم نفسی وعده دیدار بده\r\nوان گهم تا به لحد فارغ و آزاد ببر\r\nدوش می گفت به مژگان درازت بکشم\r\nیا رب از خاطرش اندیشه بیداد ببر\r\nحافظ اندیشه کن از نازکی خاطر یار\r\nبرو از درگهش این ناله و فریاد ببر\r\n', 'برای رهایی از غم خودتان را به هر آب و آتشی می زنید، جگر سوخته ای دارید که حتی با آب یک رودخانه هم خنک نمی شود. حاضر هستید همه ی مشقات را به جان بخرید به شرط اینکه به دولت و بخت خودتان برسید. به قول معروف تا کار نکنید مزدی هم دریافت نمی کنید پس تلاش کنید تا زمانی که جان در بدن دارید. '),
(251, 'شب وصل است و طی شد نامه هجر\r\nسلام فیه حتی مطلع الفجر\r\nدلا در عاشقی ثابت قدم باش\r\nکه در این ره نباشد کار بی اجر\r\nمن از رندی نخواهم کرد توبه\r\nو لو آذیتنی بالهجر و الحجر\r\nبرآی ای صبح روشن دل خدا را\r\nکه بس تاریک می بینم شب هجر\r\nدلم رفت و ندیدم روی دلدار\r\nفغان از این تطاول آه از این زجر\r\nوفا خواهی جفاکش باش حافظ\r\nفان الربح و الخسران فی التجر\r\n', 'زمان هجران به سر آمده و صبح صادق دمیده است. شب سیاه به پایان رسیده و حالا نوبت تو می باشد به عهدی که بسته ای عمل کنی. نگرانی که دوباره همه چیز به هم بریزد و از وصال یار بی بهره بمانی. دیگر طاقت هجران نداری. '),
(252, 'گر بود عمر به میخانه رسم بار دگر\r\nبجز از خدمت رندان نکنم کار دگر\r\nخرم آن روز که با دیده گریان بروم\r\nتا زنم آب در میکده یک بار دگر\r\nمعرفت نیست در این قوم خدا را سببی\r\nتا برم گوهر خود را به خریدار دگر\r\nیار اگر رفت و حق صحبت دیرین نشناخت\r\nحاش لله که روم من ز پی یار دگر\r\nگر مساعد شودم دایره چرخ کبود\r\nهم به دست آورمش باز به پرگار دگر\r\nعافیت می طلبد خاطرم ار بگذارند\r\nغمزه شوخش و آن طره ی طرار دگر\r\nراز سربسته ما بین که به دستان گفتند\r\nهر زمان با دف و نی بر سر بازار دگر\r\nهر دم از درد بنالم که فلک هر ساعت\r\nکندم قصد دل ریش به آزار دگر\r\nبازگویم نه در این واقعه حافظ تنهاست\r\nغرقه گشتند در این بادیه بسیار دگر\r\n', 'فرصت های از دست رفته را می خواهی جبران کنی. سبب ساز است و تو موفق خواهی شد. فکر می کنی کسی قدر تو را نمی داند و کارهایت خریداری ندارد اما ناراحت نباش، سرنوشت تو تغییر پیدا می کند، با همت خود می توانی این مسئله را ثابت کنی. '),
(253, 'ای خرم از فروغ رخت لاله زار عمر\r\nبازآ که ریخت بی گل رویت بهار عمر\r\nاز دیده گر سرشک چو باران چکد رواست\r\nکاندر غمت چو برق بشد روزگار عمر\r\nاین یک دو دم که مهلت دیدار ممکن است\r\nدریاب کار ما که نه پیداست کار عمر\r\nتا کی می صبوح و شکرخواب بامداد\r\nهشیار گرد هان که گذشت اختیار عمر\r\nدی در گذار بود و نظر سوی ما نکرد\r\nبیچاره دل که هیچ ندید از گذار عمر\r\nاندیشه از محیط فنا نیست هر که را\r\nبر نقطه دهان تو باشد مدار عمر\r\nدر هر طرف که ز خیل حوادث کمین گهیست\r\nزان رو عنان گسسته دواند سوار عمر\r\nبی عمر زنده ام من و این بس عجب مدار\r\nروز فراق را که نهد در شمار عمر\r\nحافظ سخن بگوی که بر صفحه جهان\r\nاین نقش ماند از قلمت یادگار عمر\r\n', 'توبه ی تو مورد قبول حق واقع می شود، کینه ها را کنار بگذار و گذشت داشته باش هر چند که عمرت می گذرد و تو تا به حال به فکر خویش نبوده ای و همیشه با حوادث جنگیده ای اما از این به بعد تصمیم گرفته ای که فرد دیگری شوی. پس کاری کن که نامت را در آینده به خوبی یاد کنند. '),
(254, 'دیگر ز شاخ سرو سهی بلبل صبور\r\nگلبانگ زد که چشم بد از روی گل به دور\r\nای گلبشکر آن که تویی پادشاه حسن\r\nبا بلبلان بی دل شیدا مکن غرور\r\nاز دست غیبت تو شکایت نمی کنم\r\nتا نیست غیبتی نبود لذت حضور\r\nگر دیگران به عیش و طرب خرمند و شاد\r\nما را غم نگار بود مایه سرور\r\nزاهد اگر به حور و قصور است امیدوار\r\nما را شرابخانه قصور است و یار حور\r\nمی خور به بانگ چنگ و مخور غصه ور کسی\r\nگوید تو را که باده مخور گو هوالغفور\r\nحافظ شکایت از غم هجران چه می کنی\r\nدر هجر وصل باشد و در ظلمت است نور\r\n', 'خوشحال باش و غصه و غم را کنار بگذار. تا دوری نکشی به وصال نمی رسی و تا رنج نبینی به خوشی هم نمی رسی، پی دست از عبادت نکش. امیدوار باش که از صبر تو گل حاجت روئیده است. شکایت نکن که در عین ظلمت نور بر تو می تابد. '),
(255, 'یوسف گمگشته بازآید به کنعان غم مخور\r\nکلبه احزان شود روزی گلستان غم مخور\r\nای دل غمدیده حالت به شود دل بد مکن\r\nوین سر شوریده بازآید به سامان غم مخور\r\nگر بهار عمر باشد باز بر تخت چمن\r\nچتر گل در سر کشی ای مرغ خوشخوان غم مخور\r\nدور گردون گر دو روزی بر مراد ما نرفت\r\nدایما یک سان نباشد حال دوران غم مخور\r\nهان مشو نومید چون واقف نه ای از سر غیب\r\nباشد اندر پرده بازی های پنهان غم مخور\r\nای دل ار سیل فنا بنیاد هستی برکند\r\nچون تو را نوح است کشتیبان ز طوفان غم مخور\r\nدر بیابان گر به شوق کعبه خواهی زد قدم\r\nسرزنش ها گر کند خار مغیلان غم مخور\r\nگر چه منزل بس خطرناک است و مقصد بس بعید\r\nهیچ راهی نیست کان را نیست پایان غم مخور\r\nحال ما در فرقت جانان و ابرام رقیب\r\nجمله می داند خدای حال گردان غم مخور\r\nحافظا در کنج فقر و خلوت شب های تار\r\nتا بود وردت دعا و درس قرآن غم مخور\r\n', 'گم کرده ی خود را پیدا می کنی و روزگار پر درد و رنجت تبدیل به خوشبختی می شود. بد به دل خود راه نده. اگر امروز به مراد نرسی فردا حتما نشانه هایی از پایان غم خواهی دید چون با خدا هستی همیشه از بلایا نجات پیدا می کنی. باز هم در خلوت قرآن بخوان تا برای رسیدن به مقصد راهت را گم نکنی. '),
(256, 'نصیحتی کنمت بشنو و بهانه مگیر\r\nهر آن چه ناصح مشفق بگویدت بپذیر\r\nز وصل روی جوانان تمتعی بردار\r\nکه در کمینگه عمر است مکر عالم پیر\r\nنعیم هر دو جهان پیش عاشقان بجوی\r\nکه این متاع قلیل است و آن عطای کثیر\r\nمعاشری خوش و رودی بساز می خواهم\r\nکه درد خویش بگویم به ناله بم و زیر\r\nبر آن سرم که ننوشم می و گنه نکنم\r\nاگر موافق تدبیر من شود تقدیر\r\nچو قسمت ازلی بی حضور ما کردند\r\nگر اندکی نه به وفق رضاست خرده مگیر\r\nچو لاله در قدحم ریز ساقیا می و مشک\r\nکه نقش خال نگارم نمی رود ز ضمیر\r\nبیار ساغر در خوشاب ای ساقی\r\nحسود گو کرم آصفی ببین و بمیر\r\nبه عزم توبه نهادم قدح ز کف صد بار\r\nولی کرشمه ساقی نمی کند تقصیر\r\nمی دوساله و محبوب چارده ساله\r\nهمین بس است مرا صحبت صغیر و کبیر\r\nدل رمیده ما را که پیش می گیرد\r\nخبر دهید به مجنون خسته از زنجیر\r\nحدیث توبه در این بزمگه مگو حافظ\r\nکه ساقیان کمان ابرویت زنند به تیر\r\n', 'فکر می کنی همیشه جوان خواهی ماند و هیچ وقت پیر نمی شوی. به نصیحت های پیران گوش کن دوستانت تو را فقط برای خوشی می خواهند به دردهایت گوش نمی دهند. توقع خود را پایین بیاور. اشتباهات خود را گردن کسان دیگر مینداز، توبه کن تا به آرزوهای مادی خود برسی. '),
(257, 'روی بنما و مرا گو که ز جان دل برگیر\r\nپیش شمع آتش پروا نه به جان گو درگیر\r\nدر لب تشنه ما بین و مدار آب دریغ\r\nبر سر کشته خویش آی و ز خاکش برگیر\r\nترک درویش مگیر ار نبود سیم و زرش\r\nدر غمت سیم شمار اشک و رخش را زر گیر\r\nچنگ بنواز و بساز ار نبود عود چه باک\r\nآتشم عشق و دلم عود و تنم مجمر گیر\r\nدر سماع آی و ز سر خرقه برانداز و برقص\r\nور نه با گوشه رو و خرقه ما در سر گیر\r\nصوف برکش ز سر و باده صافی درکش\r\nسیم درباز و به زر سیمبری در بر گیر\r\nدوست گو یار شو و هر دو جهان دشمن باش\r\nبخت گو پشت مکن روی زمین لشکر گیر\r\nمیل رفتن مکن ای دوست دمی با ما باش\r\nبر لب جوی طرب جوی و به کف ساغر گیر\r\nرفته گیر از برم وز آتش و آب دل و چشم\r\nگونه ام زرد و لبم خشک و کنارم تر گیر\r\nحافظ آراسته کن بزم و بگو واعظ را\r\nکه ببین مجلسم و ترک سر منبر گیر\r\n', 'شما ارزش هر کس را به پول و مقام آن کس می دانید و از کسانی که پول و مقام ندارند دوری می کنید. بدی ها را از خود دور کنید و سنگینی گناه را دور بریزید تا چون آب صاف و زلال شوید. پول و مقام تان در بازوی شماست همت کنید و با تلاش به پول هم می رسید. با حرف زدن کار درست نمی شود. مرد عمل باشید. '),
(258, 'هزار شکر که دیدم به کام خویشت باز\r\nز روی صدق و صفا گشته با دلم دمساز\r\nروندگان طریقت ره بلا سپرند\r\nرفیق عشق چه غم دارد از نشیب و فراز\r\nغم حبیب نهان به ز گفت و گوی رقیب\r\nکه نیست سینه ارباب کینه محرم راز\r\nاگر چه حسن تو از عشق غیر مستغنیست\r\nمن آن نیم که از این عشقبازی آیم باز\r\nچه گویمت که ز سوز درون چه می بینم\r\nز اشک پرس حکایت که من نیم غماز\r\nچه فتنه بود که مشاطه قضا انگیخت\r\nکه کرد نرگس مستش سیه به سرمه ناز\r\nبدین سپاس که مجلس منور است به دوست\r\nگرت چو شمع جفایی رسد بسوز و بساز\r\nغرض کرشمه حسن است ور نه حاجت نیست\r\nجمال دولت محمود را به زلف ایاز\r\nغزل سرایی ناهید صرفه ای نبرد\r\nدر آن مقام که حافظ برآورد آواز\r\n', 'در هر حال خدا را شاکر باش حتی اگر بلا بر تو نازل شود و از فراز و نشیب زندگی خسته شده باشی. با خودت کنار بیا با اشک و غم کاری از پیش نخواهی برد. سرنوشت حوادث زیادی برایت رقم زده. مرد عمل باش تا با همه ی آنها مقابله کنی و به دولت برسی. '),
(259, 'منم که دیده به دیدار دوست کردم باز\r\nچه شکر گویمت ای کارساز بنده نواز\r\nنیازمند بلا گو رخ از غبار مشوی\r\nکه کیمیای مراد است خاک کوی نیاز\r\nز مشکلات طریقت عنان متاب ای دل\r\nکه مرد راه نیندیشد از نشیب و فراز\r\nطهارت ار نه به خون جگر کند عاشق\r\nبه قول مفتی عشقش درست نیست نماز\r\nدر این مقام مجازی بجز پیاله مگیر\r\nدر این سراچه بازیچه غیر عشق مباز\r\nبه نیم بوسه دعایی بخر ز اهل دلی\r\nکه کید دشمنت از جان و جسم دارد باز\r\nفکند زمزمه عشق در حجاز و عراق\r\nنوای بانگ غزل های حافظ از شیراز\r\n', 'از مشکلات فرار نکنید، مرد کار و عمل باشید. دلتان را با خون دل خوردن التیام ندهید بلکه با خواند نماز دلتان را آرام کنید. از نظر ایمان در مقام بالایی هستید به زیارت عتبات عالیات مشرف می شوید. به دوستان خودتان اطمینان کنید. '),
(260, 'ای سرو ناز حسن که خوش می روی به ناز\r\nعشاق را به ناز تو هر لحظه صد نیاز\r\nفرخنده باد طلعت خوبت که در ازل\r\nببریده اند بر قد سروت قبای ناز\r\nآن را که بوی عنبر زلف تو آرزوست\r\nچون عود گو بر آتش سودا بسوز و ساز\r\nپروانه را ز شمع بود سوز دل ولی\r\nبی شمع عارض تو دلم را بود گداز\r\nصوفی که بی تو توبه ز می کرده بود دوش\r\nبشکست عهد چون در میخانه دید باز\r\nاز طعنه رقیب نگردد عیار من\r\nچون زر اگر برند مرا در دهان گاز\r\nدل کز طواف کعبه کویت وقوف یافت\r\nاز شوق آن حریم ندارد سر حجاز\r\nهر دم به خون دیده چه حاجت وضو چو نیست\r\nبی طاق ابروی تو نماز مرا جواز\r\nچون باده باز بر سر خم رفت کف زنان\r\nحافظ که دوش از لب ساقی شنید راز\r\n', 'کسانی هستند که محتاج تو می باشند و دست نیاز به سویت دراز کرده اند دست آنها را رد نکن آنها مثل پروانه به دورت می چرخند و از وجودت استفاده می کنند. عهدی را که بسته ای نشکن زیرا باعث می شود که رقیبان تو را از میدان بدر کنند. خداوند تو را یاری می کند. رازت را به کسی نگو. '),
(261, 'درآ که در دل خسته توان درآید باز\r\nبیا که در تن مرده روان درآید باز\r\nبیا که فرقت تو چشم من چنان در بست\r\nکه فتح باب وصالت مگر گشاید باز\r\nغمی که چون سپه زنگ ملک دل بگرفت\r\nز خیل شادی روم رخت زداید باز\r\nبه پیش آینه دل هر آن چه می دارم\r\nبجز خیال جمالت نمی نماید باز\r\nبدان مثل که شب آبستن است روز از تو\r\nستاره می شمرم تا که شب چه زاید باز\r\nبیا که بلبل مطبوع خاطر حافظ\r\nبه بوی گلبن وصل تو می سراید باز\r\n', 'دل تنگ هستی و روح و روان خسته ای داری مه همگی درمان می شود درهای وصال باز می شوند و شادی به رویت لبخند می زند خیالات را کنار بگذار و منتظر اتفاقات باش که همگی از خوبی و شادی می باشند. '),
(262, 'حال خونین دلان که گوید باز\r\nو از فلک خون خم که جوید باز\r\nشرمش از چشم می پرستان باد\r\nنرگس مست اگر بروید باز\r\nجز فلاطون خم نشین شراب\r\nسر حکمت به ما که گوید باز\r\nهر که چون لاله کاسه گردان شد\r\nزین جفا رخ به خون بشوید باز\r\nنگشاید دلم چو غنچه اگر\r\nساغری از لبش نبوید باز\r\nبس که در پرده چنگ گفت سخن\r\nببرش موی تا نموید باز\r\nگرد بیت الحرام خم حافظ\r\nگر نمیرد به سر بپوید باز\r\n', 'شما جواب بدی را با خوبی بدهید تا دیگران ار رفتار خودشان شرمنده شوند. ناراحت و غمگین نباشید و به خداوند توکل کنید. خودتان یاعلی بگویید و مطمئن باشید موفق می شوید. انشاالله به زیارت کعبه ی مکرمه مشرف می شوید. '),
(263, 'بیا و کشتی ما در شط شراب انداز\r\nخروش و ولوله در جان شیخ و شاب انداز\r\nمرا به کشتی باده درافکن ای ساقی\r\nکه گفته اند نکویی کن و در آب انداز\r\nز کوی میکده برگشته ام ز راه خطا\r\nمرا دگر ز کرم با ره صواب انداز\r\nبیار زان می گلرنگ مشک بو جامی\r\nشرار رشک و حسد در دل گلاب انداز\r\nاگر چه مست و خرابم تو نیز لطفی کن\r\nنظر بر این دل سرگشته خراب انداز\r\nبه نیم شب اگرت آفتاب می باید\r\nز روی دختر گلچهر رز نقاب انداز\r\nمهل که روز وفاتم به خاک بسپارند\r\nمرا به میکده بر در خم شراب انداز\r\nز جور چرخ چو حافظ به جان رسید دلت\r\nبه سوی دیو محن ناوک شهاب انداز\r\n', 'تحولی بزرگ در زندگیت به وجود بیاور و دلت را به دریا بزن. کار خوب انجام بده که نتیجه اش را خیلی زود ببینی. حسادت را از خود دور کن تا چیزهای زیبا بیشتر در نظرت بیاید با خدا باش تا شیطان از تو دور شود و آرزوهای محالت محقق گردد.  '),
(264, 'خیز و در کاسه زر آب طربناک انداز\r\nپیشتر زان که شود کاسه سر خاک انداز\r\nعاقبت منزل ما وادی خاموشان است\r\nحالیا غلغله در گنبد افلاک انداز\r\nچشم آلوده نظر از رخ جانان دور است\r\nبر رخ او نظر از آینه پاک انداز\r\nبه سر سبز تو ای سرو که گر خاک شوم\r\nناز از سر بنه و سایه بر این خاک انداز\r\nدل ما را که ز مار سر زلف تو بخست\r\nاز لب خود به شفاخانه تریاک انداز\r\nملک این مزرعه دانی که ثباتی ندهد\r\nآتشی از جگر جام در املاک انداز\r\nغسل در اشک زدم کاهل طریقت گویند\r\nپاک شو اول و پس دیده بر آن پاک انداز\r\nیا رب آن زاهد خودبین که بجز عیب ندید\r\nدود آهیش در آیینه ادراک انداز\r\nچون گل از نکهت او جامه قبا کن حافظ\r\nوین قبا در ره آن قامت چالاک انداز\r\n', 'بسیار انسان ناامیدی هستید و به جای کردار و عمل خوب صحبت از غم و غصه می کنید برای موفقیت اول اشک و آه را از خود دور کرده و برای چیزی که ماندنی نیست غصه نخورید. دلتان وقتی شفا پیدا می کند که سر بر آستان حق بنهید. بدبینی را دور بریزید و دلتان را مثل آینه صیقل بدهید. '),
(265, 'برنیامد از تمنای لبت کامم هنوز\r\nبر امید جام لعلت دردی آشامم هنوز\r\nروز اول رفت دینم در سر زلفین تو\r\nتا چه خواهد شد در این سودا سرانجامم هنوز\r\nساقیا یک جرعه ای زان آب آتشگون که من\r\nدر میان پختگان عشق او خامم هنوز\r\nاز خطا گفتم شبی زلف تو را مشک ختن\r\nمی زند هر لحظه تیغی مو بر اندامم هنوز\r\nپرتو روی تو تا در خلوتم دید آفتاب\r\nمی رود چون سایه هر دم بر در و بامم هنوز\r\nنام من رفته ست روزی بر لب جانان به سهو\r\nاهل دل را بوی جان می آید از نامم هنوز\r\nدر ازل داده ست ما را ساقی لعل لبت\r\nجرعه جامی که من مدهوش آن جامم هنوز\r\nای که گفتی جان بده تا باشدت آرام جان\r\nجان به غم هایش سپردم نیست آرامم هنوز\r\nدر قلم آورد حافظ قصه لعل لبش\r\nآب حیوان می رود هر دم ز اقلامم هنوز\r\n', 'به عاقبت کاری که می کنی بیاندیش. عده ای می خواهند از شما سوء استفاده کنند. تو هنوز تجربه کافی نداری برای همین ممکن است راه خطا بروی و ظاهربین هستی و همه چیز را بر مبنای ظاهر آن می سنجی. از خدا بخواه تا سلامتی به تو بدهد و تو هم عبادت خدا را به جا آوری. '),
(266, 'دلم رمیده لولی وشیست شورانگیز\r\nدروغ وعده و قتال وضع و رنگ آمیز\r\nفدای پیرهن چاک ماه رویان باد\r\nهزار جامه تقوا و خرقه پرهیز\r\nخیال خال تو با خود به خاک خواهم برد\r\nکه تا ز خال تو خاکم شود عبیرآمیز\r\nفرشته عشق نداند که چیست ای ساقی\r\nبخواه جام و گلابی به خاک آدم ریز\r\nپیاله بر کفنم بند تا سحرگه حشر\r\nبه می ز دل ببرم هول روز رستاخیز\r\nفقیر و خسته به درگاهت آمدم رحمی\r\nکه جز ولای توام نیست هیچ دست آویز\r\nبیا که هاتف میخانه دوش با من گفت\r\nکه در مقام رضا باش و از قضا مگریز\r\nمیان عاشق و معشوق هیچ حائل نیست\r\nتو خود حجاب خودی حافظ از میان برخیز\r\n', 'رضایت به رضای خدا باشد. آنچه سرنوشت برایت رقم زده همان می شود. تنها کاری که از دستت برمیاید این است که به درگاه خداوند دعا کنی تا شاید سرنوشت به میل خودت برگردد. همیشه امید داشته باش و به وعده های دروغین و رنگارنگ دیگران توجهی نکن. '),
(267, 'ای صبا گر بگذری بر ساحل رود ارس\r\nبوسه زن بر خاک آن وادی و مشکین کن نفس\r\nمنزل سلمی که بادش هر دم از ما صد سلام\r\nپرصدای ساربانان بینی و بانگ جرس\r\nمحمل جانان ببوس آن گه به زاری عرضه دار\r\nکز فراقت سوختم ای مهربان فریاد رس\r\nمن که قول ناصحان را خواندمی قول رباب\r\nگوشمالی دیدم از هجران که اینم پند بس\r\nعشرت شبگیر کن می نوش کاندر راه عشق\r\nشب روان را آشنایی هاست با میر عسس\r\nعشقبازی کار بازی نیست ای دل سر بباز\r\nزان که گوی عشق نتوان زد به چوگان هوس\r\nدل به رغبت می سپارد جان به چشم مست یار\r\nگر چه هشیاران ندادند اختیار خود به کس\r\nطوطیان در شکرستان کامرانی می کنند\r\nو از تحسر دست بر سر می زند مسکین مگس\r\nنام حافظ گر برآید بر زبان کلک دوست\r\nاز جناب حضرت شاهم بس است این ملتمس\r\n', 'انتظار زیاد کشیده ای. نمی دانی که آیا او هم تو را دوست دارد یا نه آیا می خواهد تو را ببیند. اضطراب زیادی داری. غصه نخور، عاشقی جز عذاب و درد چیز دیگری ندارد ولی به خاطر عشقت زندگی می کنی. منتظر باش خبر بسیار خوبی به تو می دهند و تو مورد لطف کسی قرار می گیری. '),
(268, 'گلعذاری ز گلستان جهان ما را بس\r\nزین چمن سایه آن سرو روان ما را بس\r\nمن و همصحبتی اهل ریا دورم باد\r\nاز گرانان جهان رطل گران ما را بس\r\nقصر فردوس به پاداش عمل می بخشند\r\nما که رندیم و گدا دیر مغان ما را بس\r\nبنشین بر لب جوی و گذر عمر ببین\r\nکاین اشارت ز جهان گذران ما را بس\r\nنقد بازار جهان بنگر و آزار جهان\r\nگر شما را نه بس این سود و زیان ما را بس\r\nیار با ماست چه حاجت که زیادت طلبیم\r\nدولت صحبت آن مونس جان ما را بس\r\nاز در خویش خدا را به بهشتم مفرست\r\nکه سر کوی تو از کون و مکان ما را بس\r\nحافظ از مشرب قسمت گله ناانصافیست\r\nطبع چون آب و غزل های روان ما را بس\r\n', 'تو همه چیز داری بیش از این طمع نکن. یار و ثروت و مقام یکجا به سراغت آمده است، پس قدر این نعمات را بدان از دوستی که در حق تو ریا می کند پرهیز کن. با داشتن دوستی صادق به بهشت برین دست پیدا می کنی. بی انصافی است اگر گله و شکایتی داشته باشی. '),
(269, 'دلا رفیق سفر بخت نیکخواهت بس\r\nنسیم روضه شیراز پیک راهت بس\r\nدگر ز منزل جانان سفر مکن درویش\r\nکه سیر معنوی و کنج خانقاهت بس\r\nوگر کمین بگشاید غمی ز گوشه دل\r\nحریم درگه پیر مغان پناهت بس\r\nبه صدر مصطبه بنشین و ساغر می نوش\r\nکه این قدر ز جهان کسب مال و جاهت بس\r\nزیادتی مطلب کار بر خود آسان کن\r\nصراحی می لعل و بتی چو ماهت بس\r\nفلک به مردم نادان دهد زمام مراد\r\nتو اهل فضلی و دانش همین گناهت بس\r\nهوای مسکن مولوف و عهد یار قدیم\r\nز ره روان سفرکرده عذرخواهت بس\r\nبه منت دگران خو مکن که در دو جهان\r\nرضای ایزد و انعام پادشاهت بس\r\nبه هیچ ورد دگر نیست حاجت ای حافظ\r\nدعای نیم شب و درس صبحگاهت بس\r\n', 'برای بدست آوردن پول و مقام منت دیگران را نکش. اگر خدا بخواهد همه چیز به تو می دهد، بنابراین خدا را شکر کن. خود را با دیگران مقایسه نکن. اگر دیگران مال و مکنت دارند تو هم هنر داری پس دو رنگی نکن. همان باش که هستی. به حاجتت می رسی اگر با خدا و با ایمان باشی. '),
(270, 'درد عشقی کشیده ام که مپرس\r\nزهر هجری چشیده ام که مپرس\r\nگشته ام در جهان و آخر کار\r\nدلبری برگزیده ام که مپرس\r\nآن چنان در هوای خاک درش\r\nمی رود آب دیده ام که مپرس\r\nمن به گوش خود از دهانش دوش\r\nسخنانی شنیده ام که مپرس\r\nسوی من لب چه می گزی که مگوی\r\nلب لعلی گزیده ام که مپرس\r\nبی تو در کلبه گدایی خویش\r\nرنج هایی کشیده ام که مپرس\r\nهمچو حافظ غریب در ره عشق\r\nبه مقامی رسیده ام که مپرس\r\n', 'یارتان در همه حال با شماست. به او اطمینان کنید حرف های او از صدق و صفا می باشد. به خاطر شما مصائب و مشکلات را هر چند که بسیار سخت است تحمل می کند. او شما را به عرش اعلا می رساند و مقامتان را بالا می برد. '),
(271, 'دارم از زلف سیاهش گله چندان که مپرس\r\nکه چنان ز او شده ام بی سر و سامان که مپرس\r\nکس به امید وفا ترک دل و دین مکناد\r\nکه چنانم من از این کرده پشیمان که مپرس\r\nبه یکی جرعه که آزار کسش در پی نیست\r\nزحمتی می کشم از مردم نادان که مپرس\r\nزاهد از ما به سلامت بگذر کاین می لعل\r\nدل و دین می برد از دست بدان سان که مپرس\r\nگفت وگوهاست در این راه که جان بگدازد\r\nهر کسی عربده ای این که مبین آن که مپرس\r\nپارسایی و سلامت هوسم بود ولی\r\nشیوه ای می کند آن نرگس فتان که مپرس\r\nگفتم از گوی فلک صورت حالی پرسم\r\nگفت آن می کشم اندر خم چوگان که مپرس\r\nگفتمش زلف به خون که شکستی گفتا\r\nحافظ این قصه دراز است به قرآن که مپرس\r\n', 'همه تو را اذیت می کنند چه دوست و چه آشنا و هیچ نفعی به تو نمی رسانند. دوست به تو جفا می کند و آشنا دشمنی. چاره ای نیست تحمل کن و امیدوار باش سوختن و ساختن جزئی از وجودت است. '),
(272, 'بازآی و دل تنگ مرا مونس جان باش\r\nوین سوخته را محرم اسرار نهان باش\r\nزان باده که در میکده عشق فروشند\r\nما را دو سه ساغر بده و گو رمضان باش\r\nدر خرقه چو آتش زدی ای عارف سالک\r\nجهدی کن و سرحلقه رندان جهان باش\r\nدلدار که گفتا به توام دل نگران است\r\nگو می رسم اینک به سلامت نگران باش\r\nخون شد دلم از حسرت آن لعل روان بخش\r\nای درج محبت به همان مهر و نشان باش\r\nتا بر دلش از غصه غباری ننشیند\r\nای سیل سرشک از عقب نامه روان باش\r\nحافظ که هوس می کندش جام جهان بین\r\nگو در نظر آصف جمشید مکان باش\r\n', 'عجله کن تا از دیگران عقب نیفتی. با دل نگرانی و حرف نگرانی و حرف زدن به جایی نخواهی رسید. بسیار مواظب سلامتی خودت باش. فکر می کنی کس دیگری می خواهد مقصود و نظر تو را از چنگت بیرون بکشد ولی بدان در سرنوشت تو مقصود چیز دیگریست و نصیب کس دیگری نمی شود. '),
(273, 'اگر رفیق شفیقی درست پیمان باش\r\nحریف خانه و گرمابه و گلستان باش\r\nشکنج زلف پریشان به دست باد مده\r\nمگو که خاطر عشاق گو پریشان باش\r\nگرت هواست که با خضر همنشین باشی\r\nنهان ز چشم سکندر چو آب حیوان باش\r\nزبور عشق نوازی نه کار هر مرغیست\r\nبیا و نوگل این بلبل غزل خوان باش\r\nطریق خدمت و آیین بندگی کردن\r\nخدای را که رها کن به ما و سلطان باش\r\nدگر به صید حرم تیغ برمکش زنهار\r\nو از آن که با دل ما کرده ای پشیمان باش\r\nتو شمع انجمنی یک زبان و یک دل شو\r\nخیال و کوشش پروانه بین و خندان باش\r\nکمال دلبری و حسن در نظربازیست\r\nبه شیوه نظر از نادران دوران باش\r\nخموش حافظ و از جور یار ناله مکن\r\nتو را که گفت که در روی خوب حیران باش\r\n', 'از ستم و بلایی که بر سرت آورده اند شکایت می کنی و به فر انتقام هستی ولی همگی را به خدایشان واگذار کن. مهربان باش تا دلت آرام گیرد. به عهدی که بسته ای عمل کن خدا با توست. اگر می خواهی همیشه شاد باشی، دل و زبانت یکی باشد. عمرتان طولانی است. '),
(274, 'به دور لاله قدح گیر و بی ریا می باش\r\nبه بوی گل نفسی همدم صبا می باش\r\nنگویمت که همه ساله می پرستی کن\r\nسه ماه می خور و نه ماه پارسا می باش\r\nچو پیر سالک عشقت به می حواله کند\r\nبنوش و منتظر رحمت خدا می باش\r\nگرت هواست که چون جم به سر غیب رسی\r\nبیا و همدم جام جهان نما می باش\r\nچو غنچه گر چه فروبستگیست کار جهان\r\nتو همچو باد بهاری گره گشا می باش\r\nوفا مجوی ز کس ور سخن نمی شنوی\r\nبه هرزه طالب سیمرغ و کیمیا می باش\r\nمرید طاعت بیگانگان مشو حافظ\r\nولی معاشر رندان پارسا می باش\r\n', 'تو کار خیرت را انجام بده و از بنده‌ی خدا تمنای جواب نداشته باش خداوند در جای دیگر جواب کار خوبت را می دهد. رحمت خداوند شامل حالت شده و مالی خوب به تو می رسد. گره از کارت باز می شود. تو کار خودت را بکن دشمن شکست می خورد و دست به هر چه بزنی طلا می شود.  '),
(275, 'صوفی گلی بچین و مرقع به خار بخش\r\nوین زهد خشک را به می خوشگوار بخش\r\nطامات و شطح در ره آهنگ چنگ نه\r\nتسبیح و طیلسان به می و میگسار بخش\r\nزهد گران که شاهد و ساقی نمی خرند\r\nدر حلقه چمن به نسیم بهار بخش\r\nراهم شراب لعل زد ای میر عاشقان\r\nخون مرا به چاه زنخدان یار بخش\r\nیا رب به وقت گل گنه بنده عفو کن\r\nوین ماجرا به سرو لب جویبار بخش\r\nای آن که ره به مشرب مقصود برده ای\r\nزین بحر قطره ای به من خاکسار بخش\r\nشکرانه را که چشم تو روی بتان ندید\r\nما را به عفو و لطف خداوندگار بخش\r\nساقی چو شاه نوش کند باده صبوح\r\nگو جام زر به حافظ شب زنده دار بخش\r\n', 'گناهی که مرتکب شده اید به هیچ جایی درز نمی کند این راز را در دلتان حفظ کنید. اراده کرده اید که به مقصود برسید برای این کار اول توبه کنید و شکر خدا را به جای آورید که بار کج هیچ وقت به منزل نمی رسد. '),
(276, 'باغبان گر پنج روزی صحبت گل بایدش\r\nبر جفای خار هجران صبر بلبل بایدش\r\nای دل اندربند زلفش از پریشانی منال\r\nمرغ زیرک چون به دام افتد تحمل بایدش\r\nرند عالم سوز را با مصلحت بینی چه کار\r\nکار ملک است آن که تدبیر و تامل بایدش\r\nتکیه بر تقوا و دانش در طریقت کافریست\r\nراهرو گر صد هنر دارد توکل بایدش\r\nبا چنین زلف و رخش بادا نظربازی حرام\r\nهر که روی یاسمین و جعد سنبل بایدش\r\nنازها زان نرگس مستانه اش باید کشید\r\nاین دل شوریده تا آن جعد و کاکل بایدش\r\nساقیا در گردش ساغر تعلل تا به چند\r\nدور چون با عاشقان افتد تسلسل بایدش\r\nکیست حافظ تا ننوشد باده بی آواز رود\r\nعاشق مسکین چرا چندین تجمل بایدش\r\n', 'آنچه را که مصلحت است انجام بده فقط علم برای رسیدن به مقصود و حاجت کافی نیست ایمان قوی و تحمل سختی ها ی راه هم لازم هست. بنابراین دست از غرور بردار و تلاش کن. کرم داشته باش تا به مقصود برسی. '),
(277, 'فکر بلبل همه آن است که گل شد یارش\r\nگل در اندیشه که چون عشوه کند در کارش\r\nدلربایی همه آن نیست که عاشق بکشند\r\nخواجه آن است که باشد غم خدمتگارش\r\nجای آن است که خون موج زند در دل لعل\r\nزین تغابن که خزف می شکند بازارش\r\nبلبل از فیض گل آموخت سخن ور نه نبود\r\nاین همه قول و غزل تعبیه در منقارش\r\nای که در کوچه معشوقه ما می گذری\r\nبر حذر باش که سر می شکند دیوارش\r\nآن سفرکرده که صد قافله دل همره اوست\r\nهر کجا هست خدایا به سلامت دارش\r\nصحبت عافیتت گر چه خوش افتاد ای دل\r\nجانب عشق عزیز است فرومگذارش\r\nصوفی سرخوش از این دست که کج کرد کلاه\r\nبه دو جام دگر آشفته شود دستارش\r\nدل حافظ که به دیدار تو خوگر شده بود\r\nنازپرورد وصال است مجو آزارش\r\n', 'به دست آوردن پول و سود به هر طریق ممکن جایز نیست بلکه اصل آن است که حلال و حرام مشخص باشد. با حرف زدن به جایی نمی رسید بهتر است کمی به قول هایی که داده اید و به عهدی که بسته اید فکر کنید و عمل نمایید. مواظب راهی که می روید باشید. سفر کرده ی شما به سلامت بر می گردد. انشاالله. '),
(278, 'شراب تلخ می خواهم که مردافکن بود زورش\r\nکه تا یک دم بیاسایم ز دنیا و شر و شورش\r\nسماط دهر دون پرور ندارد شهد آسایش\r\nمذاق حرص و آز ای دل بشو از تلخ و از شورش\r\nبیاور می که نتوان شد ز مکر آسمان ایمن\r\nبه لعب زهره چنگی و مریخ سلحشورش\r\nکمند صید بهرامی بیفکن جام جم بردار\r\nکه من پیمودم این صحرا نه بهرام است و نه گورش\r\nبیا تا در می صافیت راز دهر بنمایم\r\nبه شرط آن که ننمایی به کج طبعان دل کورش\r\nنظر کردن به درویشان منافی بزرگی نیست\r\nسلیمان با چنان حشمت نظرها بود با مورش\r\nکمان ابروی جانان نمی پیچد سر از حافظ\r\nولیکن خنده می آید بدین بازوی بی زورش\r\n', 'از فرصتی که داری استفاده کن وگرنه کسی دیگر از این فرصت سود می برد. به زیردستان خود هم توجه کن. اگر در عین مقام و ثروت، به زیردستان محبت کردی مردی. دامی برایت گسترده اند. '),
(279, 'خوشا شیراز و وضع بی مثالش\r\nخداوندا نگه دار از زوالش\r\nز رکن آباد ما صد لوحش الله\r\nکه عمر خضر می بخشد زلالش\r\nمیان جعفرآباد و مصلا\r\nعبیرآمیز می آید شمالش\r\nبه شیراز آی و فیض روح قدسی\r\nبجوی از مردم صاحب کمالش\r\nکه نام قند مصری برد آن جا\r\nکه شیرینان ندادند انفعالش\r\nصبا زان لولی شنگول سرمست\r\nچه داری آگهی چون است حالش\r\nگر آن شیرین پسر خونم بریزد\r\nدلا چون شیر مادر کن حلالش\r\nمکن از خواب بیدارم خدا را\r\nکه دارم خلوتی خوش با خیالش\r\nچرا حافظ چو می ترسیدی از هجر\r\nنکردی شکر ایام وصالش\r\n', 'قدر چیزها و کسانی را که در کنارتان بوده اند ندانسته و حالا بسیار پشیمانید. آنقدر عمرتان طولانی هست که دوباره همه چیز را جبران کنید. از دوستان با عقل و درایت خودتان کمک بگیرید. مال حلال به دست بیاورید چون مال حرام زود از دستتان خارج می شود. '),
(280, 'چو برشکست صبا زلف عنبرافشانش\r\nبه هر شکسته که پیوست تازه شد جانش\r\nکجاست همنفسی تا به شرح عرضه دهم\r\nکه دل چه می کشد از روزگار هجرانش\r\nزمانه از ورق گل مثال روی تو بست\r\nولی ز شرم تو در غنچه کرد پنهانش\r\nتو خفته ای و نشد عشق را کرانه پدید\r\nتبارک الله از این ره که نیست پایانش\r\nجمال کعبه مگر عذر ره روان خواهد\r\nکه جان زنده دلان سوخت در بیابانش\r\nبدین شکسته بیت الحزن که می آرد\r\nنشان یوسف دل از چه زنخدانش\r\nبگیرم آن سر زلف و به دست خواجه دهم\r\nکه سوخت حافظ بی دل ز مکر و دستانش\r\n', 'به دنبال دوستی می گردید که بتوانید درد دل خودتان را به او بگویید. با خجالت و گوشه نشینی آینده ی خوبی نخواهید داشت. بهانه و عذرها را کنار بگذارید تا دلتان زنده و جوان شود. شما به نیتتان می رسید زیرا نشانه هایی از آن به نظرتان رسیده است. '),
(281, 'یا رب این نوگل خندان که سپردی به منش\r\nمی سپارم به تو از چشم حسود چمنش\r\nگر چه از کوی وفا گشت به صد مرحله دور\r\nدور باد آفت دور فلک از جان و تنش\r\nگر به سرمنزل سلمی رسی ای باد صبا\r\nچشم دارم که سلامی برسانی ز منش\r\nبه ادب نافه گشایی کن از آن زلف سیاه\r\nجای دل های عزیز است به هم برمزنش\r\nگو دلم حق وفا با خط و خالت دارد\r\nمحترم دار در آن طره عنبرشکنش\r\nدر مقامی که به یاد لب او می نوشند\r\nسفله آن مست که باشد خبر از خویشتنش\r\nعرض و مال از در میخانه نشاید اندوخت\r\nهر که این آب خورد رخت به دریا فکنش\r\nهر که ترسد ز ملال انده عشقش نه حلال\r\nسر ما و قدمش یا لب ما و دهنش\r\nشعر حافظ همه بیت الغزل معرفت است\r\nآفرین بر نفس دلکش و لطف سخنش\r\n', 'فکر می کنید رسیدن به حاجت محال است ولی به زودی خبری بسیار خوش به شما می دهند. بی قرار و بی تاب شده اید. به هر کس که می رسید می خواهید خبری از او بگیرید اما بدانید انتظار امروز تلخ و وصال فردا بسیار شیرین است. خودتان را به دست سرنوشت سپرده اید و مطمئن هستید هر چه پیش آید رضای حق در اوست. '),
(282, 'ببرد از من قرار و طاقت و هوش\r\nبت سنگین دل سیمین بناگوش\r\nنگاری چابکی شنگی کلهدار\r\nظریفی مه وشی ترکی قباپوش\r\nز تاب آتش سودای عشقش\r\nبه سان دیگ دایم می زنم جوش\r\nچو پیراهن شوم آسوده خاطر\r\nگرش همچون قبا گیرم در آغوش\r\nاگر پوسیده گردد استخوانم\r\nنگردد مهرت از جانم فراموش\r\nدل و دینم دل و دینم ببرده ست\r\nبر و دوشش بر و دوشش بر و دوش\r\nدوای تو دوای توست حافظ\r\nلب نوشش لب نوشش لب نوش\r\n', 'برای دیدن یار هوش و حواس خودتان را از دست داده اید اما مطمئن نیستید که او هم مثل شما هست یا نه و تمام فکر و اندیشه و قلب تان تسخیر او شده و دست از زندگی روزمره کشیده اید. عشق تان بسیار عمیق و پاک است، کمی صبر کنید خداوند داروی درد شما را می داند و اوست که درمانگر است. '),
(283, 'سحر ز هاتف غیبم رسید مژده به گوش\r\nکه دور شاه شجاع است می دلیر بنوش\r\nشد آن که اهل نظر بر کناره می رفتند\r\nهزار گونه سخن در دهان و لب خاموش\r\nبه صوت چنگ بگوییم آن حکایت ها\r\nکه از نهفتن آن دیگ سینه می زد جوش\r\nشراب خانگی ترس محتسب خورده\r\nبه روی یار بنوشیم و بانگ نوشانوش\r\nز کوی میکده دوشش به دوش می بردند\r\nامام شهر که سجاده می کشید به دوش\r\nدلا دلالت خیرت کنم به راه نجات\r\nمکن به فسق مباهات و زهد هم مفروش\r\nمحل نور تجلیست رای انور شاه\r\nچو قرب او طلبی در صفای نیت کوش\r\nبجز ثنای جلالش مساز ورد ضمیر\r\nکه هست گوش دلش محرم پیام سروش\r\nرموز مصلحت ملک خسروان دانند\r\nگدای گوشه نشینی تو حافظا مخروش\r\n', 'رازها و درد دلهایت را حفظ کن و سرپوشی روی آن بگذار. خداوند تو را از بندی که در آن گرفتار هستی نجات می دهد، به شرط آنکه برای رسیدن به ایمان و صفای دلت تلاش کنی. خودت صلاح کار را بهتر می دانی اگر از نتیجه اش باخبری پس یاعلی بگو که عده ای نیز به تو کمک می کنند. '),
(284, 'هاتفی از گوشه میخانه دوش\r\nگفت ببخشند گنه می بنوش\r\nلطف الهی بکند کار خویش\r\nمژده رحمت برساند سروش\r\nاین خرد خام به میخانه بر\r\nتا می لعل آوردش خون به جوش\r\nگر چه وصالش نه به کوشش دهند\r\nهر قدر ای دل که توانی بکوش\r\nلطف خدا بیشتر از جرم ماست\r\nنکته سربسته چه دانی خموش\r\nگوش من و حلقه گیسوی یار\r\nروی من و خاک در می فروش\r\nرندی حافظ نه گناهیست صعب\r\nبا کرم پادشه عیب پوش\r\nداور دین شاه شجاع آن که کرد\r\nروح قدس حلقه امرش به گوش\r\nای ملک العرش مرادش بده\r\nو از خطر چشم بدش دار گوش\r\n', 'لطف خداوند شامل حالت شده است. توبه کن تا مژده ی بسیار خوبی به تو بدهند. انتظار و دوری به سر رسیده و به این خاطر دعاهایت می باشد. به نذری که کرده ای عمل کن. در راه خدا انفاق کن تا همیشه از چشم زخم ایمن باشی. '),
(285, 'در عهد پادشاه خطابخش جرم پوش\r\nحافظ قرابه کش شد و مفتی پیاله نوش\r\nصوفی ز کنج صومعه با پای خم نشست\r\nتا دید محتسب که سبو می کشد به دوش\r\nاحوال شیخ و قاضی و شرب الیهودشان\r\nکردم سوال صبحدم از پیر می فروش\r\nگفتا نه گفتنیست سخن گر چه محرمی\r\nدرکش زبان و پرده نگه دار و می بنوش\r\nساقی بهار می رسد و وجه می نماند\r\nفکری بکن که خون دل آمد ز غم به جوش\r\nعشق است و مفلسی و جوانی و نوبهار\r\nعذرم پذیر و جرم به ذیل کرم بپوش\r\nتا چند همچو شمع زبان آوری کنی\r\nپروانه مراد رسید ای محب خموش\r\nای پادشاه صورت و معنی که مثل تو\r\nنادیده هیچ دیده و نشنیده هیچ گوش\r\nچندان بمان که خرقه ازرق کند قبول\r\nبخت جوانت از فلک پیر ژنده پوش\r\n', 'الان موقع حرف زدن نیست، کمی تامل کن. فکری به حال خودت بکن تا از این غم رهایی پیدا کنی. تا جوانی می توانی کاری انجام دهی. نگو فعلا جوانم و وقت برای توبه زیاد است. برای رسیدن به مراد باید رنج و سختی و انتظار بکشی و اگر عجله کنی همه چیز را بر باد می دهی. '),
(286, 'دوش با من گفت پنهان کاردانی تیزهوش\r\nو از شما پنهان نشاید کرد سر می فروش\r\nگفت آسان گیر بر خود کارها کز روی طبع\r\nسخت می گردد جهان بر مردمان سختکوش\r\nوان گهم درداد جامی کز فروغش بر فلک\r\nزهره در رقص آمد و بربط زنان می گفت نوش\r\nبا دل خونین لب خندان بیاور همچو جام\r\nنی گرت زخمی رسد آیی چو چنگ اندر خروش\r\nتا نگردی آشنا زین پرده رمزی نشنوی\r\nگوش نامحرم نباشد جای پیغام سروش\r\nگوش کن پند ای پسر و از بهر دنیا غم مخور\r\nگفتمت چون در حدیثی گر توانی داشت هوش\r\nدر حریم عشق نتوان زد دم از گفت و شنید\r\nزان که آن جا جمله اعضا چشم باید بود و گوش\r\nبر بساط نکته دانان خودفروشی شرط نیست\r\nیا سخن دانسته گو ای مرد عاقل یا خموش\r\nساقیا می ده که رندی های حافظ فهم کرد\r\nآصف صاحب قران جرم بخش عیب پوش\r\n', 'زندگی را هر طور که بگیری می گذرد پس سخت نگیر تا روزگارت به خوشی بگذرد. در عین ناراحتی و غم بخند تا مرهم روی زخم های دلت باشد. هر چه می کشی از آشناست به پند پیران گوش کن. تمام حرف هایی که می زنی بر علیه خودت استفاده می شود پس عاقل باش. حریم خودت را نگهدار تا از غم رهایی یابی. '),
(287, 'ای همه شکل تو مطبوع و همه جای تو خوش\r\nدلم از عشوه شیرین شکرخای تو خوش\r\nهمچو گلبرگ طری هست وجود تو لطیف\r\nهمچو سرو چمن خلد سراپای تو خوش\r\nشیوه و ناز تو شیرین خط و خال تو ملیح\r\nچشم و ابروی تو زیبا قد و بالای تو خوش\r\nهم گلستان خیالم ز تو پرنقش و نگار\r\nهم مشام دلم از زلف سمن سای تو خوش\r\nدر ره عشق که از سیل بلا نیست گذار\r\nکرده ام خاطر خود را به تمنای تو خوش\r\nشکر چشم تو چه گویم که بدان بیماری\r\nمی کند درد مرا از رخ زیبای تو خوش\r\nدر بیابان طلب گر چه ز هر سو خطریست\r\nمی رود حافظ بی دل به تولای تو خوش\r\n', 'تصمیم تان عجولانه است. فریب ظواهر را خورده اید و تمام فکر و ذکرتان را مشغول کرده است و همه ی کارهای مهم زندگی را فراموش کرده و به دنبال مقصود خود به راه افتاده اید. هر چند که کمی مردد هستید ولی حاضرید دست به سوی خطر دراز کنید. '),
(288, 'کنار آب و پای بید و طبع شعر و یاری خوش\r\nمعاشر دلبری شیرین و ساقی گلعذاری خوش\r\nالا ای دولتی طالع که قدر وقت می دانی\r\nگوارا بادت این عشرت که داری روزگاری خوش\r\nهر آن کس را که در خاطر ز عشق دلبری باریست\r\nسپندی گو بر آتش نه که دارد کار و باری خوش\r\nعروس طبع را زیور ز فکر بکر می بندم\r\nبود کز دست ایامم به دست افتد نگاری خوش\r\nشب صحبت غنیمت دان و داد خوشدلی بستان\r\nکه مهتابی دل افروز است و طرف لاله زاری خوش\r\nمی ای در کاسه چشم است ساقی را بنامیزد\r\nکه مستی می کند با عقل و می بخشد خماری خوش\r\nبه غفلت عمر شد حافظ بیا با ما به میخانه\r\nکه شنگولان خوش باشت بیاموزند کاری خوش\r\n', 'از خواب غفلت بیدار شو و این چند صباح عمر را غنیمت دان که روزگار به کامت می باشد. موفقیت و خوشبختی به رویت لبخند می زنند. خودت هم همتی بکن، همه چیز برای رسیدن به هدف و حاجت مهیاست. برخیز، انشاالله که موفق می شوی. '),
(289, 'مجمع خوبی و لطف است عذار چو مهش\r\nلیکنش مهر و وفا نیست خدایا بدهش\r\nدلبرم شاهد و طفل است و به بازی روزی\r\nبکشد زارم و در شرع نباشد گنهش\r\nمن همان به که از او نیک نگه دارم دل\r\nکه بد و نیک ندیده ست و ندارد نگهش\r\nبوی شیر از لب همچون شکرش می آید\r\nگر چه خون می چکد از شیوه چشم سیهش\r\nچارده ساله بتی چابک شیرین دارم\r\nکه به جان حلقه به گوش است مه چاردهش\r\nاز پی آن گل نورسته دل ما یا رب\r\nخود کجا شد که ندیدیم در این چند گهش\r\nیار دلدار من ار قلب بدین سان شکند\r\nببرد زود به جانداری خود پادشهش\r\nجان به شکرانه کنم صرف گر آن دانه در\r\nصدف سینه حافظ بود آرامگهش\r\n', 'تو که از خوبی و لطف و صفا همه چیز داری چرا محبت خویش را از دیگران دریغ می کنی. هنوز در حال و هوای بچگی سیر می کنی و با این طریق از زیر کارها شانه خالی می کنی. یادت باشد که با شکستن دل ها به جایی نخواهی رسید. اگر محبت و خوبی و لطف و صفا را یکجا داشته باشی پیش خلق مقامت بالا می رود. '),
(290, 'دلم رمیده شد و غافلم من درویش\r\nکه آن شکاری سرگشته را چه آمد پیش\r\nچو بید بر سر ایمان خویش می لرزم\r\nکه دل به دست کمان ابروییست کافرکیش\r\nخیال حوصله بحر می پزد هیهات\r\nچه هاست در سر این قطره محال اندیش\r\nبنازم آن مژه شوخ عافیت کش را\r\nکه موج می زندش آب نوش بر سر نیش\r\nز آستین طبیبان هزار خون بچکد\r\nگرم به تجربه دستی نهند بر دل ریش\r\nبه کوی میکده گریان و سرفکنده روم\r\nچرا که شرم همی آیدم ز حاصل خویش\r\nنه عمر خضر بماند نه ملک اسکندر\r\nنزاع بر سر دنیی دون مکن درویش\r\nبدان کمر نرسد دست هر گدا حافظ\r\nخزانه ای به کف آور ز گنج قارون بیش\r\n', 'بر سر دو راهی قرار گرفته ای. همه ی کارها را سخت می گیری و ناامیدی. فکر می کنی تا حالا زنده بودنت بیخود بوده و تلاش هایت هدر رفته و خود را گول زده ای ولی این را بدان که از هر جا شروع کنی با منفعت است. با تجربه ای که به دست آورده ای بهتر تصمیم می گیری و راهت را انتخاب می کنی. '),
(291, 'ما آزموده ایم در این شهر بخت خویش\r\nبیرون کشید باید از این ورطه رخت خویش\r\nاز بس که دست می گزم و آه می کشم\r\nآتش زدم چو گل به تن لخت لخت خویش\r\nدوشم ز بلبلی چه خوش آمد که می سرود\r\nگل گوش پهن کرده ز شاخ درخت خویش\r\nکای دل تو شاد باش که آن یار تندخو\r\nبسیار تندروی نشیند ز بخت خویش\r\nخواهی که سخت و سست جهان بر تو بگذرد\r\nبگذر ز عهد سست و سخن های سخت خویش\r\nوقت است کز فراق تو وز سوز اندرون\r\nآتش درافکنم به همه رخت و پخت خویش\r\nای حافظ ار مراد میسر شدی مدام\r\nجمشید نیز دور نماندی ز تخت خویش\r\n', 'هیچکدام از راه هایی را که انتخاب کرده ای به هدف نرسیده است. باز هم تلاش کن بالاخره روزگار روی خوش خود را به تو نشان می دهد و دلت شاد می شود. زندگی را هر طور که بگیری می گذرد ولی یادت باشد با کسانی که به عهد خود پای بند نیستند پیمان نبند. رسیدن به مقصود برای تو دور نیست و مقامت نیز بالا می رود. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(292, 'قسم به حشمت و جاه و جلال شاه شجاع\r\nکه نیست با کسم از بهر مال و جاه نزاع\r\nشراب خانگیم بس می مغانه بیار\r\nحریف باده رسید ای رفیق توبه وداع\r\nخدای را به می ام شست و شوی خرقه کنید\r\nکه من نمی شنوم بوی خیر از این اوضاع\r\nببین که رقص کنان می رود به ناله چنگ\r\nکسی که رخصه نفرمودی استماع سماع\r\nبه عاشقان نظری کن به شکر این نعمت\r\nکه من غلام مطیعم تو پادشاه مطاع\r\nبه فیض جرعه جام تو تشنه ایم ولی\r\nنمی کنیم دلیری نمی دهیم صداع\r\nجبین و چهره حافظ خدا جدا مکناد\r\nز خاک بارگه کبریای شاه شجاع\r\n', 'کاری از دست کسی برنمی آید فقط به خدا توکل کن. کاری کرده ای که نتیجه ای منفی دارد. فقط خداست که می تواند تو را از ورطه ی خطر نجات بخشد. دست به دعا بردار و مطیع درگاه حق باش به مرحمت خدا همه چیز روبراه می شود. '),
(293, 'بامدادان که ز خلوتگه کاخ ابداع\r\nشمع خاور فکند بر همه اطراف شعاع\r\nبرکشد آینه از جیب افق چرخ و در آن\r\nبنماید رخ گیتی به هزاران انواع\r\nدر زوایای طربخانه جمشید فلک\r\nارغنون ساز کند زهره به آهنگ سماع\r\nچنگ در غلغله آید که کجا شد منکر\r\nجام در قهقهه آید که کجا شد مناع\r\nوضع دوران بنگر ساغر عشرت برگیر\r\nکه به هر حالتی این است بهین اوضاع\r\nطره شاهد دنیی همه بند است و فریب\r\nعارفان بر سر این رشته نجویند نزاع\r\nعمر خسرو طلب ار نفع جهان می خواهی\r\nکه وجودیست عطابخش کریم نفاع\r\nمظهر لطف ازل روشنی چشم امل\r\nجامع علم و عمل جان جهان شاه شجاع\r\n', 'اوضاع به این صورتی که هست نمی ماند. فردای روشنی هم در پیش است. مراقب دنیای پر از نیرنگ و فریب اطرافت باش و از کنار آنها بگذر. هر چه می خواهی از خدا بخواه چرا که فقط اوست که مظهر لطف است و حیله ندارد و می تواند تو را به مقصد برساند. '),
(294, 'در وفای عشق تو مشهور خوبانم چو شمع\r\nشب نشین کوی سربازان و رندانم چو شمع\r\nروز و شب خوابم نمی آید به چشم غم پرست\r\nبس که در بیماری هجر تو گریانم چو شمع\r\nرشته صبرم به مقراض غمت ببریده شد\r\nهمچنان در آتش مهر تو سوزانم چو شمع\r\nگر کمیت اشک گلگونم نبودی گرم رو\r\nکی شدی روشن به گیتی راز پنهانم چو شمع\r\nدر میان آب و آتش همچنان سرگرم توست\r\nاین دل زار نزار اشک بارانم چو شمع\r\nدر شب هجران مرا پروانه وصلی فرست\r\nور نه از دردت جهانی را بسوزانم چو شمع\r\nبی جمال عالم آرای تو روزم چون شب است\r\nبا کمال عشق تو در عین نقصانم چو شمع\r\nکوه صبرم نرم شد چون موم در دست غمت\r\nتا در آب و آتش عشقت گدازانم چو شمع\r\nهمچو صبحم یک نفس باقیست با دیدار تو\r\nچهره بنما دلبرا تا جان برافشانم چو شمع\r\nسرفرازم کن شبی از وصل خود ای نازنین\r\nتا منور گردد از دیدارت ایوانم چو شمع\r\nآتش مهر تو را حافظ عجب در سر گرفت\r\nآتش دل کی به آب دیده بنشانم چو شمع\r\n', 'صبر و طاقت خودتان را از دست داده اید فقط با گریه دل خویش را التیام می دهید. با ناامیدی کاری از پیش نخواهید برد تا رسیدن به وصال راهی نیست. شما به مراد خودتان می رسید و کسانی که شما را مسخره کرده اند خوار می شوند. سرتان را بالا بگیرید و چراغ دلتان را با یاد خدا روشن کنید وصال نزدیک است. '),
(295, 'سحر به بوی گلستان دمی شدم در باغ\r\nکه تا چو بلبل بی دل کنم علاج دماغ\r\nبه جلوه گل سوری نگاه می کردم\r\nکه بود در شب تیره به روشنی چو چراغ\r\nچنان به حسن و جوانی خویشتن مغرور\r\nکه داشت از دل بلبل هزار گونه فراغ\r\nگشاده نرگس رعنا ز حسرت آب از چشم\r\nنهاده لاله ز سودا به جان و دل صد داغ\r\nزبان کشیده چو تیغی به سرزنش سوسن\r\nدهان گشاده شقایق چو مردم ایغاغ\r\nیکی چو باده پرستان صراحی اندر دست\r\nیکی چو ساقی مستان به کف گرفته ایاغ\r\nنشاط و عیش و جوانی چو گل غنیمت دان\r\nکه حافظا نبود بر رسول غیر بلاغ\r\n', 'اینقدر به جوانی و شادابی و زیبایی خویش مغرور نباش. همیشه جوان و شاداب نمی مانی. فکری هم به حال پیری خود بکن، با فخر فروختن و قیافه گرفتن از اجتماع طرد می شوی و هم در جوانی و هم در پیری تنها می مانی. پس قدر جوانی خویش را بدان و شادابی خود را با دیگران تقسیم کن. '),
(296, 'طالع اگر مدد دهد دامنش آورم به کف\r\nگر بکشم زهی طرب ور بکشد زهی شرف\r\nطرف کرم ز کس نبست این دل پرامید من\r\nگر چه سخن همی برد قصه من به هر طرف\r\nاز خم ابروی توام هیچ گشایشی نشد\r\nوه که در این خیال کج عمر عزیز شد تلف\r\nابروی دوست کی شود دست کش خیال من\r\nکس نزده ست از این کمان تیر مراد بر هدف\r\nچند به ناز پرورم مهر بتان سنگ دل\r\nیاد پدر نمی کنند این پسران ناخلف\r\nمن به خیال زاهدی گوشه نشین و طرفه آنک\r\nمغبچه ای ز هر طرف می زندم به چنگ و دف\r\nبی خبرند زاهدان نقش بخوان و لا تقل\r\nمست ریاست محتسب باده بده و لا تخف\r\nصوفی شهر بین که چون لقمه شبهه می خورد\r\nپاردمش دراز باد آن حیوان خوش علف\r\nحافظ اگر قدم زنی در ره خاندان به صدق\r\nبدرقه رهت شود همت شحنه نجف\r\n', 'فکر می کنی تا حالا عمرت را به هدر داده ای به همه چیز و همه کس شک داری. دلت را پاک کن و برای رضای حق به مردم خوبی کن. کمی حوصله و صبر داشته باش و به همه ی کسانی که خوبی کرده ای جواب خوبی تو را می دهند و اگر هم جفا کنند چیزی از تو کم نمی شود. '),
(297, 'زبان خامه ندارد سر بیان فراق\r\nوگرنه شرح دهم با تو داستان فراق\r\nدریغ مدت عمرم که بر امید وصال\r\nبه سر رسید و نیامد به سر زمان فراق\r\nسری که بر سر گردون به فخر می سودم\r\nبه راستان که نهادم بر آستان فراق\r\nچگونه باز کنم بال در هوای وصال\r\nکه ریخت مرغ دلم پر در آشیان فراق\r\nکنون چه چاره که در بحر غم به گردابی\r\nفتاد زورق صبرم ز بادبان فراق\r\nبسی نماند که کشتی عمر غرقه شود\r\nز موج شوق تو در بحر بی کران فراق\r\nاگر به دست من افتد فراق را بکشم\r\nکه روز هجر سیه باد و خان و مان فراق\r\nرفیق خیل خیالیم و همنشین شکیب\r\nقرین آتش هجران و هم قران فراق\r\nچگونه دعوی وصلت کنم به جان که شده ست\r\nتنم وکیل قضا و دلم ضمان فراق\r\nز سوز شوق دلم شد کباب دور از یار\r\nمدام خون جگر می خورم ز خوان فراق\r\nفلک چو دید سرم را اسیر چنبر عشق\r\nببست گردن صبرم به ریسمان فراق\r\nبه پای شوق گر این ره به سر شدی حافظ\r\nبه دست هجر ندادی کسی عنان فراق\r\n', 'کار خود را به دست خدا بسپار. از تو دیگر کاری بر نمی آید. تو فقط باید صبر پیشه کنی. سرنوشت آنچه را که باید برایت رقم می زند پس دعا کن تا آنچه را که می خواهی پیش آید. اگر بخواهی به زور چیزی را بدست بیاوری زود هم از دست می دهی. پس با خدا و توکل به او کن. '),
(298, 'مقام امن و می بی غش و رفیق شفیق\r\nگرت مدام میسر شود زهی توفیق\r\nجهان و کار جهان جمله هیچ بر هیچ است\r\nهزار بار من این نکته کرده ام تحقیق\r\nدریغ و درد که تا این زمان ندانستم\r\nکه کیمیای سعادت رفیق بود رفیق\r\nبه مومنی رو و فرصت شمر غنیمت وقت\r\nکه در کمینگه عمرند قاطعان طریق\r\nبیا که توبه ز لعل نگار و خنده جام\r\nحکایتیست که عقلش نمی کند تصدیق\r\nاگر چه موی میانت به چون منی نرسد\r\nخوش است خاطرم از فکر این خیال دقیق\r\nحلاوتی که تو را در چه زنخدان است\r\nبه کنه آن نرسد صد هزار فکر عمیق\r\nاگر به رنگ عقیقی شد اشک من چه عجب\r\nکه مهر خاتم لعل تو هست همچو عقیق\r\nبه خنده گفت که حافظ غلام طبع توام\r\nببین که تا به چه حدم همی کند تحمیق\r\n', 'موفقیت تو به خاطر قاطعیتت می باشد و از فرصت های طلایی که برایت پیش می آید نهایت استفاده کن. آدم دقیقی هستی و عقلت را همیشه بر هوای نفست ترجیح داده ای. در مورد حاجتی که داری عمیقا تحقیق کن. '),
(299, 'اگر شراب خوری جرعه ای فشان بر خاک\r\nاز آن گناه که نفعی رسد به غیر چه باک\r\nبرو به هر چه تو داری بخور دریغ مخور\r\nکه بی دریغ زند روزگار تیغ هلاک\r\nبه خاک پای تو ای سرو نازپرور من\r\nکه روز واقعه پا وامگیرم از سر خاک\r\nچه دوزخی چه بهشتی چه آدمی چه پری\r\nبه مذهب همه کفر طریقت است امساک\r\nمهندس فلکی راه دیر شش جهتی\r\nچنان ببست که ره نیست زیر دیر مغاک\r\nفریب دختر رز طرفه می زند ره عقل\r\nمباد تا به قیامت خراب طارم تاک\r\nبه راه میکده حافظ خوش از جهان رفتی\r\nدعای اهل دلت باد مونس دل پاک\r\n', 'هر چه داری با بقیه تقسیم کن. کرم داشته باش. با خساست به جایی نمی رسی. این خوبی هاست که نامت را ماندگار می کند. اگر این کارها را انجام بدهی تا زمان مرگ خدا همیشه پشت و پناهت می باشد. با اینکه انسان با تجربه ای هستی باز هم راهت را گم می کنی. اهل عبادت باش تا دلت پاک و منزه شود. '),
(300, 'هزار دشمنم ار می کنند قصد هلاک\r\nگرم تو دوستی از دشمنان ندارم باک\r\nمرا امید وصال تو زنده می دارد\r\nو گر نه هر دمم از هجر توست بیم هلاک\r\nنفس نفس اگر از باد نشنوم بویش\r\nزمان زمان چو گل از غم کنم گریبان چاک\r\nرود به خواب دو چشم از خیال تو هیهات\r\nبود صبور دل اندر فراق تو حاشاک\r\nاگر تو زخم زنی به که دیگری مرهم\r\nو گر تو زهر دهی به که دیگری تریاک\r\nبضرب سیفک قتلی حیاتنا ابدا\r\nلان روحی قد طاب ان یکون فداک\r\nعنان مپیچ که گر می زنی به شمشیرم\r\nسپر کنم سر و دستت ندارم از فتراک\r\nتو را چنان که تویی هر نظر کجا بیند\r\nبه قدر دانش خود هر کسی کند ادراک\r\nبه چشم خلق عزیز جهان شود حافظ\r\nکه بر در تو نهد روی مسکنت بر خاک\r\n', 'از دوست شفیق خود به خاطر هم پا بودن با شما تشکر کنید. این امید است که شما را زنده نگه داشته ولی گاهی طاقت از کف داده و خواب و خوراک تان را می گیرد. زخم زبان به کسی نزن تا خودت همیشه از زخم زبان در امان باشی. از دانش خود برای رسیدن به مقصود استفاده کن تا به منظوری که می خواهی برسی '),
(301, 'ای دل ریش مرا با لب تو حق نمک\r\nحق نگه دار که من می روم الله معک\r\nتویی آن گوهر پاکیزه که در عالم قدس\r\nذکر خیر تو بود حاصل تسبیح ملک\r\nدر خلوص منت ار هست شکی تجربه کن\r\nکس عیار زر خالص نشناسد چو محک\r\nگفته بودی که شوم مست و دو بوست بدهم\r\nوعده از حد بشد و ما نه دو دیدیم و نه یک\r\nبگشا پسته خندان و شکرریزی کن\r\nخلق را از دهن خویش مینداز به شک\r\nچرخ برهم زنم ار غیر مرادم گردد\r\nمن نه آنم که زبونی کشم از چرخ فلک\r\nچون بر حافظ خویشش نگذاری باری\r\nای رقیب از بر او یک دو قدم دورترک\r\n', 'دینی که بر گردنت می باشد ادا کن و حق مردم را بجا بیاور تا هم در این دنیا و هم دنیای باقی انس و ملک ذکر و خیرت را بگویند. به هر چیزی که شک داری محک بزن تا شکت برطرف شود. خوشحال باش و حرف آخر را بزن که مرادت میسر شده است. '),
(302, 'خوش خبر باشی ای نسیم شمال\r\nکه به ما می رسد زمان وصال\r\nقصه العشق لا انفصام لها\r\nفصمت ها هنا لسان القال\r\nمالسلمی و من بذی سلم\r\nاین جیراننا و کیف الحال\r\nعفت الدار بعد عافیه\r\nفاسالوا حالها عن الاطلال\r\nفی جمال الکمال نلت منی\r\nصرف الله عنک عین کمال\r\nیا برید الحمی حماک الله\r\nمرحبا مرحبا تعال تعال\r\nعرصه بزمگاه خالی ماند\r\nاز حریفان و جام مالامال\r\nسایه افکند حالیا شب هجر\r\nتا چه بازند شب روان خیال\r\nترک ما سوی کس نمی نگرد\r\nآه از این کبریا و جاه و جلال\r\nحافظا عشق و صابری تا چند\r\nناله عاشقان خوش است بنال\r\n', 'خبری را که مدت ها در انتظارش بودی رسیده است. زمان وصال نزدیک است. قصه عشق شروع شده و زمان جدایی پایان یافته است. تو هم به کمال و جمال می رسی. میدان برای نشان دادن هنرهایت خالی است پس مرد میدان باش. '),
(303, 'شممت روح وداد و شمت برق وصال\r\nبیا که بوی تو را میرم ای نسیم شمال\r\nاحادیا بجمال الحبیب قف و انزل\r\nکه نیست صبر جمیلم ز اشتیاق جمال\r\nحکایت شب هجران فروگذاشته به\r\nبه شکر آن که برافکند پرده روز وصال\r\nبیا که پرده گلریز هفت خانه چشم\r\nکشیده ایم به تحریر کارگاه خیال\r\nچو یار بر سر صلح است و عذر می طلبد\r\nتوان گذشت ز جور رقیب در همه حال\r\nبجز خیال دهان تو نیست در دل تنگ\r\nکه کس مباد چو من در پی خیال محال\r\nقتیل عشق تو شد حافظ غریب ولی\r\nبه خاک ما گذری کن که خون مات حلال\r\n', 'شب های هجران و جدایی پایان یافته و روزگار وصل و رسیدن به مراد نزدیک است. نقشه هایی را که برای این روز کشیده ای عملی کن. حریفان و حسودان را ببخش و عذر آنها را بپذیر. نذری را که کرده ای به جا آور تا رسیدن به مقصود حلالت باشد. '),
(304, 'دارای جهان نصرت دین خسرو کامل\r\nیحیی بن مظفر ملک عالم عادل\r\nای درگه اسلام پناه تو گشاده\r\nبر روی زمین روزنه جان و در دل\r\nتعظیم تو بر جان و خرد واجب و لازم\r\nانعام تو بر کون و مکان فایض و شامل\r\nروز ازل از کلک تو یک قطره سیاهی\r\nبر روی مه افتاد که شد حل مسائل\r\nخورشید چو آن خال سیه دید به دل گفت\r\nای کاج که من بودمی آن هندوی مقبل\r\nشاها فلک از بزم تو در رقص و سماع است\r\nدست طرب از دامن این زمزمه مگسل\r\nمی نوش و جهان بخش که از زلف کمندت\r\nشد گردن بدخواه گرفتار سلاسل\r\nدور فلکی یک سره بر منهج عدل است\r\nخوش باش که ظالم نبرد راه به منزل\r\nحافظ قلم شاه جهان مقسم رزق است\r\nاز بهر معیشت مکن اندیشه باطل\r\n', 'ناراحت رقیبان و حسودان نباش همگی را شکست خواهی داد اما به شرط اینکه در کسب روزی راه حلال را پیش بگیری و از عقل و درایت خود نهایت استفاده را بکنی. نتیجه ی کارهای خوبی را که انجام داده ای خیلی زود می بینی و مشکل تو هم حل می شود. '),
(305, 'به وقت گل شدم از توبه شراب خجل\r\nکه کس مباد ز کردار ناصواب خجل\r\nصلاح ما همه دام ره است و من زین بحث\r\nنیم ز شاهد و ساقی به هیچ باب خجل\r\nبود که یار نرنجد ز ما به خلق کریم\r\nکه از سوال ملولیم و از جواب خجل\r\nز خون که رفت شب دوش از سراچه چشم\r\nشدیم در نظر ره روان خواب خجل\r\nرواست نرگس مست ار فکند سر در پیش\r\nکه شد ز شیوه آن چشم پرعتاب خجل\r\nتویی که خوبتری ز آفتاب و شکر خدا\r\nکه نیستم ز تو در روی آفتاب خجل\r\nحجاب ظلمت از آن بست آب خضر که گشت\r\nز شعر حافظ و آن طبع همچو آب خجل\r\n', 'ا کار زشتی که انجام داده ای بسیار خجالت زده هستی. منتظر بقیه سرنوشت خود باش که هرچه صلاح باشد همان خواهد شد. خودت را بی تفاوت نشان نده ثابت کن از کار که کرده ای خجالت زده هستی. ذات خوبی داری پس به درونت رجوع کن و سیاهی و ظلمت را از دلت بدر کن و بگذار دلت همچون آب صاف شود. '),
(306, 'اگر به کوی تو باشد مرا مجال وصول\r\nرسد به دولت وصل تو کار من به اصول\r\nقرار برده ز من آن دو نرگس رعنا\r\nفراغ برده ز من آن دو جادوی مکحول\r\nچو بر در تو من بی نوای بی زر و زور\r\nبه هیچ باب ندارم ره خروج و دخول\r\nکجا روم چه کنم چاره از کجا جویم\r\nکه گشته ام ز غم و جور روزگار ملول\r\nمن شکسته بدحال زندگی یابم\r\nدر آن زمان که به تیغ غمت شوم مقتول\r\nخرابتر ز دل من غم تو جای نیافت\r\nکه ساخت در دل تنگم قرارگاه نزول\r\nدل از جواهر مهرت چو صیقلی دارد\r\nبود ز زنگ حوادث هر آینه مصقول\r\nچه جرم کرده ام ای جان و دل به حضرت تو\r\nکه طاعت من بی دل نمی شود مقبول\r\nبه درد عشق بساز و خموش کن حافظ\r\nرموز عشق مکن فاش پیش اهل عقول\r\n', 'اگر دقیق باشی و با اصول و قانون پیش بروی مجال این را داری که به هدف خودت برسی. فکر می کنی چون پول و ثروت نداری تمام درها به رویت بسته شده است. دنبال راه چاره می گردی. بی تاب مباش، کفر نگو دعاهای تو مورد قبول حق واقع شده. رازهایت را فاش نکن چون شیوه ی عاقلان است. '),
(307, 'هر نکته ای که گفتم در وصف آن شمایل\r\nهر کو شنید گفتا لله در قائل\r\nتحصیل عشق و رندی آسان نمود اول\r\nآخر بسوخت جانم در کسب این فضایل\r\nحلاج بر سر دار این نکته خوش سراید\r\nاز شافعی نپرسند امثال این مسائل\r\nگفتم که کی ببخشی بر جان ناتوانم\r\nگفت آن زمان که نبود جان در میانه حائل\r\nدل داده ام به یاری شوخی کشی نگاری\r\nمرضیه السجایا محموده الخصائل\r\nدر عین گوشه گیری بودم چو چشم مستت\r\nو اکنون شدم به مستان چون ابروی تو مایل\r\nاز آب دیده صد ره طوفان نوح دیدم\r\nو از لوح سینه نقشت هرگز نگشت زایل\r\nای دوست دست حافظ تعویذ چشم زخم است\r\nیا رب ببینم آن را در گردنت حمایل\r\n', 'پشیمانی سودی ندارد. راهی را که رفته ای تا آخر باید بروی هر چند که راهی سخت می باشد به تجربه اش می ارزد. کناره نگی کسی به تو کمک نمی کند. الان وقت فکر کردن و عمل کردن است. انسان با اراده ای هستی و اگر بخواهی کاری را انجام دهی طوفان به پا می کنی. '),
(308, 'ای رخت چون خلد و لعلت سلسبیل\r\nسلسبیلت کرده جان و دل سبیل\r\nسبزپوشان خطت بر گرد لب\r\nهمچو مورانند گرد سلسبیل\r\nناوک چشم تو در هر گوشه ای\r\nهمچو من افتاده دارد صد قتیل\r\nیا رب این آتش که در جان من است\r\nسرد کن زان سان که کردی بر خلیل\r\nمن نمی یابم مجال ای دوستان\r\nگر چه دارد او جمالی بس جمیل\r\nپای ما لنگ است و منزل بس دراز\r\nدست ما کوتاه و خرما بر نخیل\r\nحافظ از سرپنجه عشق نگار\r\nهمچو مور افتاده شد در پای پیل\r\nشاه عالم را بقا و عز و ناز\r\nباد و هر چیزی که باشد زین قبیل\r\n', 'انتظار معجزه دارید و ان معجزه وقتی محقق می شود که تمام هم و غم خود را به کار ببندید. وقت تنگ است. کمی عجله کنید. فعل دستتان کوتاه است اما انشاالله به مقصود دست پیدا خواهید کرد. گاهی دست سرنوشت طوری رقم می خورد که بدون هیچگونه تلاشی مقصود و نظر خود به دنبال تان می آید. '),
(309, 'عشقبازی و جوانی و شراب لعل فام\r\nمجلس انس و حریف همدم و شرب مدام\r\nساقی شکردهان و مطرب شیرین سخن\r\nهمنشینی نیک کردار و ندیمی نیک نام\r\nشاهدی از لطف و پاکی رشک آب زندگی\r\nدلبری در حسن و خوبی غیرت ماه تمام\r\nبزمگاهی دل نشان چون قصر فردوس برین\r\nگلشنی پیرامنش چون روضه دارالسلام\r\nصف نشینان نیکخواه و پیشکاران باادب\r\nدوستداران صاحب اسرار و حریفان دوستکام\r\nباده گلرنگ تلخ تیز خوش خوار سبک\r\nنقلش از لعل نگار و نقلش از یاقوت خام\r\nغمزه ساقی به یغمای خرد آهخته تیغ\r\nزلف جانان از برای صید دل گسترده دام\r\nنکته دانی بذله گو چون حافظ شیرین سخن\r\nبخشش آموزی جهان افروز چون حاجی قوام\r\nهر که این عشرت نخواهد خوشدلی بر وی تباه\r\nوان که این مجلس نجوید زندگی بر وی حرام\r\n', 'موقعیت برای شما مهیاست. این گوی و این میدان. اگر کمی فکر و تامل کنید همه چیز یکجا نصیبتان می شود. خداوند بسیار دوستتان دارد که مقام و ثروت و یار خوب را به ما داده حالا باید دید که در مقابل این لطف الهی چه کار می کنید و چگونه شاکر هستید. '),
(310, 'مرحبا طایر فرخ پی فرخنده پیام\r\nخیر مقدم چه خبر دوست کجا راه کدام\r\nیا رب این قافله را لطف ازل بدرقه باد\r\nکه از او خصم به دام آمد و معشوقه به کام\r\nماجرای من و معشوق مرا پایان نیست\r\nهر چه آغاز ندارد نپذیرد انجام\r\nگل ز حد برد تنعم نفسی رخ بنما\r\nسرو می نازد و خوش نیست خدا را بخرام\r\nزلف دلدار چو زنار همی فرماید\r\nبرو ای شیخ که شد بر تن ما خرقه حرام\r\nمرغ روحم که همی زد ز سر سدره صفیر\r\nعاقبت دانه خال تو فکندش در دام\r\nچشم بیمار مرا خواب نه درخور باشد\r\nمن له یقتل داء دنف کیف ینام\r\nتو ترحم نکنی بر من مخلص گفتم\r\nذاک دعوای و ها انت و تلک الایام\r\nحافظ ار میل به ابروی تو دارد شاید\r\nجای در گوشه محراب کنند اهل کلام\r\n', 'یک دیدار غیر منتظره دارید که بسیار خوشحالتان می کند و پیام خوشی به شما می دهد. به سفری می روید که جز معنویت چیزی سوغات ندارد. همه روزی به دنیای باقی می روند پس این چند صباح عمر را خوبی کنید و چیزی را که برای خودتان نمی پسندید برای دیگران هم نپسندید. به کس ترحم نکنید بلکه مخلصانه کار انجام دهید. '),
(311, 'عاشق روی جوانی خوش نوخاسته ام\r\nو از خدا دولت این غم به دعا خواسته ام\r\nعاشق و رند و نظربازم و می گویم فاش\r\nتا بدانی که به چندین هنر آراسته ام\r\nشرمم از خرقه آلوده خود می آید\r\nکه بر او وصله به صد شعبده پیراسته ام\r\nخوش بسوز از غمش ای شمع که اینک من نیز\r\nهم بدین کار کمربسته و برخاسته ام\r\nبا چنین حیرتم از دست بشد صرفه کار\r\nدر غم افزوده ام آنچ از دل و جان کاسته ام\r\nهمچو حافظ به خرابات روم جامه قبا\r\nبو که در بر کشد آن دلبر نوخاسته ام\r\n', 'برای رسیدن به دولت و مقام و ثروت کاری که انجام داده اید دعا کردن است وای خداوند می گوید از تو حرکت از من برکت. هنرهای بسیاری دارید که فقط آنها را برای خودتان حفظ کرده اید پس همت کنید تا وقت تلف شده را جبران کنید. غم دلتان از بین می رود. '),
(312, 'بشری اذ السلامه حلت بذی سلم\r\nلله حمد معترف غایه النعم\r\nآن خوش خبر کجاست که این فتح مژده داد\r\nتا جان فشانمش چو زر و سیم در قدم\r\nاز بازگشت شاه در این طرفه منزل است\r\nآهنگ خصم او به سراپرده عدم\r\nپیمان شکن هرآینه گردد شکسته حال\r\nان العهود عند ملیک النهی ذمم\r\nمی جست از سحاب امل رحمتی ولی\r\nجز دیده اش معاینه بیرون نداد نم\r\nدر نیل غم فتاد سپهرش به طنز گفت\r\nالان قد ندمت و ما ینفع الندم\r\nساقی چو یار مه رخ و از اهل راز بود\r\nحافظ بخورد باده و شیخ و فقیه هم\r\n', 'کسی که به شما جفا کرده خودش الان گرفتار است. ابرهای رحمت به سوی شما می آیند و جور و ظلم به سوی او روانه است. خبر خوش را به شما و خبر بد را به او می دهند. تو رازت را حفظ کن. عهد شکسته ات به نفعت می باشد و تجربه ای عالی برای بستن عهدی دگر هست. '),
(313, 'بازآی ساقیا که هواخواه خدمتم\r\nمشتاق بندگی و دعاگوی دولتم\r\nزان جا که فیض جام سعادت فروغ توست\r\nبیرون شدی نمای ز ظلمات حیرتم\r\nهر چند غرق بحر گناهم ز صد جهت\r\nتا آشنای عشق شدم ز اهل رحمتم\r\nعیبم مکن به رندی و بدنامی ای حکیم\r\nکاین بود سرنوشت ز دیوان قسمتم\r\nمی خور که عاشقی نه به کسب است و اختیار\r\nاین موهبت رسید ز میراث فطرتم\r\nمن کز وطن سفر نگزیدم به عمر خویش\r\nدر عشق دیدن تو هواخواه غربتم\r\nدریا و کوه در ره و من خسته و ضعیف\r\nای خضر پی خجسته مدد کن به همتم\r\nدورم به صورت از در دولتسرای تو\r\nلیکن به جان و دل ز مقیمان حضرتم\r\nحافظ به پیش چشم تو خواهد سپرد جان\r\nدر این خیالم ار بدهد عمر مهلتم\r\n', 'در این فکر هستی که همه چیز را از نو شروع کنی تا جبران مافات گردد. هر چند که گناهکاری ولی راه توبه را پیش گرفته ای و از این به بعد رحمت خداوند شامل حالت می شود. خودت را به دست سرنوشت بسپار. اگرچه خسته و ضعیف شده ای ولی با کی همت باز هم جوان و پرانرژی می شوی به تمام اهداف و مقاصد خود می رسی. مطمئن باش آنقدر عمر می کنی که به آرزوهایت برسی. '),
(314, 'دوش بیماری چشم تو ببرد از دستم\r\nلیکن از لطف لبت صورت جان می بستم\r\nعشق من با خط مشکین تو امروزی نیست\r\nدیرگاه است کز این جام هلالی مستم\r\nاز ثبات خودم این نکته خوش آمد که به جور\r\nدر سر کوی تو از پای طلب ننشستم\r\nعافیت چشم مدار از من میخانه نشین\r\nکه دم از خدمت رندان زده ام تا هستم\r\nدر ره عشق از آن سوی فنا صد خطر است\r\nتا نگویی که چو عمرم به سر آمد رستم\r\nبعد از اینم چه غم از تیر کج انداز حسود\r\nچون به محبوب کمان ابروی خود پیوستم\r\nبوسه بر درج عقیق تو حلال است مرا\r\nکه به افسوس و جفا مهر وفا نشکستم\r\nصنمی لشکریم غارت دل کرد و برفت\r\nآه اگر عاطفت شاه نگیرد دستم\r\nرتبت دانش حافظ به فلک برشده بود\r\nکرد غمخواری شمشاد بلندت پستم\r\n', 'بسیار انسان وفا پیشه ای هستید و حاضر نیستید تحت هیچ شرایطی عهد خود را بشکنید. تا هر زمان که لازم باشد به پای معشوق می نشینید. تیر همه ی رقیبان و حسودان به خطا رفته است. بیماری شما به زودی شفا پیدا می کند. نگران نباشید. از غم رهایی یافته و به حاجاتتان می رسید. '),
(315, 'به غیر از آن که بشد دین و دانش از دستم\r\nبیا بگو که ز عشقت چه طرف بربستم\r\nاگر چه خرمن عمرم غم تو داد به باد\r\nبه خاک پای عزیزت که عهد نشکستم\r\nچو ذره گر چه حقیرم ببین به دولت عشق\r\nکه در هوای رخت چون به مهر پیوستم\r\nبیار باده که عمریست تا من از سر امن\r\nبه کنج عافیت از بهر عیش ننشستم\r\nاگر ز مردم هشیاری ای نصیحتگو\r\nسخن به خاک میفکن چرا که من مستم\r\nچگونه سر ز خجالت برآورم بر دوست\r\nکه خدمتی به سزا برنیامد از دستم\r\nبسوخت حافظ و آن یار دلنواز نگفت\r\nکه مرهمی بفرستم که خاطرش خستم\r\n', 'یارتان اگرچه از نظر مادی در مضیقه می باشد ولی بسیار مهربان و وفادار است. به او اعتماد کنید او تمام خوشی ها را بر خود حرام کرده تا بتواند شما را خوشبخت نماید. در رسیدن به حاجات شما هم کمکش کنید هر چند که او می گوید شرمنده است ولی شما باز هم یاری اش دهید. '),
(316, 'زلف بر باد مده تا ندهی بر بادم\r\nناز بنیاد مکن تا نکنی بنیادم\r\nمی مخور با همه کس تا نخورم خون جگر\r\nسر مکش تا نکشد سر به فلک فریادم\r\nزلف را حلقه مکن تا نکنی دربندم\r\nطره را تاب مده تا ندهی بر بادم\r\nیار بیگانه مشو تا نبری از خویشم\r\nغم اغیار مخور تا نکنی ناشادم\r\nرخ برافروز که فارغ کنی از برگ گلم\r\nقد برافراز که از سرو کنی آزادم\r\nشمع هر جمع مشو ور نه بسوزی ما را\r\nیاد هر قوم مکن تا نروی از یادم\r\nشهره شهر مشو تا ننهم سر در کوه\r\nشور شیرین منما تا نکنی فرهادم\r\nرحم کن بر من مسکین و به فریادم رس\r\nتا به خاک در آصف نرسد فریادم\r\nحافظ از جور تو حاشا که بگرداند روی\r\nمن از آن روز که دربند توام آزادم\r\n', 'کاری نکن که بعدها از کرده ی خویش هم خودت و هم دیگران ناراحت باشند. با همه کس عشق مباز. کاری کن که دائم مثل گل همیشه بهار با طراوت باشی و سرت همه جا بالا باشد. خداوند در همه حال و همه جا به فریادت رسیده و نجاتت داده است و باز هم با توست. '),
(317, 'فاش می گویم و از گفته خود دلشادم\r\nبنده عشقم و از هر دو جهان آزادم\r\nطایر گلشن قدسم چه دهم شرح فراق\r\nکه در این دامگه حادثه چون افتادم\r\nمن ملک بودم و فردوس برین جایم بود\r\nآدم آورد در این دیر خراب آبادم\r\nسایه طوبی و دلجویی حور و لب حوض\r\nبه هوای سر کوی تو برفت از یادم\r\nنیست بر لوح دلم جز الف قامت دوست\r\nچه کنم حرف دگر یاد نداد استادم\r\nکوکب بخت مرا هیچ منجم نشناخت\r\nیا رب از مادر گیتی به چه طالع زادم\r\nتا شدم حلقه به گوش در میخانه عشق\r\nهر دم آید غمی از نو به مبارک بادم\r\nمی خورد خون دلم مردمک دیده سزاست\r\nکه چرا دل به جگرگوشه مردم دادم\r\nپاک کن چهره حافظ به سر زلف ز اشک\r\nور نه این سیل دمادم ببرد بنیادم\r\n', 'بسیار انسان وارسته و با ایمانی هستید و جز بندگی خدا را نمی پذیرید. بارها از دام شیطان به سلامت رهایی پیدا کرده اید. جای شما بهشت برین است. جز خدمت به خلق به چیز دیگری فکر نمی کنید. مورد امتحان الهی قرار گرفته اید. سعی کنید مثل همیشه سربلند از این آزمون بیرون بیایید. '),
(318, 'مرا می بینی و هر دم زیادت می کنی دردم\r\nتو را می بینم و میلم زیادت می شود هر دم\r\nبه سامانم نمی پرسی نمی دانم چه سر داری\r\nبه درمانم نمی کوشی نمی دانی مگر دردم\r\nنه راه است این که بگذاری مرا بر خاک و بگریزی\r\nگذاری آر و بازم پرس تا خاک رهت گردم\r\nندارم دستت از دامن بجز در خاک و آن دم هم\r\nکه بر خاکم روان گردی به گرد دامنت گردم\r\nفرورفت از غم عشقت دمم دم می دهی تا کی\r\nدمار از من برآوردی نمی گویی برآوردم\r\nشبی دل را به تاریکی ز زلفت باز می جستم\r\nرخت می دیدم و جامی هلالی باز می خوردم\r\nکشیدم در برت ناگاه و شد در تاب گیسویت\r\nنهادم بر لبت لب را و جان و دل فدا کردم\r\nتو خوش می باش با حافظ برو گو خصم جان می ده\r\nچو گرمی از تو می بینم چه باک از خصم دم سردم\r\n', 'جلوی هواهای نفسانی خویش را بگیرید. هر چند که به وصال یار فکر می کنید ولی مطمئن نیستید که او هم مثل شما فکر می کند یا نه. حاضرید سر و جان خودتان را فدای یار کنید و گاهی هم از سربی قراری می خواهید دل به دریا زده و همه چیز را به او بگوئید. کمی صبر کنید دست تقدیر همه چیز را بر وفق مرادتان درست می کند. '),
(319, 'سال ها پیروی مذهب رندان کردم\r\nتا به فتوی خرد حرص به زندان کردم\r\nمن به سرمنزل عنقا نه به خود بردم راه\r\nقطع این مرحله با مرغ سلیمان کردم\r\nسایه ای بر دل ریشم فکن ای گنج روان\r\nکه من این خانه به سودای تو ویران کردم\r\nتوبه کردم که نبوسم لب ساقی و کنون\r\nمی گزم لب که چرا گوش به نادان کردم\r\nدر خلاف آمد عادت بطلب کام که من\r\nکسب جمعیت از آن زلف پریشان کردم\r\nنقش مستوری و مستی نه به دست من و توست\r\nآن چه سلطان ازل گفت بکن آن کردم\r\nدارم از لطف ازل جنت فردوس طمع\r\nگر چه دربانی میخانه فراوان کردم\r\nاین که پیرانه سرم صحبت یوسف بنواخت\r\nاجر صبریست که در کلبه احزان کردم\r\nصبح خیزی و سلامت طلبی چون حافظ\r\nهر چه کردم همه از دولت قرآن کردم\r\nگر به دیوان غزل صدرنشینم چه عجب\r\nسال ها بندگی صاحب دیوان کردم\r\n', 'به هر کسی زود اطمینان می کنید. قسم خورده بودید دیگر کارهای ناشایست انجام ندهید ولی عهد خود را شکسته و بسیار پشیمانید. رو به خدا آورید که شما را هدایت می کند و در زیر سایه ی اوست که به نیت و مقصود خودتان می رسید. صبر داشته باشید، نمازهایتان را به موقع بجا آورید و قرآن بخوانید که هر چه به دست می آورید از دولت قرآن می باشد. '),
(320, 'دیشب به سیل اشک ره خواب می زدم\r\nنقشی به یاد خط تو بر آب می زدم\r\nابروی یار در نظر و خرقه سوخته\r\nجامی به یاد گوشه محراب می زدم\r\nهر مرغ فکر کز سر شاخ سخن بجست\r\nبازش ز طره تو به مضراب می زدم\r\nروی نگار در نظرم جلوه می نمود\r\nوز دور بوسه بر رخ مهتاب می زدم\r\nچشمم به روی ساقی و گوشم به قول چنگ\r\nفالی به چشم و گوش در این باب می زدم\r\nنقش خیال روی تو تا وقت صبحدم\r\nبر کارگاه دیده بی خواب می زدم\r\nساقی به صوت این غزلم کاسه می گرفت\r\nمی گفتم این سرود و می ناب می زدم\r\nخوش بود وقت حافظ و فال مراد و کام\r\nبر نام عمر و دولت احباب می زدم\r\n', 'با خواب و خیال به جایی نخواهید رسید. با نشستن و فکر کردن کاری از پیش نخواهید برد اگر می خواهید به مراد و کام دلتان دست پیدا کنید یا علی بگویید و خیالپردازی را کنار بگذارید و توکل به خدا کنید. راه هموار است. انشاالله که موفق می شوید. '),
(321, 'هر چند پیر و خسته دل و ناتوان شدم\r\nهر گه که یاد روی تو کردم جوان شدم\r\nشکر خدا که هر چه طلب کردم از خدا\r\nبر منتهای همت خود کامران شدم\r\nای گلبن جوان بر دولت بخور که من\r\nدر سایه تو بلبل باغ جهان شدم\r\nاول ز تحت و فوق وجودم خبر نبود\r\nدر مکتب غم تو چنین نکته دان شدم\r\nقسمت حوالتم به خرابات می کند\r\nهر چند کاین چنین شدم و آن چنان شدم\r\nآن روز بر دلم در معنی گشوده شد\r\nکز ساکنان درگه پیر مغان شدم\r\nدر شاهراه دولت سرمد به تخت بخت\r\nبا جام می به کام دل دوستان شدم\r\nاز آن زمان که فتنه چشمت به من رسید\r\nایمن ز شر فتنه آخرزمان شدم\r\nمن پیر سال و ماه نیم یار بی وفاست\r\nبر من چو عمر می گذرد پیر از آن شدم\r\nدوشم نوید داد عنایت که حافظا\r\nبازآ که من به عفو گناهت ضمان شدم\r\n', 'هر چه داری از دسترنج خودت می باشد. گاهی فک می کنی بسیار خسته و ناتوان شده ای اما وقتی که می بینی خدا با توست همه چیز را فراموش می کنی هر چه که سرنوشت برایت رقم زده همان می شود. در زندگی به آنچه که آرزو کنی می رسی. فقط مواظب فتنه ی دیگران باش، منتظر خبر خوش باش. '),
(322, 'خیال نقش تو در کارگاه دیده کشیدم\r\nبه صورت تو نگاری ندیدم و نشنیدم\r\nاگر چه در طلبت همعنان باد شمالم\r\nبه گرد سرو خرامان قامتت نرسیدم\r\nامید در شب زلفت به روز عمر نبستم\r\nطمع به دور دهانت ز کام دل ببریدم\r\nبه شوق چشمه نوشت چه قطره ها که فشاندم\r\nز لعل باده فروشت چه عشوه ها که خریدم\r\nز غمزه بر دل ریشم چه تیر ها که گشادی\r\nز غصه بر سر کویت چه بارها که کشیدم\r\nز کوی یار بیار ای نسیم صبح غباری\r\nکه بوی خون دل ریش از آن تراب شنیدم\r\nگناه چشم سیاه تو بود و گردن دلخواه\r\nکه من چو آهوی وحشی ز آدمی برمیدم\r\nچو غنچه بر سرم از کوی او گذشت نسیمی\r\nکه پرده بر دل خونین به بوی او بدریدم\r\nبه خاک پای تو سوگند و نور دیده حافظ\r\nکه بی رخ تو فروغ از چراغ دیده ندیدم\r\n', 'برای رسیدن به مال دنیا طمع نکن که هر چه بیشتر طمع کنی مال بیشتر از کفت بیرون می رود. نقشه هایت همه نقش بر آب شده است که تقصیر خودت می باشد ولی باز هم نسیم عنایت الهی دل پر خون تو را التیام می بخشد. اراده کرده ای که همه چیز را مثل اول کنی که موفق هم می شوی. انشاالله '),
(323, 'ز دست کوته خود زیر بارم\r\nکه از بالابلندان شرمسارم\r\nمگر زنجیر مویی گیردم دست\r\nوگر نه سر به شیدایی برآرم\r\nز چشم من بپرس اوضاع گردون\r\nکه شب تا روز اختر می شمارم\r\nبدین شکرانه می بوسم لب جام\r\nکه کرد آگه ز راز روزگارم\r\nاگر گفتم دعای می فروشان\r\nچه باشد حق نعمت می گزارم\r\nمن از بازوی خود دارم بسی شکر\r\nکه زور مردم آزاری ندارم\r\nسری دارم چو حافظ مست لیکن\r\nبه لطف آن سری امیدوارم\r\n', 'از روزگار خود بسیار دلگیر و ناراحتید. فکر می کنید وجودتان به اندازه ی تار مویی با مرگ فاصله دارد. ولی باز هم خدا را شکر می کنید. کسانی هستند که اوضاع شان بدتر از شما می باشد، پس حق نعمت خدا را به جای آورید. به بازوی خودتان متکی باشید که با زورگیری از مردم به جایی نمی رسید. امیدوار باشید که نیت تان خواهید رسید. '),
(324, 'گر چه افتاد ز زلفش گرهی در کارم\r\nهمچنان چشم گشاد از کرمش می دارم\r\nبه طرب حمل مکن سرخی رویم که چو جام\r\nخون دل عکس برون می دهد از رخسارم\r\nپرده مطربم از دست برون خواهد برد\r\nآه اگر زان که در این پرده نباشد بارم\r\nپاسبان حرم دل شده ام شب همه شب\r\nتا در این پرده جز اندیشه او نگذارم\r\nمنم آن شاعر ساحر که به افسون سخن\r\nاز نی کلک همه قند و شکر می بارم\r\nدیده بخت به افسانه او شد در خواب\r\nکو نسیمی ز عنایت که کند بیدارم\r\nچون تو را در گذر ای یار نمی یارم دید\r\nبا که گویم که بگوید سخنی با یارم\r\nدوش می گفت که حافظ همه روی است و ریا\r\nبجز از خاک درش با که بود بازارم\r\n', 'چیزی را پنهان نکنید از رخسار گلگونتان همه پی به راز درونتان برده اند. فریب خورده اید با سخنان زیبا و شیرین شما را سحر کرده اند و اجازه فکر کردن را به شما نمی دهند. از خدا بخواهید عنایت او همیشه شامل حالتان بوده است. '),
(325, 'گر دست دهد خاک کف پای نگارم\r\nبر لوح بصر خط غباری بنگارم\r\nبر بوی کنار تو شدم غرق و امید است\r\nاز موج سرشکم که رساند به کنارم\r\nپروانه او گر رسدم در طلب جان\r\nچون شمع همان دم به دمی جان بسپارم\r\nامروز مکش سر ز وفای من و اندیش\r\nزان شب که من از غم به دعا دست برآرم\r\nزلفین سیاه تو به دلداری عشاق\r\nدادند قراری و ببردند قرارم\r\nای باد از آن باده نسیمی به من آور\r\nکان بوی شفابخش بود دفع خمارم\r\nگر قلب دلم را ننهد دوست عیاری\r\nمن نقد روان در دمش از دیده شمارم\r\nدامن مفشان از من خاکی که پس از من\r\nزین در نتواند که برد باد غبارم\r\nحافظ لب لعلش چو مرا جان عزیز است\r\nعمری بود آن لحظه که جان را به لب آرم\r\n', 'برای دوستان حاضری حتی جانت را هم فدا کنی ولی کمی دوستانت را امتحان کن و ببین آنها هم همین احساس را نسبت به تو دارند یا نه و فقط وقت خوشی با تو هستند. شفای دلت را از خدا بخواه. کاری کن که بعد از مردنت خوبی های تو همیشه ماندگار بماند. بیشتر با خدا باش و عبادت کن. '),
(326, 'در نهانخانه عشرت صنمی خوش دارم\r\nکز سر زلف و رخش نعل در آتش دارم\r\nعاشق و رندم و میخواره به آواز بلند\r\nوین همه منصب از آن حور پریوش دارم\r\nگر تو زین دست مرا بی سر و سامان داری\r\nمن به آه سحرت زلف مشوش دارم\r\nگر چنین چهره گشاید خط زنگاری دوست\r\nمن رخ زرد به خونابه منقش دارم\r\nگر به کاشانه رندان قدمی خواهی زد\r\nنقل شعر شکرین و می بی غش دارم\r\nناوک غمزه بیار و رسن زلف که من\r\nجنگ ها با دل مجروح بلاکش دارم\r\nحافظا چون غم و شادی جهان در گذر است\r\nبهتر آن است که من خاطر خود خوش دارم\r\n', 'در دلت غمی داری که با آن زندگی می کنی ولی بدان در این دنیا همه چیز فانی است و نمی ارزد که بخواهی زندگیت را تباه کنی. غم دلت را به کسی نگفته ای و در عین غمگینی می خندی. زندگی را هر طوری که بگیری می گذرد. پس امید داشته باش و از نعمات خدا لذت ببر. '),
(327, 'مرا عهدیست با جانان که تا جان در بدن دارم\r\nهواداران کویش را چو جان خویشتن دارم\r\nصفای خلوت خاطر از آن شمع چگل جویم\r\nفروغ چشم و نور دل از آن ماه ختن دارم\r\nبه کام و آرزوی دل چو دارم خلوتی حاصل\r\nچه فکر از خبث بدگویان میان انجمن دارم\r\nمرا در خانه سروی هست کاندر سایه قدش\r\nفراغ از سرو بستانی و شمشاد چمن دارم\r\nگرم صد لشکر از خوبان به قصد دل کمین سازند\r\nبحمد الله و المنه بتی لشکرشکن دارم\r\nسزد کز خاتم لعلش زنم لاف سلیمانی\r\nچو اسم اعظمم باشد چه باک از اهرمن دارم\r\nالا ای پیر فرزانه مکن عیبم ز میخانه\r\nکه من در ترک پیمانه دلی پیمان شکن دارم\r\nخدا را ای رقیب امشب زمانی دیده بر هم نه\r\nکه من با لعل خاموشش نهانی صد سخن دارم\r\nچو در گلزار اقبالش خرامانم بحمدالله\r\nنه میل لاله و نسرین نه برگ نسترن دارم\r\nبه رندی شهره شد حافظ میان همدمان لیکن\r\nچه غم دارم که در عالم قوام الدین حسن دارم\r\n', 'می خواهند شما را از تصمیمی که گرفته اید منصرف کنند ولی شما کار خودتان را بکنید و موانعی را که پیش پایتان می گذارند بردارید. این را هم بدانید که اگر در این راه متوسل به دروغ و ریا شوید به هدف نمی رسید. کسی شما را نصیحت می کند و راه و چاه را نشانتان می دهد. به حرفش گوش دهید تا زودتر موفق شوید. '),
(328, 'من که باشم که بر آن خاطر عاطر گذرم\r\nلطف ها می کنی ای خاک درت تاج سرم\r\nدلبرا بنده نوازیت که آموخت بگو\r\nکه من این ظن به رقیبان تو هرگز نبرم\r\nهمتم بدرقه راه کن ای طایر قدس\r\nکه دراز است ره مقصد و من نوسفرم\r\nای نسیم سحری بندگی من برسان\r\nکه فراموش مکن وقت دعای سحرم\r\nخرم آن روز کز این مرحله بربندم بار\r\nو از سر کوی تو پرسند رفیقان خبرم\r\nحافظا شاید اگر در طلب گوهر وصل\r\nدیده دریا کنم از اشک و در او غوطه خورم\r\nپایه نظم بلند است و جهان گیر بگو\r\nتا کند پادشه بحر دهان پرگهرم\r\n', 'اینقدر خودتان را دست پائین نگیرید. شما هم می توانید مثل دیگران کاری کنید که پولدار و با مقام شوید کمی همت لازم است و امید داشته باشید. نماز و ایمان فراموشتان نشود. زمانی به وصال یار و حاجتتان می رسید که تمام سعی و اهتمام خود را به کار ببندید. '),
(329, 'جوزا سحر نهاد حمایل برابرم\r\nیعنی غلام شاهم و سوگند می خورم\r\nساقی بیا که از مدد بخت کارساز\r\nکامی که خواستم ز خدا شد میسرم\r\nجامی بده که باز به شادی روی شاه\r\nپیرانه سر هوای جوانیست در سرم\r\nراهم مزن به وصف زلال خضر که من\r\nاز جام شاه جرعه کش حوض کوثرم\r\nشاها اگر به عرش رسانم سریر فضل\r\nمملوک این جنابم و مسکین این درم\r\nمن جرعه نوش بزم تو بودم هزار سال\r\nکی ترک آبخورد کند طبع خوگرم\r\nور باورت نمی کند از بنده این حدیث\r\nاز گفته کمال دلیلی بیاورم\r\nگر برکنم دل از تو و بردارم از تو مهر\r\nآن مهر بر که افکنم آن دل کجا برم\r\nمنصور بن مظفر غازیست حرز من\r\nو از این خجسته نام بر اعدا مظفرم\r\nعهد الست من همه با عشق شاه بود\r\nو از شاهراه عمر بدین عهد بگذرم\r\nگردون چو کرد نظم ثریا به نام شاه\r\nمن نظم در چرا نکنم از که کمترم\r\nشاهین صفت چو طعمه چشیدم ز دست شاه\r\nکی باشد التفات به صید کبوترم\r\nای شاه شیرگیر چه کم گردد ار شود\r\nدر سایه تو ملک فراغت میسرم\r\nشعرم به یمن مدح تو صد ملک دل گشاد\r\nگویی که تیغ توست زبان سخنورم\r\nبر گلشنی اگر بگذشتم چو باد صبح\r\nنی عشق سرو بود و نه شوق صنوبرم\r\nبوی تو می شنیدم و بر یاد روی تو\r\nدادند ساقیان طرب یک دو ساغرم\r\nمستی به آب یک دو عنب وضع بنده نیست\r\nمن سالخورده پیر خرابات پرورم\r\nبا سیر اختر فلکم داوری بسیست\r\nانصاف شاه باد در این قصه یاورم\r\nشکر خدا که باز در این اوج بارگاه\r\nطاووس عرش می شنود صیت شهپرم\r\nنامم ز کارخانه عشاق محو باد\r\nگر جز محبت تو بود شغل دیگرم\r\nشبل الاسد به صید دلم حمله کرد و من\r\nگر لاغرم وگرنه شکار غضنفرم\r\nای عاشقان روی تو از ذره بیشتر\r\nمن کی رسم به وصل تو کز ذره کمترم\r\nبنما به من که منکر حسن رخ تو کیست\r\nتا دیده اش به گزلک غیرت برآورم\r\nبر من فتاد سایه خورشید سلطنت\r\nو اکنون فراغت است ز خورشید خاورم\r\nمقصود از این معامله بازارتیزی است\r\nنی جلوه می فروشم و نی عشوه می خرم\r\n', 'بخت و اقبال با تو همراه است و روزگار روی خوش را به تو نشان می دهد. خداوند را سپاسگزار باش تا نعمات خود را از تو دریغ نکند. به عهد و پیمان خود پایبند و وفادار باش تا حق دوستی را به جای آورده باشی. '),
(330, 'تو همچو صبحی و من شمع خلوت سحرم\r\nتبسمی کن و جان بین که چون همی سپرم\r\nچنین که در دل من داغ زلف سرکش توست\r\nبنفشه زار شود تربتم چو درگذرم\r\nبر آستان مرادت گشاده ام در چشم\r\nکه یک نظر فکنی خود فکندی از نظرم\r\nچه شکر گویمت ای خیل غم عفاک الله\r\nکه روز بی کسی آخر نمی روی ز سرم\r\nغلام مردم چشمم که با سیاه دلی\r\nهزار قطره ببارد چو درد دل شمرم\r\nبه هر نظر بت ما جلوه می کند لیکن\r\nکس این کرشمه نبیند که من همی نگرم\r\nبه خاک حافظ اگر یار بگذرد چون باد\r\nز شوق در دل آن تنگنا کفن بدرم\r\n', 'فرد تنهایی هستید. تنها کس شما دلتان می باشد. یار و دوستی ندارید. کسی از دردهایتان خبر ندارد. مواظب نامحرمان و حسودان باشید و راز دلتان را به آنها نگوید که به ضرر خودتان از آن استفاده می کنند. نذری کرده اید اگر به حاجتتان رسیدید آن را حتما ادا کنید. '),
(331, 'به تیغم گر کشد دستش نگیرم\r\nوگر تیرم زند منت پذیرم\r\nکمان ابرویت را گو بزن تیر\r\nکه پیش دست و بازویت بمیرم\r\nغم گیتی گر از پایم درآرد\r\nبجز ساغر که باشد دستگیرم\r\nبرآی ای آفتاب صبح امید\r\nکه در دست شب هجران اسیرم\r\nبه فریادم رس ای پیر خرابات\r\nبه یک جرعه جوانم کن که پیرم\r\nبه گیسوی تو خوردم دوش سوگند\r\nکه من از پای تو سر بر نگیرم\r\nبسوز این خرقه تقوا تو حافظ\r\nکه گر آتش شوم در وی نگیرم\r\n', 'خودتان را مستحق زجر و عذاب می دانید چون خود شما به دنبال این کار رفته اید. امیدوارید دوباره همه چیز درست شود و دست غیبی شما را نجات بخشد. حاجتتان برآورده می شود و اطرافیان تان به یاری تان می شتابند. تقوی و ایمان خودتان را حفظ نمایید تا این زجر را کمتر حس کنید. '),
(332, 'مزن بر دل ز نوک غمزه تیرم\r\nکه پیش چشم بیمارت بمیرم\r\nنصاب حسن در حد کمال است\r\nزکاتم ده که مسکین و فقیرم\r\nچو طفلان تا کی ای زاهد فریبی\r\nبه سیب بوستان و شهد و شیرم\r\nچنان پر شد فضای سینه از دوست\r\nکه فکر خویش گم شد از ضمیرم\r\nقدح پر کن که من در دولت عشق\r\nجوان بخت جهانم گر چه پیرم\r\nقراری بسته ام با می فروشان\r\nکه روز غم بجز ساغر نگیرم\r\nمبادا جز حساب مطرب و می\r\nاگر نقشی کشد کلک دبیرم\r\nدر این غوغا که کس کس را نپرسد\r\nمن از پیر مغان منت پذیرم\r\nخوشا آن دم کز استغنای مستی\r\nفراغت باشد از شاه و وزیرم\r\nمن آن مرغم که هر شام و سحرگاه\r\nز بام عرش می آید صفیرم\r\nچو حافظ گنج او در سینه دارم\r\nاگر چه مدعی بیند حقیرم\r\n', 'آنقدر که به فکر دیگرانی به فکر خودت نیستی و فقط به این فکر می کنی به عهدی که با دوستانت بسته ای عمل نمایی. در این روزگار پر غوغا بهت اس بیشتر به فکر خود باشی تا بتوانی گلیمت را از آب بیرون بکشی. خودت را دست کم نگیر مطمئن باش صدای تو به خدایت می رسد. '),
(333, 'نماز شام غریبان چو گریه آغازم\r\nبه مویه های غریبانه قصه پردازم\r\nبه یاد یار و دیار آن چنان بگریم زار\r\nکه از جهان ره و رسم سفر براندازم\r\nمن از دیار حبیبم نه از بلاد غریب\r\nمهیمنا به رفیقان خود رسان بازم\r\nخدای را مددی ای رفیق ره تا من\r\nبه کوی میکده دیگر علم برافرازم\r\nخرد ز پیری من کی حساب برگیرد\r\nکه باز با صنمی طفل عشق می بازم\r\nبجز صبا و شمالم نمی شناسد کس\r\nعزیز من که بجز باد نیست دمسازم\r\nهوای منزل یار آب زندگانی ماست\r\nصبا بیار نسیمی ز خاک شیرازم\r\nسرشکم آمد و عیبم بگفت روی به روی\r\nشکایت از که کنم خانگیست غمازم\r\nز چنگ زهره شنیدم که صبحدم می گفت\r\nغلام حافظ خوش لهجه خوش آوازم\r\n', 'با گریه و زاری کردن و تنهایی دست به کار شدن کاری از پیش نخواهی برد. خداوند کمکت می کند به عقل پیران اعتماد کن. خودت نیز همتی کن تا هرچه زودتر به نیتت برسی به هر کس خوبی کردی نتیجه ی بد دیدی. به خدا توکل کن صبح امید نزدیک است. '),
(334, 'گر دست رسد در سر زلفین تو بازم\r\nچون گوی چه سرها که به چوگان تو بازم\r\nزلف تو مرا عمر دراز است ولی نیست\r\nدر دست سر مویی از آن عمر درازم\r\nپروانه راحت بده ای شمع که امشب\r\nاز آتش دل پیش تو چون شمع گدازم\r\nآن دم که به یک خنده دهم جان چو صراحی\r\nمستان تو خواهم که گزارند نمازم\r\nچون نیست نماز من آلوده نمازی\r\nدر میکده زان کم نشود سوز و گدازم\r\nدر مسجد و میخانه خیالت اگر آید\r\nمحراب و کمانچه ز دو ابروی تو سازم\r\nگر خلوت ما را شبی از رخ بفروزی\r\nچون صبح بر آفاق جهان سر بفرازم\r\nمحمود بود عاقبت کار در این راه\r\nگر سر برود در سر سودای ایازم\r\nحافظ غم دل با که بگویم که در این دور\r\nجز جام نشاید که بود محرم رازم\r\n', 'برای رسیدن به مقاصد خود و دیگران را فدا نکنید تا به هدف های متعالی برسید. ریا و تزویر نداشته باشید، یک رنگی راز موفقیت تان می باشد. همه ی نقشه های شما برای آینده زمانی محقق می شود که سودای طمع را کنار بگذارید و رازتان را فاش نکنید. عاقبت کارتان خیر است. '),
(335, 'در خرابات مغان گر گذر افتد بازم\r\nحاصل خرقه و سجاده روان دربازم\r\nحلقه توبه گر امروز چو زهاد زنم\r\nخازن میکده فردا نکند در بازم\r\nور چو پروانه دهد دست فراغ بالی\r\nجز بدان عارض شمعی نبود پروازم\r\nصحبت حور نخواهم که بود عین قصور\r\nبا خیال تو اگر با دگری پردازم\r\nسر سودای تو در سینه بماندی پنهان\r\nچشم تردامن اگر فاش نگردی رازم\r\nمرغ سان از قفس خاک هوایی گشتم\r\nبه هوایی که مگر صید کند شهبازم\r\nهمچو چنگ ار به کناری ندهی کام دلم\r\nاز لب خویش چو نی یک نفسی بنوازم\r\nماجرای دل خون گشته نگویم با کس\r\nزان که جز تیغ غمت نیست کسی دمسازم\r\nگر به هر موی سری بر تن حافظ باشد\r\nهمچو زلفت همه را در قدمت اندازم\r\n', 'هر راهی را که تا به حال انتخاب کرده اید به بن بست رسیده است. خسته شده اید، می خواهید همه چیز را بر ملا کنید. کمی صبر داشته باشید، بخت به شما رو کرده است. به کام دلتان می رسید ولی تا رسیدن به هدف، زجر و درد و خون دل خوردن جزئی از راهی است که باید بروید. '),
(336, 'مژده وصل تو کو کز سر جان برخیزم\r\nطایر قدسم و از دام جهان برخیزم\r\nبه ولای تو که گر بنده خویشم خوانی\r\nاز سر خواجگی کون و مکان برخیزم\r\nیا رب از ابر هدایت برسان بارانی\r\nپیشتر زان که چو گردی ز میان برخیزم\r\nبر سر تربت من با می و مطرب بنشین\r\nتا به بویت ز لحد رقص کنان برخیزم\r\nخیز و بالا بنما ای بت شیرین حرکات\r\nکز سر جان و جهان دست فشان برخیزم\r\nگر چه پیرم تو شبی تنگ در آغوشم کش\r\nتا سحرگه ز کنار تو جوان برخیزم\r\nروز مرگم نفسی مهلت دیدار بده\r\nتا چو حافظ ز سر جان و جهان برخیزم\r\n', 'به خاطر ایمان قوی و تقوی از بندگان مقرب الهی هستی. اراده کنی خداوند همه چیز به تو می دهد ولی همیشه دیگران را مقدم دانستی و برای آنها دعا می کنی فقط از خدا می خواهی بهشت را نصیب بگرداند. به آرزویت می رسی هم در این دنیا و هم در جهان آخرت. '),
(337, 'چرا نه در پی عزم دیار خود باشم\r\nچرا نه خاک سر کوی یار خود باشم\r\nغم غریبی و غربت چو بر نمی تابم\r\nبه شهر خود روم و شهریار خود باشم\r\nز محرمان سراپرده وصال شوم\r\nز بندگان خداوندگار خود باشم\r\nچو کار عمر نه پیداست باری آن اولی\r\nکه روز واقعه پیش نگار خود باشم\r\nز دست بخت گران خواب و کار بی سامان\r\nگرم بود گله ای رازدار خود باشم\r\nهمیشه پیشه من عاشقی و رندی بود\r\nدگر بکوشم و مشغول کار خود باشم\r\nبود که لطف ازل رهنمون شود حافظ\r\nوگرنه تا به ابد شرمسار خود باشم\r\n', 'از دست غریبی و بی کسی گله و شکایت نداشته باش این تصمیمی است که خودت گرفته ای. ماهی را هر وقت از آب بگیری تازه است از راهی که رفته ای برگرد. حادثه ای در پیش است که شما را از بی سامانی نجات می دهد. راه راست را پیشه ی خود کن. خداوند باز هم به تو لطف کرده است. '),
(338, 'من دوستدار روی خوش و موی دلکشم\r\nمدهوش چشم مست و می صاف بی غشم\r\nگفتی ز سر عهد ازل یک سخن بگو\r\nآن گه بگویمت که دو پیمانه درکشم\r\nمن آدم بهشتیم اما در این سفر\r\nحالی اسیر عشق جوانان مه وشم\r\nدر عاشقی گزیر نباشد ز ساز و سوز\r\nاستاده ام چو شمع مترسان ز آتشم\r\nشیراز معدن لب لعل است و کان حسن\r\nمن جوهری مفلسم ایرا مشوشم\r\nاز بس که چشم مست در این شهر دیده ام\r\nحقا که می نمی خورم اکنون و سرخوشم\r\nشهریست پر کرشمه حوران ز شش جهت\r\nچیزیم نیست ور نه خریدار هر ششم\r\nبخت ار مدد دهد که کشم رخت سوی دوست\r\nگیسوی حور گرد فشاند ز مفرشم\r\nحافظ عروس طبع مرا جلوه آرزوست\r\nآیینه ای ندارم از آن آه می کشم\r\n', 'فکر می کنید همیشه جوان می مانید. از آینده بی خبر مانده اید و فقط به خوشگذرانی و عیش می پردازید. می گویید بهشت همین جاست ولی شما اسیر هوی نفس شده اید نمی خواهید قبول کنید که جهنمی هم وجود دارد اگر به همین منوال ادامه دهید اضطراب و نگرانی و مفلسی دامنتان را می گیرد. ظاهر پسند نباشید. '),
(339, 'خیال روی تو چون بگذرد به گلشن چشم\r\nدل از پی نظر آید به سوی روزن چشم\r\nسزای تکیه گهت منظری نمی بینم\r\nمنم ز عالم و این گوشه معین چشم\r\nبیا که لعل و گهر در نثار مقدم تو\r\nز گنج خانه دل می کشم به روزن چشم\r\nسحر سرشک روانم سر خرابی داشت\r\nگرم نه خون جگر می گرفت دامن چشم\r\nنخست روز که دیدم رخ تو دل می گفت\r\nاگر رسد خللی خون من به گردن چشم\r\nبه بوی مژده وصل تو تا سحر شب دوش\r\nبه راه باد نهادم چراغ روشن چشم\r\nبه مردمی که دل دردمند حافظ را\r\nمزن به ناوک دلدوز مردم افکن چشم\r\n', 'منتظر کسی هستید که حاضرید به خاطر آن همه چیزتان را فدا کنید ولی فکر می کنید او حتی گوشه ی چشمی هم به شما نمی اندازد بدانید که مورد امتحان الهی قرار گرفته اید تا خون دل نخورید و زجر نکشید رسیدن مژده وصل برایتان خوشایند نخواهد بود. بدانید که دل دردمندتان با این مژده التیام پیدا می کند و چشمتان به قدوم یار روشن می شود. '),
(340, 'من که از آتش دل چون خم می در جوشم\r\nمهر بر لب زده خون می خورم و خاموشم\r\nقصد جان است طمع در لب جانان کردن\r\nتو مرا بین که در این کار به جان می کوشم\r\nمن کی آزاد شوم از غم دل چون هر دم\r\nهندوی زلف بتی حلقه کند در گوشم\r\nحاش لله که نیم معتقد طاعت خویش\r\nاین قدر هست که گه گه قدحی می نوشم\r\nهست امیدم که علیرغم عدو روز جزا\r\nفیض عفوش ننهد بار گنه بر دوشم\r\nپدرم روضه رضوان به دو گندم بفروخت\r\nمن چرا ملک جهان را به جوی نفروشم\r\nخرقه پوشی من از غایت دین داری نیست\r\nپرده ای بر سر صد عیب نهان می پوشم\r\nمن که خواهم که ننوشم بجز از راوق خم\r\nچه کنم گر سخن پیر مغان ننیوشم\r\nگر از این دست زند مطرب مجلس ره عشق\r\nشعر حافظ ببرد وقت سماع از هوشم\r\n', 'فعلا نمی توانی حرفی بزنی پس همان بهتر که خاموشی و رازت را فاش نمی کنی. برای رسیدن به مال دنیا بسیار تلاش می کنی چون خوشبختی را در ثروت می بینی ولی به همین مالی که داری قانع باش. هرگز ایمانت را پای پول نفروش زیرا بار گناهانت زیاد می شود. راه راست را پیش بگیر تا موفق شوی. انشاالله '),
(341, 'گر من از سرزنش مدعیان اندیشم\r\nشیوه مستی و رندی نرود از پیشم\r\nزهد رندان نوآموخته راهی بدهیست\r\nمن که بدنام جهانم چه صلاح اندیشم\r\nشاه شوریده سران خوان من بی سامان را\r\nزان که در کم خردی از همه عالم بیشم\r\nبر جبین نقش کن از خون دل من خالی\r\nتا بدانند که قربان تو کافرکیشم\r\nاعتقادی بنما و بگذر بهر خدا\r\nتا در این خرقه ندانی که چه نادرویشم\r\nشعر خونبار من ای باد بدان یار رسان\r\nکه ز مژگان سیه بر رگ جان زد نیشم\r\nمن اگر باده خورم ور نه چه کارم با کس\r\nحافظ راز خود و عارف وقت خویشم\r\n', 'از راهی که می روی می خواهند منصرفت کنند. سرزنشت می کنند و می گویند آخر کار جز بدنامی چیز دیگری نیست. خودت هم بر سر دو راهی مانده ای و بی سر و سامان هستی. فکرت کار نمی کند. همه چیز را به خدا بسپار. او همه چیز را درست می کند و بر همه چیز واقف است و التیام دهنده ی دل پر خون توست. '),
(342, 'حجاب چهره جان می شود غبار تنم\r\nخوشا دمی که از آن چهره پرده برفکنم\r\nچنین قفس نه سزای چو من خوش الحانیست\r\nروم به گلشن رضوان که مرغ آن چمنم\r\nعیان نشد که چرا آمدم کجا رفتم\r\nدریغ و درد که غافل ز کار خویشتنم\r\nچگونه طوف کنم در فضای عالم قدس\r\nکه در سراچه ترکیب تخته بند تنم\r\nاگر ز خون دلم بوی شوق می آید\r\nعجب مدار که همدرد نافه ختنم\r\nطراز پیرهن زرکشم مبین چون شمع\r\nکه سوزهاست نهانی درون پیرهنم\r\nبیا و هستی حافظ ز پیش او بردار\r\nکه با وجود تو کس نشنود ز من که منم\r\n', 'معنویت شما بسیار عالی است و دنیای فانی را جز قفسی تنگ برای خود چیز دیگری نمی بینید. خودتان را لایق چیزهایی می دانید که باقی است نه از بین رفتنی. همیشه در این فکرید که چرا از بعضی کارها غافل مانده اید. به زیارت کعبه مشرف می شوید و تنه آرزویتان رسیدن به حق تعالی می باشد و بس. '),
(343, 'چل سال بیش رفت که من لاف می زنم\r\nکز چاکران پیر مغان کمترین منم\r\nهرگز به یمن عاطفت پیر می فروش\r\nساغر تهی نشد ز می صاف روشنم\r\nاز جاه عشق و دولت رندان پاکباز\r\nپیوسته صدر مصطبه ها بود مسکنم\r\nدر شان من به دردکشی ظن بد مبر\r\nکآلوده گشت جامه ولی پاکدامنم\r\nشهباز دست پادشهم این چه حالت است\r\nکز یاد برده اند هوای نشیمنم\r\nحیف است بلبلی چو من اکنون در این قفس\r\nبا این لسان عذب که خامش چو سوسنم\r\nآب و هوای فارس عجب سفله پرور است\r\nکو همرهی که خیمه از این خاک برکنم\r\nحافظ به زیر خرقه قدح تا به کی کشی\r\nدر بزم خواجه پرده ز کارت برافکنم\r\nتورانشه خجسته که در من یزید فضل\r\nشد منت مواهب او طوق گردنم\r\n', 'عمر گرانقیمت خویش را صرف کردی بدون آنکه چیزی عایدت شود چه مادی و چه معنوی. فقط زنده بوده ای. آرزویی کرده ای بدون اینکه برای رسیدن به آن تلاش کنی. اعتقاد داری که انسان بدشانسی هستی ولی اینطور نیست شما خودت فرصت ها را از دست داده ای. باز هم وقت هست طوق بندگی خداوند را به گردن بینداز و یا علی بگو. '),
(344, 'عمریست تا من در طلب هر روز گامی می زنم\r\nدست شفاعت هر زمان در نیک نامی می زنم\r\nبی ماه مهرافروز خود تا بگذرانم روز خود\r\nدامی به راهی می نهم مرغی به دامی می زنم\r\nاورنگ کو گلچهر کو نقش وفا و مهر کو\r\nحالی من اندر عاشقی داو تمامی می زنم\r\nتا بو که یابم آگهی از سایه سرو سهی\r\nگلبانگ عشق از هر طرف بر خوش خرامی می زنم\r\nهر چند کان آرام دل دانم نبخشد کام دل\r\nنقش خیالی می کشم فال دوامی می زنم\r\nدانم سر آرد غصه را رنگین برآرد قصه را\r\nاین آه خون افشان که من هر صبح و شامی می زنم\r\nبا آن که از وی غایبم و از می چو حافظ تایبم\r\nدر مجلس روحانیان گه گاه جامی می زنم\r\n', 'زمان طولانی را گذرانده اید تا بتوانید نامی نیک و جایی خوب در دل مردم برای خود باز کنید. ولی دشمنان مدام برای بدنامی تان دام پهن می کنند و به آنچه می خواهید می رسید و به تمام کسانی که خوبی کرده اید به خوبی نتیجه می بینید و کام دلتان شیرین می شود. '),
(345, 'بی تو ای سرو روان با گل و گلشن چه کنم\r\nزلف سنبل چه کشم عارض سوسن چه کنم\r\nآه کز طعنه بدخواه ندیدم رویت\r\nنیست چون آینه ام روی ز آهن چه کنم\r\nبرو ای ناصح و بر دردکشان خرده مگیر\r\nکارفرمای قدر می کند این من چه کنم\r\nبرق غیرت چو چنین می جهد از مکمن غیب\r\nتو بفرما که من سوخته خرمن چه کنم\r\nشاه ترکان چو پسندید و به چاهم انداخت\r\nدستگیر ار نشود لطف تهمتن چه کنم\r\nمددی گر به چراغی نکند آتش طور\r\nچاره تیره شب وادی ایمن چه کنم\r\nحافظا خلد برین خانه موروث من است\r\nاندر این منزل ویرانه نشیمن چه کنم\r\n', 'از طعنه و زخم زبان مردم خسته شده اید. مدام از شما ایراد می گیرند و کسی قدر زحمتتان را نمی داند. هنرتان را رو کنید تا آنهایی که رقیب و حسود هستند شکست بخورند. بلند شوید و لباس رزم بر تن کنید. شکست ناپذیر باشید تا بتوانید ویرانه ی دلتان را آباد نمائید و به آرزویتان برسید. '),
(346, 'من نه آن رندم که ترک شاهد و ساغر کنم\r\nمحتسب داند که من این کارها کمتر کنم\r\nمن که عیب توبه کاران کرده باشم بارها\r\nتوبه از می وقت گل دیوانه باشم گر کنم\r\nعشق دردانه ست و من غواص و دریا میکده\r\nسر فروبردم در آن جا تا کجا سر برکنم\r\nلاله ساغرگیر و نرگس مست و بر ما نام فسق\r\nداوری دارم بسی یا رب که را داور کنم\r\nبازکش یک دم عنان ای ترک شهرآشوب من\r\nتا ز اشک و چهره راهت پرزر و گوهر کنم\r\nمن که از یاقوت و لعل اشک دارم گنج ها\r\nکی نظر در فیض خورشید بلنداختر کنم\r\nچون صبا مجموعه گل را به آب لطف شست\r\nکجدلم خوان گر نظر بر صفحه دفتر کنم\r\nعهد و پیمان فلک را نیست چندان اعتبار\r\nعهد با پیمانه بندم شرط با ساغر کنم\r\nمن که دارم در گدایی گنج سلطانی به دست\r\nکی طمع در گردش گردون دون پرور کنم\r\nگر چه گردآلود فقرم شرم باد از همتم\r\nگر به آب چشمه خورشید دامن تر کنم\r\nعاشقان را گر در آتش می پسندد لطف دوست\r\nتنگ چشمم گر نظر در چشمه کوثر کنم\r\nدوش لعلش عشوه ای می داد حافظ را ولی\r\nمن نه آنم کز وی این افسانه ها باور کنم\r\n', 'سعی و تلاش خود را می کنید. بر این باورید که عیب هایتان را برطرف نمائید. هدف خود را مشخص کنید و بی گدار به آب نزنید. مشورت لازمه ی کار و گنج در کنار شماست ولی شما آن را نمی بینید، دقت کنید. کسانی که با شما قرارداد یا پیمان بسته اند به عهد خودشان پایبند نیستند. '),
(347, 'صنما با غم عشق تو چه تدبیر کنم\r\nتا به کی در غم تو ناله شبگیر کنم\r\nدل دیوانه از آن شد که نصیحت شنود\r\nمگرش هم ز سر زلف تو زنجیر کنم\r\nآن چه در مدت هجر تو کشیدم هیهات\r\nدر یکی نامه محال است که تحریر کنم\r\nبا سر زلف تو مجموع پریشانی خود\r\nکو مجالی که سراسر همه تقریر کنم\r\nآن زمان کآرزوی دیدن جانم باشد\r\nدر نظر نقش رخ خوب تو تصویر کنم\r\nگر بدانم که وصال تو بدین دست دهد\r\nدین و دل را همه دربازم و توفیر کنم\r\nدور شو از برم ای واعظ و بیهوده مگوی\r\nمن نه آنم که دگر گوش به تزویر کنم\r\nنیست امید صلاحی ز فساد حافظ\r\nچون که تقدیر چنین است چه تدبیر کنم\r\n', 'برای حل مشکلات خودتان هیچ تدبیری نمی کنید. دست به دعا برنداشته اید ولی کاری انجام نمی دهید. فرصتی به دست نمی آورید تا جبران کنید اما بدانید که مشکلات تان زمانی حل می شود که همت کنید و دلتان را صاف نمائید و ایمانتان را هم از دست ندهید. به حرف کسانی که می خواهند راه های نادرست پیش پایتان بگذارند گوش ندهید موفق می شوید. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(348, 'دیده دریا کنم و صبر به صحرا فکنم\r\nو اندر این کار دل خویش به دریا فکنم\r\nاز دل تنگ گنهکار برآرم آهی\r\nکآتش اندر گنه آدم و حوا فکنم\r\nمایه خوشدلی آن جاست که دلدار آن جاست\r\nمی کنم جهد که خود را مگر آن جا فکنم\r\nبگشا بند قبا ای مه خورشیدکلاه\r\nتا چو زلفت سر سودازده در پا فکنم\r\nخورده ام تیر فلک باده بده تا سرمست\r\nعقده دربند کمر ترکش جوزا فکنم\r\nجرعه جام بر این تخت روان افشانم\r\nغلغل چنگ در این گنبد مینا فکنم\r\nحافظا تکیه بر ایام چو سهو است و خطا\r\nمن چرا عشرت امروز به فردا فکنم\r\n', 'دل به دریا زده اید. برای جبران کارهای گذشته حاضرید هر اقدامی بکنید. زمانی وجدانتان راحت می شود که همه کارها را سر و سامان دهید. مشکلات زندگی باعث شده تا سرتان به سنگ بخورد. تصمیم بگیرید برای رسیدن به مقصود کاری کنید که همه انگشت به دهان بمانند. حالا دیگر خوب و بد را تشخیص می دهید. '),
(349, 'دوش سودای رخش گفتم ز سر بیرون کنم\r\nگفت کو زنجیر تا تدبیر این مجنون کنم\r\nقامتش را سرو گفتم سر کشید از من به خشم\r\nدوستان از راست می رنجد نگارم چون کنم\r\nنکته ناسنجیده گفتم دلبرا معذور دار\r\nعشوه ای فرمای تا من طبع را موزون کنم\r\nزردرویی می کشم زان طبع نازک بی گناه\r\nساقیا جامی بده تا چهره را گلگون کنم\r\nای نسیم منزل لیلی خدا را تا به کی\r\nربع را برهم زنم اطلال را جیحون کنم\r\nمن که ره بردم به گنج حسن بی پایان دوست\r\nصد گدای همچو خود را بعد از این قارون کنم\r\nای مه صاحب قران از بنده حافظ یاد کن\r\nتا دعای دولت آن حسن روزافزون کنم\r\n', 'مدام در حال فکر کردن هستید و می خواهید راهی پیدا کنید تا کسی را به فراموشی بسپارید. دیگر خسته و رنجور شده اید. اما باز هم نمی توانید از او دست بکشید. پس دست به دعا بردارید و از خدا بخواهید صبر و مقاومت شما را بیشتر کند تا بتوانید به منظور و نظر خودتان برسید. انشالله '),
(350, 'به عزم توبه سحر گفتم استخاره کنم\r\nبهار توبه شکن می رسد چه چاره کنم\r\nسخن درست بگویم نمی توانم دید\r\nکه می خورند حریفان و من نظاره کنم\r\nچو غنچه با لب خندان به یاد مجلس شاه\r\nپیاله گیرم و از شوق جامه پاره کنم\r\nبه دور لاله دماغ مرا علاج کنید\r\nگر از میانه بزم طرب کناره کنم\r\nز روی دوست مرا چون گل مراد شکفت\r\nحواله سر دشمن به سنگ خاره کنم\r\nگدای میکده ام لیک وقت مستی بین\r\nکه ناز بر فلک و حکم بر ستاره کنم\r\nمرا که نیست ره و رسم لقمه پرهیزی\r\nچرا ملامت رند شرابخواره کنم\r\nبه تخت گل بنشانم بتی چو سلطانی\r\nز سنبل و سمنش ساز طوق و یاره کنم\r\nز باده خوردن پنهان ملول شد حافظ\r\nبه بانگ بربط و نی رازش آشکاره کنم\r\n', 'برای چاره ی کار استخاره کنید. شما را از کاری منع کرده اند و اجازه نمی دهند سخن بگوئید. برای علاج کار خود فعلا ساکت باشید. این صبرتان باعث رسیدن به مراد می شود. سر دشمنانتان به سنگ می خورد و مقامتان بالا می رود. به وصال یار می رسید. '),
(351, 'حاشا که من به موسم گل ترک می کنم\r\nمن لاف عقل می زنم این کار کی کنم\r\nمطرب کجاست تا همه محصول زهد و علم\r\nدر کار چنگ و بربط و آواز نی کنم\r\nاز قیل و قال مدرسه حالی دلم گرفت\r\nیک چند نیز خدمت معشوق و می کنم\r\nکی بود در زمانه وفا جام می بیار\r\nتا من حکایت جم و کاووس کی کنم\r\nاز نامه سیاه نترسم که روز حشر\r\nبا فیض لطف او صد از این نامه طی کنم\r\nکو پیک صبح تا گله های شب فراق\r\nبا آن خجسته طالع فرخنده پی کنم\r\nاین جان عاریت که به حافظ سپرد دوست\r\nروزی رخش ببینم و تسلیم وی کنم\r\n', 'مرحله جدیدی از زندگیتان را آغاز می کنید و به رشد عقلی رسیده اید. پس ایمان و عملتان را نیز کامل کنید. از تزویر و ریا در اطرافتان ناراحتید. لطف خدا شامل حالتان شده و نامه ای با ارزش شما می دهند که بسیار خوشحال می شوید توشه ای هم برای آخرت خودتان اندوخته کنید. '),
(352, 'روزگاری شد که در میخانه خدمت می کنم\r\nدر لباس فقر کار اهل دولت می کنم\r\nتا کی اندر دام وصل آرم تذروی خوش خرام\r\nدر کمینم و انتظار وقت فرصت می کنم\r\nواعظ ما بوی حق نشنید بشنو کاین سخن\r\nدر حضورش نیز می گویم نه غیبت می کنم\r\nبا صبا افتان و خیزان می روم تا کوی دوست\r\nو از رفیقان ره استمداد همت می کنم\r\nخاک کویت زحمت ما برنتابد بیش از این\r\nلطف ها کردی بتا تخفیف زحمت می کنم\r\nزلف دلبر دام راه و غمزه اش تیر بلاست\r\nیاد دار ای دل که چندینت نصیحت می کنم\r\nدیده بدبین بپوشان ای کریم عیب پوش\r\nزین دلیری ها که من در کنج خلوت می کنم\r\nحافظم در مجلسی دردی کشم در محفلی\r\nبنگر این شوخی که چون با خلق صنعت می کنم\r\n', 'در کمین فرصت مناسبی هستید تا کار را یکسره کنید. حرف حق را بپذیرید و برای رسیدن به هدف از ریا دوری کنید. دوستان تان نیز شما را برای رسیدن به هدف یاری می کنند. از نصیحت بزرگان استفاده کنید. عیب دیگران را بپوشانید تا مردم نیز از عیب هایتان صرف نظر کنند. '),
(353, 'چو گل هر دم به بویت جامه در تن\r\nکنم چاک از گریبان تا به دامن\r\nتنت را دید گل گویی که در باغ\r\nچو مستان جامه را بدرید بر تن\r\nمن از دست غمت مشکل برم جان\r\nولی دل را تو آسان بردی از من\r\nبه قول دشمنان برگشتی از دوست\r\nنگردد هیچ کس دوست دشمن\r\nتنت در جامه چون در جام باده\r\nدلت در سینه چون در سیم آهن\r\nببار ای شمع اشک از چشم خونین\r\nکه شد سوز دلت بر خلق روشن\r\nمکن کز سینه ام آه جگرسوز\r\nبرآید همچو دود از راه روزن\r\nدلم را مشکن و در پا مینداز\r\nکه دارد در سر زلف تو مسکن\r\nچو دل در زلف تو بسته ست حافظ\r\nبدین سان کار او در پا میفکن\r\n', 'حاضرید برای رسیدن به نیات خود هر کاری بکنید. رسیدن به حاجات برای شما محال است ولی توکل به خدا کنید هیچ کار سختی نبوده که آسان نشود. از دوستان تان کمک بگیرید. دردتان را به دیگران هم بگوئید شاید به شما کمک کنند. دلتان نمی شکند و خدا نیز کمکتان می کند. '),
(354, 'افسر سلطان گل پیدا شد از طرف چمن\r\nمقدمش یا رب مبارک باد بر سرو و سمن\r\nخوش به جای خویشتن بود این نشست خسروی\r\nتا نشیند هر کسی اکنون به جای خویشتن\r\nخاتم جم را بشارت ده به حسن خاتمت\r\nکاسم اعظم کرد از او کوتاه دست اهرمن\r\nتا ابد معمور باد این خانه کز خاک درش\r\nهر نفس با بوی رحمان می وزد باد یمن\r\nشوکت پور پشنگ و تیغ عالمگیر او\r\nدر همه شهنامه ها شد داستان انجمن\r\nخنگ چوگانی چرخت رام شد در زیر زین\r\nشهسوارا چون به میدان آمدی گویی بزن\r\nجویبار ملک را آب روان شمشیر توست\r\nتو درخت عدل بنشان بیخ بدخواهان بکن\r\nبعد از این نشکفت اگر با نکهت خلق خوشت\r\nخیزد از صحرای ایذج نافه مشک ختن\r\nگوشه گیران انتظار جلوه خوش می کنند\r\nبرشکن طرف کلاه و برقع از رخ برفکن\r\nمشورت با عقل کردم گفت حافظ می بنوش\r\nساقیا می ده به قول مستشار موتمن\r\nای صبا بر ساقی بزم اتابک عرضه دار\r\nتا از آن جام زرافشان جرعه ای بخشد به من\r\n', 'گمشده ی خویش را خیلی زود شیدا می کنید و قدر آنچه را که پیدا کرده اید بدانید. به شما بشارت پیروزی می دهند و اسمتان آوازه ی خاص و عام خواهد شد و دوستان شیطان صفت شکست خواهند خورد. الان وقت کار و همت است، امتحان کنید عدالت را برپا سازید و در همه ی کارها به عقلتان رجوع کنید. '),
(355, 'خوشتر از فکر می و جام چه خواهد بودن\r\nتا ببینم که سرانجام چه خواهد بودن\r\nغم دل چند توان خورد که ایام نماند\r\nگو نه دل باش و نه ایام چه خواهد بودن\r\nمرغ کم حوصله را گو غم خود خور که بر او\r\nرحم آن کس که نهد دام چه خواهد بودن\r\nباده خور غم مخور و پند مقلد منیوش\r\nاعتبار سخن عام چه خواهد بودن\r\nدست رنج تو همان به که شود صرف به کام\r\nدانی آخر که به ناکام چه خواهد بودن\r\nپیر میخانه همی خواند معمایی دوش\r\nاز خط جام که فرجام چه خواهد بودن\r\nبردم از ره دل حافظ به دف و چنگ و غزل\r\nتا جزای من بدنام چه خواهد بودن\r\n', 'بسیار به فکر آینده ی خودتان هستید و از سرانجام کاری که می کنید بسیار می ترسید. کم حوصله اید، به خودتان رحم کنید، بی اعتمادی را کنار بگذارید. اگر برای حاجت خویش واقعاً تلاش کرده باشید نتیجه ی آن همان می شود که می خواهید. فرجام کارتان بسیار مبارک و عالی است. '),
(356, 'دانی که چیست دولت دیدار یار دیدن\r\nدر کوی او گدایی بر خسروی گزیدن\r\nاز جان طمع بریدن آسان بود ولیکن\r\nاز دوستان جانی مشکل توان بریدن\r\nخواهم شدن به بستان چون غنچه با دل تنگ\r\nوان جا به نیک نامی پیراهنی دریدن\r\nگه چون نسیم با گل راز نهفته گفتن\r\nگه سر عشقبازی از بلبلان شنیدن\r\nبوسیدن لب یار اول ز دست مگذار\r\nکآخر ملول گردی از دست و لب گزیدن\r\nفرصت شمار صحبت کز این دوراهه منزل\r\nچون بگذریم دیگر نتوان به هم رسیدن\r\nگویی برفت حافظ از یاد شاه یحیی\r\nیا رب به یادش آور درویش پروریدن\r\n', 'بر سر دو راهی قرار گرفته اید و تصمیم گیری برایتان بسیار مشکل شده است راه اول برای شما ثروت و مال به دنبال دارد ولی با دلی تنگ و راه دوم نیک نامی و عشق و وصال ولی بدون ثروت و مال. راهی را انتخاب کنید که به وصال یار بیانجامد. فرصت را غنیمت بدانید و بسم الله بگویید توکل به خدا کنید. '),
(357, 'منم که شهره شهرم به عشق ورزیدن\r\nمنم که دیده نیالودم به بد دیدن\r\nوفا کنیم و ملامت کشیم و خوش باشیم\r\nکه در طریقت ما کافریست رنجیدن\r\nبه پیر میکده گفتم که چیست راه نجات\r\nبخواست جام می و گفت عیب پوشیدن\r\nمراد دل ز تماشای باغ عالم چیست\r\nبه دست مردم چشم از رخ تو گل چیدن\r\nبه می پرستی از آن نقش خود زدم بر آب\r\nکه تا خراب کنم نقش خود پرستیدن\r\nبه رحمت سر زلف تو واثقم ور نه\r\nکشش چو نبود از آن سو چه سود کوشیدن\r\nعنان به میکده خواهیم تافت زین مجلس\r\nکه وعظ بی عملان واجب است نشنیدن\r\nز خط یار بیاموز مهر با رخ خوب\r\nکه گرد عارض خوبان خوش است گردیدن\r\nمبوس جز لب ساقی و جام می حافظ\r\nکه دست زهدفروشان خطاست بوسیدن\r\n', 'به دنبال راه نجاتی هستید. دیگر خسته شده اید. اطرافیانتان فقط عیوب شما را به رختان می کشند و هنرتان را نمی بینند ولی شما مهد خوبی ها هستید. با شهامت به مراد دلتان می رسید. چشم امیدتان خداست، بی طاقت نشوید، حرفی که می زنید عمل کنید. شما خوبی را به اطرافیانتان یاد می دهید. مواظب باشید خطا نکنید تا همه کارهایتان به باد فنا نرود. '),
(358, 'ای روی ماه منظر تو نوبهار حسن\r\nخال و خط تو مرکز حسن و مدار حسن\r\nدر چشم پرخمار تو پنهان فسون سحر\r\nدر زلف بی قرار تو پیدا قرار حسن\r\nماهی نتافت همچو تو از برج نیکویی\r\nسروی نخاست چون قدت از جویبار حسن\r\nخرم شد از ملاحت تو عهد دلبری\r\nفرخ شد از لطافت تو روزگار حسن\r\nاز دام زلف و دانه خال تو در جهان\r\nیک مرغ دل نماند نگشته شکار حسن\r\nدایم به لطف دایه طبع از میان جان\r\nمی پرورد به ناز تو را در کنار حسن\r\nگرد لبت بنفشه از آن تازه و تر است\r\nکآب حیات می خورد از جویبار حسن\r\nحافظ طمع برید که بیند نظیر تو\r\nدیار نیست جز رخت اندر دیار حسن\r\n', 'تو تمام خوبی های را در خود داری. چشمهایت بیانگر خوش و غم های دل توست و تو از خوبی برای خود برجی ساخته ای که کسی به پایت نمی رسد. روزگارت به خوشی می گذرد. عهد با دلبری بسته ای که بسیار فرخ و خوب است. کسی نیست که شیفته ی جمال معنویت نشود دائم به فکر بخشش هستی و برای همین پیر نمی شوی. '),
(359, 'گلبرگ را ز سنبل مشکین نقاب کن\r\nیعنی که رخ بپوش و جهانی خراب کن\r\nبفشان عرق ز چهره و اطراف باغ را\r\nچون شیشه های دیده ما پرگلاب کن\r\nایام گل چو عمر به رفتن شتاب کرد\r\nساقی به دور باده گلگون شتاب کن\r\nبگشا به شیوه نرگس پرخواب مست را\r\nو از رشک چشم نرگس رعنا به خواب کن\r\nبوی بنفشه بشنو و زلف نگار گیر\r\nبنگر به رنگ لاله و عزم شراب کن\r\nزان جا که رسم و عادت عاشق کشی توست\r\nبا دشمنان قدح کش و با ما عتاب کن\r\nهمچون حباب دیده به روی قدح گشای\r\nوین خانه را قیاس اساس از حباب کن\r\nحافظ وصال می طلبد از ره دعا\r\nیا رب دعای خسته دلان مستجاب کن\r\n', 'همگان مجذوب شما می شوند و تو کسی هستی که از حسن و خوبی کم نداری. چشمهایت همه را افسون می کند و بدون هیچ کلامی با همه حرف می زند. پیمانی که بسته ای بسیار مبارک و فرخنده است روزگارت روز به روز بهتر می شود. لطف و محبت کسی دائم شامل حالت می شود. به آرزویت می رسی و جوانتر می شوی. '),
(360, 'صبح است ساقیا قدحی پرشراب کن\r\nدور فلک درنگ ندارد شتاب کن\r\nزان پیشتر که عالم فانی شود خراب\r\nما را ز جام باده گلگون خراب کن\r\nخورشید می ز مشرق ساغر طلوع کرد\r\nگر برگ عیش می طلبی ترک خواب کن\r\nروزی که چرخ از گل ما کوزه ها کند\r\nزنهار کاسه سر ما پرشراب کن\r\nما مرد زهد و توبه و طامات نیستیم\r\nبا ما به جام باده صافی خطاب کن\r\nکار صواب باده پرستیست حافظا\r\nبرخیز و عزم جزم به کار صواب کن\r\n', 'بیشتر از این به مال و ثروتی که داری تکیه نکن و به آن مغرور نشو چون مال مثل خورشید از شرق طلوع می کند و از غرب افول می نماید یاد روزی باش که قرار است کوله باری از رفتارهایت را به آخرت ببری پس کارهای شایسته انجام بده به نیایش خدا بپرداز دلت را هم صاف کن که خدا با توست. '),
(361, 'ز در درآ و شبستان ما منور کن\r\nهوای مجلس روحانیان معطر کن\r\nاگر فقیه نصیحت کند که عشق مباز\r\nپیاله ای بدهش گو دماغ را تر کن\r\nبه چشم و ابروی جانان سپرده ام دل و جان\r\nبیا بیا و تماشای طاق و منظر کن\r\nستاره شب هجران نمی فشاند نور\r\nبه بام قصر برآ و چراغ مه برکن\r\nبگو به خازن جنت که خاک این مجلس\r\nبه تحفه بر سوی فردوس و عود مجمر کن\r\nاز این مزوجه و خرقه نیک در تنگم\r\nبه یک کرشمه صوفی وشم قلندر کن\r\nچو شاهدان چمن زیردست حسن تواند\r\nکرشمه بر سمن و جلوه بر صنوبر کن\r\nفضول نفس حکایت بسی کند ساقی\r\nتو کار خود مده از دست و می به ساغر کن\r\nحجاب دیده ادراک شد شعاع جمال\r\nبیا و خرگه خورشید را منور کن\r\nطمع به قند وصال تو حد ما نبود\r\nحوالتم به لب لعل همچو شکر کن\r\nلب پیاله ببوس آنگهی به مستان ده\r\nبدین دقیقه دماغ معاشران تر کن\r\nپس از ملازمت عیش و عشق مه رویان\r\nز کارها که کنی شعر حافظ از بر کن\r\n', 'صبر و مقاومت را از دیگران یاد بگیر مقاومت و صبر تو باعث پیروزیت می شود تلاش هایت بی اجر نمی مانند تو پاداش خود را خواهی گرفت فقط عجله کن الان وقت مقاومت است. خجالت را کنار بگذار و بر حسب قراری که گذاشته ای عمل کن حاجت خود را از خدا بخواه که اوست دعایت را مستجاب می کند. '),
(362, 'ای نور چشم من سخنی هست گوش کن\r\nچون ساغرت پر است بنوشان و نوش کن\r\nدر راه عشق وسوسه اهرمن بسیست\r\nپیش آی و گوش دل به پیام سروش کن\r\nبرگ نوا تبه شد و ساز طرب نماند\r\nای چنگ ناله برکش و ای دف خروش کن\r\nتسبیح و خرقه لذت مستی نبخشدت\r\nهمت در این عمل طلب از می فروش کن\r\nپیران سخن ز تجربه گویند گفتمت\r\nهان ای پسر که پیر شوی پند گوش کن\r\nبر هوشمند سلسله ننهاد دست عشق\r\nخواهی که زلف یار کشی ترک هوش کن\r\nبا دوستان مضایقه در عمر و مال نیست\r\nصد جان فدای یار نصیحت نیوش کن\r\nساقی که جامت از می صافی تهی مباد\r\nچشم عنایتی به من دردنوش کن\r\nسرمست در قبای زرافشان چو بگذری\r\nیک بوسه نذر حافظ پشمینه پوش کن\r\n', 'کار امروزتان را به فردا موکول نکنید. گاهی هوی نفس و شیطان بر شما غلبه کرده و از راه راست دور می شوید. ذکر و تسبیح خدا را زیاد به جای آورید. در کاری که می خواهید انجام بدهید اول همت کنید و اراده داشته باشید. از تجربه ی پیران استفاده کنید در ایم صورت است که از مال و ثروت بی بهره نمی مانید. چشم عنایت خداوند به شماست. '),
(363, 'کرشمه ای کن و بازار ساحری بشکن\r\nبه غمزه رونق و ناموس سامری بشکن\r\nبه باد ده سر و دستار عالمی یعنی\r\nکلاه گوشه به آیین سروری بشکن\r\nبه زلف گوی که آیین دلبری بگذار\r\nبه غمزه گوی که قلب ستمگری بشکن\r\nبرون خرام و ببر گوی خوبی از همه کس\r\nسزای حور بده رونق پری بشکن\r\nبه آهوان نظر شیر آفتاب بگیر\r\nبه ابروان دوتا قوس مشتری بشکن\r\nچو عطرسای شود زلف سنبل از دم باد\r\nتو قیمتش به سر زلف عنبری بشکن\r\nچو عندلیب فصاحت فروشد ای حافظ\r\nتو قدر او به سخن گفتن دری بشکن\r\n', 'کارهایتان به لطف خدا رونق می گیرد به شرط اینکه آئین راستی را پیشه کنید. شما در عمل خوب و بی غش از همه پیشی می گیرید و پاداش خودتان را از حوریان و پریان بهشتی می گیرید. صید خودتان را به دست می آورید و درهای ناامیدی را یکی پس از دیگری می شکنید. قدر یار خودتان را بدانید که در این راه کمک بسیار به شما کرده است. '),
(364, 'بالابلند عشوه گر نقش باز من\r\nکوتاه کرد قصه زهد دراز من\r\nدیدی دلا که آخر پیری و زهد و علم\r\nبا من چه کرد دیده معشوقه باز من\r\nمی ترسم از خرابی ایمان که می برد\r\nمحراب ابروی تو حضور نماز من\r\nگفتم به دلق زرق بپوشم نشان عشق\r\nغماز بود اشک و عیان کرد راز من\r\nمست است یار و یاد حریفان نمی کند\r\nذکرش به خیر ساقی مسکین نواز من\r\nیا رب کی آن صبا بوزد کز نسیم آن\r\nگردد شمامه کرمش کارساز من\r\nنقشی بر آب می زنم از گریه حالیا\r\nتا کی شود قرین حقیقت مجاز من\r\nبر خود چو شمع خنده زنان گریه می کنم\r\nتا با تو سنگ دل چه کند سوز و ساز من\r\nزاهد چو از نماز تو کاری نمی رود\r\nهم مستی شبانه و راز و نیاز من\r\nحافظ ز گریه سوخت بگو حالش ای صبا\r\nبا شاه دوست پرور دشمن گداز من\r\n', 'یک آن تصمیم نادرست تان باعث یک عمر پشیمانی شما شده است. به ایمان خویش تکیه کنید با گریه و زاری کردن کاری از پیش نمی برید. از چیزی نترسید دوباره یا علی گفته و شروع کنید. با داشتن قدم اول نسیم پیروزی وزیدن می کند تا حالا فکر می کردید نقشه هایتان عملی نیست ولی با کمی تدبیر به حقیقت می پیوندد. '),
(365, 'چون شوم خاک رهش دامن بیفشاند ز من\r\nور بگویم دل بگردان رو بگرداند ز من\r\nروی رنگین را به هر کس می نماید همچو گل\r\nور بگویم بازپوشان بازپوشاند ز من\r\nچشم خود را گفتم آخر یک نظر سیرش ببین\r\nگفت می خواهی مگر تا جوی خون راند ز من\r\nاو به خونم تشنه و من بر لبش تا چون شود\r\nکام بستانم از او یا داد بستاند ز من\r\nگر چو فرهادم به تلخی جان برآید باک نیست\r\nبس حکایت های شیرین باز می ماند ز من\r\nگر چو شمعش پیش میرم بر غمم خندان شود\r\nور برنجم خاطر نازک برنجاند ز من\r\nدوستان جان داده ام بهر دهانش بنگرید\r\nکو به چیزی مختصر چون باز می ماند ز من\r\nصبر کن حافظ که گر زین دست باشد درس غم\r\nعشق در هر گوشه ای افسانه ای خواند ز من\r\n', 'هر عمل خیری که انجام می دهید نتیجه ی خوبی نمی بینید. دستتان نمک ندارد گاهی آنقدر بی طاقت می شوید که می خواهید غیر از شر کار دیگری انجام ندهید شما عمل خیر و صواب خودتان را انجام دهید. خداست که پاداش شما را می دهد. صبر داشته باشید در آینده در عمل شما نقطه ی نجاتی برایتان پیدا می شود که خودتان باور نمی کنید. '),
(366, 'نکته ای دلکش بگویم خال آن مه رو ببین\r\nعقل و جان را بسته زنجیر آن گیسو ببین\r\nعیب دل کردم که وحشی وضع و هرجایی مباش\r\nگفت چشم شیرگیر و غنج آن آهو ببین\r\nحلقه زلفش تماشاخانه باد صباست\r\nجان صد صاحب دل آن جا بسته یک مو ببین\r\nعابدان آفتاب از دلبر ما غافلند\r\nای ملامتگو خدا را رو مبین آن رو ببین\r\nزلف دل دزدش صبا را بند بر گردن نهاد\r\nبا هواداران ره رو حیله هندو ببین\r\nاین که من در جست و جوی او ز خود فارغ شدم\r\nکس ندیده ست و نبیند مثلش از هر سو ببین\r\nحافظ ار در گوشه محراب می نالد رواست\r\nای نصیحتگو خدا را آن خم ابرو ببین\r\nاز مراد شاه منصور ای فلک سر برمتاب\r\nتیزی شمشیر بنگر قوت بازو ببین\r\n', 'شما فکر می کنید این چند صباح زندگی را باید خوش بود و خوش گذراند. هر قدر که به شما می گویند فکر آینده هم باشید می گویید هستم ولی غافلید. دوستان تان با حیله و نیرنگ از شما سوء استفاده می کنند ولی نمی فهمید روزی می رسد که دیگر از عیش و طرب سیر شده اید که دیگر آه در بساط ندارید پس سرکش نباشید از زور قوت بازویتان الان استفاده کنید وقت برای خوشی زیاد است. '),
(367, 'شراب لعل کش و روی مه جبینان بین\r\nخلاف مذهب آنان جمال اینان بین\r\nبه زیر دلق ملمع کمندها دارند\r\nدرازدستی این کوته آستینان بین\r\nبه خرمن دو جهان سر فرو نمی آرند\r\nدماغ و کبر گدایان و خوشه چینان بین\r\nبهای نیم کرشمه هزار جان طلبند\r\nنیاز اهل دل و ناز نازنینان بین\r\nحقوق صحبت ما را به باد داد و برفت\r\nوفای صحبت یاران و همنشینان بین\r\nاسیر عشق شدن چاره خلاص من است\r\nضمیر عاقبت اندیش پیش بینان بین\r\nکدورت از دل حافظ ببرد صحبت دوست\r\nصفای همت پاکان و پاکدینان بین\r\n', 'ظاهرتان خلاف آن چیزی است که درونتان می باشد. خوبی ها و هنرهایتان را پنهان کرده اید. همه فکر می کنند انسان متکبر و مغروری هستید اما بر عکس به عهدهایی که بسته اید عمل می کنید. یاران و همنشینان خود را دوست دارید. به زودی از بند اسارت رها می شوید و به عافیت کامل می رسید در این زمان است که کینه و کدورت ها را دور می ریزید و دلتان را پاک می کنید. '),
(368, 'می فکن بر صف رندان نظری بهتر از این\r\nبر در میکده می کن گذری بهتر از این\r\nدر حق من لبت این لطف که می فرماید\r\nسخت خوب است ولیکن قدری بهتر از این\r\nآن که فکرش گره از کار جهان بگشاید\r\nگو در این کار بفرما نظری بهتر از این\r\nناصحم گفت که جز غم چه هنر دارد عشق\r\nبرو ای خواجه عاقل هنری بهتر از این\r\nدل بدان رود گرامی چه کنم گر ندهم\r\nمادر دهر ندارد پسری بهتر از این\r\nمن چو گویم که قدح نوش و لب ساقی بوس\r\nبشنو از من که نگوید دگری بهتر از این\r\nکلک حافظ شکرین میوه نباتیست به چین\r\nکه در این باغ نبینی ثمری بهتر از این\r\n', 'همیشه کارهایتان را نیمه کاره رها می کنید و به آخر نمی رسانید. هر چند که فکر و اندیشه تان بسیار عالیست و نتیجه ای بس موفقیت آمیز دارد ولی ناامیدی بر شما چیره شده و بی صبرید. به هنر و عقل خودتان رجوع کنید که روزگار موفق تر از تو به خود ندیده است. کارتان را به آخر برسانید که نتیجه ای بس عالی در پی دارد. '),
(369, 'به جان پیر خرابات و حق صحبت او\r\nکه نیست در سر من جز هوای خدمت او\r\nبهشت اگر چه نه جای گناهکاران است\r\nبیار باده که مستظهرم به همت او\r\nچراغ صاعقه آن سحاب روشن باد\r\nکه زد به خرمن ما آتش محبت او\r\nبر آستانه میخانه گر سری بینی\r\nمزن به پای که معلوم نیست نیت او\r\nبیا که دوش به مستی سروش عالم غیب\r\nنوید داد که عام است فیض رحمت او\r\nمکن به چشم حقارت نگاه در من مست\r\nکه نیست معصیت و زهد بی مشیت او\r\nنمی کند دل من میل زهد و توبه ولی\r\nبه نام خواجه بکوشیم و فر دولت او\r\nمدام خرقه حافظ به باده در گرو است\r\nمگر ز خاک خرابات بود فطرت او\r\n', 'حق صحبت با پیران را به جا بیاور و به تجربه های آنها بهائی قائل باش. آنها تو را به جایی هدایت می کنند که آخرش بهشت برین است. شانس به تو رو کرده آن را از دشت نده. به بخت خویش لگد نزن. نوید خبری را به تو می دهند که سرشار از رحمت است. خودت همتی داشته باش که فر دولت در انتظار تو است. '),
(370, 'گفتا برون شدی به تماشای ماه نو\r\nاز ماه ابروان منت شرم باد رو\r\nعمریست تا دلت ز اسیران زلف ماست\r\nغافل ز حفظ جانب یاران خود مشو\r\nمفروش عطر عقل به هندوی زلف ما\r\nکان جا هزار نافه مشکین به نیم جو\r\nتخم وفا و مهر در این کهنه کشته زار\r\nآن گه عیان شود که بود موسم درو\r\nساقی بیار باده که رمزی بگویمت\r\nاز سر اختران کهن سیر و ماه نو\r\nشکل هلال هر سر مه می دهد نشان\r\nاز افسر سیامک و ترک کلاه زو\r\nحافظ جناب پیر مغان مامن وفاست\r\nدرس حدیث عشق بر او خوان و ز او شنو\r\n', 'دوستانت به تو وفا نکرده اند و کارهایی را که برایشان انجام داده ای به نیم جو هم ارزش قائل نیستند ولی شما اجر و ثواب در راه خدا را در جایی دیگر دریافت می کنید. به سفری پرخیر و برکت می روید. گم شده ی بخت خویش را در آن سفر می یابید و درس عشق را در ان سفر می آموزید. انشاالله '),
(371, 'مزرع سبز فلک دیدم و داس مه نو\r\nیادم از کشته خویش آمد و هنگام درو\r\nگفتم ای بخت بخفتیدی و خورشید دمید\r\nگفت با این همه از سابقه نومید مشو\r\nگر روی پاک و مجرد چو مسیحا به فلک\r\nاز چراغ تو به خورشید رسد صد پرتو\r\nتکیه بر اختر شب دزد مکن کاین عیار\r\nتاج کاووس ببرد و کمر کیخسرو\r\nگوشوار زر و لعل ار چه گران دارد گوش\r\nدور خوبی گذران است نصیحت بشنو\r\nچشم بد دور ز خال تو که در عرصه حسن\r\nبیدقی راند که برد از مه و خورشید گرو\r\nآسمان گو مفروش این عظمت کاندر عشق\r\nخرمن مه به جوی خوشه پروین به دو جو\r\nآتش زهد و ریا خرمن دین خواهد سوخت\r\nحافظ این خرقه پشمینه بینداز و برو\r\n', 'ناامید نباشید. هر آنچه که تلاش کرده اید به زودی نتیجه و مزد آن را دریافت کنید و دوباره زنده می شوید. تکیه بر شب و تاریکی نکنید. به روزها و خورشید درخشان فکر کنید. نصیحت بزرگان را آویزه ی گوشتان قرار دهید. ستاره ی دولت تان طلوع کرده و چون خوشه ی پروین می درخشد. '),
(372, 'ای آفتاب آینه دار جمال تو\r\nمشک سیاه مجمره گردان خال تو\r\nصحن سرای دیده بشستم ولی چه سود\r\nکاین گوشه نیست درخور خیل خیال تو\r\nدر اوج ناز و نعمتی ای پادشاه حسن\r\nیا رب مباد تا به قیامت زوال تو\r\nمطبوعتر ز نقش تو صورت نبست باز\r\nطغرانویس ابروی مشکین مثال تو\r\nدر چین زلفش ای دل مسکین چگونه ای\r\nکآشفته گفت باد صبا شرح حال تو\r\nبرخاست بوی گل ز در آشتی درآی\r\nای نوبهار ما رخ فرخنده فال تو\r\nتا آسمان ز حلقه به گوشان ما شود\r\nکو عشوه ای ز ابروی همچون هلال تو\r\nتا پیش بخت بازروم تهنیت کنان\r\nکو مژده ای ز مقدم عید وصال تو\r\nاین نقطه سیاه که آمد مدار نور\r\nعکسیست در حدیقه بینش ز خال تو\r\nدر پیش شاه عرض کدامین جفا کنم\r\nشرح نیازمندی خود یا ملال تو\r\nحافظ در این کمند سر سرکشان بسیست\r\nسودای کج مپز که نباشد مجال تو\r\n', 'از زمانی که به دنیا آمدید تا آخر عمر خویش روی بدی را نخواهید دید. چهره و طینت زیبایتان ورد زبانهاست پس چرا غمگین شده اید و اینقدر آشفته حالید. دست از قهر و کینه بردارید که فالتان بسیار فرخنده است. پیشقدم شوید، مژده ای به شما می دهند و حق وفا را به جا آورید. '),
(373, 'ای خونبهای نافه چین خاک راه تو\r\nخورشید سایه پرور طرف کلاه تو\r\nنرگس کرشمه می برد از حد برون خرام\r\nای من فدای شیوه چشم سیاه تو\r\nخونم بخور که هیچ ملک با چنان جمال\r\nاز دل نیایدش که نویسد گناه تو\r\nآرام و خواب خلق جهان را سبب تویی\r\nزان شد کنار دیده و دل تکیه گاه تو\r\nبا هر ستاره ای سر و کار است هر شبم\r\nاز حسرت فروغ رخ همچو ماه تو\r\nیاران همنشین همه از هم جدا شدند\r\nماییم و آستانه دولت پناه تو\r\nحافظ طمع مبر ز عنایت که عاقبت\r\nآتش زند به خرمن غم دود آه تو\r\n', 'از سر جنگ بر خواسته اید و با خوبی ها می جنگید. از راه خویش برگردید. شما مرد جنگ و خونریزی نیستید. درونتان مملو از آرامش .و آشتی است و همه از شما تقلید می کنند. در حسرت چیزی هستید می خواهید دوباره همه را به دور هم جمع کنید. رسیدن به مقصود در یک قدمی شماست کمی صبر کنید. '),
(374, 'ای قبای پادشاهی راست بر بالای تو\r\nزینت تاج و نگین از گوهر والای تو\r\nآفتاب فتح را هر دم طلوعی می دهد\r\nاز کلاه خسروی رخسار مه سیمای تو\r\nجلوه گاه طایر اقبال باشد هر کجا\r\nسایه اندازد همای چتر گردون سای تو\r\nاز رسوم شرع و حکمت با هزاران اختلاف\r\nنکته ای هرگز نشد فوت از دل دانای تو\r\nآب حیوانش ز منقار بلاغت می چکد\r\nطوطی خوش لهجه یعنی کلک شکرخای تو\r\nگر چه خورشید فلک چشم و چراغ عالم است\r\nروشنایی بخش چشم اوست خاک پای تو\r\nآن چه اسکندر طلب کرد و ندادش روزگار\r\nجرعه ای بود از زلال جام جان افزای تو\r\nعرض حاجت در حریم حضرتت محتاج نیست\r\nراز کس مخفی نماند با فروغ رای تو\r\nخسروا پیرانه سر حافظ جوانی می کند\r\nبر امید عفو جان بخش گنه فرسای تو\r\n', 'شما لایق هر آنچه که دارید و در آینده به دست می آورید هستید. آفتاب برای شما هیچ وقت غروب نمی کند. همیشه جوان می مانید و فاتح اید. همای سعادت بالای سرتان در حال پرواز است. بسیار دانا و زیرکید. زبانتان جذاب است. چشم چراغ همه اید فقط کافی است حاجتی طلب کنید آنی مورد قبول واقع می شود. '),
(375, 'تاب بنفشه می دهد طره مشک سای تو\r\nپرده غنچه می درد خنده دلگشای تو\r\nای گل خوش نسیم من بلبل خویش را مسوز\r\nکز سر صدق می کند شب همه شب دعای تو\r\nمن که ملول گشتمی از نفس فرشتگان\r\nقال و مقال عالمی می کشم از برای تو\r\nدولت عشق بین که چون از سر فقر و افتخار\r\nگوشه تاج سلطنت می شکند گدای تو\r\nخرقه زهد و جام می گر چه نه درخور همند\r\nاین همه نقش می زنم از جهت رضای تو\r\nشور شراب عشق تو آن نفسم رود ز سر\r\nکاین سر پرهوس شود خاک در سرای تو\r\nشاهنشین چشم من تکیه گه خیال توست\r\nجای دعاست شاه من بی تو مباد جای تو\r\nخوش چمنیست عارضت خاصه که در بهار حسن\r\nحافظ خوش کلام شد مرغ سخنسرای تو\r\n', 'کم محلی و بی اعتنایی دیگر کافی است چون خودتان هم ناراحت می شوید. حالا وقت دعا و طلب دولت کردن است. وقت تلاش برای رسیدن به مقصد است. وقت آن است که با دیگران هم نفس شده و به هم کمک کنید تا زودتر به حاجتتان برسید. '),
(376, 'مرا چشمیست خون افشان ز دست آن کمان ابرو\r\nجهان بس فتنه خواهد دید از آن چشم و از آن ابرو\r\nغلام چشم آن ترکم که در خواب خوش مستی\r\nنگارین گلشنش روی است و مشکین سایبان ابرو\r\nهلالی شد تنم زین غم که با طغرای ابرویش\r\nکه باشد مه که بنماید ز طاق آسمان ابرو\r\nرقیبان غافل و ما را از آن چشم و جبین هر دم\r\nهزاران گونه پیغام است و حاجب در میان ابرو\r\nروان گوشه گیران را جبینش طرفه گلزاریست\r\nکه بر طرف سمن زارش همی گردد چمان ابرو\r\nدگر حور و پری را کس نگوید با چنین حسنی\r\nکه این را این چنین چشم است و آن را آن چنان ابرو\r\nتو کافردل نمی بندی نقاب زلف و می ترسم\r\nکه محرابم بگرداند خم آن دلستان ابرو\r\nاگر چه مرغ زیرک بود حافظ در هواداری\r\nبه تیر غمزه صیدش کرد چشم آن کمان ابرو\r\n', 'به خاطر مسائل مادی از خداوند دور شده اید. هر چند که تلاش کرده اید اما به جای اینکه به هدف نزدیکتر شوید از آن دور شده اید و این نشانه ی سست شدن ایمانتان است. باز هم به خدا توکل کنید. راه راست برایتان روشن می شود و حسودان و رقیبان شکست می خورند. '),
(377, 'خط عذار یار که بگرفت ماه از او\r\nخوش حلقه ایست لیک به در نیست راه از او\r\nابروی دوست گوشه محراب دولت است\r\nآن جا بمال چهره و حاجت بخواه از او\r\nای جرعه نوش مجلس جم سینه پاک دار\r\nکآیینه ایست جام جهان بین که آه از او\r\nکردار اهل صومعه ام کرد می پرست\r\nاین دود بین که نامه من شد سیاه از او\r\nسلطان غم هر آن چه تواند بگو بکن\r\nمن برده ام به باده فروشان پناه از او\r\nساقی چراغ می به ره آفتاب دار\r\nگو برفروز مشعله صبحگاه از او\r\nآبی به روزنامه اعمال ما فشان\r\nباشد توان سترد حروف گناه از او\r\nحافظ که ساز مطرب عشاق ساز کرد\r\nخالی مباد عرصه این بزمگاه از او\r\nآیا در این خیال که دارد گدای شهر\r\nروزی بود که یاد کند پادشاه از او\r\n', 'برای گشایش در کار خود از دوستان تان کمک بگیرید هر چند که می ترسید آنها دست رد به سینه ی سما بزنند. اخلاق و رفتار پسندیده تان اعتبار شما را صد چندان نموده است. به خدا پناه ببرید از شر شیطان که راه پیشرفت برایتان باز است. بسم الله بگوئید و قدم بگذارید و از یاد خدا غافل نشوید. '),
(378, 'گلبن عیش می دمد ساقی گلعذار کو\r\nباد بهار می وزد باده خوشگوار کو\r\nهر گل نو ز گلرخی یاد همی کند ولی\r\nگوش سخن شنو کجا دیده اعتبار کو\r\nمجلس بزم عیش را غالیه مراد نیست\r\nای دم صبح خوش نفس نافه زلف یار کو\r\nحسن فروشی گلم نیست تحمل ای صبا\r\nدست زدم به خون دل بهر خدا نگار کو\r\nشمع سحرگهی اگر لاف ز عارض تو زد\r\nخصم زبان دراز شد خنجر آبدار کو\r\nگفت مگر ز لعل من بوسه نداری آرزو\r\nمردم از این هوس ولی قدرت و اختیار کو\r\nحافظ اگر چه در سخن خازن گنج حکمت است\r\nاز غم روزگار دون طبع سخن گزار کو\r\n', 'با صدای بلند اعتراض کنید که چرا قدرت و اختیار را از شما گرفته اند. با یاد و خیال پردازی زنده اید. فکر می کنید پیش هیچ کسی سخنانتان اعتبار ندارد. مسائل روزمره و دنیوی راضی تان نمی کند به دنبال مراد و هدف که خودتان می دانید چیست می روید باید مشکلات را تحمل کنید تا به مرادتان برسید. '),
(379, 'ای پیک راستان خبر یار ما بگو\r\nاحوال گل به بلبل دستان سرا بگو\r\nما محرمان خلوت انسیم غم مخور\r\nبا یار آشنا سخن آشنا بگو\r\nبرهم چو می زد آن سر زلفین مشکبار\r\nبا ما سر چه داشت ز بهر خدا بگو\r\nهر کس که گفت خاک در دوست توتیاست\r\nگو این سخن معاینه در چشم ما بگو\r\nآن کس که منع ما ز خرابات می کند\r\nگو در حضور پیر من این ماجرا بگو\r\nگر دیگرت بر آن در دولت گذر بود\r\nبعد از ادای خدمت و عرض دعا بگو\r\nهر چند ما بدیم تو ما را بدان مگیر\r\nشاهانه ماجرای گناه گدا بگو\r\nبر این فقیر نامه آن محتشم بخوان\r\nبا این گدا حکایت آن پادشا بگو\r\nجان ها ز دام زلف چو بر خاک می فشاند\r\nبر آن غریب ما چه گذشت ای صبا بگو\r\nجان پرور است قصه ارباب معرفت\r\nرمزی برو بپرس حدیثی بیا بگو\r\nحافظ گرت به مجلس او راه می دهند\r\nمی نوش و ترک زرق ز بهر خدا بگو\r\n', 'خوش به حال شما که دوستان و اطرافیانتان واقعاً همراه و محرم رازتان می باشند. پس چرا ناراحتید و همه را نگران کرده اید. کسی از شما عنایت و یاری دارد در حق او کوتاهی نکنید که چند برابر بخشش تان در جایی دیگر جبران می شود. '),
(380, 'خنک نسیم معنبر شمامه ای دلخواه\r\nکه در هوای تو برخاست بامداد پگاه\r\nدلیل راه شو ای طایر خجسته لقا\r\nکه دیده آب شد از شوق خاک آن درگاه\r\nبه یاد شخص نزارم که غرق خون دل است\r\nهلال را ز کنار افق کنید نگاه\r\nمنم که بی تو نفس می کشم زهی خجلت\r\nمگر تو عفو کنی ور نه چیست عذر گناه\r\nز دوستان تو آموخت در طریقت مهر\r\nسپیده دم که صبا چاک زد شعار سیاه\r\nبه عشق روی تو روزی که از جهان بروم\r\nز تربتم بدمد سرخ گل به جای گیاه\r\nمده به خاطر نازک ملالت از من زود\r\nکه حافظ تو خود این لحظه گفت بسم الله\r\n', 'آنچه خواسته ای و دلخواه توست به دستت می رسد و چرخ فلک آینده ای بسیار مبارک و عالی برایت رقم زده است و انتظار به سر رسیده، خجالت را کنار بگذار و از شوق اشک شادی بریز. با علی بگو و برای حفظ حاجت خویش و آینده ای روشن کینه ها را کنار بگذار و حسودان و رقیبان را عفو کن. '),
(381, 'عیشم مدام است از لعل دلخواه\r\nکارم به کام است الحمدلله\r\nای بخت سرکش تنگش به بر کش\r\nگه جام زر کش گه لعل دلخواه\r\nما را به رندی افسانه کردند\r\nپیران جاهل شیخان گمراه\r\nاز دست زاهد کردیم توبه\r\nو از فعل عابد استغفرالله\r\nجانا چه گویم شرح فراقت\r\nچشمی و صد نم جانی و صد آه\r\nکافر مبیناد این غم که دیده ست\r\nاز قامتت سرو از عارضت ماه\r\nشوق لبت برد از یاد حافظ\r\nدرس شبانه ورد سحرگاه\r\n', 'فکر می کنید همه اشتباه می کنند و فقط شما هستید که راست می گوئید. فکر می کنید بخت با شما یار است ولی سخت در اشتباهید. شما از هر آنچه که باعث خوشبختی تان می شود دورید پس تلاش کنید. عبادت و دعا کنید تا به خوشبختی حقیقی برسید. راه پر فراز و نشیب است تحمل کنید. '),
(382, 'گر تیغ بارد در کوی آن ماه\r\nگردن نهادیم الحکم لله\r\nآیین تقوا ما نیز دانیم\r\nلیکن چه چاره با بخت گمراه\r\nما شیخ و واعظ کمتر شناسیم\r\nیا جام باده یا قصه کوتاه\r\nمن رند و عاشق در موسم گل\r\nآن گاه توبه استغفرالله\r\nمهر تو عکسی بر ما نیفکند\r\nآیینه رویا آه از دلت آه\r\nالصبر مر و العمر فان\r\nیا لیت شعری حتام القاه\r\nحافظ چه نالی گر وصل خواهی\r\nخون بایدت خورد در گاه و بی گاه\r\n', 'بسیار انسان مصممی هستید حتی اگر سنگ هم از آسمان ببارد کاری را که اراده کرده اید به پایان می رسانید. در برابر حکم الهی تسلیم محض هستید. روش تان راه درستی است برای همین خیلی زود به نتیجه می رسید. در برابر گناه می ایستید و شیطان را لعنت می کنید. برای رسیدن به حاجت باید راهی سخت و دشوار را تحمل کنید. '),
(383, 'وصال او ز عمر جاودان به\r\nخداوندا مرا آن ده که آن به\r\nبه شمشیرم زد و با کس نگفتم\r\nکه راز دوست از دشمن نهان به\r\nبه داغ بندگی مردن بر این در\r\nبه جان او که از ملک جهان به\r\nخدا را از طبیب من بپرسید\r\nکه آخر کی شود این ناتوان به\r\nگلی کان پایمال سرو ما گشت\r\nبود خاکش ز خون ارغوان به\r\nبه خلدم دعوت ای زاهد مفرما\r\nکه این سیب زنخ زان بوستان به\r\nدلا دایم گدای کوی او باش\r\nبه حکم آن که دولت جاودان به\r\nجوانا سر متاب از پند پیران\r\nکه رای پیر از بخت جوان به\r\nشبی می گفت چشم کس ندیده ست\r\nز مروارید گوشم در جهان به\r\nاگر چه زنده رود آب حیات است\r\nولی شیراز ما از اصفهان به\r\nسخن اندر دهان دوست شکر\r\nولیکن گفته حافظ از آن به\r\n', 'رضایتان همیشه به رضای خدا باشد. با زور شمشیر به جایی نمی رسید. برای درمان دل بیمارتان از خداوند شفا بطلبید. فقط از خدا مرادتان را طلب کنید که دوای دردتان نزد اوست. عمرتان طولانی است، کمی صبر و تحمل کنید. ناامید نشوید دعاهایتان پایمال نمی شود. '),
(384, 'من ترک عشق شاهد و ساغر نمی کنم\r\nصد بار توبه کردم و دیگر نمی کنم\r\nباغ بهشت و سایه طوبی و قصر و حور\r\nبا خاک کوی دوست برابر نمی کنم\r\nتلقین و درس اهل نظر یک اشارت است\r\nگفتم کنایتی و مکرر نمی کنم\r\nهرگز نمی شود ز سر خود خبر مرا\r\nتا در میان میکده سر بر نمی کنم\r\nناصح به طعن گفت که رو ترک عشق کن\r\nمحتاج جنگ نیست برادر نمی کنم\r\nاین تقواام تمام که با شاهدان شهر\r\nناز و کرشمه بر سر منبر نمی کنم\r\nحافظ جناب پیر مغان جای دولت است\r\nمن ترک خاک بوسی این در نمی کنم\r\n', 'در کاری که می خواهید انجام دهید بسیار پافشاری می کنید و از خود نیز شجاعت و شهامت به خرج می دهید. خودتان می توانید بدون کمک، همه ی کارها را سر و سامان ببخشید و کارهای بزرگ انجام دهید از چیزی نترسید. در همه حال خداوند به شما کمک می کند و شما بر همه ی دشمنانتان غلبه می کنید. '),
(385, 'به مژگان سیه کردی هزاران رخنه در دینم\r\nبیا کز چشم بیمارت هزاران درد برچینم\r\nالا ای همنشین دل که یارانت برفت از یاد\r\nمرا روزی مباد آن دم که بی یاد تو بنشینم\r\nجهان پیر است و بی بنیاد از این فرهادکش فریاد\r\nکه کرد افسون و نیرنگش ملول از جان شیرینم\r\nز تاب آتش دوری شدم غرق عرق چون گل\r\nبیار ای باد شبگیری نسیمی زان عرق چینم\r\nجهان فانی و باقی فدای شاهد و ساقی\r\nکه سلطانی عالم را طفیل عشق می بینم\r\nاگر بر جای من غیری گزیند دوست حاکم اوست\r\nحرامم باد اگر من جان به جای دوست بگزینم\r\nصباح الخیر زد بلبل کجایی ساقیا برخیز\r\nکه غوغا می کند در سر خیال خواب دوشینم\r\nشب رحلت هم از بستر روم در قصر حورالعین\r\nاگر در وقت جان دادن تو باشی شمع بالینم\r\nحدیث آرزومندی که در این نامه ثبت افتاد\r\nهمانا بی غلط باشد که حافظ داد تلقینم\r\n', 'بسیار انسان باوفایی هستید هر چند که روزگار و دوستان تان به شما جفا می کنند ولی باز هم مرام خودتان را حفظ کرده و برای رسیدن به اهداف خودتان کوشش و اهتمام زیادی می کنید. مراقب باشید گول شیطان را نخورید و به جای بهشت سر از جهنم در نیاورید. خوب می دانید که دنیا فانی است پس ارزش گناه کردن را ندارد. '),
(386, 'حالیا مصلحت وقت در آن می بینم\r\nکه کشم رخت به میخانه و خوش بنشینم\r\nجام می گیرم و از اهل ریا دور شوم\r\nیعنی از اهل جهان پاکدلی بگزینم\r\nجز صراحی و کتابم نبود یار و ندیم\r\nتا حریفان دغا را به جهان کم بینم\r\nسر به آزادگی از خلق برآرم چون سرو\r\nگر دهد دست که دامن ز جهان درچینم\r\nبس که در خرقه آلوده زدم لاف صلاح\r\nشرمسار از رخ ساقی و می رنگینم\r\nسینه تنگ من و بار غم او هیهات\r\nمرد این بار گران نیست دل مسکینم\r\nمن اگر رند خراباتم و گر زاهد شهر\r\nاین متاعم که همی بینی و کمتر زینم\r\nبنده آصف عهدم دلم از راه مبر\r\nکه اگر دم زنم از چرخ بخواهد کینم\r\nبر دلم گرد ستم هاست خدایا مپسند\r\nکه مکدر شود آیینه مهرآیینم\r\n', 'بسیار انسان آزاده و نترسی هستید. هیچ وقت کاری نمی کنید که باعث سرافکندگیتان شود. همیشه در صدد رفع عیوب و اشکالات خودتان می باشید. غمی در دل دارید که بسیار سنگین است فقط از خدا بخواهید تا ستم هایی که به شما روا شده فراموش گردد و دلتان دوباره مالامال از مهر و دوستی شود. '),
(387, 'گرم از دست برخیزد که با دلدار بنشینم\r\nز جام وصل می نوشم ز باغ عیش گل چینم\r\nشراب تلخ صوفی سوز بنیادم بخواهد برد\r\nلبم بر لب نه ای ساقی و بستان جان شیرینم\r\nمگر دیوانه خواهم شد در این سودا که شب تا روز\r\nسخن با ماه می گویم پری در خواب می بینم\r\nلبت شکر به مستان داد و چشمت می به میخواران\r\nمنم کز غایت حرمان نه با آنم نه با اینم\r\nچو هر خاکی که باد آورد فیضی برد از انعامت\r\nز حال بنده یاد آور که خدمتگار دیرینم\r\nنه هر کو نقش نظمی زد کلامش دلپذیر افتد\r\nتذرو طرفه من گیرم که چالاک است شاهینم\r\nاگر باور نمی داری رو از صورتگر چین پرس\r\nکه مانی نسخه می خواهد ز نوک کلک مشکینم\r\nوفاداری و حق گویی نه کار هر کسی باشد\r\nغلام آصف ثانی جلال الحق و الدینم\r\nرموز مستی و رندی ز من بشنو نه از واعظ\r\nکه با جام و قدح هر دم ندیم ماه و پروینم\r\n', 'رشته ی کار از دستتان خارج شده است. کار امروزتان را به فردا موکول نکنید، هر چند می دانید راهی را که انتخاب کردهاید بسیار سخت و پر فراز و نشیب است ولی به خاطر نتیجه ی شیرینش تحمل می کنید. رحمت خداوند شامل حالتان می شود و خیلی زود به مراد دلتان می رسید. و این از وفا و صداقت ایمان و حق گویی تان می باشد. '),
(388, 'در خرابات مغان نور خدا می بینم\r\nاین عجب بین که چه نوری ز کجا می بینم\r\nجلوه بر من مفروش ای ملک الحاج که تو\r\nخانه می بینی و من خانه خدا می بینم\r\nخواهم از زلف بتان نافه گشایی کردن\r\nفکر دور است همانا که خطا می بینم\r\nسوز دل اشک روان آه سحر ناله شب\r\nاین همه از نظر لطف شما می بینم\r\nهر دم از روی تو نقشی زندم راه خیال\r\nبا که گویم که در این پرده چه ها می بینم\r\nکس ندیده ست ز مشک ختن و نافه چین\r\nآن چه من هر سحر از باد صبا می بینم\r\nدوستان عیب نظربازی حافظ مکنید\r\nکه من او را ز محبان شما می بینم\r\n', 'انسان آینده نگر و عاقلی هستید. همیشه می گویید باید دید در آینده چه پیش می آید و نتیجه ی کار چه می شود. کاری را از روی هوس انجام نمی دهید اگر آن کار همراه با درد و رنج باشد ولی می دانید که در آینده برای شما نتیجه ای بسیار خوب و عالی در پی دارد. دوستان تان شما را مسخره می کنند ولی اعتنا نکنید و کار خودتان را انجام دهید. '),
(389, 'غم زمانه که هیچش کران نمی بینم\r\nدواش جز می چون ارغوان نمی بینم\r\nبه ترک خدمت پیر مغان نخواهم گفت\r\nچرا که مصلحت خود در آن نمی بینم\r\nز آفتاب قدح ارتفاع عیش بگیر\r\nچرا که طالع وقت آن چنان نمی بینم\r\nنشان اهل خدا عاشقیست با خود دار\r\nکه در مشایخ شهر این نشان نمی بینم\r\nبدین دو دیده حیران من هزار افسوس\r\nکه با دو آینه رویش عیان نمی بینم\r\nقد تو تا بشد از جویبار دیده من\r\nبه جای سرو جز آب روان نمی بینم\r\nدر این خمار کسم جرعه ای نمی بخشد\r\nببین که اهل دلی در میان نمی بینم\r\nنشان موی میانش که دل در او بستم\r\nز من مپرس که خود در میان نمی بینم\r\nمن و سفینه حافظ که جز در این دریا\r\nبضاعت سخن درفشان نمی بینم\r\n', 'چرا فکر می کنید غم دلتان را پایانی نیست دوای درد شما با خدا بودن است، راز و نیاز کردن با خداوند از درد دلتان می کاهد. افسوس گذشته را می خورید که هیچ فایده ای هم ندارد. تنها شده اید. راهتان را گم کرده اید. بلند شوید و مثل آبی روان به جنب و جوش درآیید و خودتان مشکلات تان را حل کنید. انشاالله '),
(390, 'خرم آن روز کز این منزل ویران بروم\r\nراحت جان طلبم و از پی جانان بروم\r\nگر چه دانم که به جایی نبرد راه غریب\r\nمن به بوی سر آن زلف پریشان بروم\r\nدلم از وحشت زندان سکندر بگرفت\r\nرخت بربندم و تا ملک سلیمان بروم\r\nچون صبا با تن بیمار و دل بی طاقت\r\nبه هواداری آن سرو خرامان بروم\r\nدر ره او چو قلم گر به سرم باید رفت\r\nبا دل زخم کش و دیده گریان بروم\r\nنذر کردم گر از این غم به درآیم روزی\r\nتا در میکده شادان و غزل خوان بروم\r\nبه هواداری او ذره صفت رقص کنان\r\nتا لب چشمه خورشید درخشان بروم\r\nتازیان را غم احوال گران باران نیست\r\nپارسایان مددی تا خوش و آسان بروم\r\nور چو حافظ ز بیابان نبرم ره بیرون\r\nهمره کوکبه آصف دوران بروم\r\n', 'آرزویی دارید که رسیدن به آن را محال می دانید و مدام به خودتان می گویید که این کار انجام نمی شود. خودتان را بیمار و رنجور کرده اید و غصه می خورید. نذری کرده اید و متوسل به ائمه شده اید. با خود عهدی بسته اید که اگر انجام شود، تولد دیگری می یابید. انسان های مؤمنی به شما کمک می کنند تا غم این ایام را آسان تر تحمل کنید و از این ورطه نجات یابید و به ستاره ی بخت و اقبال خودتان برسید. '),
(391, 'گر از این منزل ویران به سوی خانه روم\r\nدگر آن جا که روم عاقل و فرزانه روم\r\nزین سفر گر به سلامت به وطن بازرسم\r\nنذر کردم که هم از راه به میخانه روم\r\nتا بگویم که چه کشفم شد از این سیر و سلوک\r\nبه در صومعه با بربط و پیمانه روم\r\nآشنایان ره عشق گرم خون بخورند\r\nناکسم گر به شکایت سوی بیگانه روم\r\nبعد از این دست من و زلف چو زنجیر نگار\r\nچند و چند از پی کام دل دیوانه روم\r\nگر ببینم خم ابروی چو محرابش باز\r\nسجده شکر کنم و از پی شکرانه روم\r\nخرم آن دم که چو حافظ به تولای وزیر\r\nسرخوش از میکده با دوست به کاشانه روم\r\n', 'روزگار روی بد و سخت خود را کنار زده و روی خوش خود را به شما نشان می دهد. شما نیز در طی زندگی تجربه های کافی به دست آورده و عاقل شده اید. در جستجوی چیز با ارزشی هستید که آن را پیدا می کنید.هر ضرری دیده اید از آشنایان بوده است. از این به بعد زنجیرها پاره شده و آزاد می گردید و فرصت دارید تا برای رسیدن به مراد خودتان تلاش کنید. شکر نعمت خدا را بجا آورید. '),
(392, 'آن که پامال جفا کرد چو خاک راهم\r\nخاک می بوسم و عذر قدمش می خواهم\r\nمن نه آنم که ز جور تو بنالم حاشا\r\nبنده معتقد و چاکر دولتخواهم\r\nبسته ام در خم گیسوی تو امید دراز\r\nآن مبادا که کند دست طلب کوتاهم\r\nذره خاکم و در کوی توام جای خوش است\r\nترسم ای دوست که بادی ببرد ناگاهم\r\nپیر میخانه سحر جام جهان بینم داد\r\nو اندر آن آینه از حسن تو کرد آگاهم\r\nصوفی صومعه عالم قدسم لیکن\r\nحالیا دیر مغان است حوالتگاهم\r\nبا من راه نشین خیز و سوی میکده آی\r\nتا در آن حلقه ببینی که چه صاحب جاهم\r\nمست بگذشتی و از حافظت اندیشه نبود\r\nآه اگر دامن حسن تو بگیرد آهم\r\nخوشم آمد که سحر خسرو خاور می گفت\r\nبا همه پادشهی بنده تورانشاهم\r\n', 'بسیار انسان با گذشتی هستید هر چند که به شما جفا کرده اند و ستم روا داشته اند اما باز هم به همه چیز پایبند و معتقد هستید. خوبی ها و حسن های شما زبانزد مردم است. به مال و ثروت زیادی می رسید ولی باز هم از یاد خدا غافل نمی شوید و به خاطر ذره ذره ی چیزهایی که خدا به شما داده شاکر هستید. '),
(393, 'دیدار شد میسر و بوس و کنار هم\r\nاز بخت شکر دارم و از روزگار هم\r\nزاهد برو که طالع اگر طالع من است\r\nجامم به دست باشد و زلف نگار هم\r\nما عیب کس به مستی و رندی نمی کنیم\r\nلعل بتان خوش است و می خوشگوار هم\r\nای دل بشارتی دهمت محتسب نماند\r\nو از می جهان پر است و بت میگسار هم\r\nخاطر به دست تفرقه دادن نه زیرکیست\r\nمجموعه ای بخواه و صراحی بیار هم\r\nبر خاکیان عشق فشان جرعه لبش\r\nتا خاک لعل گون شود و مشکبار هم\r\nآن شد که چشم بد نگران بودی از کمین\r\nخصم از میان برفت و سرشک از کنار هم\r\nچون کائنات جمله به بوی تو زنده اند\r\nای آفتاب سایه ز ما برمدار هم\r\nچون آب روی لاله و گل فیض حسن توست\r\nای ابر لطف بر من خاکی ببار هم\r\nحافظ اسیر زلف تو شد از خدا بترس\r\nو از انتصاف آصف جم اقتدار هم\r\nبرهان ملک و دین که ز دست وزارتش\r\nایام کان یمین شد و دریا یسار هم\r\nبر یاد رای انور او آسمان به صبح\r\nجان می کند فدا و کواکب نثار هم\r\nگوی زمین ربوده چوگان عدل اوست\r\nوین برکشیده گنبد نیلی حصار هم\r\nعزم سبک عنان تو در جنبش آورد\r\nاین پایدار مرکز عالی مدار هم\r\nتا از نتیجه فلک و طور دور اوست\r\nتبدیل ماه و سال و خزان و بهار هم\r\nخالی مباد کاخ جلالش ز سروران\r\nو از ساقیان سروقد گلعذار هم\r\n', 'به زودی به هدف و آرزوی خود خواهی رسید و روزگار سعادت و نیکبختی فرا می رسد. بدخواهی و حسادت دیگران تأثیری در اراده تو نخواهد داشت. اعتماد به نفس خود را بیشتر کن. عشق و محبت را سرلوحه ی کار خود قرار بده. '),
(394, 'دردم از یار است و درمان نیز هم\r\nدل فدای او شد و جان نیز هم\r\nاین که می گویند آن خوشتر ز حسن\r\nیار ما این دارد و آن نیز هم\r\nیاد باد آن کو به قصد خون ما\r\nعهد را بشکست و پیمان نیز هم\r\nدوستان در پرده می گویم سخن\r\nگفته خواهد شد به دستان نیز هم\r\nچون سر آمد دولت شب های وصل\r\nبگذرد ایام هجران نیز هم\r\nهر دو عالم یک فروغ روی اوست\r\nگفتمت پیدا و پنهان نیز هم\r\nاعتمادی نیست بر کار جهان\r\nبلکه بر گردون گردان نیز هم\r\nعاشق از قاضی نترسد می بیار\r\nبلکه از یرغوی دیوان نیز هم\r\nمحتسب داند که حافظ عاشق است\r\nو آصف ملک سلیمان نیز هم\r\n', 'درمان دردتان رسیدن به وصال یار می باشد. تمام هم و غمتان فکر کردن در مورد او و یاد خاطرات شیرین با او بودن هست تنها غصه شما شکستن عهد و پیمانی است که با او بسته بودید. این شب های هجران پایان پذیر است. ولی می ترسید دست روزگار او را از شما دور کند اما خداوند دوباره به همه چیز سر و سامان می بخشد. او عادل است و حق را به حقدار می رساند. '),
(395, 'ما بی غمان مست دل از دست داده ایم\r\nهمراز عشق و همنفس جام باده ایم\r\nبر ما بسی کمان ملامت کشیده اند\r\nتا کار خود ز ابروی جانان گشاده ایم\r\nای گل تو دوش داغ صبوحی کشیده ای\r\nما آن شقایقیم که با داغ زاده ایم\r\nپیر مغان ز توبه ما گر ملول شد\r\nگو باده صاف کن که به عذر ایستاده ایم\r\nکار از تو می رود مددی ای دلیل راه\r\nکانصاف می دهیم و ز راه اوفتاده ایم\r\nچون لاله می مبین و قدح در میان کار\r\nاین داغ بین که بر دل خونین نهاده ایم\r\nگفتی که حافظ این همه رنگ و خیال چیست\r\nنقش غلط مبین که همان لوح ساده ایم\r\n', 'دل به دریا زده اید و از افشای رازتان هراسی ندارید. دیگر خسته شده اید و می خواهید همه کارها را یکسره کنید. تنها خودتان هستید که می توانید به اهدافتان برسید. برای کاری که انجام می دهید دلیل و مدرک دارید. حق کسی را هم پایمال نمی کنید. یکرنگ باشید تا مانعی سر راهتان سبز نشود و زودتر به حاجات خودتان برسید. '),
(396, 'عمریست تا به راه غمت رو نهاده ایم\r\nروی و ریای خلق به یک سو نهاده ایم\r\nطاق و رواق مدرسه و قال و قیل علم\r\nدر راه جام و ساقی مه رو نهاده ایم\r\nهم جان بدان دو نرگس جادو سپرده ایم\r\nهم دل بدان دو سنبل هندو نهاده ایم\r\nعمری گذشت تا به امید اشارتی\r\nچشمی بدان دو گوشه ابرو نهاده ایم\r\nما ملک عافیت نه به لشکر گرفته ایم\r\nما تخت سلطنت نه به بازو نهاده ایم\r\nتا سحر چشم یار چه بازی کند که باز\r\nبنیاد بر کرشمه جادو نهاده ایم\r\nبی زلف سرکشش سر سودایی از ملال\r\nهمچون بنفشه بر سر زانو نهاده ایم\r\nدر گوشه امید چو نظارگان ماه\r\nچشم طلب بر آن خم ابرو نهاده ایم\r\nگفتی که حافظا دل سرگشته ات کجاست\r\nدر حلقه های آن خم گیسو نهاده ایم\r\n', 'آفرین بر شما که در زندگیتان ریا و دورویی وجود ندارد و پایه و بنیاد زندگی را بر مبنای مهر و محبت گذاشته اید. اما چندی است که چشم به راه و منتظر خبری هستید. به عافیت و سلامت می رسید و مقامتان روز به روز بالاتر می رود. همیشه امیدوار باشید . چشم طلب فقط از خدا داشته باشید که او بشارت رسیدن حاجت و مراد را به شما می دهد. '),
(397, 'ما بدین در نه پی حشمت و جاه آمده ایم\r\nاز بد حادثه این جا به پناه آمده ایم\r\nره رو منزل عشقیم و ز سرحد عدم\r\nتا به اقلیم وجود این همه راه آمده ایم\r\nسبزه خط تو دیدیم و ز بستان بهشت\r\nبه طلبکاری این مهرگیاه آمده ایم\r\nبا چنین گنج که شد خازن او روح امین\r\nبه گدایی به در خانه شاه آمده ایم\r\nلنگر حلم تو ای کشتی توفیق کجاست\r\nکه در این بحر کرم غرق گناه آمده ایم\r\nآبرو می رود ای ابر خطاپوش ببار\r\nکه به دیوان عمل نامه سیاه آمده ایم\r\nحافظ این خرقه پشمینه مینداز که ما\r\nاز پی قافله با آتش آه آمده ایم\r\n', 'دست تقدیر مسیر زندیگتان را تغییر داده است اما در همه حال از یاد خدا غافل نشده ید. دست به دعا برداشته اید تا خداوند شما را یاری کند. کرم و بخشش خدا شامل حالتان می شود. آبروی کسانی که می خواستند شما را گناهکار جلوه دهند می رود و همه چیز روشن می شود. '),
(398, 'فتوی پیر مغان دارم و قولیست قدیم\r\nکه حرام است می آن جا که نه یار است ندیم\r\nچاک خواهم زدن این دلق ریایی چه کنم\r\nروح را صحبت ناجنس عذابیست الیم\r\nتا مگر جرعه فشاند لب جانان بر من\r\nسال ها شد که منم بر در میخانه مقیم\r\nمگرش خدمت دیرین من از یاد برفت\r\nای نسیم سحری یاد دهش عهد قدیم\r\nبعد صد سال اگر بر سر خاکم گذری\r\nسر برآرد ز گلم رقص کنان عظم رمیم\r\nدلبر از ما به صد امید ستد اول دل\r\nظاهرا عهد فرامش نکند خلق کریم\r\nغنچه گو تنگ دل از کار فروبسته مباش\r\nکز دم صبح مدد یابی و انفاس نسیم\r\nفکر بهبود خود ای دل ز دری دیگر کن\r\nدرد عاشق نشود به به مداوای حکیم\r\nگوهر معرفت آموز که با خود ببری\r\nکه نصیب دگران است نصاب زر و سیم\r\nدام سخت است مگر یار شود لطف خدا\r\nور نه آدم نبرد صرفه ز شیطان رجیم\r\nحافظ ار سیم و زرت نیست چه شد شاکر باش\r\nچه به از دولت لطف سخن و طبع سلیم\r\n', 'به قولی که داده اید عمل کنید. از صحبت کردن با نامحرمان بپرهیزید. مدتهاست که در پی حاجت خودتان این در و آن در می زنید. فکر می کنید دیگران از عهدی که با شما بسته اند پشیمان شده اند. امید داشته باشید گره ی کارتان باز می شود و کسانی کمکتان می کنند به شرط اینکه به نصایح آنها گوش کنید. به لطف خدا از دامی که برایتان پهن کرده اند رهایی پیدا می کنید و شیطان را شکست می دهید و به دولت و نیک بختی می رسید. '),
(399, 'خیز تا از در میخانه گشادی طلبیم\r\nبه ره دوست نشینیم و مرادی طلبیم\r\nزاد راه حرم وصل نداریم مگر\r\nبه گدایی ز در میکده زادی طلبیم\r\nاشک آلوده ما گر چه روان است ولی\r\nبه رسالت سوی او پاک نهادی طلبیم\r\nلذت داغ غمت بر دل ما باد حرام\r\nاگر از جور غم عشق تو دادی طلبیم\r\nنقطه خال تو بر لوح بصر نتوان زد\r\nمگر از مردمک دیده مدادی طلبیم\r\nعشوه ای از لب شیرین تو دل خواست به جان\r\nبه شکرخنده لبت گفت مزادی طلبیم\r\nتا بود نسخه عطری دل سودازده را\r\nاز خط غالیه سای تو سوادی طلبیم\r\nچون غمت را نتوان یافت مگر در دل شاد\r\nما به امید غمت خاطر شادی طلبیم\r\nبر در مدرسه تا چند نشینی حافظ\r\nخیز تا از در میخانه گشادی طلبیم\r\n', 'بلند شو همت کن و تلاش داشته باش که برای باز شدن گره از کار خود باید اول از زور بازوی خودت دوم از یک دوست کمک بگیری. از راه درست پیش برو که در راه حرام کار به جایی نخواهی برد. طمع نداشته باش و این حق توست که برای رسیدن به هدف تلاش کنی. دل پر از غمت شاد می شود و این به خاطر امیدی است که داشته ای. '),
(400, 'ما ز یاران چشم یاری داشتیم\r\nخود غلط بود آن چه ما پنداشتیم\r\nتا درخت دوستی برگی دهد\r\nحالیا رفتیم و تخمی کاشتیم\r\nگفت و گو آیین درویشی نبود\r\nور نه با تو ماجراها داشتیم\r\nشیوه چشمت فریب جنگ داشت\r\nما غلط کردیم و صلح انگاشتیم\r\nگلبن حسنت نه خود شد دلفروز\r\nما دم همت بر او بگماشتیم\r\nنکته ها رفت و شکایت کس نکرد\r\nجانب حرمت فرونگذاشتیم\r\nگفت خود دادی به ما دل حافظا\r\nما محصل بر کسی نگماشتیم\r\n', 'امیدتان به دیگران برای کمک بی جهت بوده و حال تنها مانده اید هر چند که همه قول یاری و کمک به شما داده بودند. گول حرف و ظواهر فریبنده ی آنها را خوردید اما الان هم دیر نیست اگر کمی همت کنید به تنهایی هم می توانید کارهایتان را به انجام برسانید. '),
(401, 'صلاح از ما چه می جویی که مستان را صلا گفتیم\r\nبه دور نرگس مستت سلامت را دعا گفتیم\r\nدر میخانه ام بگشا که هیچ از خانقه نگشود\r\nگرت باور بود ور نه سخن این بود و ما گفتیم\r\nمن از چشم تو ای ساقی خراب افتاده ام لیکن\r\nبلایی کز حبیب آید هزارش مرحبا گفتیم\r\nاگر بر من نبخشایی پشیمانی خوری آخر\r\nبه خاطر دار این معنی که در خدمت کجا گفتیم\r\nقدت گفتم که شمشاد است بس خجلت به بار آورد\r\nکه این نسبت چرا کردیم و این بهتان چرا گفتیم\r\nجگر چون نافه ام خون گشت کم زینم نمی باید\r\nجزای آن که با زلفت سخن از چین خطا گفتیم\r\nتو آتش گشتی ای حافظ ولی با یار درنگرفت\r\nز بدعهدی گل گویی حکایت با صبا گفتیم\r\n', 'صلاح و مشورت بهترین راهنما برای آینده روشن تان می باشد. دعای سلامتی همیشه با شماست و از بلایا ایمن هستید. در همه حال انفاق و کرم کنید که این کار روشن کننده ی راهتان می اشد و از این کار پشیمان نخواهید شد. مواظب صحبت کردنتان هم باشید که از آن به ضرر خودتان استفاده می کنند. از لحظه لحظه ی زندگیتان استفاده کنید که وقت طلاست. '),
(402, 'ما درس سحر در ره میخانه نهادیم\r\nمحصول دعا در ره جانانه نهادیم\r\nدر خرمن صد زاهد عاقل زند آتش\r\nاین داغ که ما بر دل دیوانه نهادیم\r\nسلطان ازل گنج غم عشق به ما داد\r\nتا روی در این منزل ویرانه نهادیم\r\nدر دل ندهم ره پس از این مهر بتان را\r\nمهر لب او بر در این خانه نهادیم\r\nدر خرقه از این بیش منافق نتوان بود\r\nبنیاد از این شیوه رندانه نهادیم\r\nچون می رود این کشتی سرگشته که آخر\r\nجان در سر آن گوهر یک دانه نهادیم\r\nالمنه لله که چو ما بی دل و دین بود\r\nآن را که لقب عاقل و فرزانه نهادیم\r\nقانع به خیالی ز تو بودیم چو حافظ\r\nیا رب چه گداهمت و بیگانه نهادیم\r\n', 'هنوز در اول راهید. عجله نکنید. به مرحله ی بالای زهد و عقل نرسیده اید پس صبر کنید تا کارها خراب نشود و غم به دلتان راه پیدا نکند. مواظب دوستان خودتان باشید که گرگ هستند در لباس میش. آنها می خواهند بنیادتان را از بین ببرند و دل و دینتان را بگیرند و نگذارند به مقصود برسید پس عاقل باشید. '),
(403, 'بگذار تا ز شارع میخانه بگذریم\r\nکز بهر جرعه ای همه محتاج این دریم\r\nروز نخست چون دم رندی زدیم و عشق\r\nشرط آن بود که جز ره آن شیوه نسپریم\r\nجایی که تخت و مسند جم می رود به باد\r\nگر غم خوریم خوش نبود به که می خوریم\r\nتا بو که دست در کمر او توان زدن\r\nدر خون دل نشسته چو یاقوت احمریم\r\nواعظ مکن نصیحت شوریدگان که ما\r\nبا خاک کوی دوست به فردوس ننگریم\r\nچون صوفیان به حالت و رقصند مقتدا\r\nما نیز هم به شعبده دستی برآوریم\r\nاز جرعه تو خاک زمین در و لعل یافت\r\nبیچاره ما که پیش تو از خاک کمتریم\r\nحافظ چو ره به کنگره کاخ وصل نیست\r\nبا خاک آستانه این در به سر بریم\r\n', 'حاجتی دارید و این حاجت شما مادی نیست بلکه معنوی می باشد. با خودتان شرط کرده اید دیگر غم مادیات را نخورید هر چند که می دانید مال و ثروت لازمه ی زندگیتان می باشد اما دست تقدیر آینده ای روشن برایتان رقم زده علاوه بر اینکه به نیت خودتان می رسید از مال و ثروت و مقام هم بهره مند می شوید. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(404, 'خیز تا خرقه صوفی به خرابات بریم\r\nشطح و طامات به بازار خرافات بریم\r\nسوی رندان قلندر به ره آورد سفر\r\nدلق بسطامی و سجاده طامات بریم\r\nتا همه خلوتیان جام صبوحی گیرند\r\nچنگ صبحی به در پیر مناجات بریم\r\nبا تو آن عهد که در وادی ایمن بستیم\r\nهمچو موسی ارنی گوی به میقات بریم\r\nکوس ناموس تو بر کنگره عرش زنیم\r\nعلم عشق تو بر بام سماوات بریم\r\nخاک کوی تو به صحرای قیامت فردا\r\nهمه بر فرق سر از بهر مباهات بریم\r\nور نهد در ره ما خار ملامت زاهد\r\nاز گلستانش به زندان مکافات بریم\r\nشرممان باد ز پشمینه آلوده خویش\r\nگر بدین فضل و هنر نام کرامات بریم\r\nقدر وقت ار نشناسد دل و کاری نکند\r\nبس خجالت که از این حاصل اوقات بریم\r\nفتنه می بارد از این سقف مقرنس برخیز\r\nتا به میخانه پناه از همه آفات بریم\r\nدر بیابان فنا گم شدن آخر تا کی\r\nره بپرسیم مگر پی به مهمات بریم\r\nحافظ آب رخ خود بر در هر سفله مریز\r\nحاجت آن به که بر قاضی حاجات بریم\r\n', 'عهدی را که در گذشته بسته اید و به قول هایی که داده اید، علیرغم پیمان شکنی دیگران باز هم پایبند هستید. قسم خورده اید تا قیامت عهد خودتان را نشکنید هر چند که ممکن است همگان به شما سخت بگیرند. از هنرتان استفاده کنید تا قدر جوانیتان را بدانید. از فتنه و آشوب پرهیز کنید. بزرگان به شما کمک می کنند. '),
(405, 'بیا تا گل برافشانیم و می در ساغر اندازیم\r\nفلک را سقف بشکافیم و طرحی نو دراندازیم\r\nاگر غم لشکر انگیزد که خون عاشقان ریزد\r\nمن و ساقی به هم تازیم و بنیادش براندازیم\r\nشراب ارغوانی را گلاب اندر قدح ریزیم\r\nنسیم عطرگردان را شکر در مجمر اندازیم\r\nچو در دست است رودی خوش بزن مطرب سرودی خوش\r\nکه دست افشان غزل خوانیم و پاکوبان سر اندازیم\r\nصبا خاک وجود ما بدان عالی جناب انداز\r\nبود کآن شاه خوبان را نظر بر منظر اندازیم\r\nیکی از عقل می لافد یکی طامات می بافد\r\nبیا کاین داوری ها را به پیش داور اندازیم\r\nبهشت عدن اگر خواهی بیا با ما به میخانه\r\nکه از پای خمت روزی به حوض کوثر اندازیم\r\nسخندانی و خوشخوانی نمی ورزند در شیراز\r\nبیا حافظ که تا خود را به ملکی دیگر اندازیم\r\n', 'یک دست صدا ندارد بلکه باید متحد شوید تا بتوانید پیروز شوید و بنیاد ظلم و ستم از بین ببرید. در این زمان است که به حاجات خودتان می رسید. با فردی عالی مقام ملاقات می کنید که او به شما کمک کرده و راهنمایی تان می کند. مطمئن باشید به نیات خودتان می رسید به شرط آنکه از خداوند دور نشوید. '),
(406, 'صوفی بیا که خرقه سالوس برکشیم\r\nوین نقش زرق را خط بطلان به سر کشیم\r\nنذر و فتوح صومعه در وجه می نهیم\r\nدلق ریا به آب خرابات برکشیم\r\nفردا اگر نه روضه رضوان به ما دهند\r\nغلمان ز روضه حور ز جنت به درکشیم\r\nبیرون جهیم سرخوش و از بزم صوفیان\r\nغارت کنیم باده و شاهد به بر کشیم\r\nعشرت کنیم ور نه به حسرت کشندمان\r\nروزی که رخت جان به جهانی دگر کشیم\r\nسر خدا که در تتق غیب منزویست\r\nمستانه اش نقاب ز رخسار برکشیم\r\nکو جلوه ای ز ابروی او تا چو ماه نو\r\nگوی سپهر در خم چوگان زر کشیم\r\nحافظ نه حد ماست چنین لاف ها زدن\r\nپای از گلیم خویش چرا بیشتر کشیم\r\n', 'بیشتر با خدا باشید. از کارهای ناشایست پرهیز کنید همه ی کارها با پول حل نمی شود. روزی می رسد که می بینید برای آخرت توشه ای غیر از گناه ندارید. عجله کنید و دست از گناه بردارید. خوشگذرانی گناه نیست به شرط اینکه معصیت در ان نباشد. این گوی و این میدان بسم الله بگوئید و طمع هم نکنید و پایتان را از گلیمتان درازتر نکنید. '),
(407, 'دوستان وقت گل آن به که به عشرت کوشیم\r\nسخن اهل دل است این و به جان بنیوشیم\r\nنیست در کس کرم و وقت طرب می گذرد\r\nچاره آن است که سجاده به می بفروشیم\r\nخوش هواییست فرح بخش خدایا بفرست\r\nنازنینی که به رویش می گلگون نوشیم\r\nارغنون ساز فلک رهزن اهل هنر است\r\nچون از این غصه ننالیم و چرا نخروشیم\r\nگل به جوش آمد و از می نزدیمش آبی\r\nلاجرم ز آتش حرمان و هوس می جوشیم\r\nمی کشیم از قدح لاله شرابی موهوم\r\nچشم بد دور که بی مطرب و می مدهوشیم\r\nحافظ این حال عجب با که توان گفت که ما\r\nبلبلانیم که در موسم گل خاموشیم\r\n', 'الان وقت کار و تلاش است. وقت برای تفریح زیاد دارید به چاره ی کار بیاندیشید. اگر دیر اقدام کنید هر آنچه ساخته اید تباه می شود. از هوی های نفسانی پرهیز کنید. خدا با شماست و در همه حال کمکتان می کند. '),
(408, 'ما شبی دست برآریم و دعایی بکنیم\r\nغم هجران تو را چاره ز جایی بکنیم\r\nدل بیمار شد از دست رفیقان مددی\r\nتا طبیبش به سر آریم و دوایی بکنیم\r\nآن که بی جرم برنجید و به تیغم زد و رفت\r\nبازش آرید خدا را که صفایی بکنیم\r\nخشک شد بیخ طرب راه خرابات کجاست\r\nتا در آن آب و هوا نشو و نمایی بکنیم\r\nمدد از خاطر رندان طلب ای دل ور نه\r\nکار صعب است مبادا که خطایی بکنیم\r\nسایه طایر کم حوصله کاری نکند\r\nطلب از سایه میمون همایی بکنیم\r\nدلم از پرده بشد حافظ خوشگوی کجاست\r\nتا به قول و غزلش ساز نوایی بکنیم\r\n', 'شما را به خاطر کاری که نکرده اید متهم کرده اند. دست به دعا بردارید، فقط اوست که می تواند به شما کمک کند و مرهم دل بیمارتان باشد. در موقعیتی قرار می گیرید که باید خودتان را و هنرهایتان را نشان دهید نکند که خطا رفته باشید و دوباره همه چیز را خراب کنید. حوصله به خرج دهید. همای سعادت روی شانه های شما نشسته است به شرطی که به خدا بیشتر نزدیک شوید. '),
(409, 'ما نگوییم بد و میل به ناحق نکنیم\r\nجامه کس سیه و دلق خود ازرق نکنیم\r\nعیب درویش و توانگر به کم و بیش بد است\r\nکار بد مصلحت آن است که مطلق نکنیم\r\nرقم مغلطه بر دفتر دانش نزنیم\r\nسر حق بر ورق شعبده ملحق نکنیم\r\nشاه اگر جرعه رندان نه به حرمت نوشد\r\nالتفاتش به می صاف مروق نکنیم\r\nخوش برانیم جهان در نظر راهروان\r\nفکر اسب سیه و زین مغرق نکنیم\r\nآسمان کشتی ارباب هنر می شکند\r\nتکیه آن به که بر این بحر معلق نکنیم\r\nگر بدی گفت حسودی و رفیقی رنجید\r\nگو تو خوش باش که ما گوش به احمق نکنیم\r\nحافظ ار خصم خطا گفت نگیریم بر او\r\nور به حق گفت جدل با سخن حق نکنیم\r\n', 'با غیبت کردن و تهمت زدن به دیگران کاری از پیش نمی برید. هر کسی ایرادی دارد بنابراین بزرگوار و خطاپوش باشید. حرمت خود و دیگران را حفظ کنید. از تجملات بپرهیزید. با تکیه بر مال دنیا به جایی نمی رسید. به حرف نادانان گوش ندهید هر چه می کشید از رفیق حسود می باشد. با حرف حق جدل نکن و آن را قبول کن. '),
(410, 'سرم خوش است و به بانگ بلند می گویم\r\nکه من نسیم حیات از پیاله می جویم\r\nعبوس زهد به وجه خمار ننشیند\r\nمرید خرقه دردی کشان خوش خویم\r\nشدم فسانه به سرگشتگی و ابروی دوست\r\nکشید در خم چوگان خویش چون گویم\r\nگرم نه پیر مغان در به روی بگشاید\r\nکدام در بزنم چاره از کجا جویم\r\nمکن در این چمنم سرزنش به خودرویی\r\nچنان که پرورشم می دهند می رویم\r\nتو خانقاه و خرابات در میانه مبین\r\nخدا گواه که هر جا که هست با اویم\r\nغبار راه طلب کیمیای بهروزیست\r\nغلام دولت آن خاک عنبرین بویم\r\nز شوق نرگس مست بلندبالایی\r\nچو لاله با قدح افتاده بر لب جویم\r\nبیار می که به فتوی حافظ از دل پاک\r\nغبار زرق به فیض قدح فروشویم\r\n', 'از آنچه که دارید راضی هستید و خداوند را شکر می گویید. تنها آرزویتان سلامتی برای خود و اطرافیانتان می باشد. هر موقع که سرگشته شده اید به خداوند پناه آورده و آرام گرفته اید. گواه شما همیشه خداست. طلب خود را از خدا می خواهید. هر روزتان بهتر از روز قبل می شود. زیرا دلتان پاک است. '),
(411, 'بارها گفته ام و بار دگر می گویم\r\nکه من دلشده این ره نه به خود می پویم\r\nدر پس آینه طوطی صفتم داشته اند\r\nآن چه استاد ازل گفت بگو می گویم\r\nمن اگر خارم و گر گل چمن آرایی هست\r\nکه از آن دست که او می کشدم می رویم\r\nدوستان عیب من بی دل حیران مکنید\r\nگوهری دارم و صاحب نظری می جویم\r\nگر چه با دلق ملمع می گلگون عیب است\r\nمکنم عیب کز او رنگ ریا می شویم\r\nخنده و گریه عشاق ز جایی دگر است\r\nمی سرایم به شب و وقت سحر می مویم\r\nحافظم گفت که خاک در میخانه مبوی\r\nگو مکن عیب که من مشک ختن می بویم\r\n', 'مرحبا بر شما که اصول و احکام خداوند را مو به مو انجام می دهید ولی هر گاه از خدا چیزی خواسته اید به شما نداده است و دوستان از شما خرده می گیرند. گوهر شما ایمان شماست. ناراحت نباشید این جزئی از وجودتان می باشد. روزی می رسد که قدرتان را بدانند هم اکنون نیز همراهی دارید که قدرتان را می داند. '),
(412, 'گر چه ما بندگان پادشهیم\r\nپادشاهان ملک صبحگهیم\r\nگنج در آستین و کیسه تهی\r\nجام گیتی نما و خاک رهیم\r\nهوشیار حضور و مست غرور\r\nبحر توحید و غرقه گنهیم\r\nشاهد بخت چون کرشمه کند\r\nماش آیینه رخ چو مهیم\r\nشاه بیدار بخت را هر شب\r\nما نگهبان افسر و کلهیم\r\nگو غنیمت شمار صحبت ما\r\nکه تو در خواب و ما به دیده گهیم\r\nشاه منصور واقف است که ما\r\nروی همت به هر کجا که نهیم\r\nدشمنان را ز خون کفن سازیم\r\nدوستان را قبای فتح دهیم\r\nرنگ تزویر پیش ما نبود\r\nشیر سرخیم و افعی سیهیم\r\nوام حافظ بگو که بازدهند\r\nکرده ای اعتراف و ما گوهیم\r\n', 'گنج بزرگی در کنارتان هست که نسبت با آن بی اهمیت هستید. آنقدر مغرور شده اید که به اطراف و زیر پای خودتان نگاه نمی کنید. همنشینی با دوستان تان را غنیمت بدانید و از خواب غفلت بیدار شوید. دشمنی را کر بگذارید تا بتوانید پیروز شوید و به نیات خودتان برسید باید مانند شیری باشید تا بتوانید دشمنان را که همچون ماری شما را نیش می زنند از بین ببرید. '),
(413, 'فاتحه ای چو آمدی بر سر خسته ای بخوان\r\nلب بگشا که می دهد لعل لبت به مرده جان\r\nآن که به پرسش آمد و فاتحه خواند و می رود\r\nگو نفسی که روح را می کنم از پی اش روان\r\nای که طبیب خسته ای روی زبان من ببین\r\nکاین دم و دود سینه ام بار دل است بر زبان\r\nگر چه تب استخوان من کرد ز مهر گرم و رفت\r\nهمچو تبم نمی رود آتش مهر از استخوان\r\nحال دلم ز خال تو هست در آتشش وطن\r\nچشمم از آن دو چشم تو خسته شده ست و ناتوان\r\nبازنشان حرارتم ز آب دو دیده و ببین\r\nنبض مرا که می دهد هیچ ز زندگی نشان\r\nآن که مدام شیشه ام از پی عیش داده است\r\nشیشه ام از چه می برد پیش طبیب هر زمان\r\nحافظ از آب زندگی شعر تو داد شربتم\r\nترک طبیب کن بیا نسخه شربتم بخوان\r\n', 'خواهان زیادی دارید کسانی هستند که محتاج شما می باشند آنها را از خودتان دور نکنید. شما هم از کشی دور افتاده اید و تنها امیدتان وصال یار می باشد و تنها چیزی که زندگیتان را دوباره تضمین می کند همین است. دست به دعا برداشته اید امید داشته باشید که انشاالله به وصال می رسید. '),
(414, 'چندان که گفتم غم با طبیبان\r\nدرمان نکردند مسکین غریبان\r\nآن گل که هر دم در دست بادیست\r\nگو شرم بادش از عندلیبان\r\nیا رب امان ده تا بازبیند\r\nچشم محبان روی حبیبان\r\nدرج محبت بر مهر خود نیست\r\nیا رب مبادا کام رقیبان\r\nای منعم آخر بر خوان جودت\r\nتا چند باشیم از بی نصیبان\r\nحافظ نگشتی شیدای گیتی\r\nگر می شنیدی پند ادیبان\r\n', 'تنها درمان دردتان رهائی از غربت است. باید کمی صبر کنید چون چاره ای غیر از این ندارید. زمانی روی دوست را می بینید که همه ی رقیبان را از میدان به در کرده باشید. شکایت را کار بگذارید، به پند بزرگان توجه کنید. مطمئن باشید گه از وصال یار بی نصیب نخواهید ماند. '),
(415, 'می سوزم از فراقت روی از جفا بگردان\r\nهجران بلای ما شد یا رب بلا بگردان\r\nمه جلوه می نماید بر سبز خنگ گردون\r\nتا او به سر درآید بر رخش پا بگردان\r\nمر غول را برافشان یعنی به رغم سنبل\r\nگرد چمن بخوری همچون صبا بگردان\r\nیغمای عقل و دین را بیرون خرام سرمست\r\nدر سر کلاه بشکن در بر قبا بگردان\r\nای نور چشم مستان در عین انتظارم\r\nچنگ حزین و جامی بنواز یا بگردان\r\nدوران همی نویسد بر عارضش خطی خوش\r\nیا رب نوشته بد از یار ما بگردان\r\nحافظ ز خوبرویان بختت جز این قدر نیست\r\nگر نیستت رضایی حکم قضا بگردان\r\n', 'از جفای روزگار شکایت می کنید کارهایتان آن طور که می خواهید پیش نمی رود. خیلی تلاش می کنید تا خواسته هایتان بر وفق مرادتان باشد اما باید ساخت. فعلا تقدیر برای شما اینچنین رقم زده است. همیشه باید رضایتان به رضای خدا باشد، اینطور آرامش روحی و روانیتان زیاد می شود. '),
(416, 'یا رب آن آهوی مشکین به ختن بازرسان\r\nوان سهی سرو خرامان به چمن بازرسان\r\nدل آزرده ما را به نسیمی بنواز\r\nیعنی آن جان ز تن رفته به تن بازرسان\r\nماه و خورشید به منزل چو به امر تو رسند\r\nیار مه روی مرا نیز به من بازرسان\r\nدیده ها در طلب لعل یمانی خون شد\r\nیا رب آن کوکب رخشان به یمن بازرسان\r\nبرو ای طایر میمون همایون آثار\r\nپیش عنقا سخن زاغ و زغن بازرسان\r\nسخن این است که ما بی تو نخواهیم حیات\r\nبشنو ای پیک خبرگیر و سخن بازرسان\r\nآن که بودی وطنش دیده حافظ یا رب\r\nبه مرادش ز غریبی به وطن بازرسان\r\n', 'چشم به راه کسی هستید که برایتان بسیار مهم است. مدت هاست از او خبری ندارید. و برای پیدا کردنش بسیار جستجو کرده اید اما هنوز نتیجه ای نگرفته اید ولی همیشه به خدا توکل کرده و یادتان را از خدا طلب نموده اید. آسوده خاطر باشید دیدار یار حتمی است. '),
(417, 'خدا را کم نشین با خرقه پوشان\r\nرخ از رندان بی سامان مپوشان\r\nدر این خرقه بسی آلودگی هست\r\nخوشا وقت قبای می فروشان\r\nدر این صوفی وشان دردی ندیدم\r\nکه صافی باد عیش دردنوشان\r\nتو نازک طبعی و طاقت نیاری\r\nگرانی های مشتی دلق پوشان\r\nچو مستم کرده ای مستور منشین\r\nچو نوشم داده ای زهرم منوشان\r\nبیا و از غبن این سالوسیان بین\r\nصراحی خون دل و بربط خروشان\r\nز دلگرمی حافظ بر حذر باش\r\nکه دارد سینه ای چون دیگ جوشان\r\n', 'هر قدر به خدا نزدیکتر شوید درد زمانه را کمتر حس می کنید و هر قدر هم درویشانه زندگی کنید بهتر سر و سامان می گیرید. اما شما فردی عجول و بی طاقت هستید و هر ضرری که می بینید به خاطر عجله ی بی مورد خودتان می باشد. زیاد حرص می خورید از این مسئله حذر کنید. خدا هر چه که لایق آن باشید به شما می دهد. '),
(418, 'شاه شمشادقدان خسرو شیرین دهنان\r\nکه به مژگان شکند قلب همه صف شکنان\r\nمست بگذشت و نظر بر من درویش انداخت\r\nگفت ای چشم و چراغ همه شیرین سخنان\r\nتا کی از سیم و زرت کیسه تهی خواهد بود\r\nبنده من شو و برخور ز همه سیمتنان\r\nکمتر از ذره نه ای پست مشو مهر بورز\r\nتا به خلوتگه خورشید رسی چرخ زنان\r\nبر جهان تکیه مکن ور قدحی می داری\r\nشادی زهره جبینان خور و نازک بدنان\r\nپیر پیمانه کش من که روانش خوش باد\r\nگفت پرهیز کن از صحبت پیمان شکنان\r\nدامن دوست به دست آر و ز دشمن بگسل\r\nمرد یزدان شو و فارغ گذر از اهرمنان\r\nبا صبا در چمن لاله سحر می گفتم\r\nکه شهیدان که اند این همه خونین کفنان\r\nگفت حافظ من و تو محرم این راز نه ایم\r\nاز می لعل حکایت کن و شیرین دهنان\r\n', 'به شما می گویند که چرا به دنبال پول و ثروت نمی روید و همیشه دم از شعر و عاشقی می زنید. در جواب می گویید زندگی مرفه بدون عشق معنا ندارد و به این سخن خود معتقد هستید. به دنیای فانی تکیه نمی کنید. خوشی را در پرهیز از گناه می دانید و معتقدید که باید مرد خدا بود. '),
(419, 'بهار و گل طرب انگیز گشت و توبه شکن\r\nبه شادی رخ گل بیخ غم ز دل برکن\r\nرسید باد صبا غنچه در هواداری\r\nز خود برون شد و بر خود درید پیراهن\r\nطریق صدق بیاموز از آب صافی دل\r\nبه راستی طلب آزادگی ز سرو چمن\r\nز دستبرد صبا گرد گل کلاله نگر\r\nشکنج گیسوی سنبل ببین به روی سمن\r\nعروس غنچه رسید از حرم به طالع سعد\r\nبه عینه دل و دین می برد به وجه حسن\r\nصفیر بلبل شوریده و نفیر هزار\r\nبرای وصل گل آمد برون ز بیت حزن\r\nحدیث صحبت خوبان و جام باده بگو\r\nبه قول حافظ و فتوی پیر صاحب فن\r\n', 'همت داشته باش. دست از غم بردار و به شادی بپرداز الان وقت پایکوبی است نه گوشه نشینی. طریق راستی و درستی را پیشه کن. آزاده باش و حرف دلت را بزن آنطور که می خواهی باش. طالع شما بسیار مبارک است خبر خوشی به شما می دهند که تمام حزن ها و غم ها یادتان می رود. '),
(420, 'ناگهان پرده برانداخته ای یعنی چه\r\nمست از خانه برون تاخته ای یعنی چه\r\nزلف در دست صبا گوش به فرمان رقیب\r\nاین چنین با همه درساخته ای یعنی چه\r\nشاه خوبانی و منظور گدایان شده ای\r\nقدر این مرتبه نشناخته ای یعنی چه\r\nنه سر زلف خود اول تو به دستم دادی\r\nبازم از پای درانداخته ای یعنی چه\r\nسخنت رمز دهان گفت و کمر سر میان\r\nو از میان تیغ به ما آخته ای یعنی چه\r\nهر کس از مهره مهر تو به نقشی مشغول\r\nعاقبت با همه کج باخته ای یعنی چه\r\nحافظا در دل تنگت چو فرود آمد یار\r\nخانه از غیر نپرداخته ای یعنی چه\r\n', 'تغییر حالت شما همه را در بهت و حیرت فرو برده است. گاهی با رقیبان هم داستانید، گاهی با دوستان، قدر روزگار خود را نمی دانید و با همه سر جنگ و دعوا دارید. اطرافیانتان روی شما حساب می کنند ولی افسوس که همه را پراکنده نموده اید. مشکل شما و دل تسلی تان زمان درمان می شود که به خدا و بعد به خودتان بپردازید. '),
(421, 'در سرای مغان رفته بود و آب زده\r\nنشسته پیر و صلایی به شیخ و شاب زده\r\nسبوکشان همه در بندگیش بسته کمر\r\nولی ز ترک کله چتر بر سحاب زده\r\nشعاع جام و قدح نور ماه پوشیده\r\nعذار مغبچگان راه آفتاب زده\r\nعروس بخت در آن حجله با هزاران ناز\r\nشکسته کسمه و بر برگ گل گلاب زده\r\nگرفته ساغر عشرت فرشته رحمت\r\nز جرعه بر رخ حور و پری گلاب زده\r\nز شور و عربده شاهدان شیرین کار\r\nشکر شکسته سمن ریخته رباب زده\r\nسلام کردم و با من به روی خندان گفت\r\nکه ای خمارکش مفلس شراب زده\r\nکه این کند که تو کردی به ضعف همت و رای\r\nز گنج خانه شده خیمه بر خراب زده\r\nوصال دولت بیدار ترسمت ندهند\r\nکه خفته ای تو در آغوش بخت خواب زده\r\nبیا به میکده حافظ که بر تو عرضه کنم\r\nهزار صف ز دعاهای مستجاب زده\r\nفلک جنیبه کش شاه نصره الدین است\r\nبیا ببین ملکش دست در رکاب زده\r\nخرد که ملهم غیب است بهر کسب شرف\r\nز بام عرش صدش بوسه بر جناب زده\r\n', 'خانه شما به زودی مجلس بزم و عروسی خواهد شد و همه به شوق این وصال کمر همت بسته اند. وصلت فرخنده ای است. مواظب عده ی باشید که می خواهند همه چیز را خراب کنند. از چیز نترسید دولت بخت با شماست و دعاهایتان مستجاب شده با کمی عقل و خرد همه کارهایتان نیز رو به راه می شود. '),
(422, 'ای که با سلسله زلف دراز آمده ای\r\nفرصتت باد که دیوانه نواز آمده ای\r\nساعتی ناز مفرما و بگردان عادت\r\nچون به پرسیدن ارباب نیاز آمده ای\r\nپیش بالای تو میرم چه به صلح و چه به جنگ\r\nچون به هر حال برازنده ناز آمده ای\r\nآب و آتش به هم آمیخته ای از لب لعل\r\nچشم بد دور که بس شعبده بازآمده ای\r\nآفرین بر دل نرم تو که از بهر ثواب\r\nکشته غمزه خود را به نماز آمده ای\r\nزهد من با تو چه سنجد که به یغمای دلم\r\nمست و آشفته به خلوتگه راز آمده ای\r\nگفت حافظ دگرت خرقه شراب آلوده ست\r\nمگر از مذهب این طایفه بازآمده ای\r\n', 'آفرین بر شما که هر کاری را حتی نیت کردن در دل را از بهر ثواب انجام می دهید و هیچ لذتی را بالاتر از خواندن نماز نمی دانید. بسیار صبور هستید و با همه کس کنار می آیید. نیازتان را جز خدا به احدی نگفته اید. رازتان را هم به کسی نگویید که هدف نزدیک و مرادتان در راه است. '),
(423, 'دوش رفتم به در میکده خواب آلوده\r\nخرقه تردامن و سجاده شراب آلوده\r\nآمد افسوس کنان مغبچه باده فروش\r\nگفت بیدار شو ای ره رو خواب آلوده\r\nشست و شویی کن و آن گه به خرابات خرام\r\nتا نگردد ز تو این دیر خراب آلوده\r\nبه هوای لب شیرین پسران چند کنی\r\nجوهر روح به یاقوت مذاب آلوده\r\nبه طهارت گذران منزل پیری و مکن\r\nخلعت شیب چو تشریف شباب آلوده\r\nپاک و صافی شو و از چاه طبیعت به درآی\r\nکه صفایی ندهد آب تراب آلوده\r\nگفتم ای جان جهان دفتر گل عیبی نیست\r\nکه شود فصل بهار از می ناب آلوده\r\nآشنایان ره عشق در این بحر عمیق\r\nغرقه گشتند و نگشتند به آب آلوده\r\nگفت حافظ لغز و نکته به یاران مفروش\r\nآه از این لطف به انواع عتاب آلوده\r\n', 'از خواب غفلت بیدار شو که بسیار دیر شده و عقب مانده ای رخوت و کسالت را کنار بگذار و جوهر خویش را که همانا همت و مردانگی است نشان بده. توبه را برای زمان پیری نگذار در جوانی پاک بودن هنر است. هیچ کس بی عیب نیست و خرده نگیر. '),
(424, 'از من جدا مشو که توام نور دیده ای\r\nآرام جان و مونس قلب رمیده ای\r\nاز دامن تو دست ندارند عاشقان\r\nپیراهن صبوری ایشان دریده ای\r\nاز چشم بخت خویش مبادت گزند از آنک\r\nدر دلبری به غایت خوبی رسیده ای\r\nمنعم مکن ز عشق وی ای مفتی زمان\r\nمعذور دارمت که تو او را ندیده ای\r\nآن سرزنش که کرد تو را دوست حافظا\r\nبیش از گلیم خویش مگر پا کشیده ای\r\n', 'اینقدر سنگدل نباش. غرور کاذب خویش را دور بریز کسانی هستند که تو را بسیار دوست می دارند تو هم از محبت خود بخشش کن که اگر غفلت کنی در آینده خود را سرزنش خواهی کرد. زیاده روی باعث ضرر است و پشیمانی به بار می آورد. '),
(425, 'دامن کشان همی شد در شرب زرکشیده\r\nصد ماه رو ز رشکش جیب قصب دریده\r\nاز تاب آتش می بر گرد عارضش خوی\r\nچون قطره های شبنم بر برگ گل چکیده\r\nلفظی فصیح شیرین قدی بلند چابک\r\nرویی لطیف زیبا چشمی خوش کشیده\r\nیاقوت جان فزایش از آب لطف زاده\r\nشمشاد خوش خرامش در ناز پروریده\r\nآن لعل دلکشش بین وان خنده دل آشوب\r\nوان رفتن خوشش بین وان گام آرمیده\r\nآن آهوی سیه چشم از دام ما برون شد\r\nیاران چه چاره سازم با این دل رمیده\r\nزنهار تا توانی اهل نظر میازار\r\nدنیا وفا ندارد ای نور هر دو دیده\r\nتا کی کشم عتیبت از چشم دلفریبت\r\nروزی کرشمه ای کن ای یار برگزیده\r\nگر خاطر شریفت رنجیده شد ز حافظ\r\nبازآ که توبه کردیم از گفته و شنیده\r\nبس شکر بازگویم در بندگی خواجه\r\nگر اوفتد به دستم آن میوه رسیده\r\n', 'چیزی را از دست داده اید که بسیار برایتان مهم بوده است حال می فهمید که قدر چه یاقوت گرانبهایی را ندانسته اید و به دنبال چاره اید. باز هم خدا را شاکر باشید که دوباره همه چیز مثل سابق می شود و گمشده تان بر می گردد. '),
(426, 'از خون دل نوشتم نزدیک دوست نامه\r\nانی رایت دهرا من هجرک القیامه\r\nدارم من از فراقش در دیده صد علامت\r\nلیست دموع عینی هذا لنا العلامه\r\nهر چند کآزمودم از وی نبود سودم\r\nمن جرب المجرب حلت به الندامه\r\nپرسیدم از طبیبی احوال دوست گفتا\r\nفی بعدها عذاب فی قربها السلامه\r\nگفتم ملامت آید گر گرد دوست گردم\r\nو الله ما راینا حبا بلا ملامه\r\nحافظ چو طالب آمد جامی به جان شیرین\r\nحتی یذوق منه کاسا من الکرامه\r\n', 'برای کسی که حتی از جانتان هم عزیزتر است پیغامی فرستاده اید که می ترسید به دستش نرسد حتی تا قیامت همه می دانند که تنها دردتان فراق و دوری از اوست و به هر کس می رسید احوال او را جویا می شوید و برای او سلامی در غربت می فرستید. خدا کریم است و طلب تو را هم می داند و این از جان شیرین تر را به تو می رساند. '),
(427, 'چراغ روی تو را شمع گشت پروانه\r\nمرا ز حال تو با حال خویش پروا نه\r\nخرد که قید مجانین عشق می فرمود\r\nبه بوی سنبل زلف تو گشت دیوانه\r\nبه بوی زلف تو گر جان به باد رفت چه شد\r\nهزار جان گرامی فدای جانانه\r\nمن رمیده ز غیرت ز پا فتادم دوش\r\nنگار خویش چو دیدم به دست بیگانه\r\nچه نقشه ها که برانگیختیم و سود نداشت\r\nفسون ما بر او گشته است افسانه\r\nبر آتش رخ زیبای او به جای سپند\r\nبه غیر خال سیاهش که دید به دانه\r\nبه مژده جان به صبا داد شمع در نفسی\r\nز شمع روی تواش چون رسید پروانه\r\nمرا به دور لب دوست هست پیمانی\r\nکه بر زبان نبرم جز حدیث پیمانه\r\nحدیث مدرسه و خانقه مگوی که باز\r\nفتاد در سر حافظ هوای میخانه\r\n', 'دست به هر کاری می زنید برایتان سودی ندارد و رسیدن به هدف برای شما یک افسانه شده است آینده تان را تیره و تار می بینید. خسته شده اید، وقتی که می بینید دیگران چقدر راهت به هدف می رسند. مژده ای به شما می دهند که از شنیدن آن وجودتان گرم می شود و دوباره تلاش می کنید و نتیجه ی آن را هم می بینید. '),
(428, 'سحرگاهان که مخمور شبانه\r\nگرفتم باده با چنگ و چغانه\r\nنهادم عقل را ره توشه از می\r\nز شهر هستیش کردم روانه\r\nنگار می فروشم عشوه ای داد\r\nکه ایمن گشتم از مکر زمانه\r\nز ساقی کمان ابرو شنیدم\r\nکه ای تیر ملامت را نشانه\r\nنبندی زان میان طرفی کمروار\r\nاگر خود را ببینی در میانه\r\nبرو این دام بر مرغی دگر نه\r\nکه عنقا را بلند است آشیانه\r\nکه بندد طرف وصل از حسن شاهی\r\nکه با خود عشق بازد جاودانه\r\nندیم و مطرب و ساقی همه اوست\r\nخیال آب و گل در ره بهانه\r\nبده کشتی می تا خوش برانیم\r\nاز این دریای ناپیداکرانه\r\nوجود ما معماییست حافظ\r\nکه تحقیقش فسون است و فسانه\r\n', 'نیرنگ و حیله هایی که در مورد شما به کار برده اند یکی پس از دیگری خنثی شده و این کار خداست. ملامت و ناراحتی تان دیگر تمام شده است. خودتان اراده کرده اید و غم را از دلتان بیرون نهاده اید. به دنبال حل معمایی هستید که به این زودی حل نمی شود. '),
(429, 'ساقی بیا که شد قدح لاله پر ز می\r\nطامات تا به چند و خرافات تا به کی\r\nبگذر ز کبر و ناز که دیده ست روزگار\r\nچین قبای قیصر و طرف کلاه کی\r\nهشیار شو که مرغ چمن مست گشت هان\r\nبیدار شو که خواب عدم در پی است هی\r\nخوش نازکانه می چمی ای شاخ نوبهار\r\nکآشفتگی مبادت از آشوب باد دی\r\nبر مهر چرخ و شیوه او اعتماد نیست\r\nای وای بر کسی که شد ایمن ز مکر وی\r\nفردا شراب کوثر و حور از برای ماست\r\nو امروز نیز ساقی مه روی و جام می\r\nباد صبا ز عهد صبی یاد می دهد\r\nجان دارویی که غم ببرد درده ای صبی\r\nحشمت مبین و سلطنت گل که بسپرد\r\nفراش باد هر ورقش را به زیر پی\r\nدرده به یاد حاتم طی جام یک منی\r\nتا نامه سیاه بخیلان کنیم طی\r\nزان می که داد حسن و لطافت به ارغوان\r\nبیرون فکند لطف مزاج از رخش به خوی\r\nمسند به باغ بر که به خدمت چو بندگان\r\nاستاده است سرو و کمر بسته است نی\r\nحافظ حدیث سحرفریب خوشت رسید\r\nتا حد مصر و چین و به اطراف روم و ری\r\n', 'با پرداختن به اوهام و خیالات خودتان را مشغول کرده اید. هوشیار باشید اتفاقات در راهند و باید با آنها جنگید، به خرافات اعتماد نکنید و به عقل خودتان تکیه کنید. در این صورت است که فردای شما روشن و رسیدن به مقصود نزدیک است. با سحر و فریب به مقام و پول نخواهید رسید پس با بندگی خداست که به هر آنچه طلب کنید می رسید. '),
(430, 'به صوت بلبل و قمری اگر ننوشی می\r\nعلاج کی کنمت آخرالدواء الکی\r\nذخیره ای بنه از رنگ و بوی فصل بهار\r\nکه می رسند ز پی رهزنان بهمن و دی\r\nچو گل نقاب برافکند و مرغ زد هوهو\r\nمنه ز دست پیاله چه می کنی هی هی\r\nشکوه سلطنت و حسن کی ثباتی داد\r\nز تخت جم سخنی مانده است و افسر کی\r\nخزینه داری میراث خوارگان کفر است\r\nبه قول مطرب و ساقی به فتوی دف و نی\r\nزمانه هیچ نبخشد که بازنستاند\r\nمجو ز سفله مروت که شیئه لا شی\r\nنوشته اند بر ایوان جنه الماوی\r\nکه هر که عشوه دنیی خرید وای به وی\r\nسخا نماند سخن طی کنم شراب کجاست\r\nبده به شادی روح و روان حاتم طی\r\nبخیل بوی خدا نشنود بیا حافظ\r\nپیاله گیر و کرم ورز و الضمان علی\r\n', 'برای آینده ی خود توشهای فراهم کن، پس اندازی از مال کن. زیرا آینده ای نامعلوم در انتظار توست. شاید اتفاقاتی بیافتد که مجبور شوی به پس انداز خود تکیه کنی. دیگران را از اطرافت پراکنده نکن، آنها در آینده به دردت می خورند. هیچ مقامی و مالی ماندگار نیست پس به فردا بیشتر فکر کن و به امید این مباش که مال مفتی به تو ببخشند. '),
(431, 'لبش می بوسم و در می کشم می\r\nبه آب زندگانی برده ام پی\r\nنه رازش می توانم گفت با کس\r\nنه کس را می توانم دید با وی\r\nلبش می بوسد و خون می خورد جام\r\nرخش می بیند و گل می کند خوی\r\nبده جام می و از جم مکن یاد\r\nکه می داند که جم کی بود و کی کی\r\nبزن در پرده چنگ ای ماه مطرب\r\nرگش بخراش تا بخروشم از وی\r\nگل از خلوت به باغ آورد مسند\r\nبساط زهد همچون غنچه کن طی\r\nچو چشمش مست را مخمور مگذار\r\nبه یاد لعلش ای ساقی بده می\r\nنجوید جان از آن قالب جدایی\r\nکه باشد خون جامش در رگ و پی\r\nزبانت درکش ای حافظ زمانی\r\nحدیث بی زبانان بشنو از نی\r\n', 'بسیار انسان نکته سنج و حساس در عین حال تودار و راز دارید. وقت هر کاری را می دانید الان هم زمان خوبی برای انجام کاری که نیت کرده اید نیست. کمی صبر کنید و طاقت داشته باشید. اگر عجله کنید و جلوی زبانتان را نگیرید کارها خراب می شود. به موقع همه چیز رو به راه می شود. '),
(432, 'مخمور جام عشقم ساقی بده شرابی\r\nپر کن قدح که بی می مجلس ندارد آبی\r\nوصف رخ چو ماهش در پرده راست ناید\r\nمطرب بزن نوایی ساقی بده شرابی\r\nشد حلقه قامت من تا بعد از این رقیبت\r\nزین در دگر نراند ما را به هیچ بابی\r\nدر انتظار رویت ما و امیدواری\r\nدر عشوه وصالت ما و خیال و خوابی\r\nمخمور آن دو چشمم آیا کجاست جامی\r\nبیمار آن دو لعلم آخر کم از جوابی\r\nحافظ چه می نهی دل تو در خیال خوبان\r\nکی تشنه سیر گردد از لمعه سرابی\r\n', 'تنها به مادیات نیاز ندارید بلکه به معنویات و محبت هم نیازمندید. کسی در نظرتان هست که به شما اعتنایی نمی کند. تصور می کنید رقیبتان جلوتر از شماست و همه ی درها را به رویتان بسته می بینید. انتظار می کشید و در عین حال امیدوارید اما به این هم فکر کنید که اگر به حاجتتان نرسیدید شکست نخورده اید. '),
(433, 'ای که بر ماه از خط مشکین نقاب انداختی\r\nلطف کردی سایه ای بر آفتاب انداختی\r\nتا چه خواهد کرد با ما آب و رنگ عارضت\r\nحالیا نیرنگ نقشی خوش بر آب انداختی\r\nگوی خوبی بردی از خوبان خلخ شاد باش\r\nجام کیخسرو طلب کافراسیاب انداختی\r\nهر کسی با شمع رخسارت به وجهی عشق باخت\r\nزان میان پروانه را در اضطراب انداختی\r\nگنج عشق خود نهادی در دل ویران ما\r\nسایه دولت بر این کنج خراب انداختی\r\nزینهار از آب آن عارض که شیران را از آن\r\nتشنه لب کردی و گردان را در آب انداختی\r\nخواب بیداران ببستی وان گه از نقش خیال\r\nتهمتی بر شب روان خیل خواب انداختی\r\nپرده از رخ برفکندی یک نظر در جلوه گاه\r\nو از حیا حور و پری را در حجاب انداختی\r\nباده نوش از جام عالم بین که بر اورنگ جم\r\nشاهد مقصود را از رخ نقاب انداختی\r\nاز فریب نرگس مخمور و لعل می پرست\r\nحافظ خلوت نشین را در شراب انداختی\r\nو از برای صید دل در گردنم زنجیر زلف\r\nچون کمند خسرو مالک رقاب انداختی\r\nداور دارا شکوه ای آن که تاج آفتاب\r\nاز سر تعظیم بر خاک جناب انداختی\r\nنصره الدین شاه یحیی آن که خصم ملک را\r\nاز دم شمشیر چون آتش در آب انداختی\r\n', 'انسانی عادل و حق بین هستید دل و زبانتان یکی است. اگر کسی خوبی در حق تان کند چند برابر جواب آن خوبی را می دهید. حجب و حیا شما فرشتگان را حیران می کند. هر چیزی را که طلب می کنید آنا به دست خواهید آورد. برای کوچکترین نعمت خدا شکر به جا می آورید و در عین خشم و غضب چون آب سرد و زلالید. '),
(434, 'ای دل مباش یک دم خالی ز عشق و مستی\r\nوان گه برو که رستی از نیستی و هستی\r\nگر جان به تن ببینی مشغول کار او شو\r\nهر قبله ای که بینی بهتر ز خودپرستی\r\nبا ضعف و ناتوانی همچون نسیم خوش باش\r\nبیماری اندر این ره بهتر ز تندرستی\r\nدر مذهب طریقت خامی نشان کفر است\r\nآری طریق دولت چالاکی است و چستی\r\nتا فضل و عقل بینی بی معرفت نشینی\r\nیک نکته ات بگویم خود را مبین که رستی\r\nدر آستان جانان از آسمان میندیش\r\nکز اوج سربلندی افتی به خاک پستی\r\nخار ار چه جان بکاهد گل عذر آن بخواهد\r\nسهل است تلخی می در جنب ذوق مستی\r\nصوفی پیاله پیما حافظ قرابه پرهیز\r\nای کوته آستینان تا کی درازدستی\r\n', 'غافل از عشق و محبت به اطرافیان شده اید آنقدر مشغول خودتان گشته اید که قبله تان خودپرستی و غرور شده است و فقط خودتان را می بینید. بدون عشق بودن یعنی از خدا دور شدن یعنی بی معرفت بودن و سقوط کردن. اگر می خواهید حاجتتان روا شود توبه کنید و به محبت و عشق بپردازید. '),
(435, 'با مدعی مگویید اسرار عشق و مستی\r\nتا بی خبر بمیرد در درد خودپرستی\r\nعاشق شو ار نه روزی کار جهان سر آید\r\nناخوانده نقش مقصود از کارگاه هستی\r\nدوش آن صنم چه خوش گفت در مجلس مغانم\r\nبا کافران چه کارت گر بت نمی پرستی\r\nسلطان من خدا را زلفت شکست ما را\r\nتا کی کند سیاهی چندین درازدستی\r\nدر گوشه سلامت مستور چون توان بود\r\nتا نرگس تو با ما گوید رموز مستی\r\nآن روز دیده بودم این فتنه ها که برخاست\r\nکز سرکشی زمانی با ما نمی نشستی\r\nعشقت به دست طوفان خواهد سپرد حافظ\r\nچون برق از این کشاکش پنداشتی که جستی\r\n', 'مواظب رفتارتان باشید گاهی زیاده روی می کنید، یا بیشتر به خدا روی آورید زیرا همه ی ما میهمانان ناخوانده ای هستیم که باید به آخرت برگردیم. هم نشینانتان هم از خدا دور شده اند از آنها دوری گزینید از خدا بخواهید اول سلامتی را به شما عطا کند و بعد از رموز خداوند همه چیز برایتان روشن می شود و از این طوفان بلا به سلامتی رهایی پیدا می کنید. '),
(436, 'آن غالیه خط گر سوی ما نامه نوشتی\r\nگردون ورق هستی ما درننوشتی\r\nهر چند که هجران ثمر وصل برآرد\r\nدهقان جهان کاش که این تخم نکشتی\r\nآمرزش نقد است کسی را که در این جا\r\nیاریست چو حوری و سرایی چو بهشتی\r\nدر مصطبه عشق تنعم نتوان کرد\r\nچون بالش زر نیست بسازیم به خشتی\r\nمفروش به باغ ارم و نخوت شداد\r\nیک شیشه می و نوش لبی و لب کشتی\r\nتا کی غم دنیای دنی ای دل دانا\r\nحیف است ز خوبی که شود عاشق زشتی\r\nآلودگی خرقه خرابی جهان است\r\nکو راهروی اهل دلی پاک سرشتی\r\nاز دست چرا هشت سر زلف تو حافظ\r\nتقدیر چنین بود چه کردی که نهشتی\r\n', 'از کاری که کرده اید سخت پشیمانید هر چند که تلاش زیادی می کنید تا به مقصود برسید ولی نتیجه ی خوبی برایتان ندارد. با گوشه گیری هم کاری از پیش نمی برید. آنقدر عاقل هستید که راهی برای رهایی از غم پیدا کنید. دست تقدیر برایتان اینچنین رقم زده اما چون دل پاکی دارید باز همه چیز درست می شود. '),
(437, 'ای قصه بهشت ز کویت حکایتی\r\nشرح جمال حور ز رویت روایتی\r\nانفاس عیسی از لب لعلت لطیفه ای\r\nآب خضر ز نوش لبانت کنایتی\r\nهر پاره از دل من و از غصه قصه ای\r\nهر سطری از خصال تو و از رحمت آیتی\r\nکی عطرسای مجلس روحانیان شدی\r\nگل را اگر نه بوی تو کردی رعایتی\r\nدر آرزوی خاک در یار سوختیم\r\nیاد آور ای صبا که نکردی حمایتی\r\nای دل به هرزه دانش و عمرت به باد رفت\r\nصد مایه داشتی و نکردی کفایتی\r\nبوی دل کباب من آفاق را گرفت\r\nاین آتش درون بکند هم سرایتی\r\nدر آتش ار خیال رخش دست می دهد\r\nساقی بیا که نیست ز دوزخ شکایتی\r\nدانی مراد حافظ از این درد و غصه چیست\r\nاز تو کرشمه ای و ز خسرو عنایتی\r\n', 'قصه ی زندگیتان بسیار زیبا و پر حکایت است. گاهی آنقدر شیرین است که مثل لطیفه ای از آن یاد می کنید و جوان می شوید. گاهی آنقدر غصه در آن موج می زند که تاب تحمل ندارید. قدر آن چیزی را که داشتید ندانستید و حال دارید از فراق آن می سوزید. تنها آرزویتان رهایی از غم و درد است که عنایت خداوند آن را برآورده می سازد. '),
(438, 'سبت سلمی بصدغیها فوادی\r\nو روحی کل یوم لی ینادی\r\nنگارا بر من بی دل ببخشای\r\nو واصلنی علی رغم الاعادی\r\nحبیبا در غم سودای عشقت\r\nتوکلنا علی رب العباد\r\nامن انکرتنی عن عشق سلمی\r\nتزاول آن روی نهکو بوادی\r\nکه همچون مت به بوتن دل و ای ره\r\nغریق العشق فی بحر الوداد\r\nبه پی ماچان غرامت بسپریمن\r\nغرت یک وی روشتی از امادی\r\nغم این دل بواتت خورد ناچار\r\nو غر نه او بنی آنچت نشادی\r\nدل حافظ شد اندر چین زلفت\r\nبلیل مظلم و الله هادی\r\n', 'کسی از شما طلب بخشش می کند دست رد به سینه اش نزنید برای آرام کردن خودتان به دعا و ثنای خداوند بپردازید. اگر می خواهید غرق در الطاف خداوند شوید و در بحر نعمات او شنا کنید و عزت و بزرگی خود را حفظ کنید و به غم دل دچار نشوید بخشنده باشید و دوستان تان را ببخشید. '),
(439, 'دیدم به خواب دوش که ماهی برآمدی\r\nکز عکس روی او شب هجران سر آمدی\r\nتعبیر رفت یار سفرکرده می رسد\r\nای کاج هر چه زودتر از در درآمدی\r\nذکرش به خیر ساقی فرخنده فال من\r\nکز در مدام با قدح و ساغر آمدی\r\nخوش بودی ار به خواب بدیدی دیار خویش\r\nتا یاد صحبتش سوی ما رهبر آمدی\r\nفیض ازل به زور و زر ار آمدی به دست\r\nآب خضر نصیبه اسکندر آمدی\r\nآن عهد یاد باد که از بام و در مرا\r\nهر دم پیام یار و خط دلبر آمدی\r\nکی یافتی رقیب تو چندین مجال ظلم\r\nمظلومی ار شبی به در داور آمدی\r\nخامان ره نرفته چه دانند ذوق عشق\r\nدریادلی بجوی دلیری سرآمدی\r\nآن کو تو را به سنگ دلی کرد رهنمون\r\nای کاشکی که پاش به سنگی برآمدی\r\nگر دیگری به شیوه حافظ زدی رقم\r\nمقبول طبع شاه هنرپرور آمدی\r\n', 'خوابی را که دیده اید تعبیر بسیار خوبی دارد که رسیدن به مراد است. فالی بس مبارک و فرخنده ای دارید. از راهنمایی اطرافیانتان نهایت استفاده را بکنید. مال .و ثروت هنگفتی نصیبتان می شود اما یادتان باشد که گذشته ی خویش را فراموش نکنید. ظلم نکنید که باز همه چیز را از دست خواهید داد. '),
(440, 'سحر با باد می گفتم حدیث آرزومندی\r\nخطاب آمد که واثق شو به الطاف خداوندی\r\nدعای صبح و آه شب کلید گنج مقصود است\r\nبدین راه و روش می رو که با دلدار پیوندی\r\nقلم را آن زبان نبود که سر عشق گوید باز\r\nورای حد تقریر است شرح آرزومندی\r\nالا ای یوسف مصری که کردت سلطنت مغرور\r\nپدر را بازپرس آخر کجا شد مهر فرزندی\r\nجهان پیر رعنا را ترحم در جبلت نیست\r\nز مهر او چه می پرسی در او همت چه می بندی\r\nهمایی چون تو عالی قدر حرص استخوان تا کی\r\nدریغ آن سایه همت که بر نااهل افکندی\r\nدر این بازار اگر سودیست با درویش خرسند است\r\nخدایا منعمم گردان به درویشی و خرسندی\r\nبه شعر حافظ شیراز می رقصند و می نازند\r\nسیه چشمان کشمیری و ترکان سمرقندی\r\n', 'حدیث درد و رنج و حاجت خویش را جز به باد به کشی دیگر نگفته اید چون می دانید که صدایتان را به خداوند می رساند. الطاف و رحمت ایزدی شامل حالتان شده است. به مقصود می رسید راهی را که می روید ادامه دهید فقط یادتان باشد کبر و غرور شما را از راهتان دور نسازد. نکند که طمع کنید و همت و تلاش را کنار بگذارید. همیشه با خدا باشید. '),
(441, 'چه بودی ار دل آن ماه مهربان بودی\r\nکه حال ما نه چنین بودی ار چنان بودی\r\nبگفتمی که چه ارزد نسیم طره دوست\r\nگرم به هر سر مویی هزار جان بودی\r\nبرات خوشدلی ما چه کم شدی یا رب\r\nگرش نشان امان از بد زمان بودی\r\nگرم زمانه سرافراز داشتی و عزیز\r\nسریر عزتم آن خاک آستان بودی\r\nز پرده کاش برون آمدی چو قطره اشک\r\nکه بر دو دیده ما حکم او روان بودی\r\nاگر نه دایره عشق راه بربستی\r\nچو نقطه حافظ سرگشته در میان بودی\r\n', 'ناشکری نکنید. با شکوه و شکایت از عزت نفس تان کاسته می شود. سر خویش را جهت نیاز جز به آستان خداوند جلوی هیچ بنده ای خم نکنید. با سرنوشت خودتان نجنگید آنچه سرنوشت برایتان رقم زده همان اتفاق می افتد. مرحبا بر شما که جز حلقه ی عشق به خداوند دیگری به گوش نیانداخته اید. '),
(442, 'به جان او که گرم دسترس به جان بودی\r\nکمینه پیشکش بندگانش آن بودی\r\nبگفتمی که بها چیست خاک پایش را\r\nاگر حیات گران مایه جاودان بودی\r\nبه بندگی قدش سرو معترف گشتی\r\nگرش چو سوسن آزاده ده زبان بودی\r\nبه خواب نیز نمی بینمش چه جای وصال\r\nچو این نبود و ندیدیم باری آن بودی\r\nاگر دلم نشدی پایبند طره او\r\nکی اش قرار در این تیره خاکدان بودی\r\nبه رخ چو مهر فلک بی نظیر آفاق است\r\nبه دل دریغ که یک ذره مهربان بودی\r\nدرآمدی ز درم کاشکی چو لمعه نور\r\nکه بر دو دیده ما حکم او روان بودی\r\nز پرده ناله حافظ برون کی افتادی\r\nاگر نه همدم مرغان صبح خوان بودی\r\n', 'جانتان را برای رسیدن به مقصود در طبق اخلاص گذاشته اید و ارزش زیادی برای رسیدن به هدف قائلید. معتقدید در عین سکوت باید حرف زد. کمی خسته شده اید و می خواهید همه چیزرا رها کنید ولی ندائی در دلتان می گوید که باید صبر و تحمل کنید و با خواندن نماز صبح مرادتان را از خدا بطلبید. '),
(443, 'چو سرو اگر بخرامی دمی به گلزاری\r\nخورد ز غیرت روی تو هر گلی خاری\r\nز کفر زلف تو هر حلقه ای و آشوبی\r\nز سحر چشم تو هر گوشه ای و بیماری\r\nمرو چو بخت من ای چشم مست یار به خواب\r\nکه در پی است ز هر سویت آه بیداری\r\nنثار خاک رهت نقد جان من هر چند\r\nکه نیست نقد روان را بر تو مقداری\r\nدلا همیشه مزن لاف زلف دلبندان\r\nچو تیره رای شوی کی گشایدت کاری\r\nسرم برفت و زمانی به سر نرفت این کار\r\nدلم گرفت و نبودت غم گرفتاری\r\nچو نقطه گفتمش اندر میان دایره آی\r\nبه خنده گفت که ای حافظ این چه پرگاری\r\n', 'دست به سوی هر گلی دراز می کنید خاری از آن به دستتان می نشیند یعنی اینکه به هر کسی که عشق می بازید ضرر می بینید. بسیار ناراحتید، فکر می کنید شکست خورده اید کفر می گوئید. دلتان را صاف کنید باز هم امتحان کنید شعارتان این باشد که تا پیروز نشوم از پای نمی نشینم. '),
(444, 'شهریست پرظریفان و از هر طرف نگاری\r\nیاران صلای عشق است گر می کنید کاری\r\nچشم فلک نبیند زین طرفه تر جوانی\r\nدر دست کس نیفتد زین خوبتر نگاری\r\nهرگز که دیده باشد جسمی ز جان مرکب\r\nبر دامنش مبادا زین خاکیان غباری\r\nچون من شکسته ای را از پیش خود چه رانی\r\nکم غایت توقع بوسیست یا کناری\r\nمی بی غش است دریاب وقتی خوش است بشتاب\r\nسال دگر که دارد امید نوبهاری\r\nدر بوستان حریفان مانند لاله و گل\r\nهر یک گرفته جامی بر یاد روی یاری\r\nچون این گره گشایم وین راز چون نمایم\r\nدردی و سخت دردی کاری و صعب کاری\r\nهر تار موی حافظ در دست زلف شوخی\r\nمشکل توان نشستن در این چنین دیاری\r\n', 'حال وقت نشستن نیست باید کاری کنید تا توان جوانی دارید می توانید خودتان را به هدف برسانید هر چند که این آدمیان خاکی شما را بسیار فریب داده اند ولی باز هم گذشته را فراموش کنید. توقع خودتان را کمی پائین آورید، گره کارتان باز می شود و کاری که بسیار سخت و دشوار باشد حل می گردد به شرط اینکه رازتان را فاش نکنید. '),
(445, 'تو را که هر چه مراد است در جهان داری\r\nچه غم ز حال ضعیفان ناتوان داری\r\nبخواه جان و دل از بنده و روان بستان\r\nکه حکم بر سر آزادگان روان داری\r\nمیان نداری و دارم عجب که هر ساعت\r\nمیان مجمع خوبان کنی میانداری\r\nبیاض روی تو را نیست نقش درخور از آنک\r\nسوادی از خط مشکین بر ارغوان داری\r\nبنوش می که سبکروحی و لطیف مدام\r\nعلی الخصوص در آن دم که سر گران داری\r\nمکن عتاب از این بیش و جور بر دل ما\r\nمکن هر آن چه توانی که جای آن داری\r\nبه اختیارت اگر صد هزار تیر جفاست\r\nبه قصد جان من خسته در کمان داری\r\nبکش جفای رقیبان مدام و جور حسود\r\nکه سهل باشد اگر یار مهربان داری\r\nبه وصل دوست گرت دست می دهد یک دم\r\nبرو که هر چه مراد است در جهان داری\r\nچو گل به دامن از این باغ می بری حافظ\r\nچه غم ز ناله و فریاد باغبان داری\r\n', 'کمی هم به فکر زیردستان باش تو صاحب همه چیز می شوی پس سعی کن آنهایی را که در بند غم هستند رهایی دهی. آنچه در توان داری برای خلق خدا صرف کن که هر چه به دست آورده ای از دعای خیر بندگان خدا می باشد. دوستان حسود هم زیاد داری ولی خوشحال باش که یارت همیشه با توست. '),
(446, 'صبا تو نکهت آن زلف مشک بو داری\r\nبه یادگار بمانی که بوی او داری\r\nدلم که گوهر اسرار حسن و عشق در اوست\r\nتوان به دست تو دادن گرش نکو داری\r\nدر آن شمایل مطبوع هیچ نتوان گفت\r\nجز این قدر که رقیبان تندخو داری\r\nنوای بلبلت ای گل کجا پسند افتد\r\nکه گوش و هوش به مرغان هرزه گو داری\r\nبه جرعه تو سرم مست گشت نوشت باد\r\nخود از کدام خم است این که در سبو داری\r\nبه سرکشی خود ای سرو جویبار مناز\r\nکه گر بدو رسی از شرم سر فروداری\r\nدم از ممالک خوبی چو آفتاب زدن\r\nتو را رسد که غلامان ماه رو داری\r\nقبای حسن فروشی تو را برازد و بس\r\nکه همچو گل همه آیین رنگ و بو داری\r\nز کنج صومعه حافظ مجوی گوهر عشق\r\nقدم برون نه اگر میل جست و جو داری\r\n', 'با خاطرات خودتان زندگی می کنید. دلتان گوهر اسرار زیبا و خوش است. رقیبان شما پیش رویتان قد علم کرده اند کاری نکنید که بعد های باعث پشیمانی شود. کسانی را دارید که حاضرند برای رسیدن به مقصود به شما کمک کنند. راه و روش تان آیین خوبان است اگر می خواهید گمشده ی خودتان را پیدا کنید بیشتر جستجو کنید. '),
(447, 'بیا با ما مورز این کینه داری\r\nکه حق صحبت دیرینه داری\r\nنصیحت گوش کن کاین در بسی به\r\nاز آن گوهر که در گنجینه داری\r\nولیکن کی نمایی رخ به رندان\r\nتو کز خورشید و مه آیینه داری\r\nبد رندان مگو ای شیخ و هش دار\r\nکه با حکم خدایی کینه داری\r\nنمی ترسی ز آه آتشینم\r\nتو دانی خرقه پشمینه داری\r\nبه فریاد خمار مفلسان رس\r\nخدا را گر می دوشینه داری\r\nندیدم خوشتر از شعر تو حافظ\r\nبه قرآنی که اندر سینه داری\r\n', 'کمی نصیحت پذیر باش و فقط حرف خودت را نزن. تو که دلت مثل آفتاب روشن است و مثل آیینه صاف، پس چرا با بدان نشست و برخاست داری. چرا می خواهی با سرنوشت خود بجنگی. آه و ناراحتی اطرافیانت را ببین و به آنها هم توجه کن و توکل به خدا و قرآن نما. '),
(448, 'ای که در کوی خرابات مقامی داری\r\nجم وقت خودی ار دست به جامی داری\r\nای که با زلف و رخ یار گذاری شب و روز\r\nفرصتت باد که خوش صبحی و شامی داری\r\nای صبا سوختگان بر سر ره منتظرند\r\nگر از آن یار سفرکرده پیامی داری\r\nخال سرسبز تو خوش دانه عیشیست ولی\r\nبر کنار چمنش وه که چه دامی داری\r\nبوی جان از لب خندان قدح می شنوم\r\nبشنو ای خواجه اگر زان که مشامی داری\r\nچون به هنگام وفا هیچ ثباتیت نبود\r\nمی کنم شکر که بر جور دوامی داری\r\nنام نیک ار طلبد از تو غریبی چه شود\r\nتویی امروز در این شهر که نامی داری\r\nبس دعای سحرت مونس جان خواهد بود\r\nتو که چون حافظ شبخیز غلامی داری\r\n', 'در میان عده ای ارج و قرب بالایی دارید و در جای دیگر همه منتظر ورودتان می باشند. بخت با شماست، خوشحال باشید و خدا را شکر کنید که جور و جفا از بین رفته و جای آن را مهر و وفا آمده است. نامتان پر آوازه می شود. بیشتر با خدا باشید و نماز شب بخوانید تا راهتان را گم نکنید. انشاالله  '),
(449, 'ای که مهجوری عشاق روا می داری\r\nعاشقان را ز بر خویش جدا می داری\r\nتشنه بادیه را هم به زلالی دریاب\r\nبه امیدی که در این ره به خدا می داری\r\nدل ببردی و بحل کردمت ای جان لیکن\r\nبه از این دار نگاهش که مرا می داری\r\nساغر ما که حریفان دگر می نوشند\r\nما تحمل نکنیم ار تو روا می داری\r\nای مگس حضرت سیمرغ نه جولانگه توست\r\nعرض خود می بری و زحمت ما می داری\r\nتو به تقصیر خود افتادی از این در محروم\r\nاز که می نالی و فریاد چرا می داری\r\nحافظ از پادشهان پایه به خدمت طلبند\r\nسعی نابرده چه امید عطا می داری\r\n', 'چرا با آزار دادن دیگران لذت می برید و می خواهید بندگان خدا را از هم جدا کنید. اگر می خواهید به آب زلال محبت دست پیدا کنید بهتر است برای وصل دیگران کوشش کنید. از دیگران دل می برید ولی جفا می کنید. سعی کنید دل دیگران را هم برای خودتان حفظ کنید. دیگر خودنمایی کافی است اگر غمگینید تقصیر خودتان می باشد. '),
(450, 'روزگاریست که ما را نگران می داری\r\nمخلصان را نه به وضع دگران می داری\r\nگوشه چشم رضایی به منت باز نشد\r\nاین چنین عزت صاحب نظران می داری\r\nساعد آن به که بپوشی تو چو از بهر نگار\r\nدست در خون دل پرهنران می داری\r\nنه گل از دست غمت رست و نه بلبل در باغ\r\nهمه را نعره زنان جامه دران می داری\r\nای که در دلق ملمع طلبی نقد حضور\r\nچشم سری عجب از بی خبران می داری\r\nچون تویی نرگس باغ نظر ای چشم و چراغ\r\nسر چرا بر من دلخسته گران می داری\r\nگوهر جام جم از کان جهانی دگر است\r\nتو تمنا ز گل کوزه گران می داری\r\nپدر تجربه ای دل تویی آخر ز چه روی\r\nطمع مهر و وفا زین پسران می داری\r\nکیسه سیم و زرت پاک بباید پرداخت\r\nاین طمع ها که تو از سیمبران می داری\r\nگر چه رندی و خرابی گنه ماست ولی\r\nعاشقی گفت که تو بنده بر آن می داری\r\nمگذران روز سلامت به ملامت حافظ\r\nچه توقع ز جهان گذران می داری\r\n', 'آنقدر بی تابید که دیگران از وضع شما نگران و ناراحت هستند. در این زمان است که نباید از یاد خدا غافل باشید. اتفاقاتی در شرف انجام است که شما از آن بی خبرید. هم شما را دوست دارند از تجربه های خودتان استفاده کنید. زیاده طلبی را کنار بگذارید و روزهای خوش جوانیتان را با غم نگذرانید و توقع خودتان را کم کنید. '),
(451, 'خوش کرد یاوری فلکت روز داوری\r\nتا شکر چون کنی و چه شکرانه آوری\r\nآن کس که اوفتاد خدایش گرفت دست\r\nگو بر تو باد تا غم افتادگان خوری\r\nدر کوی عشق شوکت شاهی نمی خرند\r\nاقرار بندگی کن و اظهار چاکری\r\nساقی به مژدگانی عیش از درم درآی\r\nتا یک دم از دلم غم دنیا به دربری\r\nدر شاهراه جاه و بزرگی خطر بسیست\r\nآن به کز این گریوه سبکبار بگذری\r\nسلطان و فکر لشکر و سودای تاج و گنج\r\nدرویش و امن خاطر و کنج قلندری\r\nیک حرف صوفیانه بگویم اجازت است\r\nای نور دیده صلح به از جنگ و داوری\r\nنیل مراد بر حسب فکر و همت است\r\nاز شاه نذر خیر و ز توفیق یاوری\r\nحافظ غبار فقر و قناعت ز رخ مشوی\r\nکاین خاک بهتر از عمل کیمیاگری\r\n', 'روزگار با تو یار شده و همه چیز بر وفق مرادت پیش خواهد رفت. مطمئن باش اگر مانعی سر راهت باشد فقط خداست که دست تو را می گیرد. اگر همیشه در مقابل خدا بر خاک بیفتی در عین خطر خداوند تو را نجات می بخشد و یک لحظه آرامش خاطر و راحتی را با یک دنیا زر و سیم عوض نمی کنی. برای رسیدن به مرادت تلاش کن و نذر خویش را ادا فرما. '),
(452, 'طفیل هستی عشقند آدمی و پری\r\nارادتی بنما تا سعادتی ببری\r\nبکوش خواجه و از عشق بی نصیب مباش\r\nکه بنده را نخرد کس به عیب بی هنری\r\nمی صبوح و شکرخواب صبحدم تا چند\r\nبه عذر نیم شبی کوش و گریه سحری\r\nتو خود چه لعبتی ای شهسوار شیرین کار\r\nکه در برابر چشمی و غایب از نظری\r\nهزار جان مقدس بسوخت زین غیرت\r\nکه هر صباح و مسا شمع مجلس دگری\r\nز من به حضرت آصف که می برد پیغام\r\nکه یاد گیر دو مصرع ز من به نظم دری\r\nبیا که وضع جهان را چنان که من دیدم\r\nگر امتحان بکنی می خوری و غم نخوری\r\nکلاه سروریت کج مباد بر سر حسن\r\nکه زیب بخت و سزاوار ملک و تاج سری\r\nبه بوی زلف و رخت می روند و می آیند\r\nصبا به غالیه سایی و گل به جلوه گری\r\nچو مستعد نظر نیستی وصال مجوی\r\nکه جام جم نکند سود وقت بی بصری\r\nدعای گوشه نشینان بلا بگرداند\r\nچرا به گوشه چشمی به ما نمی نگری\r\nبیا و سلطنت از ما بخر به مایه حسن\r\nو از این معامله غافل مشو که حیف خوری\r\nطریق عشق طریقی عجب خطرناک است\r\nنعوذبالله اگر ره به مقصدی نبری\r\nبه یمن همت حافظ امید هست که باز\r\nاری اسامر لیلای لیله القمر\r\n', ' تعبیر :  برای این غزل هنوز تعبیری ثبت نشده ! '),
(453, 'ای که دایم به خویش مغروری\r\nگر تو را عشق نیست معذوری\r\nگرد دیوانگان عشق مگرد\r\nکه به عقل عقیله مشهوری\r\nمستی عشق نیست در سر تو\r\nرو که تو مست آب انگوری\r\nروی زرد است و آه دردآلود\r\nعاشقان را دوای رنجوری\r\nبگذر از نام و ننگ خود حافظ\r\nساغر می طلب که مخموری\r\n', 'خود را از بقیه جدا می دانی و به خودت مغرور شده ای. فکر می کنی از تو عاقل تر کسی دیگر نیست، اگر به خدا نزدیک شوی عشق به حق تعالی تو را از بندهای غرور و ریا جدا می کند و از غم ها و دردهای دنیوی خلاص می شوی و نامت به نیکی همه جا یاد می شود و به حاجات خودت می رسی. '),
(454, 'ز کوی یار می آید نسیم باد نوروزی\r\nاز این باد ار مدد خواهی چراغ دل برافروزی\r\nچو گل گر خرده ای داری خدا را صرف عشرت کن\r\nکه قارون را غلط ها داد سودای زراندوزی\r\nز جام گل دگر بلبل چنان مست می لعل است\r\nکه زد بر چرخ فیروزه صفیر تخت فیروزی\r\nبه صحرا رو که از دامن غبار غم بیفشانی\r\nبه گلزار آی کز بلبل غزل گفتن بیاموزی\r\nچو امکان خلود ای دل در این فیروزه ایوان نیست\r\nمجال عیش فرصت دان به فیروزی و بهروزی\r\nطریق کام بخشی چیست ترک کام خود کردن\r\nکلاه سروری آن است کز این ترک بردوزی\r\nسخن در پرده می گویم چو گل از غنچه بیرون آی\r\nکه بیش از پنج روزی نیست حکم میر نوروزی\r\nندانم نوحه قمری به طرف جویباران چیست\r\nمگر او نیز همچون من غمی دارد شبانروزی\r\nمی ای دارم چو جان صافی و صوفی می کند عیبش\r\nخدایا هیچ عاقل را مبادا بخت بد روزی\r\nجدا شد یار شیرینت کنون تنها نشین ای شمع\r\nکه حکم آسمان این است اگر سازی و گر سوزی\r\nبه عجب علم نتوان شد ز اسباب طرب محروم\r\nبیا ساقی که جاهل را هنیتر می رسد روزی\r\nمی اندر مجلس آصف به نوروز جلالی نوش\r\nکه بخشد جرعه جامت جهان را ساز نوروزی\r\nنه حافظ می کند تنها دعای خواجه تورانشاه\r\nز مدح آصفی خواهد جهان عیدی و نوروزی\r\nجنابش پارسایان راست محراب دل و دیده\r\nجبینش صبح خیزان راست روز فتح و فیروزی\r\n', 'فکر دنیا و ظواهر و زیبایی آن را رها کن و خوش باش. غم و اندوه را دور بینداز و به آنچه داری قناعت کن. قناعت بزرگترین ثروت انسان است. اگر فعلاً دچار شکست و ناکامی شده ای ناراحت و ناامید نباش. روزگار فراز و نشیب بسیار دارد. پایان شب نا امیدی، صبح پیروزی است. فرصت ها را غنیمت بدان و از آن به نفع خود استفاده کن.  '),
(455, 'عمر بگذشت به بی حاصلی و بوالهوسی\r\nای پسر جام می ام ده که به پیری برسی\r\nچه شکرهاست در این شهر که قانع شده اند\r\nشاهبازان طریقت به مقام مگسی\r\nدوش در خیل غلامان درش می رفتم\r\nگفت ای عاشق بیچاره تو باری چه کسی\r\nبا دل خون شده چون نافه خوشش باید بود\r\nهر که مشهور جهان گشت به مشکین نفسی\r\nلمع البرق من الطور و آنست به\r\nفلعلی لک آت بشهاب قبس\r\nکاروان رفت و تو در خواب و بیابان در پیش\r\nوه که بس بی خبر از غلغل چندین جرسی\r\nبال بگشا و صفیر از شجر طوبی زن\r\nحیف باشد چو تو مرغی که اسیر قفسی\r\nتا چو مجمر نفسی دامن جانان گیرم\r\nجان نهادیم بر آتش ز پی خوش نفسی\r\nچند پوید به هوای تو ز هر سو حافظ\r\nیسر الله طریقا بک یا ملتمسی\r\n', 'در گذر عمر غافل مانده اید اگر می خواهید به قله های موفقیت صعود کنید از تجربه ی پیران استفاده کنید تا خودتان نیز مانند عمری طولانی و با عزت داشته باشید. خیلی زود گول ظاهر مردم را می خورید و خودتان را کوچک می شمارید. مقامتان بالا می رود و زبانزد خاص و عام می شوید. '),
(456, 'نوبهار است در آن کوش که خوشدل باشی\r\nکه بسی گل بدمد باز و تو در گل باشی\r\nمن نگویم که کنون با که نشین و چه بنوش\r\nکه تو خود دانی اگر زیرک و عاقل باشی\r\nچنگ در پرده همین می دهدت پند ولی\r\nوعظت آن گاه کند سود که قابل باشی\r\nدر چمن هر ورقی دفتر حالی دگر است\r\nحیف باشد که ز کار همه غافل باشی\r\nنقد عمرت ببرد غصه دنیا به گزاف\r\nگر شب و روز در این قصه مشکل باشی\r\nگر چه راهیست پر از بیم ز ما تا بر دوست\r\nرفتن آسان بود ار واقف منزل باشی\r\nحافظا گر مدد از بخت بلندت باشد\r\nصید آن شاهد مطبوع شمایل باشی\r\n', 'برای کاری که نیت کرده اید اگر تلاش کنید خیلی زود به مرادتان می رسی. شانس به شما رو کرده، حیف است اگر آن را از خود دور کنید. روزگارتان آنقدر خوب می شود که تمام غم ها و غصه های گذشته را فراموش می کنید. برای رسیدن به مرادتان کمی سختی می کشید ولی اگر عاقل باشید به راحتی به نیات خودتان می رسید. '),
(457, 'هزار جهد بکردم که یار من باشی\r\nمرادبخش دل بی قرار من باشی\r\nچراغ دیده شب زنده دار من گردی\r\nانیس خاطر امیدوار من باشی\r\nچو خسروان ملاحت به بندگان نازند\r\nتو در میانه خداوندگار من باشی\r\nاز آن عقیق که خونین دلم ز عشوه او\r\nاگر کنم گله ای غمگسار من باشی\r\nدر آن چمن که بتان دست عاشقان گیرند\r\nگرت ز دست برآید نگار من باشی\r\nشبی به کلبه احزان عاشقان آیی\r\nدمی انیس دل سوکوار من باشی\r\nشود غزاله خورشید صید لاغر من\r\nگر آهویی چو تو یک دم شکار من باشی\r\nسه بوسه کز دو لبت کرده ای وظیفه من\r\nاگر ادا نکنی قرض دار من باشی\r\nمن این مراد ببینم به خود که نیم شبی\r\nبه جای اشک روان در کنار من باشی\r\nمن ار چه حافظ شهرم جوی نمی ارزم\r\nمگر تو از کرم خویش یار من باشی\r\n', 'شما تلاش خودتان را کرده اید پس ناراحت نباشید و از بخت خودتان گله نکنید. از نظر شما همه چیز به آخر رسیده و زندگی تان تیره و تار گردیده است. او به عهد خود وفا نکرده و شما خوبی خودتان را ثابت کرده اید ولی باز هم امیدوارید و این رمز موفقیت شماست. '),
(458, 'ای دل آن دم که خراب از می گلگون باشی\r\nبی زر و گنج به صد حشمت قارون باشی\r\nدر مقامی که صدارت به فقیران بخشند\r\nچشم دارم که به جاه از همه افزون باشی\r\nدر ره منزل لیلی که خطرهاست در آن\r\nشرط اول قدم آن است که مجنون باشی\r\nنقطه عشق نمودم به تو هان سهو مکن\r\nور نه چون بنگری از دایره بیرون باشی\r\nکاروان رفت و تو در خواب و بیابان در پیش\r\nکی روی ره ز که پرسی چه کنی چون باشی\r\nتاج شاهی طلبی گوهر ذاتی بنمای\r\nور خود از تخمه جمشید و فریدون باشی\r\nساغری نوش کن و جرعه بر افلاک فشان\r\nچند و چند از غم ایام جگرخون باشی\r\nحافظ از فقر مکن ناله که گر شعر این است\r\nهیچ خوشدل نپسندد که تو محزون باشی\r\n', 'دعایتان فقط برای رسیدن به مال و ثروت است ولی بدانید شما ثروتی دارید که شاید دیگران حسرت آن را داشته باشند و آن نعمت سلامتی است. برای رسیدن به مقصود باید خطرها را به جان خرید. زیرک باشید و از اطرافتان غافل نشوید. بیشتر به تکامل روحی خودتان بپردازید و حرص ایام و مادیات را نخورید. '),
(459, 'زین خوش رقم که بر گل رخسار می کشی\r\nخط بر صحیفه گل و گلزار می کشی\r\nاشک حرم نشین نهانخانه مرا\r\nزان سوی هفت پرده به بازار می کشی\r\nکاهل روی چو باد صبا را به بوی زلف\r\nهر دم به قید سلسله در کار می کشی\r\nهر دم به یاد آن لب میگون و چشم مست\r\nاز خلوتم به خانه خمار می کشی\r\nگفتی سر تو بسته فتراک ما شود\r\nسهل است اگر تو زحمت این بار می کشی\r\nبا چشم و ابروی تو چه تدبیر دل کنم\r\nوه زین کمان که بر من بیمار می کشی\r\nبازآ که چشم بد ز رخت دفع می کند\r\nای تازه گل که دامن از این خار می کشی\r\nحافظ دگر چه می طلبی از نعیم دهر\r\nمی می خوری و طره دلدار می کشی\r\n', 'مطمئن باش هر خوبی که می کنی بدون اجر نمی ماند. هر غصه و دعایی که در دلت می کنی و هر اشکی که می ریزی تا عرش و هفت آسمان می رسد. تدبیری برای چاره ی کار خود نداری، فقط از خدا خواسته ای که دل بیمارت را التیام بخشد. به آنچه که می خواهی می رسی و به هر چیزی که دست بزنی تبدیل به طلا می شود. ');
INSERT INTO `faals` (`id`, `Poem`, `Interpretation`) VALUES
(460, 'سلیمی منذ حلت بالعراق\r\nالاقی من نواها ما الاقی\r\nالا ای ساروان منزل دوست\r\nالی رکبانکم طال اشتیاقی\r\nخرد در زنده رود انداز و می نوش\r\nبه گلبانگ جوانان عراقی\r\nربیع العمر فی مرعی حماکم\r\nحماک الله یا عهد التلاقی\r\nبیا ساقی بده رطل گرانم\r\nسقاک الله من کاس دهاق\r\nجوانی باز می آرد به یادم\r\nسماع چنگ و دست افشان ساقی\r\nمی باقی بده تا مست و خوشدل\r\nبه یاران برفشانم عمر باقی\r\nدرونم خون شد از نادیدن دوست\r\nالا تعسا لایام الفراق\r\nدموعی بعدکم لا تحقروها\r\nفکم بحر عمیق من سواقی\r\nدمی با نیکخواهان متفق باش\r\nغنیمت دان امور اتفاقی\r\nبساز ای مطرب خوشخوان خوشگو\r\nبه شعر فارسی صوت عراقی\r\nعروسی بس خوشی ای دختر رز\r\nولی گه گه سزاوار طلاقی\r\nمسیحای مجرد را برازد\r\nکه با خورشید سازد هم وثاقی\r\nوصال دوستان روزی ما نیست\r\nبخوان حافظ غزل های فراقی\r\n', ' تعبیر :  برای این غزل هنوز تعبیری ثبت نشده ! '),
(461, 'کتبت قصه شوقی و مدمعی باکی\r\nبیا که بی تو به جان آمدم ز غمناکی\r\nبسا که گفته ام از شوق با دو دیده خود\r\nایا منازل سلمی فاین سلماک\r\nعجیب واقعه ای و غریب حادثه ای\r\nانا اصطبرت قتیلا و قاتلی شاکی\r\nکه را رسد که کند عیب دامن پاکت\r\nکه همچو قطره که بر برگ گل چکد پاکی\r\nز خاک پای تو داد آب روی لاله و گل\r\nچو کلک صنع رقم زد به آبی و خاکی\r\nصبا عبیرفشان گشت ساقیا برخیز\r\nو هات شمسه کرم مطیب زاکی\r\nدع التکاسل تغنم فقد جری مثل\r\nکه زاد راهروان چستی است و چالاکی\r\nاثر نماند ز من بی شمایلت آری\r\nاری موثر محیای من محیاک\r\nز وصف حسن تو حافظ چگونه نطق زند\r\nکه همچو صنع خدایی ورای ادراکی\r\n', 'تهمتی به شما زده اند که به زودی دست بدخواهان رو می شود. حادثه ای بسیار عجیب اتفاق می افتد که همه چیز را عوض می کند و به آنچه که هدفتان هست می رسید به شرط آنکه زرنگ باشید.کسی به دیدارتان می آید و غم رهایتان می کند. '),
(462, 'یا مبسما یحاکی درجا من اللالی\r\nیا رب چه درخور آمد گردش خط هلالی\r\nحالی خیال وصلت خوش می دهد فریبم\r\nتا خود چه نقش بازد این صورت خیالی\r\nمی ده که گر چه گشتم نامه سیاه عالم\r\nنومید کی توان بود از لطف لایزالی\r\nساقی بیار جامی و از خلوتم برون کش\r\nتا در به در بگردم قلاش و لاابالی\r\nاز چار چیز مگذر گر عاقلی و زیرک\r\nامن و شراب بی غش معشوق و جای خالی\r\nچون نیست نقش دوران در هیچ حال ثابت\r\nحافظ مکن شکایت تا می خوریم حالی\r\nصافیست جام خاطر در دور آصف عهد\r\nقم فاسقنی رحیقا اصفی من الزلال\r\nالملک قد تباهی من جده و جده\r\nیا رب که جاودان باد این قدر و این معالی\r\nمسندفروز دولت کان شکوه و شوکت\r\nبرهان ملک و ملت بونصر بوالمعالی\r\n', 'خودتان را با خیال و رویا گول نزنید و به واقعیات فکر کنید. ناامیدی را کنار بگذارید، خداوند توبه و بازگشت تان را می پذیرد و شما را از سر در گمی نجات می دهد. چهار چیز باعث نجاتتان می شود: خداوند، نماز، ایمان، قرآن. با این چهار چیز روزگارتان تغییر می کند و به شکوه و شوکت می رسید. '),
(463, 'سلام الله ما کر اللیالی\r\nو جاوبت المثانی و المثالی\r\nعلی وادی الاراک و من علیها\r\nو دار باللوی فوق الرمال\r\nدعاگوی غریبان جهانم\r\nو ادعو بالتواتر و التوالی\r\nبه هر منزل که رو آرد خدا را\r\nنگه دارش به لطف لایزالی\r\nمنال ای دل که در زنجیر زلفش\r\nهمه جمعیت است آشفته حالی\r\nز خطت صد جمال دیگر افزود\r\nکه عمرت باد صد سال جلالی\r\nتو می باید که باشی ور نه سهل است\r\nزیان مایه جاهی و مالی\r\nبر آن نقاش قدرت آفرین باد\r\nکه گرد مه کشد خط هلالی\r\nفحبک راحتی فی کل حین\r\nو ذکرک مونسی فی کل حال\r\nسویدای دل من تا قیامت\r\nمباد از شوق و سودای تو خالی\r\nکجا یابم وصال چون تو شاهی\r\nمن بدنام رند لاابالی\r\nخدا داند که حافظ را غرض چیست\r\nو علم الله حسبی من سوالی\r\n', 'با ناله و زاری کار به جایی نمی برید دست به دعا بردارید و به خیل بندگان آشفته حال بپیوندید. باقی عمل خویش را که طولانی است به خوبی سپری می کنید. اگر زیان دیده اید در جایی دیگر مالی به شما می رسد. برای رسیدن به حاجاتتان غیر از خدا پیش کسی دست دراز نکنید که به بدنامی ان نمی ارزد. '),
(464, 'بگرفت کار حسنت چون عشق من کمالی\r\nخوش باش زان که نبود این هر دو را زوالی\r\nدر وهم می نگنجد کاندر تصور عقل\r\nآید به هیچ معنی زین خوبتر مثالی\r\nشد حظ عمر حاصل گر زان که با تو ما را\r\nهرگز به عمر روزی روزی شود وصالی\r\nآن دم که با تو باشم یک سال هست روزی\r\nوان دم که بی تو باشم یک لحظه هست سالی\r\nچون من خیال رویت جانا به خواب بینم\r\nکز خواب می نبیند چشمم بجز خیالی\r\nرحم آر بر دل من کز مهر روی خوبت\r\nشد شخص ناتوانم باریک چون هلالی\r\nحافظ مکن شکایت گر وصل دوست خواهی\r\nزین بیشتر بباید بر هجرت احتمالی\r\n', 'به مرادتان رسیده اید ولی می ترسد آنچه را که سخت به دست آورده اید از دست بدهید، مطمئن باشید که همه ی آنها ماندنی است. اتفاقی می افتد که حتی تصورش هم مایه ی تعجب است و برای شما فرصتی بسیار عالی به شمار می آید. قدر آنچه را که دارید بدانید و دست از گله و شکایت بردارید و نا شکر نباشید که خدای ناکرده باز همه را از دست می دهید. '),
(465, 'رفتم به باغ صبحدمی تا چنم گلی\r\nآمد به گوش ناگهم آواز بلبلی\r\nمسکین چو من به عشق گلی گشته مبتلا\r\nو اندر چمن فکنده ز فریاد غلغلی\r\nمی گشتم اندر آن چمن و باغ دم به دم\r\nمی کردم اندر آن گل و بلبل تاملی\r\nگل یار حسن گشته و بلبل قرین عشق\r\nآن را تفضلی نه و این را تبدلی\r\nچون کرد در دلم اثر آواز عندلیب\r\nگشتم چنان که هیچ نماندم تحملی\r\nبس گل شکفته می شود این باغ را ولی\r\nکس بی بلای خار نچیده ست از او گلی\r\nحافظ مدار امید فرج از مدار چرخ\r\nدارد هزار عیب و ندارد تفضلی\r\n', 'هر کاری را که می خواهید انجام دهید در موردش کمی فکر و تامل کنید اگر عجله کنید باعث پشیمانی تان می شود پس از فضل و عقل خودتان استفاده کنید. کمی هم صبر کنید به آنچه که اراده کرده اید می رسید. در این راه سختی و ضرر زیادی می کشید ولی می ارزد. امید داشته باشید و روزگارتان تغییر می کند. '),
(466, 'این خرقه که من دارم در رهن شراب اولی\r\nوین دفتر بی معنی غرق می ناب اولی\r\nچون عمر تبه کردم چندان که نگه کردم\r\nدر کنج خراباتی افتاده خراب اولی\r\nچون مصلحت اندیشی دور است ز درویشی\r\nهم سینه پر از آتش هم دیده پرآب اولی\r\nمن حالت زاهد را با خلق نخواهم گفت\r\nاین قصه اگر گویم با چنگ و رباب اولی\r\nتا بی سر و پا باشد اوضاع فلک زین دست\r\nدر سر هوس ساقی در دست شراب اولی\r\nاز همچو تو دلداری دل برنکنم آری\r\nچون تاب کشم باری زان زلف به تاب اولی\r\nچون پیر شدی حافظ از میکده بیرون آی\r\nرندی و هوسناکی در عهد شباب اولی\r\n', 'مرحبا بر شما هر آنچه دارید از حق تعالی می دانید حتی نعمت سلامتی را. وقتی به گذشته تان فکر می کنید می بینید چندان کار مفیدی انجام نداده اید. در حال حاضر مصلحت در این است که خاموش باشید. رازتان را با خدایتان در میان بگذارید وگرنه اگر فاش کنید دشمنانتان شکست سختی به شما می دهند، تحمل کنید، خداوند با صابران است. '),
(467, 'زان می عشق کز او پخته شود هر خامی\r\nگر چه ماه رمضان است بیاور جامی\r\nروزها رفت که دست من مسکین نگرفت\r\nزلف شمشادقدی ساعد سیم اندامی\r\nروزه هر چند که مهمان عزیز است ای دل\r\nصحبتش موهبتی دان و شدن انعامی\r\nمرغ زیرک به در خانقه اکنون نپرد\r\nکه نهاده ست به هر مجلس وعظی دامی\r\nگله از زاهد بدخو نکنم رسم این است\r\nکه چو صبحی بدمد در پی اش افتد شامی\r\nیار من چون بخرامد به تماشای چمن\r\nبرسانش ز من ای پیک صبا پیغامی\r\nآن حریفی که شب و روز می صاف کشد\r\nبود آیا که کند یاد ز دردآشامی\r\nحافظا گر ندهد داد دلت آصف عهد\r\nکام دشوار به دست آوری از خودکامی\r\n', 'از رسم روزگار بسیار گله مند هستید مدام در پی برآورده شدن حاجتتان می باشید. به زودی خبری به شما می رسانند که باعث حل شدن مشکلاتتان می شود. بسیار سخت و دشوار به مرادتان خواهید رسید طوری که در این راه پخته می شوید و تجربه های فراوان به دست می آورید. ماه رمضان ماه پر برکت و رسیدن به حاجات شما می باشد. '),
(468, 'که برد به نزد شاهان ز من گدا پیامی\r\nکه به کوی می فروشان دو هزار جم به جامی\r\nشده ام خراب و بدنام و هنوز امیدوارم\r\nکه به همت عزیزان برسم به نیک نامی\r\nتو که کیمیافروشی نظری به قلب ما کن\r\nکه بضاعتی نداریم و فکنده ایم دامی\r\nعجب از وفای جانان که عنایتی نفرمود\r\nنه به نامه پیامی نه به خامه سلامی\r\nاگر این شراب خام است اگر آن حریف پخته\r\nبه هزار بار بهتر ز هزار پخته خامی\r\nز رهم میفکن ای شیخ به دانه های تسبیح\r\nکه چو مرغ زیرک افتد نفتد به هیچ دامی\r\nسر خدمت تو دارم بخرم به لطف و مفروش\r\nکه چو بنده کمتر افتد به مبارکی غلامی\r\nبه کجا برم شکایت به که گویم این حکایت\r\nکه لبت حیات ما بود و نداشتی دوامی\r\nبگشای تیر مژگان و بریز خون حافظ\r\nکه چنان کشنده ای را نکند کس انتقامی\r\n', 'مطمئن باشید که صدای دلتان به خداوند رسیده است. هر چند که فکر می کنید بد نام شده اید ولی باز هم امیدوار باشید همت کنید تا باز هم با نام نیک از شما یاد کنند. علاوه بر اینکه پول ندارید دیگران هم از شما دوری می کنند به خدا توکل کنید بندگی حق تعالی باعث مبارکی بخت تان می شود. به فکر انتقام نباشید. شکوه و شکایت هم نکنید. '),
(469, 'انت روائح رند الحمی و زاد غرامی\r\nفدای خاک در دوست باد جان گرامی\r\nپیام دوست شنیدن سعادت است و سلامت\r\nمن المبلغ عنی الی سعاد سلامی\r\nبیا به شام غریبان و آب دیده من بین\r\nبه سان باده صافی در آبگینه شامی\r\nاذا تغرد عن ذی الاراک طائر خیر\r\nفلا تفرد عن روضها انین حمامی\r\nبسی نماند که روز فراق یار سر آید\r\nرایت من هضبات الحمی قباب خیام\r\nخوشا دمی که درآیی و گویمت به سلامت\r\nقدمت خیر قدوم نزلت خیر مقام\r\nبعدت منک و قد صرت ذائبا کهلال\r\nاگر چه روی چو ماهت ندیده ام به تمامی\r\nو ان دعیت بخلد و صرت ناقض عهد\r\nفما تطیب نفسی و ما استطاب منامی\r\nامید هست که زودت به بخت نیک ببینم\r\nتو شاد گشته به فرماندهی و من به غلامی\r\nچو سلک در خوشاب است شعر نغز تو حافظ\r\nکه گاه لطف سبق می برد ز نظم نظامی\r\n', 'خبری را که برایش انتظار کشیده اید بزودی می رسد که سرشار از سعادت و سلامت و خوشبختی می باشد. انتظار به سر رسید و الان وقت سلام و خیر مقدم است نه گریه کردن. قدوم یار برای شما بسیار پر خیر و برکت است. دلتان شاد می شود. امیدتان که به یار می نشیند دولت به سراغ تان می آید. '),
(470, 'سینه مالامال درد است ای دریغا مرهمی\r\nدل ز تنهایی به جان آمد خدا را همدمی\r\nچشم آسایش که دارد از سپهر تیزرو\r\nساقیا جامی به من ده تا بیاسایم دمی\r\nزیرکی را گفتم این احوال بین خندید و گفت\r\nصعب روزی بوالعجب کاری پریشان عالمی\r\nسوختم در چاه صبر از بهر آن شمع چگل\r\nشاه ترکان فارغ است از حال ما کو رستمی\r\nدر طریق عشقبازی امن و آسایش بلاست\r\nریش باد آن دل که با درد تو خواهد مرهمی\r\nاهل کام و ناز را در کوی رندی راه نیست\r\nره روی باید جهان سوزی نه خامی بی غمی\r\nآدمی در عالم خاکی نمی آید به دست\r\nعالمی دیگر بباید ساخت و از نو آدمی\r\nخیز تا خاطر بدان ترک سمرقندی دهیم\r\nکز نسیمش بوی جوی مولیان آید همی\r\nگریه حافظ چه سنجد پیش استغنای عشق\r\nکاندر این دریا نماید هفت دریا شبنمی\r\n', 'بسیار پریشان حال و دلتنگ هستید. تاب و تحمل ندارید فکر می کنید دیگر قدرت جنگیدن با مشکلات را ندارید و به دنبال کسی می گردید تا در این راهی که قدم گذاشته اید یاریتان کند ولی بدانید کسی کمکتان نمی کند فقط به درگاه خدا التماس کنید. نسیم دولت و مراد می وزد. کارها را برای خودتان سخت نکنید. هر چه آسان بگیرید آسانتر می گذرد. '),
(471, 'ز دلبرم که رساند نوازش قلمی\r\nکجاست پیک صبا گر همی کند کرمی\r\nقیاس کردم و تدبیر عقل در ره عشق\r\nچو شبنمی است که بر بحر می کشد رقمی\r\nبیا که خرقه من گر چه رهن میکده هاست\r\nز مال وقف نبینی به نام من درمی\r\nحدیث چون و چرا درد سر دهد ای دل\r\nپیاله گیر و بیاسا ز عمر خویش دمی\r\nطبیب راه نشین درد عشق نشناسد\r\nبرو به دست کن ای مرده دل مسیح دمی\r\nدلم گرفت ز سالوس و طبل زیر گلیم\r\nبه آن که بر در میخانه برکشم علمی\r\nبیا که وقت شناسان دو کون بفروشند\r\nبه یک پیاله می صاف و صحبت صنمی\r\nدوام عیش و تنعم نه شیوه عشق است\r\nاگر معاشر مایی بنوش نیش غمی\r\nنمی کنم گله ای لیک ابر رحمت دوست\r\nبه کشته زار جگرتشنگان نداد نمی\r\nچرا به یک نی قندش نمی خرند آن کس\r\nکه کرد صد شکرافشانی از نی قلمی\r\nسزای قدر تو شاها به دست حافظ نیست\r\nجز از دعای شبی و نیاز صبحدمی\r\n', 'کرم و لطف خدا شامل حالتان شده است پس چرا این همه مکدر و ناراحت هستید. اینقدر خودتان را با دیگران مقایسه نکنید. از آنچه که قسمتتان شده ننالید در عوض شما با نفس خودتان مرده ای را زنده می کنید و خواهان بسیار دارید. گله نکنید شما هم خریدار دارید دعای سحرگاهی تان مستجاب و نیازهایتان برآورده می شود. انشاالله  '),
(472, 'احمد الله علی معدله السلطان\r\nاحمد شیخ اویس حسن ایلخانی\r\nخان بن خان و شهنشاه شهنشاه نژاد\r\nآن که می زیبد اگر جان جهانش خوانی\r\nدیده نادیده به اقبال تو ایمان آورد\r\nمرحبا ای به چنین لطف خدا ارزانی\r\nماه اگر بی تو برآید به دو نیمش بزنند\r\nدولت احمدی و معجزه سبحانی\r\nجلوه بخت تو دل می برد از شاه و گدا\r\nچشم بد دور که هم جانی و هم جانانی\r\nبرشکن کاکل ترکانه که در طالع توست\r\nبخشش و کوشش خاقانی و چنگزخانی\r\nگر چه دوریم به یاد تو قدح می گیریم\r\nبعد منزل نبود در سفر روحانی\r\nاز گل پارسیم غنچه عیشی نشکفت\r\nحبذا دجله بغداد و می ریحانی\r\nسر عاشق که نه خاک در معشوق بود\r\nکی خلاصش بود از محنت سرگردانی\r\nای نسیم سحری خاک در یار بیار\r\nکه کند حافظ از او دیده دل نورانی\r\n', 'بسیار خوش اقبالید و اراده ی بسیار قوی دارید. معجزه ای در زندگیتان رخ می دهد که مایه ی تعجب همگان می شود. یادتان باشد رمز موفقیتان سعی و کوشش است. به سفر معنوی کربلا می روید که در آنجا دل سرگشته شما آرام می گیرد و چشم و دلتان روشن می شود. '),
(473, 'وقت را غنیمت دان آن قدر که بتوانی\r\nحاصل از حیات ای جان این دم است تا دانی\r\nکام بخشی گردون عمر در عوض دارد\r\nجهد کن که از دولت داد عیش بستانی\r\nباغبان چو من زین جا بگذرم حرامت باد\r\nگر به جای من سروی غیر دوست بنشانی\r\nزاهد پشیمان را ذوق باده خواهد کشت\r\nعاقلا مکن کاری کآورد پشیمانی\r\nمحتسب نمی داند این قدر که صوفی را\r\nجنس خانگی باشد همچو لعل رمانی\r\nبا دعای شبخیزان ای شکردهان مستیز\r\nدر پناه یک اسم است خاتم سلیمانی\r\nپند عاشقان بشنو و از در طرب بازآ\r\nکاین همه نمی ارزد شغل عالم فانی\r\nیوسف عزیزم رفت ای برادران رحمی\r\nکز غمش عجب بینم حال پیر کنعانی\r\nپیش زاهد از رندی دم مزن که نتوان گفت\r\nبا طبیب نامحرم حال درد پنهانی\r\nمی روی و مژگانت خون خلق می ریزد\r\nتیز می روی جانا ترسمت فرومانی\r\nدل ز ناوک چشمت گوش داشتم لیکن\r\nابروی کماندارت می برد به پیشانی\r\nجمع کن به احسانی حافظ پریشان را\r\nای شکنج گیسویت مجمع پریشانی\r\nگر تو فارغی از ما ای نگار سنگین دل\r\nحال خود بخواهم گفت پیش آصف ثانی\r\n', 'قدر لحظات عمر خود را بدان و آن ها را به راحتی از دست نده. راز خود را با نامحرمان در میان نگذار تا دچار دردسر و حسادت دیگران نشوی. برای رسیدن به هدف باید تلاش کنی. مراقب کارهای خود باش و با تعقل و تفکر و صبر و آرامش کار کن تا با سهل انگاری خود را دچار انحراف و شکست نکنی و پشیمان نشوی. '),
(474, 'هواخواه توام جانا و می دانم که می دانی\r\nکه هم نادیده می بینی و هم ننوشته می خوانی\r\nملامتگو چه دریابد میان عاشق و معشوق\r\nنبیند چشم نابینا خصوص اسرار پنهانی\r\nبیفشان زلف و صوفی را به پابازی و رقص آور\r\nکه از هر رقعه دلقش هزاران بت بیفشانی\r\nگشاد کار مشتاقان در آن ابروی دلبند است\r\nخدا را یک نفس بنشین گره بگشا ز پیشانی\r\nملک در سجده آدم زمین بوس تو نیت کرد\r\nکه در حسن تو لطفی دید بیش از حد انسانی\r\nچراغ افروز چشم ما نسیم زلف جانان است\r\nمباد این جمع را یا رب غم از باد پریشانی\r\nدریغا عیش شبگیری که در خواب سحر بگذشت\r\nندانی قدر وقت ای دل مگر وقتی که درمانی\r\nملول از همرهان بودن طریق کاردانی نیست\r\nبکش دشواری منزل به یاد عهد آسانی\r\nخیال چنبر زلفش فریبت می دهد حافظ\r\nنگر تا حلقه اقبال ناممکن نجنبانی\r\n', 'بزرگان درست گفته ان که دل به دل راه دارد و شما مطمئن باشید که او هم به شما فکر می کند. گره از ابروانتان باز کنید که کارها بر وفق مرادتان پیش می رود. ذات خوبی دارید و ایمانتان قوی است پس فرشتگان به شما کمک می کنند. شب را به خواب نگذران که وقت خوبی برای عبادت است همراهانتان بیشتر کارها را دشوار می کنند حلقه ی اقبال به سوی شما می آید. '),
(475, 'گفتند خلایق که تویی یوسف ثانی\r\nچون نیک بدیدم به حقیقت به از آنی\r\nشیرینتر از آنی به شکرخنده که گویم\r\nای خسرو خوبان که تو شیرین زمانی\r\nتشبیه دهانت نتوان کرد به غنچه\r\nهرگز نبود غنچه بدین تنگ دهانی\r\nصد بار بگفتی که دهم زان دهنت کام\r\nچون سوسن آزاده چرا جمله زبانی\r\nگویی بدهم کامت و جانت بستانم\r\nترسم ندهی کامم و جانم بستانی\r\nچشم تو خدنگ از سپر جان گذراند\r\nبیمار که دیده ست بدین سخت کمانی\r\nچون اشک بیندازیش از دیده مردم\r\nآن را که دمی از نظر خویش برانی\r\n', 'حسن و صفات ظاهری و باطنی شما بسیار است. گناه نمی کنید و هر سخنتان به شیرینی شکر است که همه از آن فیض می برند. مرادی دارید که رسیدن به آن را محال می دانید ولی آنقدر مقاوم و پابرجا هستید که هیچ چیز و هیچ کس نمی تواند شما را ناامید کند، پیش کسی گریه نکنید زیرا دشمنانتان شاد می شوند. '),
(476, 'نسیم صبح سعادت بدان نشان که تو دانی\r\nگذر به کوی فلان کن در آن زمان که تو دانی\r\nتو پیک خلوت رازی و دیده بر سر راهت\r\nبه مردمی نه به فرمان چنان بران که تو دانی\r\nبگو که جان عزیزم ز دست رفت خدا را\r\nز لعل روح فزایش ببخش آن که تو دانی\r\nمن این حروف نوشتم چنان که غیر ندانست\r\nتو هم ز روی کرامت چنان بخوان که تو دانی\r\nخیال تیغ تو با ما حدیث تشنه و آب است\r\nاسیر خویش گرفتی بکش چنان که تو دانی\r\nامید در کمر زرکشت چگونه ببندم\r\nدقیقه ایست نگارا در آن میان که تو دانی\r\nیکیست ترکی و تازی در این معامله حافظ\r\nحدیث عشق بیان کن بدان زبان که تو دانی\r\n', 'شما همچون زمین تشنه ای هستید که با قطره قطره ی باران سراب می شود و همچون پرنده ایی که آرزوی پرواز دارد. این امیدواری است که نجات بخش شماست. سرگشتگی تان از فرمانبرداری از نادانان است. پس زیرک باشید معامله ای انجام خواهید داد که بسیار سودمند و ناجی شما از بند گرفتاری است. '),
(477, 'دو یار زیرک و از باده کهن دومنی\r\nفراغتی و کتابی و گوشه چمنی\r\nمن این مقام به دنیا و آخرت ندهم\r\nاگر چه در پی ام افتند هر دم انجمنی\r\nهر آن که کنج قناعت به گنج دنیا داد\r\nفروخت یوسف مصری به کمترین ثمنی\r\nبیا که رونق این کارخانه کم نشود\r\nبه زهد همچو تویی یا به فسق همچو منی\r\nز تندباد حوادث نمی توان دیدن\r\nدر این چمن که گلی بوده است یا سمنی\r\nببین در آینه جام نقش بندی غیب\r\nکه کس به یاد ندارد چنین عجب زمنی\r\nاز این سموم که بر طرف بوستان بگذشت\r\nعجب که بوی گلی هست و رنگ نسترنی\r\nبه صبر کوش تو ای دل که حق رها نکند\r\nچنین عزیز نگینی به دست اهرمنی\r\nمزاج دهر تبه شد در این بلا حافظ\r\nکجاست فکر حکیمی و رای برهمنی\r\n', 'مقام و منزلتی در درگاه خداوند دارید که حاضر نیستید آن را هیچ وجه به مقام دنیوی بفروشید. در مسائل دنیوی هم موفقید و رمز موفقیت تان قناعت است و شکرگزاری خدا. دست فلک و تندباد حوادث شما را بسیار امتحان نموده اما در همه حال صابر بوده و حق را رها نکرده اید. بسیار مواظب باشید تا به دام شیطان نیافتید. فکر حکیمانه ی شما روزگار را سخت شکست داده است. '),
(478, 'نوش کن جام شراب یک منی\r\nتا بدان بیخ غم از دل برکنی\r\nدل گشاده دار چون جام شراب\r\nسر گرفته چند چون خم دنی\r\nچون ز جام بیخودی رطلی کشی\r\nکم زنی از خویشتن لاف منی\r\nسنگسان شو در قدم نی همچو آب\r\nجمله رنگ آمیزی و تردامنی\r\nدل به می دربند تا مردانه وار\r\nگردن سالوس و تقوا بشکنی\r\nخیز و جهدی کن چو حافظ تا مگر\r\nخویشتن در پای معشوق افکنی\r\n', 'در راهی قدم نهاده اید که باید ثابت قدم بوده و گاهی مثل سنگ سخت و گاهی نرم مثل جوی روان باشید یا اینکه از خودتان بگذرید تا دیگران را نجات بخشید. دلتان روشن است و می دانید کاری را که شروع کرده اید حتما به سرانجام می رسانید. اگر امید به خدا داشته باشید غم از دلتان ریشه کن می شود یا علی بگویید که رسیدن به مراد محال نیست. '),
(479, 'صبح است و ژاله می چکد از ابر بهمنی\r\nبرگ صبوح ساز و بده جام یک منی\r\nدر بحر مایی و منی افتاده ام بیار\r\nمی تا خلاص بخشدم از مایی و منی\r\nخون پیاله خور که حلال است خون او\r\nدر کار یار باش که کاریست کردنی\r\nساقی به دست باش که غم در کمین ماست\r\nمطرب نگاه دار همین ره که می زنی\r\nمی ده که سر به گوش من آورد چنگ و گفت\r\nخوش بگذران و بشنو از این پیر منحنی\r\nساقی به بی نیازی رندان که می بده\r\nتا بشنوی ز صوت مغنی هوالغنی\r\n', 'دچار کبر و غرور شده اید سعی کنید خودتان را خلاص کنید و از کارهای حرام دور کرده و به خدا نزدیک شوید. اگر بخواهید همین راه را طی کنید غم به سراغ تان می آید. به راز و نیاز بپردازید تا کامروا باشید. درست است که به چیزی نیاز ندارید اما نیازمند رحمت الهی هستید. '),
(480, 'ای که در کشتن ما هیچ مدارا نکنی\r\nسود و سرمایه بسوزی و محابا نکنی\r\nدردمندان بلا زهر هلاهل دارند\r\nقصد این قوم خطا باشد هان تا نکنی\r\nرنج ما را که توان برد به یک گوشه چشم\r\nشرط انصاف نباشد که مداوا نکنی\r\nدیده ما چو به امید تو دریاست چرا\r\nبه تفرج گذری بر لب دریا نکنی\r\nنقل هر جور که از خلق کریمت کردند\r\nقول صاحب غرضان است تو آن ها نکنی\r\nبر تو گر جلوه کند شاهد ما ای زاهد\r\nاز خدا جز می و معشوق تمنا نکنی\r\nحافظا سجده به ابروی چو محرابش بر\r\nکه دعایی ز سر صدق جز آن جا نکنی\r\n', 'بی گدار به آب می زنید و بدون فکر کاری انجام می دهید که باعث از بین رفتن دار و ندارتان می شود کسانی به شما با چشم امید رجوع می کنند انصاف نیست که آنها را از خودتان برانید. جلوه هایی از حاجات بر شما نمایان شده است از خدا بخواهید تا کامل شود. از سر صدق و وفا دعا کنید. '),
(481, 'بشنو این نکته که خود را ز غم آزاده کنی\r\nخون خوری گر طلب روزی ننهاده کنی\r\nآخرالامر گل کوزه گران خواهی شد\r\nحالیا فکر سبو کن که پر از باده کنی\r\nگر از آن آدمیانی که بهشتت هوس است\r\nعیش با آدمی ای چند پری زاده کنی\r\nتکیه بر جای بزرگان نتوان زد به گزاف\r\nمگر اسباب بزرگی همه آماده کنی\r\nاجرها باشدت ای خسرو شیرین دهنان\r\nگر نگاهی سوی فرهاد دل افتاده کنی\r\nخاطرت کی رقم فیض پذیرد هیهات\r\nمگر از نقش پراگنده ورق ساده کنی\r\nکار خود گر به کرم بازگذاری حافظ\r\nای بسا عیش که با بخت خداداده کنی\r\nای صبا بندگی خواجه جلال الدین کن\r\nکه جهان پرسمن و سوسن آزاده کنی\r\n', 'اگر می خواهید غم به سراغتان نیاید و از غم ها رهایی یابید به این فکر باشید که عاقبت باید به دیار باقی بروید بنابراین بهتر است که توشه ی راهتان از ثواب باشد. بدون زحمت نمی توان به مقام رسید باید تلاش کنید مطمئن باشد کارهای شما بدون اجر نمی ماند و جواب خودتان را دریافت می کنید. '),
(482, 'ای دل به کوی عشق گذاری نمی کنی\r\nاسباب جمع داری و کاری نمی کنی\r\nچوگان حکم در کف و گویی نمی زنی\r\nباز ظفر به دست و شکاری نمی کنی\r\nاین خون که موج می زند اندر جگر تو را\r\nدر کار رنگ و بوی نگاری نمی کنی\r\nمشکین از آن نشد دم خلقت که چون صبا\r\nبر خاک کوی دوست گذاری نمی کنی\r\nترسم کز این چمن نبری آستین گل\r\nکز گلشنش تحمل خاری نمی کنی\r\nدر آستین جان تو صد نافه مدرج است\r\nوان را فدای طره یاری نمی کنی\r\nساغر لطیف و دلکش و می افکنی به خاک\r\nو اندیشه از بلای خماری نمی کنی\r\nحافظ برو که بندگی پادشاه وقت\r\nگر جمله می کنند تو باری نمی کنی\r\n', 'همه چیز مهیاست ولی کاری انجام نمی دهی. تعلل جایز نیست وگرنه همه چیز و همه کس را از دست می دهی و در بوستان زندگی خاری هم نصیبت نمی شود.، دوراندیشی نمی کنی فرصتها را از دست می دهی برای همین است که درخت آرزوهایت به ثمر نمی نشیند. '),
(483, 'سحرگه ره روی در سرزمینی\r\nهمی گفت این معما با قرینی\r\nکه ای صوفی شراب آن گه شود صاف\r\nکه در شیشه برآرد اربعینی\r\nخدا زان خرقه بیزار است صد بار\r\nکه صد بت باشدش در آستینی\r\nمروت گر چه نامی بی نشان است\r\nنیازی عرضه کن بر نازنینی\r\nثوابت باشد ای دارای خرمن\r\nاگر رحمی کنی بر خوشه چینی\r\nنمی بینم نشاط عیش در کس\r\nنه درمان دلی نه درد دینی\r\nدرون ها تیره شد باشد که از غیب\r\nچراغی برکند خلوت نشینی\r\nگر انگشت سلیمانی نباشد\r\nچه خاصیت دهد نقش نگینی\r\nاگر چه رسم خوبان تندخوییست\r\nچه باشد گر بسازد با غمینی\r\nره میخانه بنما تا بپرسم\r\nمال خویش را از پیش بینی\r\nنه حافظ را حضور درس خلوت\r\nنه دانشمند را علم الیقینی\r\n', 'معمایی برایتان پیش آمده که لاینحل است برای حل آن به افراد ناباب مراجعه نکنید چراغ امیدتان روشن می شود و شما به جواب معمای خودتان می رسید. فعلا با غم هایتان سازش کنید با مشورت به آنچه که می خواهید می رسید. کمی تحمل کنید جواب این معما آنقدر با ارزش هست که به خاطر آن زحمت بکشید. '),
(484, 'تو مگر بر لب آبی به هوس بنشینی\r\nور نه هر فتنه که بینی همه از خود بینی\r\nبه خدایی که تویی بنده بگزیده او\r\nکه بر این چاکر دیرینه کسی نگزینی\r\nگر امانت به سلامت ببرم باکی نیست\r\nبی دلی سهل بود گر نبود بی دینی\r\nادب و شرم تو را خسرو مه رویان کرد\r\nآفرین بر تو که شایسته صد چندینی\r\nعجب از لطف تو ای گل که نشستی با خار\r\nظاهرا مصلحت وقت در آن می بینی\r\nصبر بر جور رقیبت چه کنم گر نکنم\r\nعاشقان را نبود چاره بجز مسکینی\r\nباد صبحی به هوایت ز گلستان برخاست\r\nکه تو خوشتر ز گل و تازه تر از نسرینی\r\nشیشه بازی سرشکم نگری از چپ و راست\r\nگر بر این منظر بینش نفسی بنشینی\r\nسخنی بی غرض از بنده مخلص بشنو\r\nای که منظور بزرگان حقیقت بینی\r\nنازنینی چو تو پاکیزه دل و پاک نهاد\r\nبهتر آن است که با مردم بد ننشینی\r\nسیل این اشک روان صبر و دل حافظ برد\r\nبلغ الطاقه یا مقله عینی بینی\r\nتو بدین نازکی و سرکشی ای شمع چگل\r\nلایق بندگی خواجه جلال الدینی\r\n', 'اگر کارهایتان بر مبنای هوی و هوس بگذارید مایه ی آشوب و فتنه می شود. امانتی دست شماست آن را به سلامتی دست صاحبش برسانید. شایسته تر از آن هستید که بخواهید با افراد پست معاشرت کنید. فعلا جز صبر در پیش رقیب چاره ی دیگری ندارید. شما که انسانی پاکدل هستید حیف است که سرکشی کنید. '),
(485, 'ساقیا سایه ابر است و بهار و لب جوی\r\nمن نگویم چه کن ار اهل دلی خود تو بگوی\r\nبوی یک رنگی از این نقش نمی آید خیز\r\nدلق آلوده صوفی به می ناب بشوی\r\nسفله طبع است جهان بر کرمش تکیه مکن\r\nای جهان دیده ثبات قدم از سفله مجوی\r\nدو نصیحت کنمت بشنو و صد گنج ببر\r\nاز در عیش درآ و به ره عیب مپوی\r\nشکر آن را که دگربار رسیدی به بهار\r\nبیخ نیکی بنشان و ره تحقیق بجوی\r\nروی جانان طلبی آینه را قابل ساز\r\nور نه هرگز گل و نسرین ندمد ز آهن و روی\r\nگوش بگشای که بلبل به فغان می گوید\r\nخواجه تقصیر مفرما گل توفیق ببوی\r\nگفتی از حافظ ما بوی ریا می آید\r\nآفرین بر نفست باد که خوش بردی بوی\r\n', 'حالا که همه چیز فراهم است خوش باش و این روزها را غنیمت دان. درب های رحمت به سویت باز شده است ولی مراقب باش کبر و غرور باعث نشود که غافل شوی زیرا خوشی نیز ماندگار نیست. از این روزها برای فرداهای بهتر استفاده کن. '),
(486, 'بلبل ز شاخ سرو به گلبانگ پهلوی\r\nمی خواند دوش درس مقامات معنوی\r\nیعنی بیا که آتش موسی نمود گل\r\nتا از درخت نکته توحید بشنوی\r\nمرغان باغ قافیه سنجند و بذله گوی\r\nتا خواجه می خورد به غزل های پهلوی\r\nجمشید جز حکایت جام از جهان نبرد\r\nزنهار دل مبند بر اسباب دنیوی\r\nاین قصه عجب شنو از بخت واژگون\r\nما را بکشت یار به انفاس عیسوی\r\nخوش وقت بوریا و گدایی و خواب امن\r\nکاین عیش نیست درخور اورنگ خسروی\r\nچشمت به غمزه خانه مردم خراب کرد\r\nمخموریت مباد که خوش مست می روی\r\nدهقان سالخورده چه خوش گفت با پسر\r\nکای نور چشم من بجز از کشته ندروی\r\nساقی مگر وظیفه حافظ زیاده داد\r\nکآشفته گشت طره دستار مولوی\r\n', 'پایبند به اصول دنیوی نباش به معنویات و روحیات خودت هم بپرداز و ایمانت را قویتر کن زیرا همه هر آنچه از مادیات که دارند در این دنیا باقی گذاشته و معنویات و کارهای خوب و بد را با خود به دنیای آخرت می برند. زمانی بخت با تو یار است که فرش زیر پایت حصیر باشد ولی دلت خوش و راحت و آرام باشد. '),
(487, 'ای بی خبر بکوش که صاحب خبر شوی\r\nتا راهرو نباشی کی راهبر شوی\r\nدر مکتب حقایق پیش ادیب عشق\r\nهان ای پسر بکوش که روزی پدر شوی\r\nدست از مس وجود چو مردان ره بشوی\r\nتا کیمیای عشق بیابی و زر شوی\r\nخواب و خورت ز مرتبه خویش دور کرد\r\nآن گه رسی به خویش که بی خواب و خور شوی\r\nگر نور عشق حق به دل و جانت اوفتد\r\nبالله کز آفتاب فلک خوبتر شوی\r\nیک دم غریق بحر خدا شو گمان مبر\r\nکز آب هفت بحر به یک موی تر شوی\r\nاز پای تا سرت همه نور خدا شود\r\nدر راه ذوالجلال چو بی پا و سر شوی\r\nوجه خدا اگر شودت منظر نظر\r\nزین پس شکی نماند که صاحب نظر شوی\r\nبنیاد هستی تو چو زیر و زبر شود\r\nدر دل مدار هیچ که زیر و زبر شوی\r\nگر در سرت هوای وصال است حافظا\r\nباید که خاک درگه اهل هنر شوی\r\n', 'تو که خودت بی تجربه ای چگونه به دیگران امر و نهی می کنی. تو که از عشق بویی نبرده ای چطوری صحبت از عشق می کنی و به راه راست دعوت می کنی. اول خودت را بساز و روزگار را تجربه کن. وقتی که حس کردی نور خدا دلت را روشن کرده آن وقت حق رهبری داری و روزگارت تغییر می کند و اگر حاجتی داری باید با خدا باشی. '),
(488, 'سحرم هاتف میخانه به دولتخواهی\r\nگفت بازآی که دیرینه این درگاهی\r\nهمچو جم جرعه ما کش که ز سر دو جهان\r\nپرتو جام جهان بین دهدت آگاهی\r\nبر در میکده رندان قلندر باشند\r\nکه ستانند و دهند افسر شاهنشاهی\r\nخشت زیر سر و بر تارک هفت اختر پای\r\nدست قدرت نگر و منصب صاحب جاهی\r\nسر ما و در میخانه که طرف بامش\r\nبه فلک بر شد و دیوار بدین کوتاهی\r\nقطع این مرحله بی همرهی خضر مکن\r\nظلمات است بترس از خطر گمراهی\r\nاگرت سلطنت فقر ببخشند ای دل\r\nکمترین ملک تو از ماه بود تا ماهی\r\nتو دم فقر ندانی زدن از دست مده\r\nمسند خواجگی و مجلس تورانشاهی\r\nحافظ خام طمع شرمی از این قصه بدار\r\nعملت چیست که فردوس برین می خواهی\r\n', 'باز هم راه برگشت برایت باز است و خداوند توبه ی تو را می پذیرد. سرت را به درگاه حق پائین انداز و تقصیر کارهایت را به گردن روزگار نیانداز از خدا کمک بگیر، راهی را پیش گرفته ای که بسیار تاریک و پرخطر است. توقع خود را کم کن چه کاری کرده ای که حالا توقع بهشت داری. '),
(489, 'ای در رخ تو پیدا انوار پادشاهی\r\nدر فکرت تو پنهان صد حکمت الهی\r\nکلک تو بارک الله بر ملک و دین گشاده\r\nصد چشمه آب حیوان از قطره سیاهی\r\nبر اهرمن نتابد انوار اسم اعظم\r\nملک آن توست و خاتم فرمای هر چه خواهی\r\nدر حکمت سلیمان هر کس که شک نماید\r\nبر عقل و دانش او خندند مرغ و ماهی\r\nباز ار چه گاه گاهی بر سر نهد کلاهی\r\nمرغان قاف دانند آیین پادشاهی\r\nتیغی که آسمانش از فیض خود دهد آب\r\nتنها جهان بگیرد بی منت سپاهی\r\nکلک تو خوش نویسد در شان یار و اغیار\r\nتعویذ جان فزایی افسون عمر کاهی\r\nای عنصر تو مخلوق از کیمیای عزت\r\nو ای دولت تو ایمن از وصمت تباهی\r\nساقی بیار آبی از چشمه خرابات\r\nتا خرقه ها بشوییم از عجب خانقاهی\r\nعمریست پادشاها کز می تهیست جامم\r\nاینک ز بنده دعوی و از محتسب گواهی\r\nگر پرتوی ز تیغت بر کان و معدن افتد\r\nیاقوت سرخ رو را بخشند رنگ کاهی\r\nدانم دلت ببخشد بر عجز شب نشینان\r\nگر حال بنده پرسی از باد صبحگاهی\r\nجایی که برق عصیان بر آدم صفی زد\r\nما را چگونه زیبد دعوی بی گناهی\r\nحافظ چو پادشاهت گه گاه می برد نام\r\nرنجش ز بخت منما بازآ به عذرخواهی\r\n', 'بخت و اقبال و سعادت همراه تو است. به زودی به کام و آرزوی خود می رسی. در هنگام توانایی و سعادت به نیازمندان خدمت کن تا به این وسیله از نعمت های خداوند سپاسگزار باشی. غرور و خودپسندی را از خود دور کن. '),
(490, 'در همه دیر مغان نیست چو من شیدایی\r\nخرقه جایی گرو باده و دفتر جایی\r\nدل که آیینه شاهیست غباری دارد\r\nاز خدا می طلبم صحبت روشن رایی\r\nکرده ام توبه به دست صنم باده فروش\r\nکه دگر می نخورم بی رخ بزم آرایی\r\nنرگس ار لاف زد از شیوه چشم تو مرنج\r\nنروند اهل نظر از پی نابینایی\r\nشرح این قصه مگر شمع برآرد به زبان\r\nور نه پروانه ندارد به سخن پروایی\r\nجوی ها بسته ام از دیده به دامان که مگر\r\nدر کنارم بنشانند سهی بالایی\r\nکشتی باده بیاور که مرا بی رخ دوست\r\nگشت هر گوشه چشم از غم دل دریایی\r\nسخن غیر مگو با من معشوقه پرست\r\nکز وی و جام می ام نیست به کس پروایی\r\nاین حدیثم چه خوش آمد که سحرگه می گفت\r\nبر در میکده ای با دف و نی ترسایی\r\nگر مسلمانی از این است که حافظ دارد\r\nآه اگر از پی امروز بود فردایی\r\n', 'بسیار دلتنگ هستید و به دنبال کسی می گردید که درد دلتان را به او بگوئید ولی کسی را پیدا نمی کنید از غصه مثل شمع آب می شوید بدون اینکه پروانه ای پیش رویتان باشد. شاید یار از شما دور باشد ولی خدا با شماست و ندایی در دلتان می گوید که فردایتان حتما از امروزتان می باشد. '),
(491, 'به چشم کرده ام ابروی ماه سیمایی\r\nخیال سبزخطی نقش بسته ام جایی\r\nامید هست که منشور عشقبازی من\r\nاز آن کمانچه ابرو رسد به طغرایی\r\nسرم ز دست بشد چشم از انتظار بسوخت\r\nدر آرزوی سر و چشم مجلس آرایی\r\nمکدر است دل آتش به خرقه خواهم زد\r\nبیا ببین که که را می کند تماشایی\r\nبه روز واقعه تابوت ما ز سرو کنید\r\nکه می رویم به داغ بلندبالایی\r\nزمام دل به کسی داده ام من درویش\r\nکه نیستش به کس از تاج و تخت پروایی\r\nدر آن مقام که خوبان ز غمزه تیغ زنند\r\nعجب مدار سری اوفتاده در پایی\r\nمرا که از رخ او ماه در شبستان است\r\nکجا بود به فروغ ستاره پروایی\r\nفراق و وصل چه باشد رضای دوست طلب\r\nکه حیف باشد از او غیر او تمنایی\r\nدرر ز شوق برآرند ماهیان به نثار\r\nاگر سفینه حافظ رسد به دریایی\r\n', 'با خیال روی یار زندگی می کنید و امیدتان به جوابی است که او می دهد. دیگر از انتظار خسته شده اید و دلتان محزون شده و از آتش عشق می سوزید. ناامید شده اید و حاضرید برای رسیدن به یار هر کاری انجام دهید. امیدوار باشید رضایتان به رضای خدا باشد زیرا به آنچه که طلب کرده اید می رسید. '),
(492, 'سلامی چو بوی خوش آشنایی\r\nبدان مردم دیده روشنایی\r\nدرودی چو نور دل پارسایان\r\nبدان شمع خلوتگه پارسایی\r\nنمی بینم از همدمان هیچ بر جای\r\nدلم خون شد از غصه ساقی کجایی\r\nز کوی مغان رخ مگردان که آن جا\r\nفروشند مفتاح مشکل گشایی\r\nعروس جهان گر چه در حد حسن است\r\nز حد می برد شیوه بی وفایی\r\nدل خسته من گرش همتی هست\r\nنخواهد ز سنگین دلان مومیایی\r\nمی صوفی افکن کجا می فروشند\r\nکه در تابم از دست زهد ریایی\r\nرفیقان چنان عهد صحبت شکستند\r\nکه گویی نبوده ست خود آشنایی\r\nمرا گر تو بگذاری ای نفس طامع\r\nبسی پادشایی کنم در گدایی\r\nبیاموزمت کیمیای سعادت\r\nز همصحبت بد جدایی جدایی\r\nمکن حافظ از جور دوران شکایت\r\nچه دانی تو ای بنده کار خدایی\r\n', 'با کسانی دوستی و رفاقت کنید که حق دوستی را بجا آورند، پارسا باشند و زخم دلتان را مرهمی گذارند نه اینکه عهد بشکنند و حق آشنایی را فراموش کنند. همتی کنید تا دلتان باز هم مثل موم نرم شود. از همنشینی با یار بد پرهیز کنید تا سعادتمند شوید. شکایت نکنید همه ی این مسائل کار خدا بوده تا به خودتان بیایید. '),
(493, 'ای پادشه خوبان داد از غم تنهایی\r\nدل بی تو به جان آمد وقت است که بازآیی\r\nدایم گل این بستان شاداب نمی ماند\r\nدریاب ضعیفان را در وقت توانایی\r\nدیشب گله زلفش با باد همی کردم\r\nگفتا غلطی بگذر زین فکرت سودایی\r\nصد باد صبا این جا با سلسله می رقصند\r\nاین است حریف ای دل تا باد نپیمایی\r\nمشتاقی و مهجوری دور از تو چنانم کرد\r\nکز دست بخواهد شد پایاب شکیبایی\r\nیا رب به که شاید گفت این نکته که در عالم\r\nرخساره به کس ننمود آن شاهد هرجایی\r\nساقی چمن گل را بی روی تو رنگی نیست\r\nشمشاد خرامان کن تا باغ بیارایی\r\nای درد توام درمان در بستر ناکامی\r\nو ای یاد توام مونس در گوشه تنهایی\r\nدر دایره قسمت ما نقطه تسلیمیم\r\nلطف آن چه تو اندیشی حکم آن چه تو فرمایی\r\nفکر خود و رای خود در عالم رندی نیست\r\nکفر است در این مذهب خودبینی و خودرایی\r\nزین دایره مینا خونین جگرم می ده\r\nتا حل کنم این مشکل در ساغر مینایی\r\nحافظ شب هجران شد بوی خوش وصل آمد\r\nشادیت مبارک باد ای عاشق شیدایی\r\n', 'ای که از خوبی زبانزد هستی حال وقت انجام کار ثواب است آن را پس نزن. همیشه از این فرصت ها به دست نمی آید. به زیردستان خود بیشتر توجه کن. حریفانت دارند از تو پیشی می گیرند پس تو هم باید مثل باد راه را طی کنی تا درد تو درمان شود، به کام دل می رسی، تسلیم قسمت باش چون لطف خدا شاملت می شود. به وصال می رسی. شاد باش. '),
(494, 'ای دل گر از آن چاه زنخدان به درآیی\r\nهر جا که روی زود پشیمان به درآیی\r\nهش دار که گر وسوسه عقل کنی گوش\r\nآدم صفت از روضه رضوان به درآیی\r\nشاید که به آبی فلکت دست نگیرد\r\nگر تشنه لب از چشمه حیوان به درآیی\r\nجان می دهم از حسرت دیدار تو چون صبح\r\nباشد که چو خورشید درخشان به درآیی\r\nچندان چو صبا بر تو گمارم دم همت\r\nکز غنچه چو گل خرم و خندان به درآیی\r\nدر تیره شب هجر تو جانم به لب آمد\r\nوقت است که همچون مه تابان به درآیی\r\nبر رهگذرت بسته ام از دیده دو صد جوی\r\nتا بو که تو چون سرو خرامان به درآیی\r\nحافظ مکن اندیشه که آن یوسف مه رو\r\nبازآید و از کلبه احزان به درآیی\r\n', 'دچار وسوسه شده ای بر سر دو راهی مانده ای کاری کن که سر از بهشت در بیاوری نه جهنم. درست است که تشنه هستی ولی نداری و تشنگی بهتر از گناه کردن است. خدا پاداش صبر و اجر ثواب تو را می دهد و سربلند از راهی که انتخاب کرده ای در می آیی زیرا فکر درست و راه راست را طی کردی تا خانه ی محزون تو شاد شود. '),
(495, 'می خواه و گل افشان کن از دهر چه می جویی\r\nاین گفت سحرگه گل بلبل تو چه می گویی\r\nمسند به گلستان بر تا شاهد و ساقی را\r\nلب گیری و رخ بوسی می نوشی و گل بویی\r\nشمشاد خرامان کن و آهنگ گلستان کن\r\nتا سرو بیاموزد از قد تو دلجویی\r\nتا غنچه خندانت دولت به که خواهد داد\r\nای شاخ گل رعنا از بهر که می رویی\r\nامروز که بازارت پرجوش خریدار است\r\nدریاب و بنه گنجی از مایه نیکویی\r\nچون شمع نکورویی در رهگذر باد است\r\nطرف هنری بربند از شمع نکورویی\r\nآن طره که هر جعدش صد نافه چین ارزد\r\nخوش بودی اگر بودی بوییش ز خوش خویی\r\nهر مرغ به دستانی در گلشن شاه آمد\r\nبلبل به نواسازی حافظ به غزل گویی\r\n', 'دیگر از خدا چه می خواهید. به دنبال چه چیزی می گردید همه چیز در کنار شماست، چشمانتان را باز کنید گلستانی از معرفت و دلجویی در کنارتان هست. امروز که خریداری دارید می توانید گنج عشق طلب کنید وگرنه فردا دیر است. خوش اخلاقی تان باعث جذب دیگران است و این نوای دلتان هست که حافظ را به غزل گویی تشویق می کند. ');
